g2:c.PROCEDURE.g2:a.TEXT:
_jmail-on-unsolicited-email(email: structure)

{
  Not currently invoked by bridge.
}

begin

    post "Received unsolicited e-mail:
[email]";

end

_jmail-get-gsi-config() = (structure)

  io:class gsi-interface;
  names: sequence = sequence (the symbol jmail-delivery-confirmation, the symbol download-attachment-directory-path);

  config: structure= structure ( );
  PropertyName: symbol; 
  val: item-or-value; 
  err: class error;
  ClassName: symbol = the symbol jmail-interface;

begin

  begin
    io = call g2-current-remote-interface();
  end on error (err)
    delete err;
    return config;
  end;
    
  if there exists a gsi-interface jmail that is of the class named by ClassName such that (jmail is the same object as io) then begin
    
    { --- By design we should not expect an error, i.e. all these attributes should exist and have a value, but to be on the safe side, we still trap errors }
    for PropertyName = each symbol in names do

      begin
        val = the value that is an attribute of jmail named by PropertyName;
        config = change-evaluated-attribute (config , propertyName, val);
      end on error (err)
        delete err without permanence checks;
      end;

    end; 
  end;

  return config;
end

_jmail-on-error(msg: value) = (integer)

  io:class network-interface;
  s: symbol;
  p: class procedure;
  err, jerr:class error;

begin

  begin
    io = call g2-current-remote-interface();
  end on error (err)
    delete err;
    return 0 
  end;

  s = the jmail-bridge-error-procedure-callback of io;   

  if there exists a procedure p named by s then
    call p(io, msg);

  return 1;
end

_jmail-on-delivery-report(info: value) = (integer)

  io: class network-interface;
  s: symbol;
  p: class procedure;
  err: class error;

begin

  begin
    io = call g2-current-remote-interface();
  end on error (err)
    delete err;
    return 0
  end; 

  s = the jmail-bridge-delivery-confirmation-procedure-callback of io;

  if there exists a procedure p named by s then
    call p(io, info);

  return 1;

end

_jmail-startup (Object: class gfr-startup-object, InitialStartup: truth-value, Modules: class symbol-list, Client: class object)
 
  devstuffName: symbol = the symbol gdev-internals-workspaces; 
  Err: class error;

begin

  {--- For devstuff development environment } 
  if the item named by devstuffName exists and _jmail-internals is not a member of the item-list named by devstuffName then
    insert _jmail-internals at the end of the item-list named by devstuffName;

  {--- No startup required for module restart }
  if not(InitialStartup) then return;

end on error (Err)
  call gerr-dispatch (Err, this procedure);
end
