g2:c.PROCEDURE.g2:a.TEXT:
reset-console()
display:class hla-display;
begin
for display = each hla-display upon console do
   change the background-color of display to blue;
end;
change the text of RTIEXEC-DISPLAY to "RTIEXEC";
change the text of DISPLAY-HLA-BRIDGE-SERVER to "HLA Bridge";
change the text of DISPLAY-SERVER-FEDERATION to "Federation Connection";
change the text of DISPLAY-SERVER-PUBLISH to "Publish Objects";
change the text of DISPLAY-SERVER-TIME-CONSTRAINED to "Time Constrained";
change the text of DISPLAY-HLA-BRIDGE-CLIENT to "HLA Bridge";
change the text of DISPLAY-CLIENT-FEDERATION to "Federation Connection";
change the text of DISPLAY-CLIENT-PUBLISH to "Publish Objects";
change the text of DISPLAY-CLIENT-TIME-REGULATION to "Time Regulation";
change the text of DISPLAY-C-SERVER to "C++ Server Process"

end

display(display:class hla-display, msg:text, display-color:symbol)
begin
change the text of display to msg;
case (display-color) of
red: change the background-color of display to red;
green: change the background-color of display to green;
blue: change the background-color of display to blue;
gold: change the background-color of display to gold;
otherwise : change the background-color of display to black;
end
end

g2:c.METHOD.g2:a.TEXT:
grtl-show-properties (target: class a-g2hla-configuration {The object to view.}, Win: class ui-client-item {Display it in this view.}) = (truth-value)

{
This method will open the property dialogs of an object if defined. Returns true if the OK button of the dialog was activated (and therefore applied changes). Otherwise it returns false if no changes were applied.
}
  ret: truth-value = true;
  dlgDef: class gdu-dialog-definition;

begin

  if grtl-use-native-user-interface(win)  and there exists a gdu-dialog-definition dlgDef such that (the gdu-target-class of dlgDef = the class of target or the gdu-target-class of dlgDef = the symbol that is a superior-class of the class of target) then

    ret = call gdu-show-native-dialog(target, dlgDef, the symbol g2i-dialog-instance, true, win)
  else
    ret = call next method;

  return ret;
end

g2i-dialog-specify-components (Target: class a-g2hla-configuration, Dlg: class gdu-dialog-definition, Win: class g2-window)

  x:integer = 0;
  w:integer = 1;
  y:integer = 2;
  h:integer;
  start-y:integer;

begin
  start-y = 1;
  h = 1;
  call gdu-add-text-box-control(dlg, the symbol rti-exec-file, the symbol rtiexec-process-exec-file, true, true, false, x, y, w, h);
  y = y + h;

  h = 1;
  call gdu-add-text-box-control(dlg, the symbol hla-bridge-exec-file, the symbol g2-hla-bridge-exec-file, true, true, false, x, y, w, h);
  y = y + h;

  h = 1;
  call gdu-add-text-box-control(dlg, the symbol c-process-exec-file, the symbol c-server-process-exec-file, true, true, false, x, y, w, h);
  y = y + h;

  call gdu-add-group-control(dlg, the symbol exec-group, "Executables", x, start-y, w, y - start-y);
  start-y = y; y = y + 1;

  h = 1;
  call gdu-add-check-box-control(dlg, the symbol step-mode, the symbol _step-demo, true, true, x, y, w, h);
  y = y + h;

  call gdu-add-group-control(dlg, the symbol options-group, "Demo Options", x, start-y, w, y - start-y);
  y = y + 1;

  call grtl-dialog-set-title(target, dlg);
end

g2:c.PROCEDURE.g2:a.TEXT:
_stop-servers-and-rtiexec(cfg: class a-g2hla-configuration, io: class g2-interface-to-hla, win: class ui-client-item)

    err-sym: symbol;
    err-text: text;
    destroy-federation-excution: truth-value;
    N: integer;
    pid, tm: float;
    proc-exists: truth-value;
    hla-class: class hla-object-model-definition;

begin 
    { -----  Kill C server program if necessary ---- }
    pid = the _c-process-pid of cfg;
    conclude that the _c-process-pid of cfg = 0.0;

    proc-exists = call g2-process-exists (pid);
    if proc-exists then
        call g2-kill-process (pid);

    call display(display-c-server, "C++ Server Offline", the symbol blue); 

    { ----- Resign and destroy the federation ----- }
    call _status-update-msg ("Resigning and destroying the federation", Win);
    call display(display-client-federation,"Resigning from Federation", the symbol gold); 
 
    if the destroy-federation-execution of cfg = the symbol on then
        destroy-federation-excution = true
    else
        destroy-federation-excution = false;

    err-sym, err-text = call ghla-resign-and-destroy-federation (io, destroy-federation-excution);
    if err-sym /= the symbol NO-ERROR then
        signal err-sym, err-text; 

    call display(display-client-federation,"Resigned from Federation", the symbol blue); 


    { ----- Disconnect form bridge and kill the process -----}
    call _status-update-msg("Disconnecting from bridge and killing process", Win);

    change the text of the gsi-connection-configuration of io to "none";    
    call display(display-hla-bridge-client,"HLA Client Bridge Offline", the symbol blue);
 
    // Kill client bridge process (if it exists)
    pid = the client-bridge-pid of cfg;
    conclude that the client-bridge-pid of cfg = 0.0;
    proc-exists = call g2-process-exists (pid);
    if proc-exists then
        call g2-kill-process (pid);

    // Kill server bridge process (if it exists)
    pid = the server-bridge-pid of cfg;
    conclude that the server-bridge-pid of cfg = 0.0;
    proc-exists = call g2-process-exists(pid);
    if proc-exists then
      call g2-kill-process(pid);

    { ----- Only need to continue if we need to kill RtiExec ----- }
    if not(destroy-federation-excution) then
        return; 


    { ----- kill RtiExec -----}
    call _status-update-msg("Killing RtiExec", Win);

    pid = the _rtiexec-process-pid of cfg;
    conclude that the _rtiexec-process-pid of cfg = 0.0;

    proc-exists = call g2-process-exists (pid);
    if proc-exists then
        call g2-kill-process (pid); 

    call display(rtiexec-display, "RTIEXEC Offline",the symbol blue); 
end

_connect-to-bridge(io: class gsi-interface, connection-cmd : text, process-cmd: text, last-pid: float, Win: class ui-client-item ) = (float)
    pid: float = 0.0; 
    I: integer;
begin
    { Already connected ? }
    if the gsi-interface-status of io = 2 then
        return pid;

    { Check if bridge process is running and reachable. }
    if the gsi-interface-status of io < 2 then begin

        i = 10;
        repeat 
            if the gsi-interface-status of io <= 0 then
                change the text of the gsi-connection-configuration of io to connection-cmd ;
 
            do in parallel until one completes
                wait until the gsi-interface-status of io receives a value;
                wait for 1 second;
            end;

            I = i - 1;
            exit if the gsi-interface-status of io = 2 or I = 0;
        end; 
    end;

    { If we are  still not connected, lets launch the process }
    if the gsi-interface-status of io < 2 then begin

        pid = call _launch-a-process (last-pid, process-cmd, Win);

        i = 20;
        repeat 
            if the gsi-interface-status of io <= 0 OR (I= 10 and the gsi-interface-status of io <= 2) then
                change the text of the gsi-connection-configuration of io to connection-cmd;
 
            do in parallel until one completes
                wait until the gsi-interface-status of io receives a value;
                wait for 1 second;
            end;

            exit if the gsi-interface-status of io = 2;

            i = i - 1;
            if i = 0 then begin
                change the text of the gsi-connection-configuration of io to "none";
                signal the symbol ERROR-CONNECTION, "demo interface connection to bridge failed. Ensure the bridge process is running.";
            end;
        end; 
    end; 

    return pid;
end

_create-rtiexec(cfg: class a-g2hla-configuration, io: class g2-interface-to-hla, Win: class ui-client-item)

    err-sym: symbol;
    err-text: text;
    pid: float;

begin
    { ----- We need to spawn the rtiexec ---- }
   call _status-update-msg("Launching RtiExec.", Win);
   call display(rtiexec-display, "Launching RTIEXEC", the symbol gold);

   pid = call _launch-a-process (the _rtiexec-process-pid of cfg, the rtiexec-process-exec-file of cfg ,  Win);

    conclude that the _rtiexec-process-pid of cfg = pid;
    if pid < 0 then
        begin
           call display(rtiexec-display, "RTIEXEC Failure",the symbol red);
        signal the symbol NO-RTIEXEC-PROCESS, "Could not start RTIExec process";
        end 
        else call display(rtiexec-display, "RTIEXEC Online",the symbol green); 
end

_launch-a-process(last-pid: float, cmd: text, Win: class ui-client-item) = (float)
    pid: float;
    proc-exists: truth-value;
    I: integer;
begin
    pid = last-pid;

    if pid > 0.0 then       proc-exists = call g2-process-exists (pid)
    else                      proc-exists = false;

    if not(proc-exists) then begin
        call _status-update-msg("Launching [cmd]", Win);
{
        if the text of the g2-window-operating-system-type of Win = "win32" then
            pid = call g2-spawn-process-to-run-command-line (cmd)
        else 
            pid = call g2-spawn-process-with-arguments (cmd);
}
        pid = call g2-spawn-process-with-arguments (cmd); 

        if pid > 0 then begin
            wait for 4 seconds;

            I = 5;
            repeat
                wait for 1 second;
                I = I - 1;
                proc-exists = call g2-process-exists (pid);

                exit if I = 0 or proc-exists ;
            end;
        end;
    end;

    if not(proc-exists) then
        return -1;
 
    return pid;
end

_initialize-hla-public-activities(cfg: class a-g2hla-configuration, io: class g2-interface-to-hla, Win: class ui-client-item)

    err-sym: symbol;
    err-text: text; 
    sym: symbol;
    N, I: integer;
    hla-attr-handles: class float-array;
    hla-class-handle: float;
    hla-class: class  hla-object-model-definition;
    object: class object;
begin 
    call display(display-client-publish,"HLA Object Configuration", the symbol gold); 

    { ----- Subscribed to HLA classes ----- }
    for hla-class = each hla-object-model-definition do

        N = the array-length of the hla-subscribed-attribute-names of hla-class;
        if N > 0 then begin

            call _status-update-msg ("Subscribing hla object class [the hla-class-name of hla-class], [N] attributes, g2-class [the g2-class-name of hla-class]", Win);
 
            sym = the g2-class-name of hla-class;
            create a instance object of the class named by sym;

            err-sym, err-text, hla-class-handle, hla-attr-handles = call ghla-subscribe-object-class-attributes (io, the hla-class-name of hla-class, object, the hla-subscribed-attribute-names of hla-class, true); 

            conclude that the hla-class-handle of hla-class = hla-class-handle;
 
            N = the array-length of hla-attr-handles;
            change the array-length of the hla-subscribed-attribute-handles of hla-class to N;

            if N > 0 then
                call g2-array-copy(hla-attr-handles, the hla-subscribed-attribute-handles of hla-class);

            delete hla-attr-handles;
            delete object;

            if err-sym /= the symbol NO-ERROR then
                signal err-sym, err-text;  
        end;
    end;

 
    { ----- Publish HLA classes ----- }
    for hla-class = each hla-object-model-definition do

        N = the array-length of the hla-published-attribute-names of hla-class;
        if N > 0 then begin

            call _status-update-msg ("Publishing hla object class [the hla-class-name of hla-class], [N] Attributes, G2-Class: [the g2-class-name of hla-class]", Win);

            sym = the g2-class-name of hla-class;
            create a instance object of the class named by sym;

           err-sym, err-text, hla-class-handle, hla-attr-handles = call ghla-publish-object-class (io, the hla-class-name of hla-class, object,the hla-published-attribute-names of hla-class); 

            conclude that the hla-class-handle of hla-class = hla-class-handle;
 
            N = the array-length of hla-attr-handles;
            change the array-length of the hla-published-attribute-handles of hla-class to N;

            if N > 0 then
                call g2-array-copy(hla-attr-handles, the hla-published-attribute-handles of hla-class);

            delete hla-attr-handles;
            delete object;

            if err-sym /= the symbol NO-ERROR then
                signal err-sym, err-text;  
        end;
    end;
 
    call display(display-client-publish,"HLA Object Published", the symbol green); 
 
    { ----- Enable advisory system ----- }
    call _status-update-msg ("Enabling class relevance advisory switch", Win);
    err-sym, err-text = call ghla-enable-class-relevance-advisory-switch (io);

    if err-sym /= the symbol NO-ERROR then
        signal err-sym, err-text; 


    { ----- Enable attribute advisory relevance ----- }
    call _status-update-msg ("Enabling attribute relevance advisory switch", Win);
    err-sym, err-text = call ghla-enable-attribute-relevance-advisory-switch (io);

    if err-sym /= the symbol NO-ERROR then
        signal err-sym, err-text; 

 
    { ----- Enable time constrained. We wait until we get a reply from the request, i.e. the time advance got granted. ----- }
{
    call _status-update-msg ("Enabling time constrained", Win);
    err-sym, err-text = call ghla-enable-time-constrained (io);

     if err-sym /= the symbol NO-ERROR then
        signal err-sym, err-text; 

    i = 30;
    repeat 
        do in parallel until one completes
            wait until the federate-time-advance-granted of io receives a value;
            wait for 1 second;
        end;

        exit if the federate-time-advance-granted of io;

        i = i - 1;
        if i = 0 then
                signal the symbol ERROR-TIME-CONSTRAINED, "Enabling time constrained failed.";
    end;
}

    { ----- Enable time regulation. We wait until we get a reply from the request, i.e. the time advance got granted. ----- }

    call _status-update-msg ("Enabling time regulation", Win);
    err-sym, err-text = call ghla-enable-time-regulation (io, the federate-time of io, the lookahead-time of cfg); 

    if err-sym /= the symbol NO-ERROR then
            signal err-sym, err-text; 

    i = 30;
    repeat 
        do in parallel until one completes
            wait until the federate-time-advance-granted of io receives a value;
            wait for 1 second;
        end;

        exit if the federate-time-advance-granted of io;

        i = i - 1;
        if i = 0 then
            signal the symbol ERROR-TIME-CONSTRAINED, "Enabling time regulation failed.";
    end;

    call display(DISPLAY-CLIENT-TIME-REGULATION ,"Time Regulation", the symbol green);
end

_spawn-server-simulation-processes(cfg: class a-g2hla-configuration, Win: class ui-client-item)
    cmd: text;
    pid: float;
begin
    call _status-update-msg("Launching g2-server process.", Win);

    { ---- C processes used for simulation ----- }
    cmd = "[the c-server-process-exec-file of cfg]";
    call display(display-c-server, "Launching", the symbol gold); 

    pid = call _launch-a-process (the _c-process-pid of cfg, cmd,  Win);

    conclude that the _c-process-pid of cfg = pid;
    if pid < 0 then begin
        call display(display-c-server, "C++ Server Launch Error", the symbol red); 
        signal the symbol NO-M1-PROCESS, "Could not start C++ server process"; 
    end;

    call display(display-c-server, "C++ Server Online", the symbol green); 
 
    { ----- G2 process used for simulation. In same G2 process but running with its own bridge to hla. ----- }
     start g2-server-simulation-process (cfg, g2-server-interface, Win);

    wait for 5 seconds;
end

_create-federation-and-join(cfg: class a-g2hla-configuration, io: class g2-interface-to-hla, Win: class ui-client-item)

    err-sym: symbol;
    err-text: text;
    i: integer;
    cmd: text;
    pid: float;
begin
    { ----- We need to connect/spawn a g2hla bridge ----- }
    call display(DISPLAY-HLA-BRIDGE-CLIENT ,"Initializing HLA Client Bridge", the symbol gold); 
    pid = call _connect-to-bridge (io, "tcp-ip host @"localhost@" port-number [the client-bridge-port-number of cfg]", "[the g2-hla-bridge-exec-file of cfg] [the client-bridge-port-number of cfg]", the client-bridge-pid of cfg, Win);

    conclude that the client-bridge-pid of cfg = pid;
    if pid < 0 then begin
        call display(DISPLAY-HLA-BRIDGE-CLIENT ,"HLA Client Bridge Offline", the symbol red); 
        signal the symbol NO-G2HLA-PROCESS, "Could not start G2HLA process";
    end;
    call display(DISPLAY-HLA-BRIDGE-CLIENT ,"HLA Client Bridge Online", the symbol green); 

    { ----- Create and join this federation. ----- }
    call display(display-server-federation,"Initializing Federation", the symbol gold); 
    call _status-update-msg ("Creating Federation '[the federation-name of cfg]' and joining it as '[the federate-name of cfg]'", Win);

    err-sym, err-text = call ghla-create-and-join-federation(io, the federation-name of cfg, the federate-name of cfg);
    if err-sym /= the symbol NO-ERROR then begin
        call display(display-client-federation,"Federation Offline", the symbol red); 
        signal err-sym, err-text; 
    end;

    call _status-update-msg ("Joined Federation", Win); 
    call display(display-client-federation,"Federation Online", the symbol green); 
end

_initialize-vehicles-for-simulation(Win: class ui-client-item)
    veh: class a-vehicle;
    N: integer;
begin
    for veh = each a-vehicle do
        N = random(100, 99999);
        conclude that the hla-object-handle of veh = 0.0;
        conclude that the vin of veh = "V-[N]";
        conclude that the fuel-level of veh = 0.0;
        conclude that the initial-fuel-level of veh = 9999.0;
        conclude that the location of veh = 0.0;
        conclude that the initial-location of veh = 100.0;
        conclude that the last-hla-update-timestamp of veh = 0.0;

        call _status-update-msg ("Initialized vehicle vin=[the vin of veh]", Win);
    end;
end

run-demonstration(cfg: class a-g2hla-configuration, io: class g2-interface-to-hla, win: class ui-client-item)
 
    err-sym: symbol;
    err-text: text;
    pid, tm: float;
    proc-exists: truth-value;
    hla-object-handle: float;
begin
    conclude that the federate-time of io = 0.0;
    conclude that the _shutdown-sync-point-achieved of io = false;

    { ---- lets initialize the federation and federate ----- }
    call _status-update-msg("Starting demonstration", Win);

    call _initialize-vehicles-for-simulation (Win);
    call _create-rtiexec(cfg, io, Win);
    call _spawn-server-simulation-processes (cfg, Win); 
    call _create-federation-and-join (cfg, io, Win);
    call _initialize-hla-public-activities (cfg, io, Win); 

    { ----- Register one object ----- }
    call _status-update-msg ("Registering object instance", Win);

    err-sym, err-text, hla-object-handle = call ghla-register-object-instance (io, the hla-class-handle of vehicle-class-def, the hla-object-name of demo-vehicle );

    if err-sym /= the symbol NO-ERROR then
        signal err-sym, err-text; 
 
    conclude that the hla-object-handle of demo-vehicle = hla-object-handle; 
    call update-hla-object-attr-values (cfg, io, demo-vehicle);

    { ----- Our main loop. ----- }
    conclude that the _run-demonstration of cfg = true;
    conclude that the _step-demo of cfg = false;

    repeat
        allow other processing;
        exit if not(the _run-demonstration of cfg);

        if the _run-in-step-mode of cfg = the symbol on then begin
            wait until the _run-demonstration of cfg receives a value OR the _run-in-step-mode of cfg receives a value OR the _step-demo of cfg receives a value;
        end;


        if the _run-in-step-mode of cfg = the symbol off or (the _run-in-step-mode of cfg = the symbol on AND the _step-demo of cfg) then begin

            conclude that the _step-demo of cfg = false;

            if err-sym /= the symbol NO-ERROR then
                signal err-sym, err-text; 

            { Request a simulation time advance. tm is new desired time. }
            tm  = the federate-time of io + the time-step of cfg;

            err-sym, err-text = call ghla-time-advance-request (io, tm);
            if err-sym /= the symbol NO-ERROR then
                signal err-sym, err-text; 

            { Wait for the time grant signal sent by HLA before we generate new attribute values. The previous call set the following flag to false while sending a time advance request. Here we are waiting until the RTI calls the method hla-cb-time-advance-grant which automatically sets the following flag to true. }

            repeat
                wait until the federate-time-advance-granted of io receives a value;
                exit if the federate-time-advance-granted of io; 
            end;

            err-sym, err-text = call ghla-request-class-attribute-value-update (io, the hla-class-handle of vehicle-class-def, the hla-subscribed-attribute-names of vehicle-class-def);
 
        end;
    end;

    { ---- Inform other Federates that they should shutdown ---- }
    err-sym, err-text = call ghla-register-federation-synchronization-point (io, "Shutdown", "tag");
    

    { ----- Starting to shutdown -----}
    call _status-update-msg("Starting shutdown procedures", Win);
    call _stop-demonstration ( cfg, io, Win);

    do in parallel until one completes 
        wait for 15 seconds;
        wait until the _shutdown-sync-point-achieved of io receives a value;
    end;

    call _stop-servers-and-rtiexec (cfg, io, Win); 
    call _status-update-msg("End of Demonstration", Win);
end

update-hla-object-attr-values(cfg:class a-g2hla-configuration, io: class g2-interface-to-hla, object: class a-vehicle) 

    retraction-handle: float;
    err-sym: symbol;
    err-text: text;
    tm: float;
begin
   { ----- Determine which attributes need to be updated. The RTI informs us if there is any other Federate that has interest in some of our attributes. If so we need to send the new information to the RTI otherwise we can skip the operation. We could always send updated values, but it would add overhead ----- }
    
    { We don't use this information in this simple version, and always update all attributes }

    tm = the federate-time of io + the time-step of cfg;

    err-sym, err-text, retraction-handle = call ghla-update-attribute-values (io, the hla-object-handle of object, tm, object, the hla-published-attribute-names of vehicle-CLASS-DEF , "");
end

_stop-demonstration(cfg: class a-g2hla-configuration, io: class g2-interface-to-hla, win: class ui-client-item)

    err-sym: symbol;
    err-text: text;
    destroy-federation-excution: truth-value;
    N: integer;
    pid, tm: float;
    proc-exists: truth-value;
    hla-class: class  hla-object-model-definition;
begin
    call display(DISPLAY-CLIENT-TIME-REGULATION ,"Time Regulation", the symbol blue); 

    { ----- Remove published object ---- }
    call display(DISPLAY-CLIENT-PUBLISH ,"Removing HLA Objects", the symbol gold); 

    call _status-update-msg ("Deleting published HLA object instance", Win);

    tm = the federate-time of io + the lookahead-time of cfg;
    err-sym, err-text = call ghla-delete-object-instance (io, the hla-object-handle of demo-vehicle, tm, "End of demonstration");

    if err-sym /= the symbol NO-ERROR then 
        signal err-sym, err-text; 

    { ----- Unpublish HLA object class ----- }
    for hla-class = each hla-object-model-definition do 

        call _status-update-msg ("Unpublishing object class [the hla-class-name of hla-class] ", Win);

        N = the array-length of the hla-published-attribute-names of hla-class;
        if N > 0 then begin 

            err-sym, err-text = call ghla-unpublish-object-class (io, the hla-class-handle of hla-class);

            if err-sym /= the symbol NO-ERROR then
                signal err-sym, err-text; 
        end;
    end;


    { ----- Unsubscribe HLA object class ----- }
    for hla-class = each hla-object-model-definition do 

        call _status-update-msg ("Unpublishing object class [the hla-class-name of hla-class] ", Win);

        N = the array-length of the hla-subscribed-attribute-names of hla-class;
        if N > 0 then begin 

            err-sym, err-text = call ghla-unsubscribe-object-class (io, the hla-class-handle of hla-class);

            if err-sym /= the symbol NO-ERROR then
                signal err-sym, err-text; 
        end;
    end; 

    call display(DISPLAY-CLIENT-PUBLISH ,"HLA Object Definitions", the symbol blue);  
end

g2:c.METHOD.g2:a.TEXT:
ghla-cb-federation-synchronized(hla-interface: class g2-interface-to-hla , label: text )

{This callback method is called by the G2-HLA bridge on behalf of the RTI to inform the G2 application that the Federation is synchronized at the specified synchronization point. The RTI will only inform each Federate part of a Federation that the synchronization point has been reached, after each Federate made a call to hla-synchronization-point-achieved . }
 
begin
    { Since we are only using a synchronization point at shutdown time, we do test the label argument for simplicity }

    conclude that the _shutdown-sync-point-achieved of hla-interface = true;
end

ghla-cb-announce-synchronization-point(hla-interface: class g2-interface-to-hla , label: text , tag: text )

{This callback method is called by the G2-HLA bridge on behalf of the RTI to inform the G2 application that a Federate registered a synchronization point by calling the method hla-register-federation-synchronization-point. The G2 application should perform any required operations to achieve the requested synchronization and then make a call to hla-synchronization-point-achieved. }

begin
    { Here we are going to conclude that we achieved the syncronization point without any other processing }

    call ghla-synchronization-point-achieved (hla-interface , label);
end

ghla-cb-synchronization-point-registration-succeeded(hla-interface: class g2-interface-to-hla, label: text )
{This callback method is called by the G2-HLA bridge on behalf of the RTI to inform the G2 application that the hla-register-federation-synchronization-point method call succeeded. This method is only called for the Federate that issued the request by calling hla-register-federation-synchronization-point. }
begin end

ghla-cb-turn-updates-off-for-object-instance (io: class g2-interface-to-hla, hla-object-handle: float, hla-attribute-names: class symbol-array)
    I, N: integer;
begin
    if there exists a a-vehicle obj such that (the hla-object-handle of obj = hla-object-handle) then begin 

        { We don't use this information in this version, and always update all attributes }
        call _status-update-msg ("Turn on updates for hla object with handle [hla-object-handle]'", gfr-default-window);
    end;

    delete hla-attribute-names;
end

ghla-cb-turn-updates-on-for-object-instance (io: class g2-interface-to-hla, hla-object-handle: float, hla-attribute-names: class symbol-array)

    I, N: integer;
begin
    if there exists a a-vehicle obj such that (the hla-object-handle of obj = hla-object-handle) then begin 

        { We don't use this information in this version, and always update all attributes }
        call _status-update-msg("Turn on updates for hla object with handle [hla-object-handle]'", gfr-default-window);
    end;

    delete hla-attribute-names;
end

ghla-cb-remove-object-instance (io: class g2-interface-to-hla , hla-object-handle: float, hla-time: float, tag: text) 

  obj: class a-vehicle ; 

begin
  { ----- Search if such an object already exists ----- }
  if there exists a a-vehicle obj such that (the hla-object-handle of obj = hla-object-handle) then begin
      { ----- Create a new object and store it on a bin workspace ----- }
      delete obj;
      call _status-update-msg("[the text of the names of io] => Object deletion for '[hla-object-handle]', with tag '[tag]'", gfr-default-window); 
  end else begin
      call _status-update-msg( "[the text of the names of io] => **** Object delete failed for '[hla-object-handle]'", gfr-default-window);
  end; 
end

ghla-cb-discover-object-instance (io: class g2-interface-to-hla , hla-class-handle: float, hla-object-handle: float, hla-object-name: text) 

  wks: class kb-workspace = ghla-demo-1;
  obj: class a-vehicle ;
  err-sym: symbol;
  err-text: symbol;
  N, Y: integer;

begin
    { ----- Search if such an object already exists ----- }
    if not(there exists a a-vehicle obj such that (the hla-object-handle of obj = hla-object-handle)) then begin

        { ----- Create a new object and store it on a bin workspace ----- }
       create a a-vehicle obj; 
        N = the count of each a-vehicle upon wks + 1;
        Y = -50 * N;
        transfer obj to wks at (0, Y); 

        conclude that the last-hla-update-timestamp of obj = the federate-time of io;
        conclude that the hla-object-name of obj = hla-object-name;
        conclude that the hla-object-handle of obj = hla-object-handle;

        call _status-update-msg( "[the text of the names of io] => Object discovery for '[hla-object-name]', handle [hla-object-handle]", gfr-default-window); 

    end else begin

        call _status-update-msg ("[the text of the names of io] => **** Object discovery failed for '[hla-object-handle]'", gfr-default-window);
    end;
end

ghla-cb-reflect-attribute-values (io: class g2-interface-to-hla, hla-time: float, hla-object-handle: float, hla-attribute-names: class symbol-array, new-object: class item, tag: text)

  obj: class a-vehicle; 
  I,x: integer;

 begin
    { ----- Search if such an object already exists ----- }
    if there exists a a-vehicle obj such that (the hla-object-handle of obj = hla-object-handle) then begin

        { ----- Refresh the attribute values with the new ones provided by HLA.  attr-list indicates the list of attribute values that changed. In our case we are going to copy all public attributes to the already existing object. ----- }

        { Copy attribute values that changed }
        for I = 0 to the array-length of hla-attribute-names - 1 do
            if hla-attribute-names [I] = the symbol FUEL-LEVEL then
                conclude that the fuel-level of obj = the fuel-level of new-object;
            if hla-attribute-names [I] = the symbol LOCATION 
              then begin
                conclude that the location of obj = the location of new-object;
                x = floor(the location of new-object);
                move demo-vehicle to (x,0);
              end;
        end;

    end else begin

        call _status-update-msg ("[the text of the names of io] => **** Object value reflection failed for '[hla-object-handle]'. No object exists!", gfr-default-window);
    end;

    delete new-object;
    delete hla-attribute-names;
end

ghla-cb-provide-attribute-value-update (cfg:class a-g2hla-configuration, io: class g2-interface-to-hla , hla-object-handle: float, hla-attribute-names: class symbol-array)
  
  obj: class a-vehicle;
  err-sym: symbol;
  err-text: text; 

begin
  { ----- Search if such an object already exists ----- }
    if there exists a a-vehicle obj such that (the hla-object-handle of obj = hla-object-handle) then begin

        { ----- Refresh the attribute values if necessary and send them to HLA.  The requested attribute values are stored in attr-list. In this simple example we don't change the values here but send update information to HLA ----- }

        call update-hla-object-attr-values (cfg, io, obj); 

    end else begin

        call _status-update-msg ("**** Object value update failed for '[hla-object-handle]'. No object exists!", gfr-default-window);

    end;

    delete hla-attribute-names;
end

ghla-cb-announce-synchronization-point(hla-interface: class g2-server-test-interface , label: text , tag: text )
begin
    call ghla-synchronization-point-achieved (hla-interface , label);
    conclude that the _run-demonstration of hla-interface = false;
end

ghla-cb-reflect-attribute-values (io: class g2-server-test-interface, hla-time: float, hla-object-handle: float, hla-attribute-names: class symbol-array, new-object: class item, tag: text)

  obj: class a-vehicle; 
  I: integer;

begin
    call _status-update-msg ("[the text of the names of io] => Reflect attribute values for object with handle [hla-object-handle]. Tag: [tag]", gfr-default-window);
 
    { Copy attribute values that changed }
    for I = 0 to the array-length of hla-attribute-names - 1 do
        if hla-attribute-names [I] = the symbol INITIAL-FUEL-LEVEL then begin
            conclude that the initial-fuel-level of io = the initial-fuel-level of new-object;
            conclude that the last-fuel-level of io = the initial-fuel-level of new-object; 
        end;
        if hla-attribute-names [I] = the symbol INITIAL-LOCATION then begin
            conclude that the initial-location of io = the initial-location of new-object;
            conclude that the last-location of io = the initial-location of new-object; 
        end;
    end;

    delete new-object;
    delete hla-attribute-names;
end

g2:c.PROCEDURE.g2:a.TEXT:
_g2-server-update-attribute-values(io: class g2-server-test-interface, hla-object-handle: float)

    err-sym: symbol;
    err-text: text; 
    Q: float;
    tm: float;
    veh: class a-vehicle;
    hla-attr-names: class symbol-array; 
    retraction-handle: float;
begin
    if hla-object-handle <= 0.0 or not(the _run-demonstration of io) then
        return;

    { Provide new values }
    create a a-vehicle veh;

    Q = the last-fuel-level of io - 0.5;
    conclude that the last-fuel-level of io = Q;  
    conclude that the fuel-level of veh = Q;

    
    if vehicle-direction is right then Q = the last-location of io + 1.0;
    if vehicle-direction is left then Q = the last-location of io - 1.0; 
    if q > 600 then conclude that vehicle-direction is left;
    if q < 0 then conclude that vehicle-direction is right; 
    conclude that the last-location of io = Q;
    conclude that the location of veh = Q;

    create a symbol-array hla-attr-names;
    change the array-length of hla-attr-names to 1;
    change hla-attr-names[0] = the symbol location;

    tm  = the federate-time of io {+ the time-step of io};

    err-sym, err-text, retraction-handle = call ghla-update-attribute-values (io, the vehicle-hla-object-handle-discovered of io, tm, veh, hla-attr-names, "");

    delete veh;
    delete hla-attr-names;
 
    if err-sym /= the symbol NO-ERROR then
        signal err-sym, err-text; 
end

g2-server-simulation-process(cfg: class a-g2hla-configuration, io: class g2-server-test-interface , win: class ui-client-item)
 
    err-sym: symbol;
    err-text: text;
    pid, tm: float;
    I: integer;
    cmd: text;
    proc-exists: truth-value;
    hla-object-handle: float;
begin
    conclude that the federate-time of io = 0.0;
    conclude that the vehicle-hla-class-handle of io = 0.0;
    conclude that the vehicle-hla-object-handle-discovered of io = 0.0;
    conclude that the vehicle-hla-object-name-discovered of IO = "";
    conclude that the last-fuel-level of io = 0.0;
    conclude that the last-location of io = 0.0;
    conclude that the initial-fuel-level of io = 0.0;
    conclude that the initial-location of io = 0.0;

    { ---- connect/start bridge process ---- }
    call display(display-hla-bridge-server,"Initializing HLA Server Bridge", the symbol gold); 

    pid = call _connect-to-bridge (io, "tcp-ip host @"localhost@" port-number [the server-bridge-port-number of cfg]" , "[the g2-hla-bridge-exec-file of cfg] [the server-bridge-port-number of cfg]", the server-bridge-pid of cfg , Win);

    call display(display-hla-bridge-server,"HLA Server Bridge Online", the symbol green);
    conclude that the server-bridge-pid of cfg = pid;
 
    { ---- lets initialize the federation and federate ----- }
    call _status-update-msg("g2-server Starting demonstration", Win);
    call display(display-server-federation,"Initializing Federation", the symbol gold); 
    conclude that the _run-demonstration of io = true;

    err-sym, err-text = call ghla-create-and-join-federation(io, the federation-name of cfg, "g2-server");
    if err-sym /= the symbol NO-ERROR 
    then begin
        call display(display-server-federation,"Federation Offline", the symbol red); 
        signal err-sym, err-text;
    end
    else call display(display-server-federation,"Federation Online", the symbol green); 

    call _g2-server-initialize ( cfg, io, Win); 
    
    { ----- Our main loop. ----- }
    repeat
        allow other processing;
        exit if not(the _run-demonstration of io);

        { Request a simulation time advance. tm is new desired time. }
        tm  = the federate-time of io + the time-step of io;

        err-sym, err-text = call ghla-time-advance-request (io, tm);
        if err-sym /= the symbol NO-ERROR then
            signal err-sym, err-text; 

        { Wait for the time grant signal sent by HLA before we generate new attribute values. The previous call set the following flag to false while sending a time advance request. Here we are waiting until the RTI calls the method hla-cb-time-advance-grant which automatically sets the following flag to true. }

        repeat
            wait until the federate-time-advance-granted of io receives a value or the _run-demonstration of io receives a value;
            exit if the federate-time-advance-granted of io or not(the _run-demonstration of io);
        end;

{        call _g2-server-update-attribute-values (io, the vehicle-hla-object-handle-discovered of io);
}
    end;

    { ----- Starting to shutdown -----} 
    call display(display-server-time-constrained,"Time Constrained", the symbol blue); 
    call display(DISPLAY-SERVER-PUBLISH, "Publish Vehicle and attributes", the symbol blue);
    call _status-update-msg("g2-server Shutdown procedures", Win);
    call display(display-server-federation,"Resigning from Federation", the symbol gold); 
 
    err-sym, err-text = call ghla-resign-and-destroy-federation (io, false);
    call display(display-server-federation,"Resigned from Federation", the symbol blue); 
 
    allow other processing;
    change the text of the gsi-connection-configuration of io to "none";
    call display(display-hla-bridge-server,"HLA Server Bridge Offline", the symbol blue);
 
    if the server-bridge-pid of cfg > 0.0 then begin
      proc-exists = call g2-process-exists (the server-bridge-pid of cfg);
      if proc-exists then
        call g2-kill-process (the server-bridge-pid of cfg);
    end;
    conclude that the server-bridge-pid of cfg = 0.0;

    if err-sym /= the symbol NO-ERROR then
        signal err-sym, err-text; 
end

_g2-server-initialize(cfg: class a-g2hla-configuration, io: class g2-server-test-interface , Win: class ui-client-item)

    err-sym: symbol;
    err-text: text; 
    sym: symbol;
    N, I: integer;
    hla-attr-handles: class float-array;
    hla-class-handle: float;
    hla-class: class  hla-object-model-definition = vehicle-class-def;
    object: class object = demo-vehicle ;
    attr-names: class symbol-array;
begin
    call display(display-server-publish,"HLA Object Configuration", the symbol gold);
 
    { ----- Enable advisory system ----- }
    call _status-update-msg ("Enabling class relevance advisory switch", Win);
    err-sym, err-text = call ghla-enable-class-relevance-advisory-switch (io);

    if err-sym /= the symbol NO-ERROR then
        signal err-sym, err-text; 

    { ----- Enable attribute advisory relevance ----- }
    call _status-update-msg ("Enabling attribute relevance advisory switch", Win);
    err-sym, err-text = call ghla-enable-attribute-relevance-advisory-switch (io);

    if err-sym /= the symbol NO-ERROR then
        signal err-sym, err-text; 

 
    { ----- Subscribed to HLA classes ----- }
    create a symbol-array attr-names;
    change the array-length of attr-names to 2;
    change attr-names[0] = the symbol initial-fuel-level;
    change attr-names[1] = the symbol initial-location;
 
    err-sym, err-text, hla-class-handle, hla-attr-handles = call ghla-subscribe-object-class-attributes (io, the hla-class-name of hla-class, object, attr-names, true); 

    delete hla-attr-handles;
    delete attr-names;

    if err-sym /= the symbol NO-ERROR then
        signal err-sym, err-text;  
 
    { ----- Publish HLA classes ----- }
    create a symbol-array attr-names;
    change the array-length of attr-names to 1; 
    change attr-names[0] = the symbol location;
 
    err-sym, err-text, hla-class-handle, hla-attr-handles = call ghla-publish-object-class (io, the hla-class-name of hla-class, object, attr-names); 

    delete hla-attr-handles;
    delete attr-names; 

    conclude that the vehicle-hla-class-handle of io = hla-class-handle;

    if err-sym /= the symbol NO-ERROR then
       begin
        call display(display-server-publish,"Publish Vehicle and attribute failure", the symbol red);
        signal err-sym, err-text;  
       end
       else call display(display-server-publish,"Publish Vehicle and attributes", the symbol green); 
 
    { ----- Enable time constrained. We wait until we get a reply from the request, i.e. the time advance got granted. ----- }

        call _status-update-msg ("Enabling time constrained", Win);
 
       err-sym, err-text = call ghla-enable-time-constrained (io);

         if err-sym /= the symbol NO-ERROR then
            begin
            call display(display-server-time-constrained,"Enabling time failure", the symbol red); 
            signal err-sym, err-text; 
            end
            else call display(display-server-time-constrained,"Enabling time constrained granted", the symbol green); 

        i = 30;
        repeat 
            do in parallel until one completes
                wait until the federate-time-advance-granted of io receives a value;
                wait for 1 second;
            end;

            exit if the federate-time-advance-granted of io;

            i = i - 1;
            if i = 0 then 
                signal the symbol ERROR-TIME-CONSTRAINED, "Enabling time constrained failed.";
        end;  
end

g2:c.METHOD.g2:a.TEXT:
ghla-cb-remove-object-instance (io: class g2-server-test-interface , hla-object-handle: float, hla-time: float, tag: text) 

    obj: class a-vehicle ; 
begin
    if the vehicle-hla-object-handle-discovered of io /= hla-object-handle then begin
        call _status-update-msg("g2-server delete error", gfr-default-window); 
        return;
    end;

    conclude that the vehicle-hla-object-handle-discovered of io =-1.0;
    conclude that the vehicle-hla-object-name-discovered of io = "";

    call _status-update-msg(" [the text of the names of io] => Remove of object with handle [hla-object-handle]", gfr-default-window); 
end

ghla-cb-discover-object-instance (io: class g2-server-test-interface , hla-class-handle: float, hla-object-handle: float, hla-object-name: text) 

    obj: class a-vehicle ;
    err-sym: symbol;
    err-text: text;
    attr-names: class symbol-array;
begin
    conclude that the vehicle-hla-object-handle-discovered of io = hla-object-handle;
    conclude that the vehicle-hla-object-name-discovered of io = hla-object-name;

    conclude that the last-fuel-level of io = 0.0;
    conclude that the last-location of io = 0.0;

    call _status-update-msg(" [the text of the names of io] => Discovery of [hla-object-name],[hla-object-handle]", gfr-default-window);

    { ----- Request initial attribute values ----- }
    create a symbol-array attr-names;
    change the array-length of attr-names to 2;
    change attr-names[0] = the symbol initial-fuel-level;
    change attr-names[1] = the symbol initial-location; 

    err-sym, err-text = call ghla-request-object-attribute-value-update (io, hla-object-handle, attr-names);

    delete attr-names;

    if err-sym /= the symbol NO-ERROR then
        signal err-sym, err-text; 

    { ----- Change Attribute Ownership ----- }
    create a symbol-array attr-names;
    change the array-length of attr-names to 1;
    change attr-names[0] = the symbol location; 

    err-sym, err-text = call ghla-attribute-ownership-acquisition-if-available(io, hla-object-handle, attr-names);

    delete attr-names;

    if err-sym /= the symbol NO-ERROR then
        signal err-sym, err-text; 
end

ghla-cb-provide-attribute-value-update (io: class g2-server-test-interface, hla-object-handle: float, hla-attribute-names: class symbol-array)

begin 
    call _g2-server-update-attribute-values (io, the vehicle-hla-object-handle-discovered of io);
    delete hla-attribute-names;
end

g2:c.PROCEDURE.g2:a.TEXT:
_ghla-demo-startup (Object: class gfr-startup-object, InitialStartup: truth-value, Modules: class symbol-list, Client: class object)
 
  devstuffName: symbol = the symbol gdev-internals-workspaces; 
  Err: class error;
  mak-rtidir: text;
  g2-version-text: text;
  dir-sep: text;
  file-ext: text;
  rtiexec: text;
  default-dir: text;
begin

  {--- For devstuff development environment } 
  if the item named by devstuffName exists and ghla-demo-top-level is not a member of the item-list named by devstuffName then
    insert ghla-demo-top-level at the end of the item-list named by devstuffName;

  { -- initialize GHLA-DEMO-CONFIG -- }
  default-dir = call grtl-get-installation-directory ();
  g2-version-text = call g2-get-software-version ();
  if is-contained-in-text("Windows", g2-version-text) then
    begin
      dir-sep = "\"; file-ext = ".exe";
      rtiexec = "\bin\rtiexec.exe";
    end
  else
    begin
      dir-sep = "/"; file-ext = "";
      rtiexec = "/bin/rtiexec";
    end;
  
  mak-rtidir = call g2-get-environment-variable ("MAK_RTIDIR");
  if length-of-text(mak-rtidir) > 0 then
    conclude that the rtiexec-process-exec-file of GHLA-DEMO-CONFIG = "[mak-rtidir][rtiexec]";

  conclude that the g2-hla-bridge-exec-file of GHLA-DEMO-CONFIG =
    "[default-dir][dir-sep]hla[dir-sep]bin[dir-sep]g2-hla[file-ext] -ersdawf";
  conclude that the c-server-process-exec-file of GHLA-DEMO-CONFIG =
    "[default-dir][dir-sep]hla[dir-sep]examples[dir-sep]G2Demo_Federate[dir-sep]G2Demo[file-ext]";
  
end on error (Err)
  call gerr-dispatch (Err, this procedure, gfr-default-window);
end
