g2:c.PROCEDURE.g2:a.TEXT:
arm-check-for-temp-and-home (Win: class g2-window) = (truth-value)

Passed: truth-value = true;

begin

post "The check-for-temp-and-home is a place holder procedure and requires all procedures be manually checked for bad file system paths statements.";
return Passed;

end

arm-check-for-inform-statements (ISExpected: integer, LogFile: text, Win: class g2-window) = (truth-value)

Proc: class procedure;
Code, Et: text;
File: class g2-stream;
InformFound: truth-value;
ISActual: integer = 0;
En: symbol;
Passed: truth-value = true;
Modul: symbol;

begin

for Proc = each procedure do
    begin 
        Code = the text of Proc;
        InformFound = true;
    end on error (En, Et) 
        InformFound = false;
    end;
    Modul = gfr-get-module-of-item (Proc);
    if InformFound and (Modul /= the symbol TOP and Modul /= the symbol GRM and Modul /= the symbol DEVTOOLS and Modul /= the symbol GDADEV1) and is-contained-in-text ("inform the", Code) then
        ISActual = ISActual + 1;
end;

if ISActual /= ISExpected then
    post "The GDA procedural code contained additional inform statements - it should be checked manually."
else
    post "The GDA procedural code is OK - no additional informs statements were found.";

File = call g2-open-file-for-append (LogFile, true);
call g2-write-line (File, "");
call g2-write-line (File, "Inform Statement Check");
call g2-write-line (File, ""); 
call g2-write-line (File, "Inform Statements -- Expected: [ISExpected]  Found: [ISActual]"); 
call g2-close-file (File);

return Passed;

end

arm-count-items-with-notes (LogFile: text, Win: class g2-window) = (truth-value)

Counter: integer = 0;
File: class g2-stream;
Itm: class item;
ItemModule: symbol;

begin

inform the operator that "Starting count of items with notes";
File = call g2-open-file-for-append (LogFile, true);
call g2-write-line (File, "");
call g2-write-line (File, "Checking Items with Notes"); 
call g2-write-line (File, ""); 
for Itm = each item do
    Counter = Counter + 1;
    if the item-notes of Itm exists then begin
        ItemModule = gfr-get-module-of-item (Itm);
        if ItemModule /= the symbol DEVTOOLS and ItemModule /= the symbol TOP then begin
            inform the operator that "An item with notes exists. [the item-notes of Itm]";
        call g2-write-line (File, "The following item had notes - ");
        call g2-write-line (File, "    [the item-notes of Itm]"); 
            return false;
        end;
    end;
end;
inform the operator that "All items ([Counter]) checked, no notes";
inform the operator that "Finished count of items with notes";
call g2-write-line (File, "No notes found in any item.");
call g2-close-file (File);
return true;

end

arm-cleanup-gold-prefs ()

MachPref: class gold-machine-configuration;
UserPref: class gold-user-preference;

begin

post "Cleaning up Gold prefs";

for MachPref = each gold-machine-configuration do
    make MachPref transient;
    delete MachPref;
end;
for UserPref = each gold-user-preference do
    make UserPref transient;
    delete UserPref;
end;

post "Finished cleaning up Gold prefs"; 

end

arm-save-gdanol-modules (SaveType: symbol, Win: class g2-window) = (truth-value)

FileText: text;
Modul: symbol;
ModulesToSave: sequence = the g2-array-sequence of gdanol-saved-modules;
GdaDir, NolDir: text;
GdaToSave: sequence = the g2-array-sequence of gda-saved-modules;
NolToSave: sequence = the g2-array-sequence of nol-saved-modules; 
N, Len: integer;
Result: truth-value;
Passed: truth-value = true;
Verified: truth-value = the verified of release-save-object;

{***************************************************************
This procedure saves the Gda & Nol modules.  If the save type 
is SOURCE then the current-file-for-module is used to save back to 
the source area, usually a sandbox.  The list for this process is 
obtained from gdanol-saved-modules.  If the save type is  DEV or 
DIST Gda & Nol are saved separately to lists provided by the arrays
gda-saved-modules and nol-saved-modules.
****************************************************************}

begin

post "Starting GDA/NOLmodule save@.@.@.";

if Verified is false then begin
    post "Target saving directories have not been confirmed.  Verify directories, set verification to true & try again";
    return false;
end;

if SaveType = the symbol SOURCE then begin
    for Modul = each symbol in ModulesToSave do
        if there exists a saving-parameters SP such that (the containing-module of SP = Modul) then
            Result = call g2-save-module ("[the text of the current-file-for-module of SP]", true, Win, Modul, false);
        if Result is false then begin
            post "Failed to save module [Modul] in SOURCE";
            return false;
        end;
    end;
end else begin
    if SaveType = the symbol DEV then begin
        GdaDir = the gda-dev-directory of release-save-object;
        NolDir = the nol-dev-directory of release-save-object;
    end else if SaveType = the symbol DIST then begin
        GdaDir = the gda-dist-directory of release-save-object;
        NolDir = the nol-dist-directory of release-save-object;
    end else begin
        post "Unknown save type specified";
        return false;
    end;
    for Modul = each symbol in GdaToSave do
        FileText = "[GdaDir]\[(lower-case-text ("[Modul]"))].kb";
        Result = call g2-save-module (FileText, true, Win, Modul, false);
        if Result is false then begin
            post "Failed to save GDA module [Modul] in [SaveType]";
            return false;
        end;
    end;
    for Modul = each symbol in NolToSave do
        FileText = "[NolDir]\[(lower-case-text ("[Modul]"))].kb";
        Result = call g2-save-module (FileText, true, Win, Modul, false);
        if Result is false then begin
            post "Failed to save NOL module [Modul] in [SaveType]";
            return false;
        end;
    end;
end;

post "Finished GDA/NOL module save";
return Passed;

end

arm-set-copyrights () = (truth-value)

Passed: truth-value = false;
WS: class kb-workspace;

begin

{*** Set GDA & NOL copyright notices ***}
WS = the workspace of _gdl-copyright;
change the text of _gdl-release-version-text to the text of current-gdanol-version;
change the text of _gdl-g2-use-version-text to the text of current-g2-version;
change the text of _gdl-copyright to the text of gcc-copyright-info;
change the size of WS to minimum;
make WS permanent;

WS = the workspace of _nol-copyright; 
change the text of _nol-release-version-text to the text of current-gdanol-version;
change the text of _nol-g2-use-version-text to the text of current-g2-version;
change the text of _nol-copyright to the text of gcc-copyright-info; 
change the size of WS to minimum;
make WS permanent;

WS = the workspace of gcc-copyright-date;
change the text of gcc-copyright-date to the text of gcc-copyright-info;
change the size of WS to minimum;
make WS permanent;

WS = the workspace of gdl-package-information-text;
change the text of gdl-package-information-text to the text of gdl-package-info;
change the size of WS to minimum;
make WS permanent;  

Passed = true;
return Passed;

end

arm-initialize (LogFile: text, Win: class g2-window)

CycleText: text;
File: class g2-stream;

begin

CycleText = call arm-set-cycle-code (Win);
change the text of cycle-number to CycleText;
make cycle-number permanent;

conclude that the generate-statistics of gdl-engine = false;

if not (the Verified of release-save-object) then
    signal the symbol GRM-INCOMPLETE-INITIALIZATION, "Incomplete Initialization - Target directories not verified";

post "Finished GRM initialization";

File = call g2-open-file-for-write (LogFile);
call g2-write-line (File, "GRM Initialization completed.");
call g2-close-file (File);

end

arm-write-formatted-line (File: class g2-stream, FormSeq: sequence, DataSeq: sequence)

ExtraLine: truth-value = false;
N: integer = the number of elements in FormSeq;
K: integer = the number of elements in DataSeq;
Len, TLen: integer;
RText: text = "";
RText2: text = "";
T, T2: text;

begin

if N = 0 or K = 0 then begin
    post "Either the Format or Data sequences or both are empty";
    return;
end; 
if N /= K then begin
    post "Format sequence ([N]) & Data sequence ([K]) are not commensurate";
    return;
end;

for K = 0 to N - 1 do
    T = DataSeq [K];
    T2 = "";
    TLen = length-of-text (T);    {*** Warning: Invalid below if T has been modified ***}
    Len = FormSeq [K];

    if TLen > Len then begin    {*** Trim T, T2 gets the overflow ***}
        ExtraLine = true;
        T2 = "[(if K = 0 then "  " else "")][(get-from-text (T, Len + 1, TLen))]";
        T2 = pad (T2, Len, " ");
        T = get-from-text (T, 1, Len);
    end else if TLen < Len then begin    {*** Pad T, space-fill T2 ***}
        T = pad (T, Len, " ");
        T2 = pad (T2, Len, " ");
    end;
        
    if K = 0 then begin
        RText = "[T]";
        RText2 = "[T2]";
    end else begin
        RText = "[RText] [T]";
        RText2 = "[RText2] [T2]";
    end;
end;

call g2-write-line (File, RText);
if Extraline then
    call g2-write-line (File, RText2);

end

grm-declare-stable-workspace (WS: class kb-workspace, Win: class g2-window)

ItemConfiguration: sequence;
TextOfItemConfiguration: text;
ExistingConfig, ActualConfig: structure;
StableConfig: structure  = structure (type: the symbol declare-properties,
    clauses: structure (declared-properties: structure (stable-for-dependent-compilations: true)));
ReplaceBack: truth-value = false;

begin

{***********  Bug workaround for G2 5.0 Rev 3  ***************}
TextOfItemConfiguration = the text of the item-configuration of WS;
if find-next-pattern ("nothing", TextOfItemConfiguration ,1) /= structure (start-position: 0, end-position: 0) then begin
    ReplaceBack = true;
    TextOfItemConfiguration = find-and-replace-pattern ("nothing", "foobooroo", TextOfItemConfiguration, 1, length-of-text (TextOfItemConfiguration)); 
    change the text of the item-configuration of WS to TextOfItemConfiguration; 
end;


{***********************************************************************}
ItemConfiguration = the item-configuration of WS;
if there exists a structure ExistingConfig in ItemConfiguration such that (the type of ExistingConfig = the symbol DECLARE-PROPERTIES) then begin
    inform the operator that "[ExistingConfig]";
    ActualConfig = change-attribute (ExistingConfig, clauses, change-attribute (the clauses of ExistingConfig, declared-properties, structure (stable-for-dependent-compilations: true)));
    inform the operator that "[ActualConfig]"; 
end else begin
    ActualConfig = StableConfig;
end;
conclude that the item-configuration of WS = insert-at-end (ItemConfiguration, ActualConfig);

{***********  Bug workaround for G2 5.0 Rev 3  ***************}
TextOfItemConfiguration = the text of the item-configuration of WS;
if ReplaceBack then begin
    TextOfItemConfiguration = find-and-replace-pattern( "foobooroo", "nothing",TextOfItemConfiguration, 1, length-of-text (TextOfItemConfiguration));
    change the text of the item-configuration of WS to TextOfItemConfiguration;
end;
{***********************************************************************} 

end

grm-recount-items-with-notes (LogFile: text, Win: class g2-window) = (truth-value)

Itm: class item;
ItemModule: symbol;
Counter: integer = 0;
File: class g2-stream;

begin

post "Starting recount of items with notes"; 
File = call g2-open-file-for-append (LogFile, true);
call g2-write-line (File, "");
call g2-write-line (File, "Recounting Items with Notes.");

for Itm = each item do
    Counter = Counter + 1;
    if the item-notes of Itm exists then begin
        ItemModule = gfr-get-module-of-item (Itm);
        if ItemModule /= the symbol DEVTOOLS and ItemModule /= the symbol TOP then begin
            post "An item with notes exists. [the item-notes of Itm]";
            call g2-write-line (File, "    An item with notes exists. [the item-notes of Itm]");
            return false;
        end;
    end;
end;

post "All items ([Counter]) re-checked, no notes";
post "Finished recount of items with notes";
call g2-write-line (File, "    No Items with Notes were found.");
call g2-close-file (File);

return true;

end

grm-remove-unused-modules (Win: class g2-window)

ModInf: class module-information;
OrderedModules: sequence = sequence (the symbol GDADEV1, the symbol PROTOOLS, the symbol PTROOT);
DeleteModule: symbol;

begin

for ModInf = each module-information do
    if gfr-get-module-of-item (ModInf) = the symbol GQM then 
        conclude that the directly-required-modules of ModInf = sequence (the symbol GQS);
    if gfr-get-module-of-item (ModInf) = the symbol GRL then 
        conclude that the directly-required-modules of ModInf = sequence (the symbol JAVALINK, the symbol G2EVLISS, the symbol GQS ); 
    if gfr-get-module-of-item (ModInf) = the symbol GLF then 
        conclude that the directly-required-modules of ModInf = sequence (the symbol GFR); 
    if gfr-get-module-of-item (ModInf) = the symbol GENS then 
        conclude that the directly-required-modules of ModInf = sequence (the symbol GFR); 
end;
for DeleteModule = each symbol in OrderedModules do
    call g2-delete-module (DeleteModule, true);
end;

end

grm-automated-destructive-prep (Win: class g2-window)= (truth-value)

En: symbol;
Et: text;
File: class g2-stream;
LogFile: text = "c:\gensym\gdanol-log.txt";
NOLRPC: class nol-rpc-interface;

begin

post "Starting destructive preparation";
File = call g2-open-file-for-append (LogFile, true);
call g2-write-line (File, "Starting destructive preparation.");
call g2-close-file (File); 

call grm-count-incomplete-items (LogFile, Win);
allow other processing;

call grm-auto-denude (Win);
allow other processing; 

call grm-declare-stable-items (Win);
allow other processing;

call grm-strip-items-of-authors (Win);
allow other processing;

call grm-remove-unused-modules (Win);

call grm-recount-items-with-notes (LogFile, Win);
allow other processing;

change the text of the g2-user-mode of Win to "administrator";
show the subworkspace of gdl-copyright-notice at the center of the screen;
change the text of the g2-user-mode of Win to "developer";

File = call g2-open-file-for-append (LogFile, true);
call g2-write-line (File, "");
call g2-write-line (File, "Looking for named RPC Objects.");
 
for NOLRPC = each nol-rpc-interface do
    if NOLRPC has a name then begin
        call g2-write-line (File, "Deleted RPC - [the name of NOLRPC]");
        make NOLRPC transient;
        delete NOLRPC;
    end;
end; 

call g2-write-line (File, "Finished looking for named RPC Objects."); 

post "Finished destructive preparation"; 
post "At this point, the following steps will be performed.
    1)  Save to Development
    2)  Flush change logs
    3)  Text Strip
    4)  Make Proprietary
    5)  Save to Distribution"; 

{*** Save to development ***}
call grm-save-gdanol-modules (the symbol DEV, Win); 

{*** Do the package prep stuff ***}
call g2-enter-package-preparation-mode ();
call g2-flush-change-log-for-entire-kb ();
call g2-strip-texts-now ();
call g2-make-workspaces-proprietary-now ();
call g2-leave-package-preparation-mode (); 

{*** Save to distribution ***}
call grm-save-gdanol-modules (the symbol DIST, Win);

call g2-write-line (File, ""); 
call g2-write-line (File, "Finished destructive preparation.");
call g2-close-file (File);

return true;

end 

on error (En, Et) 
    post "The Automated build has FAILED because [Et]";
    if File exists then
        call g2-close-file (File); 
    return false;
end

