end

c.GRM-PROCEDURE.g2:a.TEXT:
release-set-title-blocks (Step: class grm-step, Win: class g2-window) = (truth-value)

BldData: class grm-data-object = release-build-data;
CRLF: text = "
";
En, Mod: symbol;
Et, LogText: text;
Passed: truth-value;
TB: class uil-title-block;
GDAVersionMods: class symbol-list = the arm-gda-version-modules of BldData;
NOLVersionMods: class symbol-list = the arm-nol-version-modules of BldData; 

begin

LogText = "--- Setting Title Blocks -- [the current subsecond time as a time stamp]---[CRLF]";

Passed, LogText = call arm-set-title-blocks-info (LogText, Win);

for TB = each uil-title-block do
    Mod = the containing-module of TB; 
    if Mod is a member of GDAVersionMods or Mod is a member of NOLVersionMods then
        LogText =  "[LogText][CRLF]Module: [the uil-module-title of TB][CRLF]  Version: [the version of the icon-variables of TB]";
end; 

call grm-log-step (Step, LogText, Passed, Win);
return Passed;

end

on error (En, Et)
    call grm-signal-error (Step, En, Et, Win);
    return false;
end

release-set-version-info-objects (Step: class grm-step, Win: class g2-window) = (truth-value)

BldData: class grm-data-object = release-build-data;
CRLF: text = "
";
En, Mod: symbol;
Et, LogText: text;
LTRes: class gfr-local-text-resource;
Passed: truth-value;
VInfo: class gfr-version-information-object;

begin

LogText = "--- GDA/NOL Version Infos  (Pkg, Desc, Build#, Oldest#, Copyright, MinG2) -- [the current subsecond time as a time stamp] ---[CRLF]"; 

Passed, LogText = call arm-set-version-info (LogText);

LogText = "[LogText][CRLF][CRLF]       Local Text Resources  (Mod, ResGrp, GfrVer)";

for LTRes = each gfr-local-text-resource do
    Mod = the containing-module of LTRes;
    if Mod is a member of the arm-gda-version-modules of BldData then begin
        conclude that the gfr-version of LTRes = the arm-gda-long-version of release-build-data;
        LogText = "[LogText][crlf]    [the containing-module of LTRes]  [the gfr-resource-group of LTRes]  [the gfr-version of LTRes]  [the gfr-language of LTRes]";
    end;
    if Mod is a member of the arm-nol-version-modules of BldData then begin
        conclude that the gfr-version of LTRes = the arm-nol-long-version of release-build-data;
        LogText = "[LogText][crlf]    [the containing-module of LTRes]  [the gfr-resource-group of LTRes]  [the gfr-version of LTRes]  [the gfr-language of LTRes]";
    end; 
end;

call grm-log-step (Step, LogText, Passed, Win);
return Passed;

end

on error (En, Et)
    call grm-signal-error (Step, En, Et, Win); 
    return false;
end

release-initialize-palette-blocks (Step: class grm-step, Win: class g2-window) = (truth-value)

Blk: class gdl-object;
En: symbol;
Et, LogText: text;
Passed: truth-value = true;

begin

LogText = "--- Initializing palette blocks ---
";

for Blk = each gdl-object do
    if gfr-item-is-palette-object (Blk) then begin
        if the nol-is-permanent of Blk exists then 
            conclude that the nol-is-permanent of Blk = false; 
        call gcc-safe-make-permanent (Blk); 
    end;
end;

LogText = "[LogText]
    Finished initializing palette blocks"; 
 
call grm-log-step (Step, LogText, Passed, Win);
return Passed;

end on error (En, Et)
    call grm-signal-error (Step, En, Et, Win);
    return false;
end

release-set-system-tables (Step: class grm-step, Win: class g2-window) = (truth-value)

CRLF: text = "
";
En: symbol;
Itm: class item;
Et, LogText: text;
Passed: truth-value = true;

begin

LogText = "--- Setting system tables -- [the current subsecond time as a time stamp] ---[CRLF]";

for Itm = each debugging-parameters do
    change the text of the warning-message-level of Itm to "2 (kb errors and deficiencies)";
    change the text of the tracing-message-level of Itm to "0 (no trace messages)";
    change the text of the breakpoint-level of Itm to "0 (no breakpoints)";
    change the text of the tracing-and-breakpoints-enabled@? of Itm to "no";
    make Itm permanent;
end;

for Itm = each timing-parameters do
    change the text of the scheduler-mode of Itm to "real time";
    change the text of the minimum-scheduling-interval of Itm to "0.05 seconds";   {Is this best??}
    change the text of the uninterrupted-procedure-execution-limit of Itm to "1 minute";
    change the text of the milliseconds-to-sleep-when-idle
        of Itm to "10";
    make Itm permanent;
end;

for Itm = each data-server-parameters do
    change the text of the g2-meter-data-service-on of Itm
        to "no";
    make Itm permanent;
end;

for Itm = each simulation-parameters do
    change the text of the simulator-on of Itm to "no";
    make Itm permanent;
end;

for Itm = each kb-configuration do
     change the text of the simulated-optional-modules of Itm to "do not simulate ";
     change the text of the item-configuration of Itm to "none";
     change the text of the initial-g2-user-mode-for-this-kb of Itm to "developer";
     make Itm permanent;
end;
 
for Itm = each fonts do
     change the text of the font-for-attribute-tables of Itm to "large";
     change the text of the font-for-attribute-displays of Itm to "small";
     change the text of the font-for-statements of Itm to "large";
     change the text of the font-for-free-text of Itm to "large";
     change the text of the font-for-editing of Itm to "large";
     change the text of the font-for-descriptions of Itm to "small";
     make Itm permanent;
end; 

for Itm = each editor-parameters do
     change the text of the minimum-width-for-edit-box of Itm to "650";
     change the text of the author-recording-enabled@? of Itm to "yes";
     make Itm permanent;
end;

for Itm = each menu-parameters do
    change the text of the walking-menus@? of Itm to "yes";
    make Itm permanent;
end; 

for Itm = each miscellaneous-parameters do
    change the text of the backward-compatibility-features of Itm to "ignore duplicate list element error";
    make Itm permanent;
end;

for Itm = each drawing-parameters do
    change the text of the allow-scheduled-drawing@? of Itm to "yes";
    change the text of the paint-mode@? of Itm to "yes";
    make Itm permanent;
end;

call grm-log-step (Step, LogText, Passed, Win);
return Passed; 

end

on error (En, Et)
    call grm-signal-error (Step, En, Et, Win);
    return false;
end

release-check-extraneous-wss (Step: class grm-step, Win: class g2-window) = (truth-value)

CRLF: text = "
";
En: symbol;
Et, LogText, ModText: text;
Extras: integer = 4;
Passed: truth-value = true;
Dlg: class uil-dialog;
WS: class kb-workspace;
FText: class borderless-free-text;
UilWS: truth-value; 

begin

LogText = "--- Checking for extra workspaces -- [the current subsecond time as a time stamp]  ---[CRLF]";

for WS = each kb-workspace do
    if the item superior to WS does not exist and the name of WS does not exist and not (there exists a gfr-public-bin upon WS) and not (there exists a uil-title-block upon WS) then begin
        UilWS= false;    {--- Workspace is UIL, GQS, or other utility ---}
        ModText = release-get-module-ws (WS);
        if is-contained-in-text ("uil", ModText) then UilWS = true;
        if is-contained-in-text ("gqs", ModText) then UilWS = true; 
        if is-contained-in-text ("uifile", ModText) then UilWS = true; 
        if is-contained-in-text ("gens", ModText) then UilWS = true; 
        if is-contained-in-text ("glf", ModText) then UilWS = true; 
        if is-contained-in-text ("ptroot", ModText) then UilWS = true;
        if is-contained-in-text ("sys-mod", ModText) then UilWS = true; 
        if is-contained-in-text ("javalink", ModText) then UilWS = true;
        if is-contained-in-text ("g2-pi", ModText) then UilWS = true;
        if is-contained-in-text ("gdluisup", ModText) then UilWS = true; 
        if there exists an object-definition OD upon WS such that (OD is the same object as item-encap) then 
            UilWS = true;    {-- This should be removed as soon as possible ??? --}
        for FText =  each borderless-free-text upon WS do
            if is-contained-in-text ("Application Component",the text of FText) then 
                UilWS = true;
        end;
        if not (UilWS) then begin
            LogText = "[LogText][CRLF]    Potential Extra WS - [ModText]"; 
        end;
    end; 
end; 

call grm-log-step (Step, LogText, Passed, Win);
return Passed; 

end

on error (En, Et)
    call grm-signal-error (Step, En, Et, Win);
    return false;
end

g2:c.PROCEDURE.g2:a.TEXT:
_arm-make-item-stable (Item: class item, Win: class g2-window)

T: text = the text of the item-configuration of Item;
Separator: text = "; ";

begin

if T = "none" then begin
    T = "";
    Separator = "";
end;
if not (is-contained-in-text ("stable-for-dependent-compilations", T)) then begin
    if Item is a class-definition then 
        T = "[T][Separator] declare properties as follows : stable-for-dependent-compilations, stable-hierarchy"
    else 
        T = "[T][Separator] declare properties as follows: stable-for-dependent-compilations";
    change the text of the item-configuration of Item to T; 
    make Item permanent;
end;

end

arm-declare-module-stable (ModuleName: symbol, Win: class g2-window)

Item: class item;
ItemName: value;

{This makes all private procedures and definitions stable}

begin

for Item = each procedure do
    ItemName = call g2-name-for-item (Item);
    if (get-from-text ("[ItemName]", 1, 1) = "_") and  gfr-get-module-of-item (Item) = ModuleName then begin
        call _arm-make-item-stable (Item, Win);
        allow other processing;
    end;
end;
for Item = each definition do
    ItemName = call g2-name-for-item (Item); 
    if (get-from-text ("[ItemName]", 1, 1) = "_") and  gfr-get-module-of-item (Item) = ModuleName then begin
        call _arm-make-item-stable (Item, Win);
        allow other processing;
    end;
end;

end

c.GRM-PROCEDURE.g2:a.TEXT:
release-declare-modules-stable (Step: class grm-step, Win: class g2-window) = (truth-value)

CRLF: text = "
";
En, ModuleName: symbol;
Et, LogText: text;
ModuleArray: class symbol-array = stable-module-array;
N: integer;
Passed: truth-value = true;

begin

LogText = "--- Declaring modules stable -- [the current subsecond time as a time stamp] ---[CRLF]";

for N = 0 to the array-length of ModuleArray - 1 do
    ModuleName = ModuleArray [N];
    call arm-declare-module-stable (ModuleName, Win);
end; 

call grm-log-step (Step, LogText, Passed, Win);
return Passed; 

end

on error (En, Et)
    call grm-signal-error (Step, En, Et, Win);
    return false;
end

release-position-palettes (Step: class grm-step, Win: class g2-window) = (truth-value)

En: symbol;
Et, LogText: text;
Passed: truth-value = true;
PH: class gcc-palette-header;
Xs, Ys: integer;
WS: class kb-workspace;
UB: class uil-goto-workspace-button; 

begin

LogText = "--- Scaling & positioning palettes -- [the current subsecond time as a time stamp] ---
";

create a kb-workspace WS;
show WS with its center at the top left corner of the screen;
Xs = call g2-x-in-window (142, WS, Win);
Ys = call g2-y-in-window (-36, WS, Win);
 
delete WS;
for PH = each gcc-palette-header do
    if the workspace WS of PH exists then begin
        show WS on Win at full scale with its top left corner at (Xs, Ys) in the screen;
        change the size of WS to minimum; {***GBS added before 3.0 Alpha 4 build because we have been leaving palettes with extra space***}
        make WS permanent;
        hide WS;
    end;
end;
WS = nol-api;
show WS on Win at full scale with its top left corner at (Xs, Ys) in the screen;
hide WS;
for UB = each uil-goto-workspace-button upon nol-api do
    if there exists a subworkspace WS of UB then begin 
        show WS on Win at full scale with its top left corner at (Xs, Ys) in the screen;
        hide WS; 
    end;
end; 

call grm-log-step (Step, LogText, Passed, Win);
return Passed;

end

on error (En, Et)
    call grm-signal-error (Step, En, Et, Win);
    return false;
end

