g2:c.PROCEDURE.g2:a.TEXT:
release-insert-protools (Step: class grm-step, Win: class g2-window) = (text)

CRLF: text = "
";
LogText, NotesText: text;
ModInfo: class module-information;
Mods: sequence;

{***************************************************************
This whole mess is a kludge to allow us to save out GDA source
which has protools stuff in it which is related (via a protools relation)
to objects in the GQSVIEWS hierarchy which do not require Protools
as they are not source modules.
*****************************************************************}

begin

if there exists a module-information ModInfo such that (the containing-module of ModInfo = the symbol GFR) then begin 
    Mods = the directly-required-modules of ModInfo;
    Mods = insert-at-end (Mods, the symbol PTROOT);
    conclude that the directly-required-modules of ModInfo = Mods;
    LogText = "[CRLF]    Added Protools to GFR.  There should be no";
    LogText = "[LogText][CRLF]    Notes on the protools module-information object";
    LogText = "[LogText][CRLF]    [the text of the notes of ModInfo][CRLF]";
end else
    LogText = "[CRLF]    FAILED -- Could not find module-information for module GFR";

return LogText;

end

c.GRM-PROCEDURE.g2:a.TEXT:
release-empty-authors (Step: class grm-step, Win: class g2-window) = (truth-value)

CRLF: text = "
";
En: symbol;
Et, LogText: text;
Passed: truth-value = true;
Itm: class item;
N: integer  = 0;
M: integer = 0;
Mod: symbol;
ModSeq: sequence; 

begin

LogText = "--- Clearing authors & hiding name boxes-- [the current subsecond time as a time stamp] ---[CRLF]";

ModSeq = insert-at-end (insert-at-end (the g2-array-sequence of stable-module-array, the symbol GDADLG), the symbol GDABSDLG);

change the text of the warning-message-level of debugging-parameters to " 0 (no warning messages) ";

for Itm = each definition do
    begin
        Mod = gfr-get-module-of-item (Itm);
        if Mod is a member of ModSeq then begin 
            change the text of the authors of Itm to "none";
            make Itm permanent;
            N = N + 1;
        end; 
    end on error (En, Et) 
        M = M + 1; 
    end;
end;
LogText = "[LogText][CRLF]    [N] definitions done, [M] errors";
allow other processing; 
N = 0; 
M = 0;
    
for Itm = each procedure do
    begin
        Mod = gfr-get-module-of-item (Itm); 
        if Mod is a member of ModSeq then begin 
            change the text of the authors of Itm to "none";
            make Itm permanent;
            N = N + 1;
        end; 
    end on error (En, Et) 
        M = M + 1; 
    end;
end; 
LogText = "[LogText][CRLF]    [N] procedures done, [M] errors ";
allow other processing; 
N = 0; 
M = 0; 

for Itm = each relation do
    begin
        Mod = gfr-get-module-of-item (Itm); 
        if Mod is a member of ModSeq then begin 
            change the text of the authors of Itm to "none";
            make Itm permanent;
            N = N + 1;
        end;  
    end on error (En, Et) 
        M = M + 1; 
    end;
end; 
LogText = "[LogText][CRLF]    [N] relations done, [M] errors ";
allow other processing; 
{-- Note that the following will not catch whenever rules since they are not active --}
N = 0; 
M = 0; 

for Itm = each rule do
    begin
        Mod = gfr-get-module-of-item (Itm); 
        if Mod is a member of ModSeq then begin 
            change the text of the authors of Itm to "none";
            make Itm permanent;
            N = N + 1;
        end;  
    end on error (En, Et) 
        M = M + 1; 
    end;
end; 
LogText = "[LogText][CRLF]    [N] rules done, [M] errors ";
N = 0; 
M = 0; 

for Itm = each module-information do
    begin
        Mod = gfr-get-module-of-item (Itm); 
        if Mod is a member of ModSeq then begin 
            change the text of the authors of Itm to "none";
            make Itm permanent;
            N = N + 1;
        end; 
    end on error (En, Et) 
        M = M + 1; 
    end;
end; 
LogText = "[LogText][CRLF]    [N] module-informations done, [M] errors "; 

for Itm = each user-menu-choice do
    begin
        Mod = gfr-get-module-of-item (Itm); 
        if Mod is a member of ModSeq then begin 
            change the text of the authors of Itm to "none";
            make Itm permanent;
            N = N + 1;
        end; 
    end on error (En, Et) 
        M = M + 1; 
    end;
end; 
LogText = "[LogText][CRLF]    [N] menu-choices done, [M] errors ";
N = 0; 
M = 0; 

for Itm = each system-table do
    begin
        Mod = gfr-get-module-of-item (Itm); 
        if Mod is a member of ModSeq then begin 
            change the text of the authors of Itm to "none";
            make Itm permanent;
            N = N + 1;
        end; 
    end on error (En, Et) 
        M = M + 1; 
    end;
end; 
LogText = "[LogText][CRLF]    [N] system-tables done, [M] errors ";

change the text of the warning-message-level of debugging-parameters to " 2 (kb errors and deficiencies) ";
make debugging-parameters permanent; 
 
call grm-log-step (Step, LogText, Passed, Win);
return Passed;

end

on error (En, Et)
    call grm-signal-error (Step, En, Et, Win);
    return false;
end

release-uninitialize-gold-books (Step: class grm-step, Win: class g2-window) = (truth-value)

CRLF: text = "
";
Array: class g2-array;
BldData: class grm-data-object = release-build-data;
Book: class gold-book;
DocSeg: text = the grm-doc-segment of BldData;
En, Mod: symbol; 
Et, LogText: text;
ModulesWithBooks: sequence = sequence (the symbol GDA, the symbol NOL); 
Passed: truth-value = true;
Table: class _gold-table; 

begin

LogText = "--- Uninitializing Gold Books -- [the current subsecond time as a time stamp] ---[CRLF]";

for Book = each gold-book do
    Mod = the containing-module of Book;
    if Mod is a member of ModulesWithBooks then begin
        LogText = "--- Gold Book paths -- [the grm-doc-segment of release-build-data] -- [the current subsecond time as a time stamp] ---[CRLF]";
        LogText = call grm-set-doc-paths (LogText);
        conclude that the _gold-initialized-p of Book = false;
        for Table = each _gold-table that is an attribute of Book do
            for Array = each g2-array that is an attribute of Table do
                change the array-length of Array to 0;
                call gfr-array-copy-elements-to-initial-values (Array);
            end;
        end; 
        make Book permanent; 
        LogText = "[LogText][CRLF]        Calling gold-uninitialize for: [the name of Book][CRLF]";
    end;
end;

call grm-log-step (Step, LogText, Passed, Win);
return Passed;

end

on error (En, Et)
    call grm-signal-error (Step, En, Et, Win);
    return false;
end

release-delete-protools (Step: class grm-step, Win: class g2-window) = (truth-value)

CRLF: text = "
";
En: symbol;
Et, LogText: text;
ExpNotes: integer = 17;
ModInfo: class module-information;
Mods: sequence;
N, N1, N2, X: integer;
Passed: truth-value = true;
UData: class integer-parameter = release-inspect-integer;

begin

LogText = "--- Deleting Gdadev1, Protools & Ptroot -- [the current subsecond time as a time stamp] ---[CRLF]";

{*** Get rid of Gdadev1, Protools & Ptroot ***}
call g2-delete-module (the symbol GDADEV1, true); 
call g2-delete-module (the symbol PROTOOLS, true);
call g2-delete-module (the symbol PTROOT, true);
allow other processing;

{*** Show module-information objects with notes (before cleanup) ***}
conclude that Udata = -1; 
call g2-start-inspect-session ("show on a workspace every module-information with notes", release-inspect-callback, UData, 1, 1.0);
wait until (UData >= 0) checking every 1 second;
N1 = UData;

for ModInfo = each module-information do
    Mods = the directly-required-modules of ModInfo;
    X = the number of elements in Mods - 1;
    N = 0;
    repeat
        exit if N > X;
        if Mods [N] = the symbol GDADEV1 or Mods [N] = the symbol PROTOOLS or Mods [N] = the symbol PTROOT then begin
            Mods = remove (Mods, N);
            conclude that the directly-required-modules of ModInfo = Mods;
            X = X - 1;
        end else
            N = N + 1;
    end;
end; 

{*** Show module-information objects with notes (after cleanup) ***}
conclude that Udata = -1; 
call g2-start-inspect-session ("show on a workspace every module-information with notes", release-inspect-callback, UData, 1, 1.0);
wait until (UData >= 0) checking every 1 second;
N2 = UData;

LogText = "[LogText][CRLF]    Removing Protools";
LogText = "[LogText][CRLF]    Module Information Objects with Notes -- Before Cleanup: [N1]  After Cleanup: [N2]";

{*** Show items with notes ***}
conclude that Udata = -1; 
call g2-start-inspect-session ("show on a workspace every item with notes", release-inspect-callback, UData, 1, 1.0);
wait until (UData >= 0) checking every 1 second;
LogText = "[LogText][CRLF]        Items with Notes, ([ExpNotes] expected):  [UData]";

call grm-log-step (Step, LogText, Passed, Win);
return Passed;

end on error (En, Et)
    call grm-signal-error (Step, En, Et, Win);
    return false;
end

