release-create-dist-and-dev-directories (Step: class grm-step, Win: class g2-window) = (truth-value)

BldData: class grm-data-object = release-build-data;
CRLF: text = "
";
CycleCode: text = the grm-cycle-code of BldData;
DirExists: truth-value;
En: symbol;
DevDir, DistDir, Et, LogText: text; 
Passed: truth-value = true;
GDAShortVersion: text = the arm-gda-short-version of BldData;
NOLShortVersion: text = the arm-nol-short-version of BldData; 
Separator: text = "/";

begin

call grm-open-log ("Release-GdaNol", Win);
LogText = "--- Creating GDA DEV & DIST directories -- [the current subsecond time as a time stamp]---[CRLF]";

if (is-contained-in-text("\", the grm-utils-path of BldData)) then begin
  Separator = "\";
end;

{--- GDA directory ---}
DevDir = "[the grm-utils-path of BldData][Separator]gda";
DirExists = call g2-directory-exists (DevDir); 
if not (DirExists) then begin    {--- Try to create it ---} 
    call g2-spawn-process-to-run-command-line ("mkdir [DevDir]");
    LogText = "[LogText][CRLF]    Creating GDA Directory: [DevDir][CRLF]";
    wait for 5 seconds; 
    DirExists = call g2-directory-exists (DevDir);
    if not (DirExists) then begin   {--- Failed to create it ---} 
        LogText = "[LogText]    Missing [DevDir]";
        Passed = false;
        go to LOGSTEP;
    end;
end;
    
LogText = "[LogText]    Verified [DevDir]"; 

{--- GDA dev directory ---}
DevDir = "[the grm-utils-path of BldData][Separator]gda[Separator]dev";        
DirExists = call g2-directory-exists (DevDir); 
if not (DirExists) then begin    {--- Try to create it ---} 
    call g2-spawn-process-to-run-command-line ("mkdir [DevDir]");
    LogText = "[LogText][CRLF]    Creating GDA Dev Directory: [DevDir][CRLF]";
    wait for 5 seconds; 
    DirExists = call g2-directory-exists (DevDir);
    if not (DirExists) then begin   {--- Failed to create it ---} 
        LogText = "[LogText]    Missing [DevDir]";
        Passed = false;
        go to LOGSTEP;
    end;
end;
LogText = "[LogText]    Verified [DevDir]";

{--- GDA dev version-cycle subdirectory ---}
DevDir = "[the grm-utils-path of BldData][Separator]gda[Separator]dev[Separator][GDAShortVersion]-[the grm-cycle-code of BldData]";
DirExists = call g2-directory-exists (DevDir);
if not (DirExists) then begin    {--- Try to create it ---} 
    call g2-spawn-process-to-run-command-line ("mkdir [DevDir]");
    LogText = "[LogText][CRLF]    Creating GDA Dev Version-Cycle Subdirectory: [DevDir][CRLF]";
    wait for 5 seconds; 
    DirExists = call g2-directory-exists (DevDir);
    if not (DirExists) then begin   {--- Failed to create it ---} 
        LogText = "[LogText]    Missing [DevDir]";
        Passed = false;
        go to LOGSTEP;
    end;
end;
LogText = "[LogText]    Verified [DevDir]"; 

{--- GDA dist directory ---} 
DistDir = "[the grm-utils-path of BldData][Separator]gda[Separator]dist";
DirExists = call g2-directory-exists (DistDir);
if not (DirExists) then begin    {--- Try to create it ---}
    call g2-spawn-process-to-run-command-line ("mkdir [DistDir]");
    LogText = "[LogText][CRLF]    Creating GDA Dist Directory: [DistDir][CRLF]";
    wait for 5 seconds;
    DirExists = call g2-directory-exists (DistDir);
    if not (DirExists) then begin   {--- Failed to create it ---} 
        LogText = "[LogText]    Missing [DistDir]";
        Passed = false;
        go to LOGSTEP; 
    end;
end;                
LogText = "[LogText]    Verified [DistDir]";

{--- GDA dist bin subdirectory ---}
DistDir = "[the grm-utils-path of BldData][Separator]gda[Separator]dist[Separator]bin";
call g2-spawn-process-to-run-command-line ("mkdir [DistDir]");
LogText = "[LogText][CRLF]    Creating GDA Dist bin Subdirectory: [DistDir][CRLF]";
wait for 5 seconds; 
DirExists = call g2-directory-exists (DistDir);
if not (DirExists) then begin
    LogText = "[LogText]    Missing [DistDir]";
    Passed = false;
    go to LOGSTEP;
end;
LogText = "[LogText]    Verified [DistDir]";

{--- GDA dist kbs subdirectory ---}
DistDir = "[the grm-utils-path of BldData][Separator]gda[Separator]dist[Separator]kbs";
{--- Try to create it ---} 
call g2-spawn-process-to-run-command-line ("mkdir [DistDir]");
LogText = "[LogText][CRLF]    Creating GDA Dist kbs Subdirectory: [DistDir][CRLF]";
wait for 5 seconds; 
DirExists = call g2-directory-exists (DistDir);
if not (DirExists) then begin    {--- Failed to create it ---}
    LogText = "[LogText]    Missing [DistDir]";
    Passed = false;
    go to LOGSTEP;
end;
LogText = "[LogText]    Verified [DistDir]";

LogText = "[LogText][CRLF]--- Creating NOL DEV & DIST directories -- [the current subsecond time as a time stamp]---[CRLF]"; 

{--- NOL directory ---}
DevDir = "[the grm-utils-path of BldData][Separator]nol";   
DirExists = call g2-directory-exists (DevDir); 
if not (DirExists) then begin    {--- Try to create it ---}
    call g2-spawn-process-to-run-command-line ("mkdir [DevDir]");
    LogText = "[LogText][CRLF]    Creating NOL Directory: [DevDir][CRLF]";
    wait for 5 seconds;
    DirExists = call g2-directory-exists (DevDir);
    if not (DirExists) then begin    {--- Failed to create it ---}
        LogText = "[LogText]    Missing [DevDir]";
        Passed = false;
        go to LOGSTEP; 
    end;
end;                
LogText = "[LogText]    Verified [DevDir]"; 

{--- NOL dev directory ---}
DevDir = "[the grm-utils-path of BldData][Separator]nol[Separator]dev";      
DirExists = call g2-directory-exists (DevDir); 
if not (DirExists) then begin    {--- Try to create it ---}
    call g2-spawn-process-to-run-command-line ("mkdir [DevDir]");
    LogText = "[LogText][CRLF]    Creating NOL Dev Directory: [DevDir][CRLF]";
    wait for 5 seconds;
    DirExists = call g2-directory-exists (DevDir);
    if not (DirExists) then begin    {--- Failed to create it ---}
        LogText = "[LogText]    Missing [DevDir]";
        Passed = false;
        go to LOGSTEP; 
    end;
end;                
LogText = "[LogText]    Verified [DevDir]";

{--- NOL dev version-cycle subdirectory ---}
DevDir = "[the grm-utils-path of BldData][Separator]nol[Separator]dev[Separator][NOLShortVersion]-[the grm-cycle-code of BldData]";
DirExists = call g2-directory-exists (DevDir);
if not (DirExists) then begin    {--- Try to create it ---}
    call g2-spawn-process-to-run-command-line ("mkdir [DevDir]");
    LogText = "[LogText][CRLF]    Creating NOL Dev Version-Cycle Subdirectory: [DevDir][CRLF]";
    wait for 5 seconds; 
    DirExists = call g2-directory-exists (DevDir);
    if not (DirExists) then begin    {--- Failed to create it ---}
        LogText = "[LogText]    Missing [DevDir]";
        Passed = false;
        go to LOGSTEP;
    end;
end;
LogText = "[LogText]    Verified [DevDir]"; 

{--- NOL dist directory ---} 
DistDir = "[the grm-utils-path of BldData][Separator]nol[Separator]dist";
DirExists = call g2-directory-exists (DistDir);
if not (DirExists) then begin    {--- Try to create it ---}
    call g2-spawn-process-to-run-command-line ("mkdir [DistDir]");
    LogText = "[LogText][CRLF]    Creating NOL Dev Directory: [DistDir][CRLF]";
    wait for 5 seconds;
    DirExists = call g2-directory-exists (DistDir);
    if not (DirExists) then begin   {--- Failed to create it ---} 
        LogText = "[LogText]    Missing [DistDir]";
        Passed = false;
        go to LOGSTEP; 
    end;
end;                
LogText = "[LogText]    Verified [DistDir]";

{--- NOL dist bin subdirectory ---}
DistDir = "[the grm-utils-path of BldData][Separator]nol[Separator]dist[Separator]bin";
{--- Try to create it ---} 
call g2-spawn-process-to-run-command-line ("mkdir [DistDir]");
LogText = "[LogText][CRLF]    Creating NOL Dist bin Subdirectory: [DistDir][CRLF]";
wait for 5 seconds; 
DirExists = call g2-directory-exists (DistDir);
if not (DirExists) then begin    {--- Failed to create it ---}
    LogText = "[LogText]    Missing [DistDir]";
    Passed = false;
    go to LOGSTEP;
end;
LogText = "[LogText]    Verified [DistDir]";

{--- NOL dist kbs subdirectory ---}
DistDir = "[the grm-utils-path of BldData][Separator]nol[Separator]dist[Separator]kbs";
{--- Try to create it ---} 
call g2-spawn-process-to-run-command-line ("mkdir [DistDir]");
LogText = "[LogText][CRLF]    Creating NOL Dist kbs Subdirectory: [DistDir][CRLF]";
wait for 5 seconds; 
DirExists = call g2-directory-exists (DistDir);
if not (DirExists) then begin    {--- Failed to create it ---}
    LogText = "[LogText]    Missing [DistDir]";
    Passed = false;
    go to LOGSTEP;
end;
LogText = "[LogText]    Verified [DistDir]";

LOGSTEP:

call grm-log-step (Step, LogText, Passed, Win);
return Passed;

end

on error (En, Et)
    call grm-signal-error (Step, En, Et, Win);
    return false;
end

release-check-networking-objects (Step: class grm-step, Win: class g2-window) = (truth-value)

Cnt: integer;
CRLF: text = "
";
En: symbol;
Itm: class item;
Et, LogText: text;
Passed: truth-value = true;

begin

LogText = "--- Checking network objects -- [the current subsecond time as a time stamp] ---[CRLF]";

Cnt = 0;
for Itm = each gpe-window do 
    Cnt = Cnt + 1;
end;
if Cnt /= 2 then 
    LogText = "[LogText][CRLF]  Did not find the proper number ([Cnt]) of gpe-windows"
else
   LogText = "[LogText][CRLF]  gpe-windows checked okay";

Cnt = 0;
for Itm = each gpe-remote-g2-process do 
    Cnt = Cnt + 1;
end;
if Cnt > 1 then 
    LogText = "[LogText][CRLF]  [Cnt] instances of gpe-remote-g2-process found"
else
    LogText = "[LogText][CRLF]  gpe-remote-g2-process checked okay"; 

Cnt = 0;
for Itm = each gcc-routing-object do 
    Cnt = Cnt + 1;
end;
if Cnt > 1 then 
    LogText = "[LogText][CRLF]  [Cnt] instances of gcc-routing-object found"
else
    LogText = "[LogText][CRLF]  gcc-routing-object checked okay"; 

Cnt = 0;
for Itm = each gdl-data-transmitter do 
    Cnt = Cnt + 1;
end;
if Cnt > 1 then 
    LogText = "[LogText][CRLF]  [Cnt] instances of gdl-data-transmitter found"
else
    LogText = "[LogText][CRLF]  gdl-data-transmitter checked okay";

Cnt = 0;
for Itm = each gdl-belief-transmitter do 
    Cnt = Cnt + 1;
end;
if Cnt > 1 then 
    LogText = "[LogText][CRLF]  [Cnt] instances of gdl-belief-transmitter found"
else
    LogText = "[LogText][CRLF]  gdl-belief-transmitter checked okay"; 

Cnt = 0;
for Itm = each gdl-control-transmitter do 
    Cnt = Cnt + 1;
end;
if Cnt > 1 then 
    LogText = "[LogText][CRLF]  [Cnt] instances of gdl-control-transmitter found"
else
    LogText = "[LogText][CRLF]  gdl-control-transmitter checked okay"; 

call grm-log-step (Step, LogText, Passed, Win);
return Passed; 

end

on error (En, Et)
    call grm-signal-error (Step, En, Et, Win);
    return false;
end

release-position-dialog (Step: class grm-step, Win: class g2-window) = (truth-value)

BldData: class grm-data-object = release-build-data;
Dlg: class uil-dialog;
WS: class kb-workspace; 
En, Mod: symbol;
Et, LogText: text;
Passed: truth-value = true;


begin

LogText = "--- Scaling & positioning dialogs -- [the current subsecond time as a time stamp] ---
";

for Dlg = each uil-dialog do
    Mod = the containing-module of Dlg;
    if Mod is a member of the g2-array-sequence of the grm-modules-to-save of BldData then begin
        if the subworkspace WS of Dlg exists then begin
            show WS at full scale with its center  at the center of the screen;
            hide WS;
        end;
    end;
end; 

call grm-log-step (Step, LogText, Passed, Win);
return Passed;

end

on error (En, Et)
    call grm-signal-error (Step, En, Et, Win);
    return false;
end

