release-cleanup-gfr-bins (Step: class grm-step, Win: class g2-window) = (truth-value)

Bin: class gfr-public-bin;
CRLF: text = "
";
En, Mod: symbol;
Et, LogText: text;
Itm: class item;
Passed: truth-value = true;
WS: class  kb-workspace;

begin

LogText = "--- Cleaning up GFR bins -- [the current subsecond time as a time stamp] ---[CRLF]";

for Bin = each gfr-public-bin do
    if the subworkspace WS of Bin exists then 
        for Itm = each item upon WS do
            if Itm is a gdl-path-item or Itm is a gdl-encapsulation-block or Itm is a gfr-module-setting then begin
                make Itm transient;
                delete Itm;
            end;
        end;
end; 

call grm-log-step (Step, LogText, Passed, Win);
return Passed; 

end

on error (En, Et)
    call grm-signal-error (Step, En, Et, Win);
    return false;
end

release-denude-stuff (Step: class grm-step, Win: class g2-window) = (truth-value)

CRLF: text = "
";
En, Mod: symbol;
Et, LogText: text;
Passed: truth-value = true;
WS: class KB-Workspace;
GSW: class gcc-subworkspace-icon;
USW: class uil-goto-workspace-button;
GVI: class gfr-version-information-object;
CP: class connection-post;
Proc: class procedure;
FuncDef: class function-definition; 

begin

LogText = "--- Denuding various GDA objects -- [the current subsecond time as a time stamp] ---[CRLF]";

WS = gcc-top-level;
for GSW = each gcc-subworkspace-icon upon WS do
    call hide-name-boxes-and-name-boxes-in-ws-hierarchy (GSW)
end;

WS = gda-top-level;
for CP = each connection-post upon WS do
    call hide-name-boxes-and-name-boxes-in-ws-hierarchy (CP);
end;   

WS = gda-basic-dialogs-ws;
call hide-name-boxes-and-name-boxes-in-ws-hierarchy (WS);

WS = gda-basic-top-level;
GVI = the gfr-version-information-object upon WS;
call hide-name-boxes-and-name-boxes-in-ws-hierarchy (GVI); 

WS = gda-core2-ws;
for GSW = each gcc-subworkspace-icon upon WS do
    call hide-name-boxes-and-name-boxes-in-ws-hierarchy (GSW)
end; 

WS = gda-dialogs-ws;
call hide-name-boxes-and-name-boxes-in-ws-hierarchy (WS); 

WS = the workspace of gdl-top-level;
for GSW = each gcc-subworkspace-icon upon WS do
    if GSW is not a gcc-palette-workspace then 
        call hide-name-boxes-and-name-boxes-in-ws-hierarchy (GSW);
end;  

WS = gqm-top-level;
GVI = the gfr-version-information-object upon WS;
call hide-name-boxes-and-name-boxes-in-ws-hierarchy (GVI);
for USW = each uil-goto-workspace-button upon WS do
   if the uil-button-arrow-color icon-color of USW = the symbol cadet-blue then 
        call hide-name-boxes-and-name-boxes-in-ws-hierarchy (USW);
end;

WS = gqmv-top-level;
GVI = the gfr-version-information-object upon WS; 
call hide-name-boxes-and-name-boxes-in-ws-hierarchy (GVI);

WS = neuron-line-top-level;
for CP = each connection-post upon WS do
    call hide-name-boxes-and-name-boxes-in-ws-hierarchy (CP);
end;   
for Proc = each procedure do 
    if the g2-icon-background of the item-color-pattern of Proc exists and the g2-icon-background of the item-color-pattern of Proc = the symbol red then 
            conclude that the g2-icon-background of the item-color-pattern of Proc = the symbol light-gray;
end;
for FuncDef = each function-definition do
    if the text-color of FuncDef exists and the text-color of FuncDef = the symbol red then 
        conclude that the item-color-pattern of FuncDef = structure (background-color: the symbol background, text-color: the symbol foreground);
end; 

call grm-log-step (Step, LogText, Passed, Win);
return Passed; 

end

on error (En, Et)
    call grm-signal-error (Step, En, Et, Win);
    return false;
end

