release-check-incomplete-procs (Step: class grm-step, Win: class g2-window) = (truth-value)

CRLF: text = "
";
En, Mod: symbol;
Et, LogText: text;
Passed: truth-value = true;
Proc: class procedure;
ItemModule: symbol;
NotesError: class error;
Counter: integer = 0; 

begin

LogText = "--- Checking incomplete procedures -- [the current subsecond time as a time stamp] ---[CRLF]";

for Proc = each procedure do
    Counter = Counter + 1;
    ItemModule = gfr-get-module-of-item (Proc);
    if the item-status of Proc = the symbol INCOMPLETE and ItemModule /= the symbol DEVTOOLS and ItemModule /= the symbol RELEASE-GDANOL and ItemModule /= the symbol GDADEV1 and ItemModule /= the symbol PROTOOLS and ItemModule /= the symbol PTROOT and ItemModule /= the symbol G2EVLISS and ItemModule /= the symbol JAVALINK and ItemModule /= the symbol GRTL then begin
        LogText = "[LogText][CRLF]    Incomplete procedure: [the name of Proc]";
        call grm-log-step (Step, LogText, false, Win); 
        return false;
    end;
end;

LogText = "[LogText][CRLF]All items ([Counter]) checked -- no Incomplete Procedures were found"; 

call grm-log-step (Step, LogText, Passed, Win);
return Passed; 

end on error (En, Et)
    call grm-signal-error (Step, En, Et, Win);
    return false;
end

release-set-copyrights (Step: class grm-step, Win: class g2-window) = (truth-value)

BldData: class grm-data-object = release-build-data;
CRLF: text = "
";
En, Mod: symbol;
Et, LogText: text;
Passed: truth-value = true;
WS: class kb-workspace;

begin

LogText = "--- Setting copyrights -- [the current subsecond time as a time stamp] ---[CRLF]"; 

{*** Set GDA & NOL copyright notices ***}
WS = the workspace of _gdl-copyright;
change the text of _gdl-release-version-text to the arm-gda-long-version of BldData;
change the text of _gdl-g2-use-version-text to the grm-long-version of BldData;
change the text of _gdl-copyright to the arm-gcc-copyright of BldData;
change the size of WS to minimum;
make WS permanent;

WS = the workspace of _nol-copyright; 
change the text of _nol-release-version-text to the arm-nol-long-version of BldData;
change the text of _nol-g2-use-version-text to the grm-long-version of BldData;
change the text of _nol-copyright to the arm-gcc-copyright of BldData; 
change the size of WS to minimum;
make WS permanent;

WS = the workspace of gcc-copyright-date;
change the text of gcc-copyright-date to the arm-gcc-copyright of BldData;
change the size of WS to minimum;
make WS permanent;

WS = the workspace of gdl-package-information-text;
change the text of gdl-package-information-text to the arm-gdl-copyright of BldData;
change the size of WS to minimum;
make WS permanent; 

call grm-log-step (Step, LogText, Passed, Win);
return Passed;

end

on error (En, Et)
    call grm-signal-error (Step, En, Et, Win); 
    return false;
end

