    return false;
end

release-copy-exes-and-demos (Step: class grm-step, Win: class g2-window) = (truth-value)

CRLF: text = "
";
BldData: class grm-data-object = release-build-data;
CopyLog: text = "[the grm-utils-path of BldData]\copylog.txt";
CycleCode: text = "[the grm-cycle-code of BldData]";
File: class g2-stream;
FileExists, ProcessExists: truth-value;
En: symbol;
Et, Line, LogText: text;
MaxWait: integer = 60;
N: integer = 0;
Passed: truth-value = true;
PID, StartTime: float;
GDAShortVer: text = "[the arm-gda-short-version of BldData]";
NOLShortVer: text = "[the arm-nol-short-version of BldData]"; 

begin

LogText = "--- Copying executables and demos -- [the current subsecond time as a time stamp] ---[CRLF]";

go to LOGSTEP; {do not copy examples any more --binghe}

{*** Launch the batch file to do the copying ***}
PID = call g2-spawn-process-to-run-command-line ("[the grm-source-path of BldData]\gdanol\common\build\tools\CopyDemosToDist.bat [the grm-source-path of BldData] [the grm-utils-path of BldData] [GDAShortVer]-[CycleCode] [NOLShortVer]-[CycleCode] > [CopyLog]");
if PID = -1 then begin
    LogText = "[LogText][CRLF]    CopyDemosToDist.bat failed to launch";
    Passed = false;
    go to LOGSTEP;
end else begin
    StartTime = the current subsecond time;
    LogText = "[LogText][CRLF]    Launched CopyDemosToDist.bat - PID = [PID]";
    repeat
        wait for 5 seconds;
        ProcessExists = call g2-process-exists (PID);
        exit if not (ProcessExists);
        if the current subsecond time > StartTime + 300.0 then begin
            LogText = "[LogText][CRLF]    -- The copy command process may have hung --";
            LogText = "[LogText][CRLF]    FAILED -- probable failure";
            exit if true;
        end;
    end;
end;

repeat
    wait for 2 seconds;
    N = N + 2;
    FileExists = call g2-file-exists (CopyLog);
    exit if FileExists or N > MaxWait;
end;

if N > MaxWait then begin
    LogText = "[LogText][CRLF]    Could not find the batch log file for the Exe & Demo copy";
    LogText = "[LogText][CRLF]    after [N] seconds -- copy may have failed";
    Passed = false;
    go to LOGSTEP;
end else
    File = call g2-open-file-for-read (CopyLog);
    conclude that the text-conversion-style of File is grm-text-conversion-style;
    LogText = "[LogText][CRLF]    --- Inserting Copy log entries ---";

repeat
    Line = call g2-read-line (File);
    exit if the g2-stream-status of File = the symbol END-OF-FILE-REACHED;
    LogText = "[LogText][CRLF]      [Line]";
end;

call g2-close-file (File);
LogText = "[LogText][CRLF][CRLF]    --- End of Copy log entries";
LogText = "[LogText][CRLF]    Finished copying executables and demos";

LOGSTEP:

call grm-log-step (Step, LogText, Passed, Win);
return Passed;

end

on error (En, Et)
    call grm-signal-error (Step, En, Et, Win);
    return false;
end
