g2:c.PROCEDURE.g2:a.TEXT:
gdadev-write-all-procedures-current (TestP: truth-value, Client: class ui-client-item)
Proc: class procedure;
TestLimit : integer = 10;
N: integer = 0;
ModuleOfProc: symbol;
AA: sequence;
Stamp: structure;
begin
    for Proc = each procedure do
        exit if TestP and  N > TestLimit; 
        if Proc is not a method-declaration then begin
            ModuleOfProc = gfr-get-module-of-item (Proc);
            if ModuleOfProc is a member of gdadev-module-list then begin
                AA = the authors of the authors of Proc;
                if AA[0] exists and (AA[0] = the symbol msn or AA[0] = the symbol apps) then begin
                    Stamp = the timestamp of the authors of Proc;
                    if the year of Stamp = 1999 and the month of Stamp >= 2 then call write-proc-to-file (Proc);
                end;
            end;    
        end;
        N = N + 1;
    end;
    call protools-inform (Client, "Done writing out [N] procedures");
end

gdadev-write-all-procedures-old (TestP: truth-value, Client: class ui-client-item)
Proc: class procedure;
TestLimit : integer = 10;
N: integer = 0;
ModuleOfProc: symbol;
begin
    for Proc = each procedure do
        exit if TestP and  N > TestLimit;
        if Proc is not a method-declaration then begin
            ModuleOfProc = gfr-get-module-of-item (Proc);
            if ModuleOfProc is a member of gdadev-module-list then call write-proc-to-file (Proc);
        end;
        N = N + 1;
    end;
end

write-proc-to-file (Proc: class procedure)
Strm: class g2-stream;
NameString: text;
PosnOfSep: integer;
begin 
    NameString = lower-case-text ((if the name N of Proc exists then "[N]" else the text of the qualified-name of Proc));
    PosnOfSep = position-of-text ("::", NameString);
    if (PosnOfSep > 0) then
        NameString = "[get-from-text(NameString, 1, PosnOfSep - 1)]__[get-from-text(NameString, PosnOfSep + 2, length-of-text (NameString))]";
    Strm = call g2-open-file-for-write ("[gdadev-current-scratch-directory]/[NameString].[gdadev-current-file-suffix]");
    inform the operator that "Wrote [gdadev-current-scratch-directory]/[NameString].[gdadev-current-file-suffix]";
    call g2-write-line (Strm, the text of Proc);
    call g2-close-file (Strm);
end

gdl-name-all-dialog-texts()

{This is used in internationalization.  If a text is named, it gets saved to a flat file in the "save configuration" action.  When reloaded, the translated text is substituted in the dialog, using the name as the key}

DLG: class uil-dialog;
WS: class kb-workspace;
FT: class free-text;
BFT: class borderless-free-text;
new-name: symbol;
Root: text;
I: integer;
begin
  for DLG = each uil-dialog do
     WS = the subworkspace of DLG;
     Root = "[the name of DLG]";
     i = 0;
     for FT = each free-text upon WS do
        if the name of FT does not exist then begin
           repeat
              new-name = Symbol("[root]-text-[i]");
              if the item named by new-name does not exist then exit if true else i = i + 1;
           end;
           change the name of FT to new-name;
           make FT permanent;
        end;
     end;
     for BFT = each borderless-free-text upon WS do
        if the name of BFT does not exist then begin
           repeat
              new-name = Symbol("[root]-text-[i]");
              if the item named by new-name does not exist then exit if true else i = i + 1;
           end;
           change the name of BFT to new-name;
           make BFT permanent;
        end;
     end;
  end;
end

get-command-line ()
I: integer;
NthArg: text;
begin 
    for i = 0 to 10 do
        NthArg = call g2-get-command-line-argument-from-launch (i);
        inform the operator that "[i]th arg = [NthArg]";
    end;
end

keep-tw-busy(Win: class g2-window)
WS: class kb-workspace = the workspace of this procedure;
I: integer = 0;
begin
    repeat
       show WS on Win;
        wait for 10 seconds;
        hide WS;
        if remainder(I, 2) = 0 then change the background-color of ws to blue else change the background-color of ws to red;
        I = I + 1;
    end;
end

find-bad-menu-option-items()
OD: class object-definition;
T, ET: text;
Mod: symbol;
EN, classname: symbol;
itm: class item;
begin
  for OD = each object-definition do
     allow other processing;
     mod = gfr-get-module-of-item(OD);
     if mod /= the symbol uildefs and mod /= the symbol uilroot and mod /= the symbol uil then begin
         T = the text of the class-specific-attributes of OD;
         classname = the name of OD;
         if is-contained-in-text("an instance", T) then begin
             create an instance ITM of the class named by classname;
             delete ITM;
         end on error(EN, ET) inform the operator that "[ET]"; end;
     end;
  end;
  inform the operator that "ALL DONE!!!!";
end

show-procs-changed(pattern: text)
P : class procedure;
FT: class free-text;
txt: text = "The procedures change in [Pattern]";
auth-txt: text;
POS: integer;
EN: symbol;
ET: text;
I: integer = 1;
begin    
    for P = each procedure do
        begin
            auth-txt = the text of the authors of P;
            POS = position-of-text(pattern, auth-txt);
            if POS > 0 then begin
                txt = "[txt] 
[I]. [the name of P] by [auth-txt]";
                inform the operator that "[the name of P]";
                I = I + 1;
            end; 
        end on error (EN,ET) 
        end;      
    end;
    inform the operator that "txt = [txt]";
    create a free-text FT;
    change the text of FT to txt;
    transfer FT to this workspace;
end

gda-accept-proc(txt: text) = (integer)
begin
    change the text of helipad to txt;
    return 1;
end

get-module-information-object(Target-module: symbol) = (class module-information)
{Returns the module-information object corresponding to the symbol name}
module: symbol;
module-object: class module-information;
begin
   for module-object = each module-information do
       if the text of the top-level-module of module-object = "[target-module]" then return module-object;
   end;
   signal the symbol error, "Module named by [target-module] does not exist";
end

text-to-symbol-list(T: text) = (class symbol-list)
{Takes comma-separated text string and returns as a list of symbols in the contents of the text string}
SL: class symbol-list;
S: symbol;
pos: integer;
begin
    create a symbol-list SL;
    repeat
      pos = position-of-text(",", T);
      exit if pos = 0;
      S = symbol(get-from-text(T, 1, pos - 1));
      insert S at the end of the symbol list SL;
      T = omit-from-text(T, 1, pos);
   end;
   insert symbol(T) at the end of the symbol list SL;
   return SL;
end

module-is-required(Parent: symbol, Child: symbol) = (truth-value)
{Returns true if child module is required by parent module}
Parent-module: class module-information;
required-modules: class symbol-list;
result: truth-value = false;
S: symbol;
begin
   Parent-module = call get-module-information-object(parent);
   if the text of the directly-required-modules of parent-module = "none" then return false; 
   required-modules = call TEXT-TO-SYMBOL-LIST(the text of the directly-required-modules of parent-module);

   for S = each symbol in required-modules do
       if S = Child then begin
           result = true; go to DONE;
       end;
       result = call module-is-required(S, Child);
       if result then go to DONE;
   end;
DONE:
   delete required-modules;
   return result;
end

g2:c.FUNCTION-DEFINITION.g2:a.TEXT:
Get-Module(X) =
    (if X is a kb-workspace then get-module-ws(X) 
       else if X is a connection then get-module-connection(X)
      else get-module-item(X))

