            end;
        
    end;
end

gfr-check-for-unused-translations (KeyList: class item-list, WS: class kb-workspace, Win: class g2-window)
NRes: integer = the array-length of text-resource-name-array;
X, Pos: integer;
ResName, Key: symbol;
ResInUse: truth-value = false;
KeysSymList, UsedKeysList: class symbol-list;
TextList: class text-list;
GFRLocal: class gfr-local-text-resource;
Elem: class symbol-array;
begin
    for X = 0 to (NRes - 1) do if use-text-resources[X] is true then 
        begin
            if ResInUse then
                inform the operator that "You tried to specify multiple text resources.  That's not supported at this time.  Using only the first encountered ([ResName])"
            else begin
                ResInUse = true;
                ResName = text-resource-name-array [X];
            end;
        end;
    end;
    GFRLocal = call gfr-get-local-text-resource (the gfr-text-resource-group named by ResName, gfr-language (Win), Win);
    create a symbol-list KeysSymList;
    create a text-list TextList;
    call gfr-get-all-unsubstituted-messages (GFRLocal, KeysSymList, TextList, Win);
    delete TextList;
    create a symbol-list UsedKeysList;
    for Elem = each symbol-array in KeyList do 
        insert Elem[0] at the end of UsedKeysList 
    end;
    for Key = each symbol in KeysSymList do
        Pos = call __gfr-get-position-of-element-in-list (Key, UsedKeysList);
        if Pos = -1 then inform the operator that "[Key] is not used." 
    end;
    delete KeysSymList;
    transfer UsedKeysList to WS;
    inform the operator that "Done checking for unused procedures.";
end

run-search-on-selected-resources (ModName: text, WS: class kb-workspace, RefList: class item-list, Win: class g2-window) 
N: integer = the array-length of text-resource-name-array;
X: integer;
ListOfRes: class symbol-list;
ArrOfRes: class symbol-array;
Res: symbol;
begin
    create a symbol-list ListOfRes;
    for X = 0  to (N - 1) do
        if use-text-resources[X] is true then insert text-resource-name-array[X] at the end of ListOfRes;
    end;
    create a symbol-array ArrOfRes;
    change the array-length of ArrOfRes to the number of elements in ListOfRes;
    X = 0;    
    for Res = each symbol in ListofRes do
        change ArrOfRes[X] = Res;
        X = X + 1;
    end;
    delete ListOfRes;
    call gfr-search-for-proper-use-of-text-resources(ModName, ArrOfRes, WS, RefList, Win);
    delete ArrOfRes;
        
end

sack-all-reference-notes (Proc: class procedure, Win: class g2-window)
Elem: class symbol-array;
Perm: truth-value;
begin
    for Elem = each symbol-array that is gfr-text-referencing Proc do
        Perm = call g2-system-predicate (Elem, the symbol permanent); 
            if Perm then begin
                make Elem transient;
                transfer Elem off;
            end; 
        delete Elem;
    end;
end

gfr-filter-out-references-with-text(BadList: class item-list, GoodList: item-or-value, Win: class g2-window)
Elem: class symbol-array;
TempGood: truth-value = false; Perm: truth-value = false; 
UnsubTxt: text;
GFRLocal: class gfr-local-text-resource;
begin
    if GoodList is not a gfr-array-bin then begin
        create a gfr-array-bin GoodList;
        TempGood = true;
    end;
    for Elem = each symbol-array in BadList do
        allow other processing;
        GFRLocal = call gfr-get-local-text-resource (the gfr-text-resource-group named by Elem[1], gfr-language (Win), Win);
        UnsubTxt = call gfr-get-unsubstituted-message (GFRLocal, Elem[0], Win);
        if UnsubTxt = "" then begin
            inform the operator for the next 2 minutes that "[Elem[2]] references the symbol [Elem[0]] in text resource group [Elem[1]].  This symbol is not translated for [gfr-language(Win)].";
        end else begin
            Perm = call g2-system-predicate (Elem, the symbol permanent); 
            if Perm then begin
                make Elem transient;
                transfer Elem off;
            end;
            remove Elem from BadList;
            insert Elem at the end of GoodList;
        end;
    end;
    if TempGood then delete GoodList;
    inform the operator that "Done with filtering.";
end

store-list-of-references-on-subworkspace(List: class gfr-array-bin, Win: class g2-window)
Scale: integer = 50; X,Y,Z, NumSubW: integer; NumElem: integer = the number of elements in List;
Elem: class symbol-array;
Trans: truth-value;
begin
    if NumElem = 0 then return;
    if the subworkspace SubW of List exists then begin
        Z = round (sqrt (the number of elements in List));
        NumSubW= the count of each symbol-array upon SubW;
        if NumSubW /= NumElem then begin
            X = round (NumSubW / Z);
            Y = NumSubW - X * Z;
            for Elem = each symbol-array in List do
                Trans = call g2-system-predicate (Elem, the symbol transient);
                if Trans then transfer Elem to SubW at (Scale*X, Scale*Y);
                X = X + 1;
                if X > Z then begin
                    X = 0;
                    Y = Y + 1;
                end;
                make Elem permanent;
            end;
        end;
    end;
end

remove-first-word-from-text(Txt: text) = (text, text)
Word, Remainder: text;
X, Pos: integer; PosOld: integer = length-of-text(Txt);
Lead: text;
P: class text-array = punctuation-and-spaces;
IP: integer = 7; {the length of the punctuation and spaces array}
begin
    Lead = get-from-text(Txt,1,1);
    for X = 0 to IP - 1 do
        if Lead = P[X] then begin
            Txt = omit-from-text (Txt, 1,1);
            Word, Remainder = call remove-first-word-from-text(Txt);
            return Word, Remainder;
        end;
    end;
    for X = 0 to IP - 1 do
        Pos = position-of-text (P[X], Txt);
        if PosOld < Pos or Pos = 0 then Pos = PosOld; 
        PosOld = Pos;
    end;
    Word = get-from-text(Txt, 1, Pos - 1);
    Remainder = omit-from-text (Txt, 1, Pos - 1 ); 
    return Word, Remainder;
end

clear-list(List: class g2-list)
X: item-or-value ;
begin
    for X = each item-or-value in List do
        remove X from List;
        if X is an item then delete X;
    end;
end

extract-translation-symbols-from-text (Sym: symbol, PrcNm: text, Txt: text, SymList: class symbol-list) = (integer)
Ans: integer = 0; 
Hit: integer = 0;
Pos: integer;
NewSym: symbol;
TheWord, SymbolWord, SymText, Body, BackText: text;
Lngth: integer = length-of-text("[Sym]");
{returns number of occurances in integer plus fills up SymList with the symbol following the translation symbol passed in through Sym}

begin
    Pos = position-of-text ("[Sym]", Txt);
    if Pos = 0 then begin
        if the number of elements in SymList = 0 then inform the operator that "Failure: could not find known text string in [PrcNm]."
    end else begin
        {Trim Leading text from Body}
        Body = omit-from-text (Txt, 1, Pos+Lngth);
        
        TheWord, Body = call remove-first-word-from-text(Body);
        SymbolWord, Body = call remove-first-word-from-text(Body); 
        SymText, Body = call remove-first-word-from-text(Body); 
        if TheWord = "the" and SymbolWord = "symbol" then begin
            Hit = 1;
            insert symbol(SymText) at the end of SymList;
        end else begin
            if Pos > 30 and is-contained-in-text ("gfr-default-language", get-from-text (Txt, Pos - 25, Pos)) then begin end 
            else 
            inform the operator for the next 5 minutes that "I couldn't parse '[TheWord] [SymbolWord] [SymText]' (or something like that)  in the procedure [PrcNm][(if Pos > 30 then " and a reference to default language could not be found in the text string [get-from-text (Txt, Pos - 25, Pos) ]" else "")]";
        end;
        Ans = call extract-translation-symbols-from-text (Sym, PrcNm, Body, SymList);
        Ans = Ans + Hit;
    end;
    return Ans;
end

gfr-search-for-proper-use-of-text-resources (ModName: text, ResArr: class symbol-array, WS: class kb-workspace, RefList: class item-list, Win: class g2-window)
X, Occur: integer; Y: integer = 0; N: integer = the array-length of ResArr - 1;
Proc: class procedure;
Meth: class method;
ProcMod, ProcName, ProcText: text;
SymbList: class symbol-list;
Key: symbol;
Reference: class symbol-array;
begin 
    create a symbol-list SymbList;
    for Proc = each procedure do
      if Proc is not a method-declaration then begin
        allow other processing;
        ProcMod = get-module-item(Proc);
        if ProcMod = ModName then for X = 0 to N do 
            if is-contained-in-text ("[ResArr[X]]", the text of Proc) then begin
                ProcText = the text of Proc;
                if Proc is a method then begin
                    Meth = Proc;
                    ProcName = the text of the qualified-name of Meth;
                end else ProcName = (if the name of Proc exists then "[the name of Proc]"else "NoName");
                call clear-list (SymbList);
                Occur = call extract-translation-symbols-from-text(ResArr[X], ProcName, ProcText, SymbList);
                allow other processing;
                for Key = each symbol in SymbList do
                    create a symbol-array Reference by cloning _gfr-symbol-pair-template;
                    change Reference[0] = Key;
                    change Reference[1] = ResArr[X];
                    change Reference[2] = symbol(ProcName);
                    insert Reference at the end of RefList;
                    conclude that Proc is gfr-text-referenced-by Reference;
                end;
                Y = Y + 1;                 
            end;
        end;          
      end;
    end;
    delete SymbList;
    inform the operator that "Done with gfr-search-for-proper-use-of-text-resources ";
end

dummy-screen (NewList: class symbol-list, OldList: class symbol-list)
AttrName: symbol;
begin
    for AttrName = each symbol in OldList do
        if AttrName is not a member of NewList then remove AttrName from OldList;
    end;
end

shake-attributes-with-definition-references (SymbList: class symbol-list , CountDown: class integer-parameter)
Proc: class procedure;
AttrText, InitText, AttrString: text;
AttrNameS: symbol;
PairList: class g2-list;
Pos1: integer;
ObjDef: class definition;
begin
    if the number of elements in SymbList = 0 then return;
    conclude that CountDown = the count of each definition;
    for ObjDef = each definition do
        AttrText = the text of the class-specific-attributes of ObjDef;
        InitText = the text of the attribute-initializations of ObjDef;
        for AttrNameS = each symbol in SymbList do
            AttrString = " is [AttrNameS]";
            allow other processing;
            if (is-contained-in-text (AttrString, AttrText) or is-contained-in-text (AttrString, InitText)) then begin
                remove AttrNameS from SymbList;
                exit if (true);
            end;
         end;
        conclude that CountDown = Countdown - 1;
    end;
    inform the operator that "Done with shake-procedures-with-definition-references" 
end

shake-attributes-with-parameter-references (SymbList: class symbol-list, Counter: class integer-parameter)
AttrS, AttrName: symbol;
SymPar: class symbolic-parameter;
Val: value;
begin
    conclude that Counter = the count of each symbolic-parameter;
    if the number of elements in SymbList = 0 then return;
    for SymPar = each symbolic-parameter do
        for AttrName =  each symbol in SymbList do
            if SymPar = AttrName then begin 
                remove AttrName from SymbList;
                exit if (true);
            end;
     
        end;
        conclude that Counter = Counter - 1;
    end;
    inform the operator that "Done with shake-attributes-with-parameter-references";
end

shake-attributes-with-gms-names (SymbList: class symbol-list , Counter: class integer-parameter) 
AttrNameS: symbol;
AttrText: text;
begin
    conclude that Counter = the number of elements in SymbList;
    if Counter = 0 then return;
    for AttrNameS = each symbol in SymbList do 
        AttrText= "[AttrNameS]";
        if is-contained-in-text ("GMS", AttrText) then remove AttrNameS from SymbList;
        conclude that Counter = Counter - 1;
    end;
    inform the operator that "Done with shake-attributes-with-gms-names ";
end

shake-attributes-with-uil-names (SymbList: class symbol-list , Counter: class integer-parameter) 
AttrNameS: symbol;
VA: class value-array;
PairList: class g2-list;
IVText, AttrText: text;
ValidItem: truth-value;
begin
    conclude that Counter = the number of elements in SymbList;
    if Counter = 0 then return;
    for AttrNameS = each symbol in SymbList do 
        AttrText= "[AttrNameS]";
        if is-contained-in-text ("UIL", AttrText) then remove AttrNameS from SymbList;
        conclude that Counter = Counter - 1;
    end;
    inform the operator that "Done with shake-attributes-with-uil-names ";
end

shake-attributes-with-array-references (SymbList: class symbol-list , Counter: class integer-parameter)
AttrNameS: symbol;
VA: class value-array;
IVText, AttrText: text;
begin
    conclude that Counter = the number of elements in SymbList;
    if Counter = 0 then return;
    for AttrNameS = each symbol in SymbList do
        AttrText= "[AttrNameS]";
        allow other processing; 
        for VA = each value-array do
            IVText = the text of the initial-values of VA;
            allow other processing; 
            if is-contained-in-text (AttrText, IVText) then remove AttrNameS from SymbList;
        end;
        conclude that Counter = Counter - 1;
    end;
    inform the operator that "Done with shake-attributes-with-array-references ";
end

shake-attributes-with-menu-choice-references (ProcList: class item-list, ListOfPairs: class item-list, Counter: class integer-parameter) 
Proc: class procedure;
Meth: class method;
ProcNameS: symbol;
UMC: class user-menu-choice;
PairList: class g2-list;
UText, StartText: text;
ValidItem: truth-value;
begin
    conclude that Counter = the number of elements in ProcList;
    if Counter = 0 then return;
    for PairList = each g2-list in ListOfPairs do 
        ProcNameS = the second item-or-value in PairList; 
        if ProcNameS is not no-name-for-procedure then begin
            StartText= "start [ProcNameS]";
            allow other processing; 
            for UMC = each user-menu-choice do
                ValidItem = call module-exceptions (UMC);
                if ValidItem then begin
                    UText = the text of the action of UMC;
                    allow other processing; 
                    if is-contained-in-text (StartText, UText) then begin
                        Proc = the first item-or-value in PairList;    
                        remove PairList from ListOfPairs; 
                        delete PairList;  
                        remove Proc from ProcList;
                        exit if (true);
                    end;
                end;   
            end;
        conclude that Counter = Counter - 1;
        end;
    end;
    inform the operator that "Done with shake-procedures-with-rule-references ";
end

shake-attributes-with-grobj-references (SymbList: class symbol-list , Counter: class integer-parameter)
AttrNameS: symbol;
UG: class uil-grobj;
UserText, AttrText: text;
ValidItem: truth-value;
begin
    conclude that Counter = the number of elements in SymbList;
    if Counter = 0 then return;
    for AttrNameS= each symbol in SymbList do 
        AttrText= "[AttrNameS]";
        allow other processing; 
        for UG = each uil-grobj do
            if the user-data of UG has a current value then begin
                UserText = the text of the user-data of UG;
                allow other processing; 
                if is-contained-in-text (AttrText, UserText) then begin
                    remove AttrNameS from SymbList;
                    exit if (true);
                end;   
            end;
        end;
        conclude that Counter = Counter - 1; 
    end;
    inform the operator that "Done with shake-attributes-with-grobj-references ";
end

shake-attributes-with-remote-procedure-references (SymbList: class symbol-list,Counter: class integer-parameter) 
Proc: class procedure;
Meth: class method;
AttrNameS: symbol;
RPD: class remote-procedure-declaration;
PairList: class g2-list;
RPDText, AttrText: text;
ValidItem: truth-value;
begin
    conclude that Counter = the number of elements in SymbList;
    if Counter = 0 then return;
    for AttrNameS = each symbol in SymbList do
        AttrText= "[AttrNameS]";
        allow other processing; 
        for RPD = each remote-procedure-declaration do
            RPDText = the text of the name-in-remote-system of RPD;
            allow other processing; 
            if is-contained-in-text (AttrText, RPDText) then begin
                remove AttrNameS from SymbList;
                exit if (true);
            end;
        end;
        conclude that Counter = Counter - 1; 
    end;
    inform the operator that "Done with shake-attributes-with-remote-procedure-references ";
end

shake-attributes-with-message-references (SymbList: class symbol-list, Counter: class integer-parameter)
Meth: class method;
AttrNameS, AttrName: symbol;
IV: item-or-value ;
Val: value;
Msg: class message;
begin
   if the number of elements in SymbList = 0 then return;
   conclude that Counter = the count of each message;
   for Msg = each message do
        for AttrName =  each symbol that is a user-defined attribute name of Msg do
            if the item-or-value IV that is an attribute of Msg named by AttrName exists then begin
                if (IV is a value or IV is a variable-or-parameter) and IV has a current value then begin
                    if IV is a variable-or-parameter then Val = the current value of IV else Val = IV;
                    if Val is a symbol then begin
                        allow other processing;
                        for AttrNameS = each symbol in SymbList do
                            if  Val = AttrNameS then remove AttrNameS from SymbList;
                        end;
                    end;
                end;
            end;
        end;
        conclude that Counter = Counter - 1;
    end; 
    inform the operator that "Done with shake-attributes-with-message-references ";
end

shake-attributes-with-rule-references (SymbList: class symbol-list, Counter: class integer-parameter) 
Proc: class procedure;
Meth: class method;
AttrNameS: symbol;
R: class rule;
RText, AttrText: text;
ValidItem: truth-value;
begin
    conclude that Counter = the number of elements in SymbList;
    if Counter = 0 then return;
    for AttrNameS = each symbol in SymbList do 
        AttrText= "[AttrNameS]";
        allow other processing; 
        for R = each rule do
            ValidItem = call module-exceptions (R);
            if ValidItem then begin
                RText = the text of R;
                allow other processing; 
                if is-contained-in-text (AttrText, RText) then remove AttrNameS from SymbList;  
            end;
        end;
        conclude that Counter = Counter - 1;
    end;
    inform the operator that "Done with shake-procedures-with-rule-references ";
end

shake-attributes-with-uil-scroll-references (SymbList: class symbol-list , CountDown: class integer-parameter)
AttrText, InitText, AttrString: text;
AttrName: symbol;
Pos1: integer;
ObjDef: class definition;
begin
    if the number of elements in SymbList = 0 then return;
    conclude that CountDown = the number of elements in SymbList ;
    ObjDef = uil-scroll-area;
        AttrText = the text of the class-specific-attributes of ObjDef;
        InitText = the text of the attribute-initializations of ObjDef;
        for AttrName = each symbol in SymbList do
            AttrString = "[AttrName]";
            allow other processing;
            if (is-contained-in-text (AttrString, AttrText) or is-contained-in-text (AttrString, InitText)) then remove AttrName from SymbList;
            conclude that CountDown = Countdown - 1;
        end;
    inform the operator that "Done with shake-procedures-with-definition-references" 
end

shake-attributes-with-object-references (SymbList: class symbol-list, Counter: class integer-parameter)
Proc: class procedure;
Meth: class method;
AttrS, AttrName: symbol;
IV: item-or-value ;
Val: value;
PairList: class g2-list;
Obj: class object;
begin
   if the number of elements in SymbList = 0 then return;
    conclude that Counter = the count of each object;
   for Obj = each object do
        for AttrName =  each symbol that is a user-defined attribute name of Obj do
            if the item-or-value IV that is an attribute of Obj named by AttrName exists then begin
                if (IV is a value or IV is a variable-or-parameter) and IV has a current value then begin
                    if IV is a variable then collect data Val = the current value of IV end else Val = IV;
                    if Val is a symbol then begin
                        allow other processing;
                        for AttrS = each symbol in SymbList do 
                            if  Val = AttrS then remove AttrS from SymbList;
                        end;
                    end;
                end;
            end;
        end;
        conclude that Counter = Counter - 1;
    end;
    inform the operator that "Done with shake-attributes-with-object-references ";
end

shake-attributes-with-procedure-references (SymbList: class symbol-list, CountDown: class integer-parameter)
Proc2: class procedure;
Meth: class method;
AttrName: text;
AttrSym: symbol;
Pos1: integer;
Proc2List: class item-list;
Proc2Text: text;
begin
    create an item-list Proc2List;
    for Proc2 = each procedure do insert Proc2 at the end of Proc2List end;
    for Proc2 = each procedure in Proc2List do
        if Proc2 is a method-declaration then remove Proc2 from Proc2List else begin
            allow other processing;
        end;
    end;
    conclude that Countdown = the number of elements in SymbList;
    for AttrSym = each symbol in SymbList do
        AttrName = "[AttrSym]";
        for Proc2 = each procedure in Proc2List do
            Proc2Text = the text of Proc2;
            allow other processing; 
            if is-contained-in-text (AttrName, the text of Proc2) then begin
                remove AttrSym from SymbList;
                exit if (true);
            end;
        end;
        conclude that CountDown = Countdown - 1;
    end;
    delete Proc2List;
    inform the operator that "Done with shake-procedures-with-procedure-references "
end

collect-some-attributes (AttList: class symbol-list )
Itm: class item;
ItmDef: class definition;
Y: integer = 0;
ValidMod: truth-value;
AttrName, ClassName: symbol;
begin
    for Itm = each item do
        ClassName = the class of Itm;
        if there exists a definition ItmDef named by ClassName then begin
          ValidMod = call module-exceptions (ItmDef); 
          if ValidMod then for AttrName = each symbol that is a user-defined attribute name of Itm do
            insert AttrName at the end of AttList; 
          end;
        end;
    end; 
end

collect-all-attributes (AttList: class symbol-list)
ItmDef : class definition;
Itm: class item;
AttrName, ClassName: symbol;
ValidMod: truth-value;
Pair: class g2-list;
begin
    for Itm = each item do
        ClassName = the class of Itm;
        if there exists a definition ItmDef named by ClassName then begin
          ValidMod = call module-exceptions (ItmDef); 
          if ValidMod then for AttrName = each symbol that is a user-defined attribute name of Itm do
            insert AttrName at the end of AttList; 
          end;
        end;
    end; 
end

master-attribute-shaker (SymbList: class symbol-list, Counter: class integer-parameter)

begin
    call collect-all-attributes (SymbList);
    allow other processing;
    call shake-attributes-with-procedure-references (SymbList, Counter);
    allow other processing;
    call shake-attributes-with-object-references (SymbList, Counter) ;
    allow other processing;
    call shake-attributes-with-rule-references (SymbList, Counter) ;
    call shake-attributes-with-message-references (SymbList, Counter) ; 
    call shake-attributes-with-remote-procedure-references (SymbList, Counter) ; 
    call shake-attributes-with-grobj-references (SymbList, Counter) ; 
    call shake-attributes-with-array-references (SymbList, Counter) ; 
    call shake-attributes-with-uil-names (SymbList, Counter) ; 
    call shake-attributes-with-gms-names (SymbList, Counter) ; 
    call shake-attributes-with-uil-scroll-references (SymbList, Counter) ; 
    conclude that Counter = 0;
    inform the operator that "Done with master-tree-shaker " 
end

master-item-tree-shaker (ProcList: class item-list, ListOfPairs: class item-list, Counter: class integer-parameter)
Proc: class procedure;
begin
    call collect-all-procedures (ProcList);
    allow other processing;
    call shake-procedures-with-procedure-references (ProcList, Counter);
    allow other processing;
    call create-a-symbol-list-of-remaining-procedures (ProcList, ListOfPairs);
    allow other processing;
    call shake-procedures-with-definition-references (ProcList, ListOfPairs, Counter);
    allow other processing;
    call shake-procedures-with-object-references (ProcList, ListOfPairs, Counter);
    call shake-procedures-with-rule-references (ProcList, ListOfPairs, Counter); 
    call shake-procedures-with-message-references (ProcList, ListOfPairs, Counter); 
    call shake-procedures-with-remote-procedure-references (ProcList, ListOfPairs, Counter); 
    call shake-procedures-with-grobj-references (ProcList, ListOfPairs, Counter); 
    call shake-procedures-with-menu-choice-references (ProcList, ListOfPairs, Counter); 
    call shake-procedures-with-array-references (ProcList, ListOfPairs, Counter); 
    conclude that Counter = 0;
    inform the operator that "Done with master-tree-shaker " 
end

shake-procedures-with-array-references (ProcList: class item-list, ListOfPairs: class item-list, Counter: class integer-parameter) 
Proc: class procedure;
Meth: class method;
ProcNameS: symbol;
VA: class value-array;
PairList: class g2-list;
IVText, ProcText: text;
ValidItem: truth-value;
begin
    conclude that Counter = the number of elements in ProcList;
    if Counter = 0 then return;
    for PairList = each g2-list in ListOfPairs do 
        ProcNameS = the second item-or-value in PairList; 
        if ProcNameS is not no-name-for-procedure then begin
            ProcText= "[ProcNameS]";
            allow other processing; 
            for VA = each value-array do
                IVText = the text of the initial-values of VA;
                allow other processing; 
                if is-contained-in-text (ProcText, IVText) then begin
                    Proc = the first item-or-value in PairList;    
                    remove PairList from ListOfPairs; 
                    delete PairList;  
                    remove Proc from ProcList;
                    exit if (true);
                end;   
            end;
        conclude that Counter = Counter - 1;
        end;
    end;
    inform the operator that "Done with shake-procedures-with-grobj-references ";
end

shake-procedures-with-menu-choice-references (ProcList: class item-list, ListOfPairs: class item-list, Counter: class integer-parameter) 
Proc: class procedure;
Meth: class method;
ProcNameS: symbol;
UMC: class user-menu-choice;
PairList: class g2-list;
UText, StartText: text;
ValidItem: truth-value;
begin
    conclude that Counter = the number of elements in ProcList;
    if Counter = 0 then return;
    for PairList = each g2-list in ListOfPairs do 
        ProcNameS = the second item-or-value in PairList; 
        if ProcNameS is not no-name-for-procedure then begin
            StartText= "start [ProcNameS]";
            allow other processing; 
            for UMC = each user-menu-choice do
                ValidItem = call module-exceptions (UMC);
                if ValidItem then begin
                    UText = the text of the action of UMC;
                    allow other processing; 
                    if is-contained-in-text (StartText, UText) then begin
                        Proc = the first item-or-value in PairList;    
                        remove PairList from ListOfPairs; 
                        delete PairList;  
                        remove Proc from ProcList;
                        exit if (true);
                    end;
                end;   
            end;
        conclude that Counter = Counter - 1;
        end;
    end;
    inform the operator that "Done with shake-procedures-with-rule-references ";
end

shake-procedures-with-grobj-references (ProcList: class item-list, ListOfPairs: class item-list, Counter: class integer-parameter) 
Proc: class procedure;
Meth: class method;
ProcNameS: symbol;
UG: class uil-grobj;
PairList: class g2-list;
UserText, ProcText: text;
ValidItem: truth-value;
begin
    conclude that Counter = the number of elements in ProcList;
    if Counter = 0 then return;
    for PairList = each g2-list in ListOfPairs do 
        ProcNameS = the second item-or-value in PairList; 
        if ProcNameS is not no-name-for-procedure then begin
            ProcText= "[ProcNameS]";
            allow other processing; 
            for UG = each uil-grobj do
                if the user-data of UG has a current value then begin
                    UserText = the text of the user-data of UG;
                    allow other processing; 
                    if is-contained-in-text (ProcText, UserText) then begin
                        Proc = the first item-or-value in PairList;    
                        remove PairList from ListOfPairs; 
                        delete PairList;  
                        remove Proc from ProcList;
                        exit if (true);
                    end;
                end;   
            end;
        conclude that Counter = Counter - 1;
        end;
    end;
    inform the operator that "Done with shake-procedures-with-grobj-references ";
end

