shake-procedures-with-definition-references (ProcList: class item-list, ListOfPairs: class item-list, CountDown: class integer-parameter)
Proc: class procedure;
AttrText, InitText, ProcString: text;
ProcNameS: symbol;
PairList: class g2-list;
Pos1: integer;
ObjDef: class definition;
begin
    if the number of elements in ProcList = 0 then return;
    conclude that CountDown = the count of each definition;
    for ObjDef = each definition do
        AttrText = the text of the class-specific-attributes of ObjDef;
        InitText = the text of the attribute-initializations of ObjDef;
        for PairList = each g2-list in ListOfPairs do
            ProcNameS = the second item-or-value in PairList;
            if ProcNameS is not no-name-for-procedure then begin
                ProcString = "is [ProcNameS]";
                allow other processing;
                if (is-contained-in-text (ProcString, AttrText) or is-contained-in-text (ProcString, InitText)) then begin 
                    Proc = the first item-or-value in PairList;    
                    remove PairList from ListOfPairs; 
                    delete PairList;  
                    remove Proc from ProcList;
                end;
            end;
        end;
        conclude that CountDown = Countdown - 1;
    end;
    inform the operator that "Done with shake-procedures-with-definition-references" 
end

shake-procedures-with-object-references (ProcList: class item-list, ListOfPairs: class item-list, Counter: class integer-parameter)
Proc: class procedure;
Meth: class method;
ProcNameS, AttrName: symbol;
IV: item-or-value ;
Val: value;
PairList: class g2-list;
Obj: class object;
begin
   if the number of elements in ProcList = 0 then return;
    conclude that Counter = the count of each object;
   for Obj = each object do
        for AttrName =  each symbol that is a user-defined attribute name of Obj do
            if the item-or-value IV that is an attribute of Obj named by AttrName exists then begin
                if (IV is a value or IV is a variable-or-parameter) and IV has a current value then begin
                    if IV is a variable then collect data Val = the current value of IV end else Val = IV;
                    if Val is a symbol then begin
                        allow other processing;
                        for PairList = each g2-list in ListOfPairs do 
                            ProcNameS = the second item-or-value in PairList; 
                            if  Val = ProcNameS then begin
                                Proc = the first item-or-value in PairList;
                                remove PairList from ListOfPairs;
                                delete PairList;
                                remove Proc from ProcList;
                            end;
                        end;
                    end;
                end;
            end;
        end;
        conclude that Counter = Counter - 1;
    end;
    inform the operator that "Done with shake-procedures-with-object-references ";
end

print-tree-list (PairList: class item-list, FileS: class text-parameter)
FileExists: truth-value;
FileName, NewLine: text;
Stream: class g2-stream ;
Proc, Proc2: class procedure;
Pair: class item-list;
ProcNameS: symbol;
X: integer = 0;
begin
    FileName = FileS;
    FileExists = call g2-file-exists (FileName);
    if FileExists then call g2-delete-file (FileName);
    Stream = call g2-open-file-for-write (FileName);
    for Pair = each item-list in PairList do
        X = X + 1;
        Proc = the first item in Pair;
        ProcNameS = the second item in Pair;
        NewLine = "[ProcNameS]";
        call g2-write-line (Stream, NewLine);
        if there exists a procedure Proc2 such that (Proc is called-by Proc2) then begin
            NewLine = "     is called by [the name of Proc2]";
            call g2-write-line (Stream, NewLine);
        end;
    end;
    call g2-close-file (Stream);
end

create-a-symbol-list-of-remaining-procedures (ProcList: class item-list, ListOfPairs: class item-list)
Proc, Proc2: class procedure;
Meth: class method;
ProcName: text;
ProcNameS: symbol;
PairList: class g2-list;
Pos1: integer;
begin
    for Proc = each procedure in ProcList do
        if Proc is a method then begin
            Meth = Proc;
            ProcName = the text of the qualified-name of Meth; 
            if ProcName /= "none" then begin 
                Pos1 = position-of-text("::",ProcName);
                ProcName = omit-from-text(ProcName,1,Pos1 + 1);
                ProcNameS = symbol (ProcName);
            end else
                ProcNameS = the symbol no-name-for-procedure;
        end else
            if the name of Proc exists then ProcNameS = the name of Proc else ProcNameS = the symbol no-name-for-procedure; 
        create a g2-list PairList;
        insert Proc at the beginning of PairList;
        insert ProcNameS at the end of PairList;
        insert PairList at the end of ListOfPairs;
    end;
end

shake-procedures-with-procedure-references (ProcList: class item-list, CountDown: class integer-parameter)
Proc, Proc2: class procedure;
Meth: class method;
ProcName: text;
Pos1: integer;
Proc2List: class item-list;
Proc2Text: text;
begin
    create an item-list Proc2List by cloning ProcList;
    for Proc2 = each procedure do insert Proc2 at the end of Proc2List end;
    for Proc2 = each procedure in Proc2List do
        if Proc2 is a method-declaration then remove Proc2 from Proc2List else begin
            allow other processing;
            Proc2Text = the text of Proc2;
            if not(is-contained-in-text("call", Proc2Text) or is-contained-in-text("start", Proc2Text) or is-contained-in-text("the symbol", Proc2Text)) then remove Proc2 from Proc2List;
        end;
    end;
    conclude that Countdown = the number of elements in ProcList;
    for Proc = each procedure in ProcList do
        if Proc is a method then begin
            Meth = Proc;
            ProcName = the text of the qualified-name of Meth;
            if ProcName /= "none" then begin
                Pos1 = position-of-text("::",ProcName);
                ProcName = omit-from-text(ProcName,1,Pos1 + 1);
            end;
        end else
            if the name of Proc exists then ProcName = "[the name of Proc]" else ProcName = "none";
        {inform the operator for the next 15 seconds that "The name of this procedure is [ProcName]";}
        if ProcName/= "none" then begin
            for Proc2 = each procedure in Proc2List do
                Proc2Text = the text of Proc2;
                allow other processing; 
                if Proc is not the same object as Proc2 then begin 
                    if is-contained-in-text (ProcName, the text of Proc2) then begin 
                        remove Proc from ProcList;
                        conclude that Proc is called-by Proc2;
                        exit if (true);
                    end;
                end;
            end;
        end;
        conclude that CountDown = Countdown - 1;
    end;
    delete Proc2List;
    inform the operator that "Done with shake-procedures-with-procedure-references "
end

collect-some-procedures (ProcList: class item-list)
Proc: class procedure;
Y: integer = 0;
ValidMod: truth-value;
begin
    for Proc = each procedure do
        ValidMod = call module-exceptions (Proc);
        if ValidMod then begin
            insert Proc at the end of ProcList;
            Y = Y + 1;
            exit if Y > 0;
        end;
    end;
end

module-exceptions (Proc: class item) = (truth-value)
Mod: text;
Answer: truth-value = false;
begin
    Mod = get-module-item (Proc);
    if not(Mod = "sys-mod" or Mod = "protools" or Mod = "gdadev1" or Mod = "devapps" or Mod = "spreadsh" or Mod = "gms" or Mod = "gfr"  or Mod = "gsp" or Mod = "gmq")then begin 
        if not(get-from-text(Mod,1,3) = "uil")then Answer = true; 
    end;
    allow other processing;
    return Answer;
end

collect-all-procedures (ProcList: class item-list)
Proc: class procedure;
ValidMod: truth-value ;
begin
    for Proc = each procedure do
        ValidMod = call module-exceptions (Proc);
        if ValidMod then insert Proc at the end of ProcList;
    end;
end

master-tree-shaker (ProcList: class item-list, ListOfPairs: class item-list, Counter: class integer-parameter)
Proc: class procedure;
begin
    call collect-all-procedures (ProcList);
    allow other processing;
    call shake-procedures-with-procedure-references (ProcList, Counter);
    allow other processing;
    call create-a-symbol-list-of-remaining-procedures (ProcList, ListOfPairs);
    allow other processing;
    call shake-procedures-with-definition-references (ProcList, ListOfPairs, Counter);
    allow other processing;
    call shake-procedures-with-object-references (ProcList, ListOfPairs, Counter);
    call shake-procedures-with-rule-references (ProcList, ListOfPairs, Counter); 
    call shake-procedures-with-message-references (ProcList, ListOfPairs, Counter); 
    call shake-procedures-with-remote-procedure-references (ProcList, ListOfPairs, Counter); 
    call shake-procedures-with-grobj-references (ProcList, ListOfPairs, Counter); 
    call shake-procedures-with-menu-choice-references (ProcList, ListOfPairs, Counter); 
    call shake-procedures-with-array-references (ProcList, ListOfPairs, Counter); 
    conclude that Counter = 0;
    inform the operator that "Done with master-tree-shaker " 
end

check-all-modules-for-extra-declarations() 
M: class module-information;
T: text;
begin
    for M = each module-information do
        T = the text of the top-level-module of M;
        inform the operator that "working on module [T]";
        allow other processing;
        call check-extra-declarations (T, "/home/apps/[t]-extra-declarations");
    end;
end

variable-match(Text: text, Pattern: text, CharList: class text-list ) = (truth-value)
Pos, BPos, EPos, PatLen: integer;
CommentText, PatAsAttr: text;
Matched, InBracket, PatternP: truth-value;
begin
    EPos = 0;
    PatLen = length-of-text(Pattern);
    PatternP = false;
 repeat
    Matched = false;
    InBracket = false; 
    Pos = position-of-text(Pattern, Text);
    if Pos > 0 then begin
        Matched = call check-if-variable(Pattern, Pos, PatLen, Text, CharList);
        if Matched then begin
            CommentText = omit-from-text(Text, 1, Pos); 
            BPos = position-of-text("{", CommentText);
            EPos = position-of-text("}", CommentText); 
            if EPos > 0 and BPos = 0 or BPos > EPos then InBracket = true; 
        end;
        if Matched and InBracket then Matched = false;
        if Matched then begin
            PatAsAttr = omit-from-text(Text, 1, Pos + PatLen); 
            BPos = position-of-text("of", PatAsAttr); 
            if BPos > 0 and BPos < 5 then Matched = false;
        end; 
      end;
      if not(PatternP) and Matched and not(InBracket) then PatternP = true;
      exit if Pos = 0 or Matched; 
      if EPos > Pos and InBracket then Text = omit-from-text(Text, 1, EPos + 1) else Text = omit-from-text(Text, 1, Pos + 1); 
   end; 
   if PatternP then return PatternP;
   if Matched then begin if not(InBracket) then return true  else return false; end else return false;
 end

check-extra-declarations(ModuleName: text, Filename: text) 
WS: class kb-workspace;
Proc: class procedure;
Stream: class g2-stream;
VarName, Line: text;
UnUsedVars: class text-list;
WSList, ProcList: class item-list;
N, NProcs: integer;
EN: symbol; ET: text;
begin  
{Collect workspaces assigned to Module}
    conclude that  extra-declarations-progress = "Collecting workspaces";
    create an item-list WSList;
    begin
        for WS = each kb-workspace do
            if the item superior to WS does not exist and the text of the module-assignment of WS = ModuleName then insert WS at the end of WSList;
        end;
    end on error (En, Et) end;  {Ignore errors}
    conclude that  extra-declarations-progress = "Collecting procedures"; 
    create an item-list ProcList;
    for WS = each kb-workspace in WSList do
        call collect-items-in-workspace-hierarchy(WS, the symbol Procedure, ProcList);
    end;
    delete WSList;

    NProcs = the number of elements in ProcList;
    N = 0;
    Stream = call g2-open-file-for-write(FileName);
    for Proc = each procedure in ProcList do
        if Proc is not a method-declaration then begin
            UnUsedVars = call find-extra-variable-declaration(Proc);
            if the number of elements in UnUsedVars /= 0 then begin
                Line = "";   
                for VarName = each text in UnUsedVars do
                   if Line = "" then Line = " [VarName]" else Line = " [Line],[VarName]";
                end;
                Line = "[(if Proc is a method then the text of the qualified-name of Proc else the name of Proc)] :  [Line]"; 
               call g2-write-line(Stream, Line); 
            end;
        end on error (EN, ET) inform the operator that "[en], [et]"; end;
        allow other processing;  
        N = N + 1;
        conclude that  extra-declarations-progress = "Completed  [N] of [NProcs]"; 
    end; { for proc }
    call g2-close-file(Stream);
    delete ProcList;
end
on error (En, Et)
    if Stream exists then
        call g2-close-file (Stream);
end

remove-comments-from-text(TextWithComments: text) = (text)
Pos, EPos, Pos1: integer;
begin
    Pos = position-of-text("{", TextWithComments);
 {   Pos1 = position-of-text("[("{")]", TextWithComments);  }
    if Pos > 0 then begin   {  and Pos /= Pos1 }
        EPos = position-of-text("}", TextWithComments);
        if EPos > 0 then TextWithComments = omit-from-text(TextWithComments, Pos, EPos);
    end;
    Pos = position-of-text("{", TextWithComments); 
{    Pos1 = position-of-text("[("{")]", TextWithComments); }
    if Pos > 0 then TextWithComments = call remove-comments-from-text(TextWithComments);
    return TextWithComments;
end

collect-items-in-workspace-hierarchy(Itm: class item, TargetClass: symbol, ItemList: class item-list)
WS, SubWS: class kb-workspace;
WsList: class item-list;
SearchItm: class item;
begin
{initially seed the list of workspaces to check, and insert Itm itself into ItemList if it is TargetClass}
    create an item-list WsList;
    if Itm is a kb-workspace then insert Itm at the beginning of WsList else if the subworkspace WS of Itm exists then insert WS at the beginning of WsList;
    if Itm is an instance of the class named by (TargetClass) then insert Itm at the end of ItemList;
    repeat
        exit if the first item WS in WsList does not exist;
        remove WS from WsList;
        for SearchItm = each item upon WS do
             if SearchItm is an instance of the class named by (TargetClass) then insert SearchItm at the end of ItemList; 
             if the subworkspace SubWS of SearchItm exists then insert SubWS at the end of WsList;
        end;
    end;
    delete WsList;
end

remove-space(TextWithSpace: text, CharList: class text-list) = (text)
Index, TLen: integer;
AChar: text;
begin
    Index = 1;
    TLen = length-of-text(TextWithSpace);
    repeat
      if Index = 1 or Index = length-of-text(TextWithSpace) then begin
        AChar = get-from-text(TextWithSpace, Index, Index);
        if AChar is not a member of CharList and AChar /= "-"  then begin
            TextWithSpace = omit-from-text(TextWithSpace, Index, Index);
            TLen = length-of-text(TextWithSpace); 
        end else Index = Index + 1;
       end else Index = Index + 1;
      exit if Index > TLen; 
    end;
    return TextWithSpace;
end

get-alpha-numeric-list() = (class text-list)
CharList:class text-list;
begin
    create a text-list CharList;
    insert "a" at the end of CharList;
    insert "b" at the end of CharList; 
    insert "c" at the end of CharList; 
    insert "d" at the end of CharList; 
    insert "e" at the end of CharList; 
    insert "f" at the end of CharList; 
    insert "g" at the end of CharList; 
    insert "h" at the end of CharList; 
    insert "i" at the end of CharList; 
    insert "j" at the end of CharList;
    insert "k" at the end of CharList; 
    insert "l" at the end of CharList; 
    insert "m" at the end of CharList; 
    insert "n" at the end of CharList; 
    insert "o" at the end of CharList; 
    insert "p" at the end of CharList; 
    insert "q" at the end of CharList; 
    insert "r" at the end of CharList;    
    insert "s" at the end of CharList; 
    insert "t" at the end of CharList; 
    insert "u" at the end of CharList; 
    insert "v" at the end of CharList; 
    insert "w" at the end of CharList; 
    insert "x" at the end of CharList; 
    insert "y" at the end of CharList; 
    insert "z" at the end of CharList;
    insert "1" at the end of CharList; 
    insert "2" at the end of CharList;    
    insert "3" at the end of CharList; 
    insert "4" at the end of CharList; 
    insert "5" at the end of CharList; 
    insert "6" at the end of CharList; 
    insert "7" at the end of CharList; 
    insert "8" at the end of CharList; 
    insert "9" at the end of CharList; 
    insert "0" at the end of CharList; 
    return CharList;
end

check-if-variable(Pat: text, Pos: integer, PatLen: integer, TextOfPat: text, CharList: class text-list) = (truth-value)
SText, EText: text;
begin
    if Pos > 1 then SText = get-from-text(TextOfPat, Pos - 1, Pos - 1) else SText = "!";
    if length-of-text(TextOfPat) > Pos + PatLen then
            EText =  get-from-text(TextOfPat, Pos + PatLen, Pos + PatLen) else EText = "!";
    if  SText is a member of CharList or EText is a member of CharList then return false else return true; 
end

find-extra-variable-declaration(AnyProcedure: class procedure)  = (class text-list)
TextOfProcedure, DVariable, TBuffer, TArea: text;
Position, NPosition, BeginPos, Comma, Semicolon, Colon: integer;
UnDecl, CharList : class text-list;
PatMatch: truth-value;
begin
    CharList = call get-alpha-numeric-list ();
    create a text-list UnDecl;
    TextOfProcedure = the text of AnyProcedure;
    if get-from-text(TextOfProcedure, 1, 2) = ".." then return UnDecl; {Text stripped}
    Position = position-of-text(")", TextOfProcedure)  + 1;
    TArea = TextOfProcedure;
    NPosition = 0;
    repeat
        BeginPos = position-of-text("begin", TArea);
        NPosition =  NPosition + BeginPos; 
        PatMatch = call check-if-variable ("begin", BeginPos, 5, TArea, CharList);
        exit if PatMatch;
        if not(PatMatch) then TArea = omit-from-text(TArea, 1, BeginPos);
    end;
    BeginPos = NPosition;
    TBuffer = get-from-text(TextOfProcedure,Position, BeginPos - 1);
    NPosition  = position-of-text(")", TBuffer);
    if NPosition > 0 and NPosition + Position < BeginPos then begin
        TBuffer = get-from-text(TBuffer, NPosition, length-of-text(TBuffer));
    end; 
    TArea = get-from-text(TextOfProcedure,BeginPos + 4,length-of-text(TextOfProcedure) - 3);
 {   TArea = call remove-comments-from-text(TArea);  }
    TBuffer = call remove-comments-from-text(TBuffer); 
    Colon = position-of-text(":", TBuffer); 
    Comma = position-of-text(",", TBuffer); 
    if Comma < Colon and Comma > 0 then Position = Comma else Position = Colon;
    if Position > 0 then begin
        DVariable = get-from-text(TBuffer, 1, Position);
        DVariable = call remove-space(DVariable, CharList);
        PatMatch = call variable-match (TArea, DVariable, CharList);
{ inform the operator that "patmatch ? [DVariable] [PatMatch] ";}
        if not(PatMatch) then insert DVariable at the end of  UnDecl;
        TBuffer = omit-from-text(TBuffer, 1, Position);
    end;
    repeat 
        Position = 9999999;
        Comma = position-of-text(",", TBuffer);
        if Comma > 0 then Position  = Comma;
        Colon = position-of-text(":",TBuffer);
        if Colon > 0 and Colon < Position then Position = Colon; 
        Semicolon = position-of-text(";", TBuffer);
        if Semicolon > 0 and Semicolon < Position then begin
            Position = -1;
            TBuffer = omit-from-text(TBuffer, 1,  Semicolon);
        end;
        if Position > 0 and Position /=  9999999 then begin
            Position = Position - 1;
            DVariable = get-from-text(TBuffer, 1,  Position);
            TBuffer = omit-from-text(TBuffer, 1,  Position + 1); 
            DVariable = call remove-space(DVariable, CharList); 
            PatMatch = call variable-match (TArea, DVariable, CharList);
{inform the operator that " [DVariable] ? [PatMatch]";}
            if not(PatMatch) then insert DVariable at the end of UnDecl;
        end;
{inform the operator that "loop - [Position] ";  wait for 3 seconds; }
        exit if Position = 0 or Position = 9999999; 
    end;

{ for DVariable = each text in UnDecl do
       inform the operator that " In [the name of AnyProcedure] - unused [DVariable]";
   end;
   if the number of elements in UnDecl = 0 then
       inform the operator that "In [the name of AnyProcedure] - All variables used";}

   delete CharList;
   return UnDecl;
end

_gsp-remove-uil-buttons-from-sys-mod()
WS, Subws: class kb-workspace;
CP: class connection-post;
Butt: class uil-grobj;
IL: class item-list;
FT: class borderless-free-text;
begin
    create an item-list IL;
    insert G2-SYSTEM-PROCEDURES at the beginning of IL;
    repeat
        exit if the first kb-workspace in IL does not exist;
        WS = the first kb-workspace in IL;
        remove WS from IL;
        for Butt = each uil-grobj upon WS do
            create a connection-post CP;
            transfer CP to WS at (the item-x-position of Butt, the item-y-position of Butt);
            make CP permanent;
            if the subworkspace Subws of Butt exists then begin
                insert Subws at the end of IL;
                make Subws transient;
                make Subws the subworkspace of CP;
                make Subws permanent;
            end;
            if the name of Butt exists then begin
                create a borderless-free-text FT;
                change the text of FT to "[the name of Butt]";
                transfer FT to the workspace of Butt at (the item-x-position of Butt + 20 + round(0.5*the item-width of FT), the item-y-position of Butt);
                make FT permanent;
            end;
            make Butt transient;
            delete Butt;
            allow other processing;
        end;
    end;
end

test-find-next-button-or-box (itm: class item, win: class g2-window)
next-itm: class item;
dist: integer;
begin
   next-itm, dist = call FIND-prev-BUTTON-OR-BOX (itm, 20, 40);
   call protools-flash-on-item (next-itm, 5, win);
end

cleanup-master-dialog(ITM: class item, win: class g2-window)
GDI: class gdl-dialog-intermediary;
DLG: class uil-dialog;
dlg-name: symbol;
begin
if there exists a gdl-dialog-intermediary GDI such that (the id of GDI = the class of ITM) then begin
    dlg-name = symbol(the dialog of GDI);
    if there exists a uil-dialog named by dlg-name then begin
       call RESTORE-ALIGN-AND-COMPILE-DIALOG-FIELDS (the uil-dialog named by dlg-name,10,8);
    end;
end;
call gdl-alert (gdl-text-resources, the symbol GDL-DONE, itm, win); 
end

restore-align-and-compile-dialog-fields(dlg: class uil-dialog, field-gap: integer, label-gap: integer)
W: class kb-workspace = the subworkspace of dlg;
begin
  call uil-clear-transient-dialog-pool (DLG);   
  if the user-data of dlg exists and the user-data of dlg is a text and the user-data of dlg /= "" and not(there exists a gdl-lite-passive-text upon W) then begin
        call gdl-restore-labels-and-buttons(dlg);
        wait for 6 seconds;
  end;
 
  call ALIGN-DIALOG-FIELDS (dlg, field-gap, label-gap);
  wait for 3 seconds;
  call gdl-compile-labels-and-buttons(dlg);
  make dlg permanent;
end

find-prev-button-or-box(itm: class item, xtol: integer, ytol:integer) = (class item, integer)
W: class kb-workspace = the workspace of itm;
x, y, dist, mindist, xpos, ypos: integer;
next-borb: class item = itm;  {failed return value}
rbox: class uil-radio-box;
test-itm: class item;
begin
   if itm is a uil-radio-button then begin
       {find first button of set}
       rbox = the uil-radio-box that is the-group-master-of itm;
       itm = call uil-get-selection-box-lead-button(rbox);
   end;
   xpos = the item-x-position of itm - round(0.5*the item-width of itm); {left side}
   ypos = the item-y-position of itm + round(0.5*the item-height of itm); {top edge}

   mindist = 9999999;
   for test-itm = each item upon W do
       if (test-itm is a uil-radio-button or test-itm is a uil-edit-box) and test-itm is not the same object as itm then begin
           x = the item-x-position of test-itm - round(0.5*the item-width of test-itm); {left edge}
           y = the item-y-position of test-itm - round(0.5*the item-height of test-itm); {bottom edge}
           if y > ypos and y - ypos < ytol and abs(x - xpos) < xtol then dist = (x - xpos)*(x - xpos) + (y - ypos)*(y - ypos) else dist = 9999999; {only move down}
           if dist < mindist then begin
               next-borb = test-itm;
               mindist = dist;
           end;
       end;
    end;

    if next-borb is a uil-radio-button then begin
       {find first button of set}
       rbox = the uil-radio-box that is the-group-master-of next-borb;
       next-borb = call uil-get-selection-box-lead-button(rbox);
   end;

    return next-borb, mindist;
end

find-next-button-or-box(itm: class item, xtol: integer, ytol: integer) = (class item, integer)
W: class kb-workspace = the workspace of itm;
x, y, dist, mindist, xpos, ypos: integer;
ebox: class uil-edit-box;
butt: class uil-radio-button;
next-borb: class item = itm;  {failed return value}
rbox: class uil-radio-box;
test-itm: class item;
begin
   if itm is a uil-radio-button then begin
       {find last button of set}
       rbox = the uil-radio-box that is the-group-master-of itm;
       itm = call uil-get-selection-box-last-button(rbox);
   end;
   xpos = the item-x-position of itm - round(0.5*the item-width of itm); {left side}
   ypos = the item-y-position of itm - round(0.5*the item-height of itm); {bottom edge}

   mindist = 9999999;
   for test-itm = each item upon W do
       if (test-itm is a uil-radio-button or test-itm is a uil-edit-box) and test-itm is not the same object as itm then begin
           x = the item-x-position of test-itm - round(0.5*the item-width of test-itm); {left edge}
           y = the item-y-position of test-itm + round(0.5*the item-height of test-itm); {top edge}
           if y < ypos and ypos - y < ytol and abs(x - xpos) < xtol then dist = (x - xpos)*(x - xpos) + (y - ypos)*(y - ypos) else dist = 9999999; {only move down}
           if dist < mindist then begin
               next-borb = test-itm;
               mindist = dist;
           end;
       end;
    end;
    return next-borb, mindist;
end

create-borb-group-list-for-dialog(dlg: class uil-dialog) = (class item-list) {borbs = buttons and boxes}
W: class kb-workspace;
rbox: class uil-radio-box;
ebox: class uil-edit-box;
butt: class uil-button;
borb-group-list: class item-list;
borb-list: class item-list;
itm, next-itm, prev-itm: class item;
dlg-items: class item-list;
n: integer;
line-up-tol: integer = 20;
vertical-tol: integer = 30;
begin
   
{Create list of all radio box groups and edit boxes on dialog}
   create an item-list dlg-items;
   for rbox = each uil-radio-box in the uil-dialog-grobj-list of dlg do
       butt = call uil-get-selection-box-lead-button(rbox);
       insert butt at the end of the item list dlg-items;
   end;
   for ebox = each uil-edit-box upon the subworkspace of dlg do
       insert ebox at the end of the item list dlg-items;
   end;

{find "top" items by testing one-by-one} 
   create an item-list borb-group-list;
   for itm = each item in dlg-items do
       prev-itm = call FIND-PREV-BUTTON-OR-BOX(itm, line-up-tol, vertical-tol);
       if prev-itm is the same object as itm then begin
           create a item-list borb-list;
           insert borb-list at the end of the item list borb-group-list;
           insert itm at the beginning of the item list borb-list;
       end;
   end;
   delete dlg-items;

{now develop lists from each top item} 
   for borb-list = each item-list in borb-group-list do
       n = 1;
       itm = the first item in borb-list;  
       repeat 
          next-itm = call find-next-button-or-box(itm, line-up-tol, vertical-tol);
          exit if next-itm is the same object as itm; 
          n = n + 1;
          insert next-itm at the end of the item list borb-list;
          itm = next-itm;
       end;
   end;

   return borb-group-list;
end

align-dialog-fields(dlg: class uil-dialog, field-gap: integer, label-gap: integer)
W: class kb-workspace = the subworkspace of dlg;
rbox: class uil-radio-box;
borb-group-list, borb-list: class item-list;
key-itm, itm: class item;
xpos, ypos, x, y, new-center: integer;
label: class gdl-lite-passive-text;
extra-horizontal-space: integer = 10;  {space for radio button border}
extra-vertical-space: integer = 8;  {space for radio button-border}
M: class gcc-dot-marker;
begin
{first make subws big enough so that moves will work, not bump against ws edge}
   create a gcc-dot-marker M;
   transfer M to w at (500,500);
   transfer M off;
   transfer M to w at (-500, -500);
   delete M;

   borb-group-list = call CREATE-BORB-GROUP-LIST-FOR-DIALOG(dlg);
   for borb-list = each item-list in borb-group-list do
       key-itm = the first item in borb-list;
       label = call LABEL-NEAREST-TO-ITEM (key-itm);
       new-center = the item-x-position of key-itm - round(0.5*the item-width of key-itm) - label-gap - round(0.5*the item-width of label);
       if key-itm is a uil-radio-button then new-center = new-center - extra-horizontal-space;
       move label to (new-center, the item-y-position of key-itm);
       repeat
           key-itm = the first item in borb-list;
           remove key-itm from the item list borb-list;
           exit if the first item in borb-list does not exist;
           itm = the first item in borb-list;
           label = call LABEL-NEAREST-TO-ITEM (itm);
           if key-itm is a uil-radio-button then begin  {find last button}
               rbox = the uil-radio-box that is the-group-master-of key-itm;
               key-itm = call uil-get-selection-box-last-button(rbox);
           end;
           xpos = the item-x-position of key-itm - round(0.5*the item-width of key-itm); {left side}
           ypos = the item-y-position of key-itm - round(0.5*the item-height of key-itm); {bottom edge}
           if key-itm is a uil-radio-button then begin
                xpos = xpos - extra-horizontal-space;  {leave room for border}
                ypos = ypos - extra-vertical-space;
           end;
           y = ypos - field-gap - round(0.5*the item-height of itm);
           x = xpos + round(0.5*the item-width of itm);
           if itm is a uil-radio-button then begin
                x = x + extra-horizontal-space;
                y = y - extra-vertical-space; 
           end;
           move itm to (x, y);
           if itm is a uil-radio-button then call uil-move-grobj(itm);
           new-center = xpos - label-gap - round(0.5*the item-width of label);
           move label to (new-center, y);
      end;
      delete borb-list;
   end;
   delete borb-group-list;
   change the size of w to minimum;
end

label-nearest-to-item(itm: class item) = (class gdl-lite-passive-text)
W: class kb-workspace = the workspace of itm;
x, y, dist, mindist: integer;
xbox: integer = the item-x-position of itm - round(0.5*the item-width of itm);
ybox: integer = the item-y-position of itm;
closest-label, label: class gdl-lite-passive-text;
begin
   mindist = 9999999;
   for label = each gdl-lite-passive-text upon W do
       x = the item-x-position of label + round(0.5*the item-width of label); {right edge}
       y = the item-y-position of label;
       dist = (x - xbox)*(x - xbox) + (y - ybox)*(y - ybox);
       if dist < mindist then begin
            closest-label = label;
            mindist = dist;
       end;
    end;
    if closest-label does not exist then signal the symbol error, "No label for grobj";
    return closest-label;
end

align-label-of-item(ITM: class item, gap: integer)
left-edge: integer = the item-x-position of itm - round(0.5*the item-width of itm);
y: integer = the item-y-position of itm;
label: class gdl-lite-passive-text;
new-center: integer;
begin
   label = call LABEL-NEAREST-TO-ITEM (itm);
   if itm is a uil-radio-button then gap = gap + 4;
   new-center = left-edge - gap - round(0.5*the item-width of label);
   move label to (new-center, y);
end

align-labels-of-dialog(dlg: class uil-dialog, gap: integer)
rbox: class uil-radio-box;
ebox: class uil-edit-box;
butt: class uil-button;
begin
   for rbox = each uil-radio-box in the uil-dialog-grobj-list of dlg do
        butt = call uil-get-selection-box-lead-button(rbox);
        call align-label-of-item(butt, gap);
    end;
   for ebox = each uil-edit-box upon the subworkspace of dlg do
        call align-label-of-item(ebox, gap);
   end;
end

navigate-to-master-dialog(ITM: class item, win: class g2-window)
GDI: class gdl-dialog-intermediary;
DLG: class uil-dialog;
dlg-name: symbol;
begin
if there exists a gdl-dialog-intermediary GDI such that (the id of GDI = the class of ITM) then begin
    dlg-name = symbol(the dialog of GDI);
    if there exists a uil-dialog U named by dlg-name or there exists a uil-dialog U such that (the id of U ="[dlg-name]") then begin
       call protools-flash-on-item (U, 5, Win);
    end;
end;
end

hide-name-boxes-and-name-boxes-in-ws-hierarchy(Itm: class item)
{Item can be a workspace or an item with a subworkspace}
ItmToStrip: class item;
begin
    if Itm is a kb-workspace then begin
        for ItmToStrip = each item upon Itm do
            call g2-hide-name-box (ItmToStrip);
            call g2-hide-attribute-displays (ItmToStrip);
            allow other processing;
            if the subworkspace of ItmToStrip exists then call hide-name-boxes-and-name-boxes-in-ws-hierarchy(the subworkspace of ItmToStrip);
        end;
    end else begin
        call g2-hide-name-box (Itm);
        call g2-hide-attribute-displays (Itm);
        allow other processing;
        if the subworkspace of Itm exists then call hide-name-boxes-and-name-boxes-in-ws-hierarchy(the subworkspace of Itm); 
    end;
end

devtool-remove-devtool-change-indicator (AppsVersion: text, Client: class object)
DCI: class devtool-change-message;
NumDeleted: integer = 0;
NumTotal: integer;
ModSequence: sequence;
begin
    NumTotal = the count of each devtool-change-message;
    for DCI = each devtool-change-message do
        if the change-in-version of DCI = AppsVersion then begin
            make DCI transient;
            delete DCI;
            NumDeleted = NumDeleted + 1;
        end;
    end;
    inform the operator that "[NumDeleted] out of [NumTotal] devtool-change-messages have been removed from this KB.";
end

devtool-initialize-devtool-change-indicator (DCI: class devtool-change-message, Client: class object)
Win: class g2-window = (if Client is a g2-window then Client else gfr-default-window);
AppsVersion: text;
Author: text = the text of the g2-window-user-name-in-operating-system of Win;
Time: text;
Param: class text-parameter;
begin
    if there exists a gdl-application-package GAP such that (GAP is the same object as A-GDL-APPLICATION-PACKAGE-NAMED-GDL) then
        AppsVersion = the gfr-version-description of A-GDL-APPLICATION-PACKAGE-NAMED-GDL
    else
        AppsVersion = "4.0 Rev. 0";
    conclude that the change-in-version of DCI = AppsVersion;
    if is-contained-in-text ("apps", Author) then begin
         call protools-inform (Client, "You are creating changes as the user [Author].  You should enter your initials into the devtool-change-message author attribute to record your change effectively.");
    end;
    
    transfer DCI to the mouse of Win;
    conclude that DCI is _protools-related-to-window Win; 

end

devtool-initialize-devtool-to-do (DTD: class protools-to-do, Client: class object)
Win: class g2-window = (if Client is a g2-window then Client else gfr-default-window);
AppsVersion: text;
Author: text = the text of the g2-window-user-name-in-operating-system of Win;
begin
    if there exists a gdl-application-package GAP such that (GAP is the same object as A-GDL-APPLICATION-PACKAGE-NAMED-GDL) then
        AppsVersion = the gfr-version-description of A-GDL-APPLICATION-PACKAGE-NAMED-GDL
    else
        AppsVersion = "4.0 Rev. 0";
    conclude that the protools-target-software-version of DTD = AppsVersion;
    if is-contained-in-text ("apps", Author) then begin
        
    end;
    transfer DTD to the mouse of Win;
    conclude that DTD is _protools-related-to-window Win; 
end
