g2:c.PROCEDURE.g2:a.TEXT:
gspc-util-replace-btn-hide-buttons ()
OLD-B, NEW-B: class object;
SUP: class item;

begin   
    for OLD-B = each g2-sys-proc-hide-button do
        allow other processing; 
        if the item superior to the workspace of OLD-B exists and the item superior to the workspace of OLD-B has a name and is-contained-in-text ("spc-", "[the name of the item superior to the workspace of OLD-B]") then
            begin
                create a uil-hide-button NEW-B;        
                transfer NEW-B to the workspace of OLD-B at (the item-x-position of OLD-B, the item-y-position of OLD-B);    
                make the workspace of NEW-B permanent; 
                make OLD-B transient;   
            end;
    end;
end

gspc-util-replace-btn-navigation-button-with-uil-button (OLD-B: class object)
NEW-B: class object;
ITM, NEW-WS: class item;

begin
    if OLD-B is a btn-hide-button then create a uil-hide-button NEW-B     
    else create a uil-goto-workspace-button NEW-B;        
    transfer NEW-B to the workspace of OLD-B at (the item-x-position of OLD-B, the item-y-position of OLD-B);
    make the workspace of NEW-B permanent;
    if NEW-B is a uil-hide-button then return;
    if OLD-B has a name then change the name of NEW-B to the name of OLD-B;
    create a kb-workspace NEW-WS;
    make NEW-WS the subworkspace of NEW-B;
    make NEW-B permanent; 
    change the background-color of NEW-WS to the background-color of the subworkspace of OLD-B;
    for ITM = each item upon the subworkspace of OLD-B do
        allow other processing;
        if ITM is not a definition then 
            begin
                make ITM transient;
                transfer ITM to NEW-WS;
                make ITM permanent;
            end;
    end;
    make OLD-B transient;   
    change the size of NEW-WS to minimum; 
    make NEW-B permanent;     
end

gspc-util-one-key-descending-sort (UNSORTED: class item-list, KEY: symbol) 
TEMP-LIST: class item-list;
ITM: class item;
MIN-VALUE: item-or-value;

begin
    {inform the operator that "[the name of this procedure]";}
    if the number of elements in UNSORTED < 2 then return;

    create a item-list TEMP-LIST;
    for ITM = each item in UNSORTED do
        allow other processing;
        insert ITM at the end of the item list TEMP-LIST;
        remove ITM from the item list UNSORTED;
    end;

    repeat 
        allow other processing;
        exit if the number of elements in TEMP-LIST = 0;
        MIN-VALUE = the minimum over each item ITM in TEMP-LIST of (the item that is an attribute of ITM named by KEY);
        if there exists an item ITM in TEMP-LIST such that (the item that is an attribute of ITM named by KEY = MIN-VALUE) then 
            begin
                insert ITM at the beginning of the item list UNSORTED;
                remove ITM from the item list TEMP-LIST;
            end;
    end; 
  
    delete TEMP-LIST;
   { inform the operator that "[the name of this procedure] Done";}
end

gspc-util-is-an-inferior-class (ITM: class item, CLASS-NAME: symbol) = (truth-value)
SUB-CLASS: symbol;
IS-A-SUBCLASS: truth-value;

begin
    if the class of ITM = CLASS-NAME then return true;

    for SUB-CLASS = each symbol that is an inferior-class of CLASS-NAME do
        allow other processing;
        {inform the operator that "[SUB-CLASS] is an inferior class of [CLASS-NAME]";}
        if ITM is an instance of the class named by (SUB-CLASS) then return true; 
        IS-A-SUBCLASS = call this procedure (ITM, SUB-CLASS);
        if IS-A-SUBCLASS then return true;
    end;
    
    return false;
end

gspc-util-one-key-ascending-sort (UNSORTED: class item-list, KEY: symbol) 
TEMP-LIST: class item-list;
ITM: class item;
MIN-VALUE: item-or-value;

begin
    if the number of elements in UNSORTED < 2 then return;

    create a item-list TEMP-LIST;
    for ITM = each item in UNSORTED do
        allow other processing;
        insert ITM at the end of the item list TEMP-LIST;
        remove ITM from the item list UNSORTED;
    end;

    repeat 
        allow other processing;
        exit if the number of elements in TEMP-LIST = 0;
        MIN-VALUE = the minimum over each item ITM in TEMP-LIST of (the item that is an attribute of ITM named by KEY);
        if there exists an item ITM in TEMP-LIST such that (the item that is an attribute of ITM named by KEY = MIN-VALUE) then 
            begin
                insert ITM at the end of the item list UNSORTED;
                remove ITM from the item list TEMP-LIST;
            end;
    end; 
  
    delete TEMP-LIST;
end

gspc-util-strip-trailing-blanks (TEXT: text) = (text)

N, L: quantity;
CHAR: text;

begin
    L = length-of-text (TEXT);
    if L = 0 then return "";
    if get-from-text (TEXT, L, L) /= " " then 
        begin 
            {inform the operator that "Text = '[TEXT]'";}
            return TEXT;
        end;

    for N = L down to 2 do
        allow other processing;
        CHAR = get-from-text (TEXT, N, N);
        
        if CHAR = " " and get-from-text (TEXT, N - 1, N - 1) /= " " then 
            begin    
                {inform the operator that "Text = '[get-from-text (TEXT, 1, N - 1)]'";}
                return get-from-text (TEXT, 1, N - 1);
            end;   
    end;

    {inform the operator that "Text = '[TEXT]'";}
    return TEXT;
end

gspc-util-copy-attributes (Source: class item, Target: class item) 

S, TargetS: symbol;
SrcAtt, TargAtt: item-or-value;

begin
    for S = each symbol that is a user-defined attribute name of Source do
        allow other processing;
        {*** Both objects have attribute name.  Source is none and Target is not an item ***} 
        if there exists a symbol TargetS that is a user-defined attribute name of Target such that (S = TargetS) then begin             
            if the item-or-value that is an attribute of Source named by S does not exist and (the item-or-value that is an attribute of Target named by S does not exist or the item-or-value that is an attribute of Target named by S is not an item) then
                begin
                    change the text of the item-or-value that is an attribute of Target named by S to "none";                        
                end
                else begin    {*** Source & Target have values or Target is none ***}
                    SrcAtt = the item-or-value that is an attribute of Source named by S;
                    TargAtt = the item-or-value that is an attribute of Target named by S;   
                    if (SrcAtt is a parameter or 
                        SrcAtt is a quantity or 
                        SrcAtt is a symbol or 
                        SrcAtt is a text or 
                        SrcAtt is a truth-value) and 
                        (TargAtt does not exist or
                        TargAtt is a parameter or 
                        TargAtt is a quantity or 
                        TargAtt is a symbol or 
                        TargAtt is a text or 
                        TargAtt is a truth-value) then
                            conclude that the item-or-value that is an attribute of Target named by S = SrcAtt;                          
                    end;     
            end;
    end;
end

gspc-util-delete-list-and-contents (LIST: class item-list) 
ITM: class item;

begin   
    for ITM = each item in LIST do 
        allow other processing;
        delete ITM;
    end;
    
    delete LIST;
end

gspc-util-fix-label-for-printing (L: class message, WS: class kb-workspace)
X, Y: quantity;

begin
    make L transient; 
    {wait for 2 seconds;}
    X = the item-x-position of L;
    Y = the item-y-position of L;
    transfer L to this workspace;
    transfer L to WS at (X, Y);
                  
end

gspc-util-create-inner-workspace-borders (RWSB: class gspc-util-workspace-border-corner, LWSB: class gspc-util-workspace-border-corner)
LTWSB, RTWSB: class object;
C: class connection;
WS: class kb-workspace;

begin
    create a gspc-util-thin-workspace-border-corner RTWSB;
    transfer RTWSB to the workspace of RWSB at (the item-x-position of RWSB - 15, the item-y-position of RWSB - 15);
    change the upper-region icon-color of RTWSB to black;
    change the lower-region icon-color of RTWSB to white;
    LTWSB = call gspc-util-create-borders (RTWSB);    
    
    conclude that RTWSB is gspc-util-related-to RWSB;
    create a connection of class gspc-util-permanent-relation connected between RTWSB newly locating it at left 1 and RWSB newly locating it at left 1 with style diagonal;
    
    move LTWSB to (the item-x-position of LWSB + 15, the item-y-position of LWSB + 15);
    conclude that LTWSB is gspc-util-related-to LWSB;
    create a connection of class gspc-util-permanent-relation connected between LTWSB newly locating it at left 1 and LWSB newly locating it at left 1 with style diagonal;

    WS = the workspace of RWSB;
    change the text of the workspace-margin of WS to "0";
    change the size of WS to minimum;
    make WS permanent;
end

gspc-util-create-borders (URB: class gspc-util-border-corner) = (class item)
LLB: class object;
C: class connection;

begin
    {}

    make URB transient;
    if URB is a gspc-util-workspace-border-corner then create a gspc-util-lower-left-workspace-border-corner LLB
    else create a gspc-util-border-corner LLB by cloning URB; 
    transfer LLB to the workspace of URB at (the item-x-position of URB - 200, the item-y-position of URB - 200);
    rotate LLB by 180 degrees; 
    if LLB is not a gspc-util-thin-border-corner then
        begin
            change the upper-region icon-color of LLB to black;
            change the lower-region icon-color of LLB to white;
        end;
   
    create a connection C of the class named by the border-edge-class of URB connected between LLB at left-port and URB at bottom-port;
    if C is not a gspc-util-thin-border-edge then change the stripe stripe-color of C to black;
    if C is a gspc-util-thin-border-edge then change the color-pattern of C so that light-color is black, dark-color is white;
    create a connection C of the class named by the border-edge-class of URB connected between LLB at bottom-port and URB at left-port;    
    if C is a gspc-util-thin-border-edge then change the color-pattern of C so that light-color is black, dark-color is white;
    
    make URB permanent;
    make LLB permanent;

    if URB is a gspc-util-workspace-border-corner then start gspc-util-create-inner-workspace-borders (URB, LLB);

    return LLB;
end

gspc-util-delete-borders (BORDER: class gspc-util-border-corner)
ITM: class item;

begin
    if BORDER is a gspc-util-workspace-border-corner then 
        begin
            ITM = the object that is gspc-util-related-to BORDER;
            make the gspc-util-thin-workspace-border-corner connected to ITM transient;
            delete the gspc-util-thin-workspace-border-corner connected to ITM;
            make ITM transient;
            delete ITM;
        end;
    

    ITM = the gspc-util-border-corner connected to BORDER;
    make ITM transient;
    delete ITM;
    
    make BORDER transient;
    delete BORDER;
     
end

gspc-util-reverse-border-colors (URB: class gspc-util-border-corner)
LLB: class object;
UR, LR: symbol;

begin
    UR = the upper-region icon-color of URB;
    LR = the lower-region icon-color of URB;
    LLB = the gspc-util-border-corner connected to URB;

    change the upper-region icon-color of URB to the value of LR;
    change the lower-region icon-color of URB to the value of UR;
    change the upper-region icon-color of LLB to the value of UR;
    change the lower-region icon-color of LLB to the value of LR;

    change the stripe stripe-color of the gspc-util-border-edge connected at the bottom-port of URB to (if the stripe stripe-color of the gspc-util-border-edge connected at the bottom-port of URB is black then the symbol white else the symbol black);
    change the stripe stripe-color of the gspc-util-border-edge connected at the bottom-port of LLB to (if the stripe stripe-color of the gspc-util-border-edge connected at the bottom-port of LLB is black then the symbol white else the symbol black);

    make URB permanent;
    make LLB permanent;
end

gspc-util-clean-gspcs-scroll-items-from-uil-dialog (DIALOG: class uil-dialog, INITIATOR: item-or-value, W: class g2-window, TOP-INITIATOR: item-or-value, ACTIONS: item-or-value)
SCROLL-AREA: class gspcs-scroll-area;
WS: class kb-workspace;

begin
    WS = the subworkspace of DIALOG;
    for SCROLL-AREA = each gspcs-scroll-area upon WS do
        allow other processing;
        call gspcs-clean-up-scroll-area-items (SCROLL-AREA);
    end;
end

gspc-util-show-user-screen-at-pre-defined-position (SCREEN: class item, POSITION: symbol, W: class g2-window) P: class procedure;
{This will not work in a runtime G2.}
begin if W is the same object as gspc-util-all-g2-windows then begin if POSITION = gspc-util-lit-top-left then show the subworkspace of SCREEN with its top left corner at the top left corner of the screen else if POSITION = gspc-util-lit-top-center then show the subworkspace of SCREEN with its top center at the top center of the screen else if POSITION = gspc-util-lit-top-right then show the subworkspace of SCREEN with its top right corner at the top right corner of the screen else if POSITION = gspc-util-lit-bottom-left then show the subworkspace of SCREEN with its bottom left corner at the bottom left corner of the screen else if POSITION = gspc-util-lit-bottom-center then show the subworkspace of SCREEN with its bottom center at the bottom center of the screen else if POSITION = gspc-util-lit-bottom-right then show the subworkspace of SCREEN with its bottom right corner at the bottom right corner of the screen else if POSITION = gspc-util-lit-center then show the subworkspace of SCREEN with its center at the center of the screen else show the subworkspace of SCREEN; end else {Single-user} begin if POSITION = gspc-util-lit-top-left then show the subworkspace of SCREEN on W with its top left corner at the top left corner of the screen else if POSITION = gspc-util-lit-top-center then show the subworkspace of SCREEN on W with its top center at the top center of the screen else if POSITION = gspc-util-lit-top-right then show the subworkspace of SCREEN on W with its top right corner at the top right corner of the screen else if POSITION = gspc-util-lit-bottom-left then show the subworkspace of SCREEN on W with its bottom left corner at the bottom left corner of the screen else if POSITION = gspc-util-lit-bottom-center then show the subworkspace of SCREEN on W with its bottom center at the bottom center of the screen else if POSITION = gspc-util-lit-bottom-right then show the subworkspace of SCREEN on W with its bottom right corner at the bottom right corner of the screen else if POSITION = gspc-util-lit-center then show the subworkspace of SCREEN on W with its center at the center of the screen else show the subworkspace of SCREEN on W; end; end

gspc-util-hide-workspace (BUTTON: class item, WS: class kb-workspace, W: class g2-window)
begin hide WS on W;
end

gspc-util-hide-then-delete-workspace-and-superior-object (BUTTON: class item, WS: class kb-workspace, W: class g2-window)

begin
    hide WS on W;
    wait for 5 seconds;   
    delete the item superior to WS;
end

gspc-util-show-user-screen (SCREEN: class item, W: class g2-window)
P: class procedure;

{This will not work in a runtime G2.}

begin
    if the screen-position of SCREEN exists then 
        begin
            if the screen-position of SCREEN = gspc-util-lit-offset-position then call gspc-util-show-user-screen-at-offset-position (SCREEN, the display-x-offset of SCREEN, the display-y-offset of SCREEN, W)
            else call gspc-util-show-user-screen-at-pre-defined-position (SCREEN, the screen-position of SCREEN, W);
        end
    else if W is the same object as gspc-util-all-g2-windows then show the subworkspace of SCREEN
        
        else {Single-user, no screen position.}
        show the subworkspace of SCREEN on W;      
end

gspc-util-show-user-screen-at-offset-position (SCREEN: class item, X-OFFSET: quantity, Y-OFFSET: quantity, W: class g2-window)
P: class procedure;

{This will not work in a runtime G2.}

begin
    if W is the same object as gspc-util-all-g2-windows then 
        begin   
            create a procedure P;
            change the text of P to "gspc-util-show-ws (WS: class item) begin show WS [X-OFFSET] units to the right of and [Y-OFFSET] units below the top left corner of the screen; end";
            call P (the subworkspace of SCREEN);
            delete P;
        end
    else {Single-user}
        begin
            create a procedure P;
            change the text of P to "gspc-util-show-ws (WS: class item, W: class g2-window) begin show WS on W [X-OFFSET] units to the right of and [Y-OFFSET] units below the top left corner of the screen; end";
            call P (the subworkspace of SCREEN, W);
            delete P;
        end;
end

gspc-util-move-attribute-values-to-workspace-fields
 (ITM: class item, WS: class kb-workspace)
FIELD: class message;
VALU: item-or-value;

begin
    for FIELD = each gspc-util-attribute-value-message upon WS do
        allow other processing;
        if the attribute-name of FIELD does not exist then inform the operator that "[the name of this procedure]: ERROR!!!  The attribute name of a [the class of FIELD] is missing.";
        if the item that is an attribute of ITM named by the attribute-name of FIELD exists then 
            begin
                VALU = the item that is an attribute of ITM named by the attribute-name of FIELD;
                if VALU is a symbol then change the text of FIELD to lower-case-text ("[VALU]")
                else change the text of FIELD to "[VALU]";
            end;      
        {Special case, asking for ITM's name.}
        if the attribute-name of FIELD is name and ITM has a name then change the text of FIELD to "[the name of ITM]";
                
        change the text-color of FIELD to the field-text-color of FIELD;
        change the background-color of FIELD to the field-background-color of FIELD;
        change the border-color of FIELD to the field-border-color of FIELD;      
    end;
end

gspc-util-move-attribute-values-to-workspace-fields-new
 (ITM: class item, WS: class kb-workspace)
FIELD: class message;
VALU: item-or-value;
Vt, Ant: text;

begin
    for FIELD = each gspc-util-attribute-value-message upon WS do
        allow other processing;
        if the attribute-name of FIELD does not exist then inform the operator that "[the name of this procedure]: ERROR!!!  The attribute name of a [the class of FIELD] is missing.";
        if the item that is an attribute of ITM named by the attribute-name of FIELD exists then 
            begin
                Ant = "[the attribute-name of FIELD]";

                VALU = the item that is an attribute of ITM named by the attribute-name of FIELD; 

                if VALU is a symbol then
                    Vt = lower-case-text ("[VALU]")
                else 
                    Vt = "[VALU]"; 

                change the text of FIELD to "[Ant] = [Vt]";

            end;      
        {Special case, asking for ITM's name.}
        if the attribute-name of FIELD is name and ITM has a name then change the text of FIELD to "[the name of ITM]";
                
        change the text-color of FIELD to the field-text-color of FIELD;
        change the background-color of FIELD to the field-background-color of FIELD;
        change the border-color of FIELD to the field-border-color of FIELD;      
    end;
end

gspc-util-test-for-all-numbers (TEXT: text) = (truth-value)

N: quantity;
CHAR: text;

begin
    for N = 1 to length-of-text (TEXT) do
        allow other processing;
        CHAR = get-from-text (TEXT, N, N);
        if not (text-begins-with-quantity (CHAR)) then return false;
    end;

    return true;
end

gspc-util-move-field-values-into-attribute-values (OBJ: class item, WS: class item) 
FIELD: class gspc-util-attribute-value-message;
VALU: value;
S: symbol;
FIELD-IS-NUMERIC: truth-value;

{Does not make OBJ permanent.}

begin
    for FIELD = each gspc-util-attribute-value-message upon WS do
        allow other processing;
        {inform the operator that "Testing [the attribute-name of FIELD] for a [the class of OBJ]";}
        if the attribute-name of FIELD does not exist or not (there exists a symbol S that is a user-defined attribute name of OBJ such that (S = the attribute-name of FIELD)) then go to SKIP-FIELD;

        VALU = the text of FIELD;
        FIELD-IS-NUMERIC = call gspc-util-test-for-all-numbers (VALU);
        if FIELD-IS-NUMERIC and VALU /= "" then VALU = quantity (VALU);              
        
        if the convert-to-symbol of FIELD is true and VALU is not a quantity then call gspc-util-convert-value-to-symbol (OBJ, FIELD, VALU)
        else {Do not convert to symbols.}
            begin
                if the convert-to-truth-values of FIELD is true then
                    begin
                        if VALU = "true" then VALU = true;
                        if VALU = "false" then VALU = false;
                    end;
                conclude that the item that is an attribute of OBJ named by the attribute-name of FIELD = VALU;
                {inform the operator that "just concluded that [the attribute-name of FIELD] = [VALU] for a [the class of OBJ]";}
            end;  
    SKIP-FIELD:
        if the attribute-name of FIELD exists and the attribute-name of  FIELD is name and the text of FIELD /= "" then change the name of OBJ to symbol (the text of FIELD);                      
    end;
end

gspc-util-convert-value-to-symbol (OBJ: class item, FIELD: class message, VALU: value) 

begin               
    if the text of FIELD /= "" then 
       begin                        
            VALU = symbol (the text of FIELD);
            conclude that the item that is an attribute of OBJ named by the attribute-name of FIELD = VALU;
        end;
    if the text of FIELD = "" then change the text of the item that is an attribute of OBJ named by the attribute-name of FIELD to "none";            
end

gspc-util-initialize-buttons-on-a-workapce (DATA-OBJECT: class item, WS: class kb-workspace) 
B: class object;

{Initializes buttons on WS and reflects their state based on the attribute values in DATA-OBJECT.}

begin

{Create selection boxes to "team" the buttons.}   
    {call btn-initialize-buttons-on-a-workapce (WS);}

{Initialize buttons' states based on the attributes of DATA-OBJECT.}
    {for B = each btn-button upon WS do
        allow other processing;
        if the on-value of B exists and the item that is an attribute of DATA-OBJECT named by the attribute-name of B exists and the on-value of B = the item that is an attribute of DATA-OBJECT named by the attribute-name of B then 
            begin
                conclude that the toggle-state of B is on;
                call the procedure named by the configuration-method of B (B);
            end
        else if the off-value of B exists and the item that is an attribute of DATA-OBJECT named by the attribute-name of B exists and the off-value of B = the item that is an attribute of DATA-OBJECT named by the attribute-name of B then 
            begin
                conclude that the toggle-state of B is off;
                call the procedure named by the configuration-method of B (B);
            end;
    end;}
end

gspc-util-move-button-values-into-attribute-values (OBJ: class item, WS: class item) 
B: class item;
VALU: value;
S: symbol;

begin
    for B = each btn-button upon WS do
        allow other processing;
        if the attribute-name of B does not exist or not (there exists a symbol S that is a user-defined attribute name of OBJ such that (S = the attribute-name of B)) then go to SKIP-BUTTON;
        if the attribute-name of B exists and the on-value of B exists and the toggle-state of B is on then conclude that the item that is an attribute of OBJ named by the attribute-name of B = the on-value of B
        else if the attribute-name of B exists and the off-value of B exists then conclude that the item that is an attribute of OBJ named by the attribute-name of B = the off-value of B;
    SKIP-BUTTON:
    end;
end

gspc-util-get-message-by-attribute-name-value (WS: class kb-workspace, ATTR-NAME: symbol) = (item-or-value)
M: class message;

{Must be exactly 1 message upon WS whose attribute-name = ATTR-NAME.}

begin               
    if there exists a message M upon WS such that (the attribute-name of M exists and the attribute-name of M = ATTR-NAME) then return M
    else return "No message found.";     
end
