g2:c.PROCEDURE.g2:a.TEXT:
_gdd-find-variable-in-layer (Variables: sequence, LayerName : Symbol, Tag : Text, HighValue : truth-value) = (structure)

Variable : structure;
TaggedVariable : structure;
VariableValue: integer = 0;

begin  

if HighValue = false then VariableValue = 1000;

for Variable = each structure in Variables do
    {-- Check to see if the variable is part of the named layer --} 
    if is-contained-in-text ("[LayerName]", "[the VARIABLE-NAME of Variable]") then begin 
        if is-contained-in-text (Tag, "[the VARIABLE-NAME of Variable]") then begin
            if HighValue then begin
                if the INITIAL-VALUE of Variable >= VariableValue then begin
                    TaggedVariable = Variable;
                    VariableValue = the INITIAL-VALUE of Variable;
                end;
            end else begin
                if the INITIAL-VALUE of Variable <= VariableValue then begin
                    TaggedVariable = Variable;
                    VariableValue = the INITIAL-VALUE of Variable;
                end;
            end;
        end;  {-- If a "Tagged" Variable --} 
    end;   {-- If part of this layer --} 
end;    {-- for Variable --}

{??? This seems to be missing a lot; TaggedVariable isn't always set up ???}

return TaggedVariable;

end

_gdd-make-anchor-points-inactive (Icon: structure, LayerName : Symbol, Style : symbol) = (structure)
    Variable : structure;
    Variables : sequence;
    NewVariables : sequence = sequence ();
    StripVariable : truth-value;
    MaxVariable : structure;
    MaxValue: integer = 0;
    Index : integer;
    AnchorPoints : integer = 2;
    Tag : text = "-Y";
    HighValue : truth-value = true;
begin   
 
    If Style = the symbol HORIZONTAL then
    begin
        Tag = "-X";
        HighValue = false;
    end;

    if the VARIABLES of Icon exists then
    begin
        for Index = 1 to AnchorPoints do
            Variables = the VARIABLES of Icon;  
            Variable = call _gdd-find-variable-in-layer (Variables, LayerName, Tag, HighValue);
            Icon = call _gdd-remove-variable (Icon, the VARIABLE-NAME of Variable, the INITIAL-VALUE of Variable);  
        end;
    end;

   return Icon; 
end

_gdd-remove-variable (Icon: structure, VariableName : symbol, Val : item-or-value) = (structure)
    Layers : sequence = the LAYERS of Icon;
    NewLayers : sequence = sequence ();
    Layer : structure;
    Variable : structure;
    Variables : sequence;
    NewVariables : sequence = sequence ();
    LineElement : structure;
    LineElements : sequence;
    Point : structure;
    Points : sequence;
    NewPoints : sequence;
    NewLineElements : sequence;
begin
    for Layer = each structure in Layers do

        LineElements = the LINE-DRAWING-ELEMENTS of Layer;
        NewLineElements = sequence (); 
 
        for LineElement = each structure in LineElements do
            NewPoints = sequence ();

            case (the TYPE of LineElement) of 
                POINT:
                    begin
                        Point = the POINTS of LineElement;

                        if is-contained-in-text ("[VariableName]", "[the X of Point]") then 
                            Point = change-attribute (Point, X, Val);

                        if is-contained-in-text ("[VariableName]", "[the Y of Point]") then 
                            Point = change-attribute (Point, Y, Val);

                        LineElement = change-attribute (LineElement, POINTS, Point);               
                    end;
                TEXT: 
                    begin
                        Point = the POINT of LineElement;

                        if is-contained-in-text ("[VariableName]", "[the X of Point]") then 
                            Point = change-attribute (Point, X, Val);

                        if is-contained-in-text ("[VariableName]", "[the Y of Point]") then 
                            Point = change-attribute (Point, Y, Val);

                        if the LABEL of LineElement =  VariableName then
                            LineElement = change-attribute (LineElement, LABEL, Val);                       

                        LineElement = change-attribute (LineElement, POINT, Point);     
                    end;
                otherwise:
                    begin
                        Points = the POINTS of LineElement;
                        for Point = each structure in Points do

                            if is-contained-in-text ("[VariableName]", "[the X of Point]") then
                            begin
                                Point = change-attribute (Point, X, Val); 
                            end;

                            if is-contained-in-text ("[VariableName]", "[the Y of Point]") then 
                                Point = change-attribute (Point, Y, Val); 

                            NewPoints = insert-at-end (NewPoints, Point);
                             
                        end;
                        LineElement = change-attribute (LineElement, POINTS, NewPoints);                         
                    end;

            end; { Case }

            NewLineElements = insert-at-end (NewLineElements, LineElement); 

       end; { LineElements }

       Layer = change-attribute (Layer, LINE-DRAWING-ELEMENTS, NewLineElements);
       NewLayers = insert-at-end (NewLayers, Layer);

   end; { Layers }

   Icon = change-attribute (Icon, LAYERS, NewLayers);

   Variables = the VARIABLES of Icon; 

   for Variable = each structure in Variables do
       if VariableName /= the VARIABLE-NAME of Variable then
           NewVariables = insert-at-end (NewVariables, Variable);
   end;    

   if the count of each structure in NewVariables = 0 then
       Icon = remove-attribute (Icon, VARIABLES)
   else 
       Icon = change-attribute (Icon, VARIABLES, NewVariables); 
 
   return Icon;
end

_gdd-generate-variables (Layer : structure) = (sequence, structure) 
    LineElements : sequence;
    LineElement: structure;
    Points : sequence;   
    Point : structure; 
    NewLineElements : sequence = sequence (); 
    NewPoints : sequence = sequence (); 
    NewPoint : structure;
    LineElementsCount : integer;
    LayerName : symbol = the REGION-OR-COLOR of Layer;
    Variables : sequence = sequence ();
    Variable : structure;
    LineElementIndex : integer = 0;
    PointIndex : integer = 0;
    X, Y : symbol;
    Label : text;
    FontSize : symbol;
    LabelVariable : symbol;
begin

    LineElements = the LINE-DRAWING-ELEMENTS of Layer;
    LineElementsCount = the count of each structure in LineElements;
 
    for LineElement = each structure in LineElements do
        LineElementIndex = LineElementIndex + 1;

        case (the TYPE of LineElement) of 
            POINT:
                begin
                    Point = the POINTS of LineElement;
                    if the X of Point is not a quantity or the Y of Point is not a quantity then return Variables, Layer;

                    X = symbol("[LayerName]-[LineElementIndex]-POINT-X[PointIndex]");
                    Variable = structure (VARIABLE-NAME : X, INITIAL-VALUE : the X of Point);
                    Variables = insert-at-end (Variables, Variable); 

                    Y = symbol("[LayerName]-[LineElementIndex]-POINT-Y[PointIndex]"); 
                    Variable = structure (VARIABLE-NAME : Y, INITIAL-VALUE : the Y of Point);
                    Variables = insert-at-end (Variables, Variable);
                    
                    NewPoint = evaluated-structure (the symbol X, X, the symbol Y, Y);
                    NewLineElements = insert-at-end (NewLineElements, (structure (TYPE : the symbol POINT, POINTS : NewPoint)));
                end;
          TEXT:
                begin
                    Point = the POINT of LineElement;
                    if the X of Point is not a quantity or the Y of Point is not a quantity then return Variables, Layer; 

                    Label = the LABEL of LineElement;
                    FontSize = the FONT-SIZE of LineElement;

                    X = symbol("[LayerName]-[LineElementIndex]-TEXT-X[PointIndex]");
                    Variable = structure (VARIABLE-NAME : X, INITIAL-VALUE : the X of Point);
                    Variables = insert-at-end (Variables, Variable); 

                    Y = symbol("[LayerName]-[LineElementIndex]-TEXT-Y[PointIndex]"); 
                    Variable = structure (VARIABLE-NAME : Y, INITIAL-VALUE : the Y of Point);
                    Variables = insert-at-end (Variables, Variable);

                    LabelVariable = symbol("[LayerName]-[LineElementIndex]-TEXT-LABEL"); 
                    Variable = structure (VARIABLE-NAME : LabelVariable, INITIAL-VALUE : Label);
                    Variables = insert-at-end (Variables, Variable); 
                    
                    NewPoint = evaluated-structure (the symbol X, X, the symbol Y, Y);
                    NewLineElements = insert-at-end (NewLineElements, (structure (TYPE : the symbol TEXT, LABEL: LabelVariable, POINT: NewPoint, FONT-SIZE : FontSize)));

                end; 
          LINES:
                begin
                    Points = the POINTS of LineElement;
                    NewPoints = sequence ();
                    for Point = each structure in Points do
                        if the X of Point is not a quantity or the Y of Point is not a quantity then return Variables, Layer; 

                        X = symbol ("[LayerName]-[LineElementIndex]-LINES-X[PointIndex]");
                        Variable = structure (VARIABLE-NAME : X, INITIAL-VALUE : the X of Point);
                        Variables = insert-at-end (Variables, Variable); 

                        Y = symbol ("[LayerName]-[LineElementIndex]-LINES-Y[PointIndex]"); 
                        Variable = structure (VARIABLE-NAME : Y, INITIAL-VALUE : the Y of Point);
                        Variables = insert-at-end (Variables, Variable);

                        if the ARC of Point exists then
                            NewPoints = insert-at-end (NewPoints, (structure (ARC: TRUE, X : X, Y : Y)))
                        else
                            NewPoints = insert-at-end (NewPoints, (structure (X : X, Y : Y))); 
                        PointIndex = PointIndex + 1;
 
                    end;
                    NewLineElements = insert-at-end (NewLineElements, (structure (TYPE : the symbol LINES, POINTS : NewPoints)));
                    PointIndex = 0;              
                end;
            CIRCLE:
               begin
                    Points = the POINTS of LineElement;
                    NewPoints = sequence ();
                    for Point = each structure in Points do
                        if the X of Point is not a quantity or the Y of Point is not a quantity then return Variables, Layer; 

                        X = symbol ("[LayerName]-[LineElementIndex]-CIRCLE-X[PointIndex]");
                        Variable = structure (VARIABLE-NAME : X, INITIAL-VALUE : the X of Point);
                        Variables = insert-at-end (Variables, Variable); 

                        Y = symbol ("[LayerName]-[LineElementIndex]-CIRCLE-y[PointIndex]"); 
                        Variable = structure (VARIABLE-NAME : Y, INITIAL-VALUE : the Y of Point);
                        Variables = insert-at-end (Variables, Variable);

                        NewPoints = insert-at-end (NewPoints, (structure (X : X, Y : Y)));
                        PointIndex = PointIndex + 1;
 
                    end;
                    NewLineElements = insert-at-end (NewLineElements, (structure (TYPE : the symbol CIRCLE, POINTS : NewPoints)));
                    PointIndex = 0;              
                end;         
            FILLED-CIRCLE:
               begin
                    Points = the POINTS of LineElement;
                    NewPoints = sequence ();
                    for Point = each structure in Points do
                        if the X of Point is not a quantity or the Y of Point is not a quantity then return Variables, Layer; 

                        X = symbol ("[LayerName]-[LineElementIndex]-FILLED-CIRCLE-X[PointIndex]");
                        Variable = structure (VARIABLE-NAME : X, INITIAL-VALUE : the X of Point);
                        Variables = insert-at-end (Variables, Variable); 

                        Y = symbol ("[LayerName]-[LineElementIndex]-FILLED-CIRCLE-Y[PointIndex]"); 
                        Variable = structure (VARIABLE-NAME : Y, INITIAL-VALUE : the Y of Point);
                        Variables = insert-at-end (Variables, Variable);

                        NewPoints = insert-at-end (NewPoints, (structure (X : X, Y : Y)));
                        PointIndex = PointIndex + 1;
 
                    end;
                    NewLineElements = insert-at-end (NewLineElements, (structure (TYPE : the symbol FILLED-CIRCLE, POINTS : NewPoints)));
                    PointIndex = 0;              
                end;   
            FILLED-RECTANGLE:
                begin
                    Points = the POINTS of LineElement;
                    if the X of Points[0] is not a quantity or the Y of Points[0] is not a quantity then return Variables, Layer; 
                    if the X of Points[1] is not a quantity or the Y of Points[1] is not a quantity then return Variables, Layer; 

                    NewPoints = sequence ();

                    X = symbol ("[LayerName]-[LineElementIndex]-FILLED-POLYGON-X0");
                    Variable = structure (VARIABLE-NAME : X, INITIAL-VALUE : the X of Points[0]);
                    Variables = insert-at-end (Variables, Variable); 

                    Y = symbol ("[LayerName]-[LineElementIndex]-FILLED-POLYGON-Y0"); 
                    Variable = structure (VARIABLE-NAME : Y, INITIAL-VALUE : the Y of Points[0]);
                    Variables = insert-at-end (Variables, Variable);

                    NewPoints = insert-at-end (NewPoints, (structure (X : X, Y : Y))); 

                    X = symbol ("[LayerName]-[LineElementIndex]-FILLED-POLYGON-X1");
                    Variable = structure (VARIABLE-NAME : X, INITIAL-VALUE : the X of Points[1]);
                    Variables = insert-at-end (Variables, Variable); 

                    Y = symbol ("[LayerName]-[LineElementIndex]-FILLED-POLYGON-Y1"); 
                    Variable = structure (VARIABLE-NAME : Y, INITIAL-VALUE : the Y of Points[0]);
                    Variables = insert-at-end (Variables, Variable);

                    NewPoints = insert-at-end (NewPoints, (structure (X : X, Y : Y))); 

                    X = symbol ("[LayerName]-[LineElementIndex]-FILLED-POLYGON-X2");
                    Variable = structure (VARIABLE-NAME : X, INITIAL-VALUE : the X of Points[1]);
                    Variables = insert-at-end (Variables, Variable); 

                    Y = symbol ("[LayerName]-[LineElementIndex]-FILLED-POLYGON-Y2"); 
                    Variable = structure (VARIABLE-NAME : Y, INITIAL-VALUE : the Y of Points[1]);
                    Variables = insert-at-end (Variables, Variable);

                    NewPoints = insert-at-end (NewPoints, (structure (X : X, Y : Y))); 

                    X = symbol ("[LayerName]-[LineElementIndex]-FILLED-POLYGON-X3");
                    Variable = structure (VARIABLE-NAME : X, INITIAL-VALUE : the X of Points[0]);
                    Variables = insert-at-end (Variables, Variable); 

                    Y = symbol ("[LayerName]-[LineElementIndex]-FILLED-POLYGON-Y3"); 
                    Variable = structure (VARIABLE-NAME : Y, INITIAL-VALUE : the Y of Points[1]);
                    Variables = insert-at-end (Variables, Variable);

                    NewPoints = insert-at-end (NewPoints, (structure (X : X, Y : Y))); 

                    NewLineElements = insert-at-end (NewLineElements, (structure (TYPE : the symbol FILLED-POLYGON, POINTS : NewPoints)));
                    PointIndex = 0;              
                end;      
            FILLED-POLYGON:
                begin
                    Points = the POINTS of LineElement;
                    NewPoints = sequence ();
                    for Point = each structure in Points do
                        if the X of Point is not a quantity or the Y of Point is not a quantity then return Variables, Layer; 

                        X = symbol ("[LayerName]-[LineElementIndex]-FILLED-POLYGON-X[PointIndex]");
                        Variable = structure (VARIABLE-NAME : X, INITIAL-VALUE : the X of Point);
                        Variables = insert-at-end (Variables, Variable); 

                        Y = symbol ("[LayerName]-[LineElementIndex]-FILLED-POLYGON-Y[PointIndex]"); 
                        Variable = structure (VARIABLE-NAME : Y,
                                                    INITIAL-VALUE : the Y of Point);
                        Variables = insert-at-end (Variables, Variable);

                        NewPoints = insert-at-end (NewPoints, (structure (X : X, Y : Y)));
                        PointIndex = PointIndex + 1;
 
                    end;
                    NewLineElements = insert-at-end (NewLineElements, (structure (TYPE : the symbol FILLED-POLYGON, POINTS : NewPoints)));
                    PointIndex = 0;              
                end; 
            OUTLINE:
                begin
                    Points = the POINTS of LineElement;
                    NewPoints = sequence ();
                    for Point = each structure in Points do
                        if the X of Point is not a quantity or the Y of Point is not a quantity then return Variables, Layer; 

                        X = symbol ("[LayerName]-[LineElementIndex]-OUTLINE-X[PointIndex]");
                        Variable = structure (VARIABLE-NAME : X, INITIAL-VALUE : the X of Point);
                        Variables = insert-at-end (Variables, Variable); 

                        Y = symbol ("[LayerName]-[LineElementIndex]-OUTLINE-Y[PointIndex]"); 
                        Variable = structure (VARIABLE-NAME : Y, INITIAL-VALUE : the Y of Point);
                        Variables = insert-at-end (Variables, Variable);

                        NewPoints = insert-at-end (NewPoints, (structure (X : X, Y : Y)));
                        PointIndex = PointIndex + 1;
 
                    end;
                    NewLineElements = insert-at-end (NewLineElements, (structure (TYPE : the symbol OUTLINE, POINTS : NewPoints)));
                    PointIndex = 0;              
                end;                       
            otherwise:
                Point = structure ();
        end;

    end;
 
    return Variables, structure (REGION-OR-COLOR : LayerName, LINE-DRAWING-ELEMENTS : NewLineElements);
end

_gdd-get-icon-layer (Icon : structure, LayerName : symbol) = (structure, integer)    
    Layer : structure;
    Layers : sequence;
    LayerIndex : integer = 0;
    LayerCount : integer;
    LayerFound : truth-value = false;
begin    
    Layers = the LAYERS of Icon;
    LayerCount = the count of each structure in Layers;
 
    if LayerCount > 0 then 
    begin
        repeat
            if the REGION-OR-COLOR of Layers[LayerIndex] = LayerName then
                    LayerFound = true;
            exit if LayerFound or LayerIndex >= (LayerCount - 1);
            LayerIndex = LayerIndex + 1;
        end;
    end;

    if LayerFound then
        Layer = Layers[LayerIndex]
    else
        Layer = structure ();
   
    return Layer, LayerIndex;
end

_gdd-generate-tracking-label (Icon : structure, Info : class gdd-class-information-object) = (structure)
    Layer : structure;
    Layers : sequence;
    LayerColor : symbol;
    Point : structure;
    LineElements : sequence = sequence ();
    X, Y : integer;
    XOrigin : integer;
    YOrigin : integer;
    Offset : integer;
    Label : text;
    LabelSize : symbol;
    MinimumValue : integer;
    MaximumValue : integer;
    Regions : sequence;
    LabelRegion : structure;
    LabelName : symbol = the symbol TRACKING-LABEL;
    StartPoint : value;
    Alignment : symbol;
    Length : integer;
begin
    
    XOrigin = the GDD-HORIZONTAL-CENTER of Info;
    YOrigin = the GDD-VERTICAL-CENTER of Info;
    LayerColor = the GDD-TRACKING-LABEL-COLOR of Info;
    LabelSize = the GDD-TRACKING-LABEL-SIZE of Info;
    Offset = the GDD-TRACKING-LABEL-OFFSET of Info;
    MaximumValue = the GDD-MAXIMUM-VALUE of Info;
    MinimumValue = the GDD-MINIMUM-VALUE of Info;
 
    if Info is a gdd-radial-class-information-object then
    begin
         StartPoint = the GDD-START-POINT of Info; 
         X, Y = call _gdd-rotate-point (XOrigin, YOrigin - Offset, XOrigin, YOrigin, StartPoint);
    end
    else
    begin
        Alignment = the GDD-ALIGNMENT of Info;
        Length = the GDD-LENGTH of Info;
        if Alignment = the symbol HORIZONTAL then
        begin
            X = XOrigin + Offset + Length; 
            Y = YOrigin;
        end 
        else 
        begin
            X = XOrigin; 
            Y = YOrigin - Offset - Length;
        end 
    end;
  
    Point = structure (X: X, Y: Y);
    Label = "[MinimumValue]";

    LineElements = insert-at-end (LineElements, structure (TYPE: the symbol TEXT, LABEL: Label, POINT : Point, FONT-SIZE : LabelSize));               


    If the REGIONS of Icon exists then
        Regions = the REGIONS of Icon
    else
        Regions = sequence ();
 
    Layers = the LAYERS of Icon;
 
    LabelRegion = structure (REGION-NAME : symbol("[the NAME of Info]-[LabelName]"), COLOR-NAME : LayerColor);
 
    Layer = structure (REGION-OR-COLOR : symbol("[the NAME of Info]-[LabelName]"), LINE-DRAWING-ELEMENTS : LineElements); 

    Regions = insert-at-end (Regions, LabelRegion); 
    Layers = insert-at-end (Layers, Layer);

    Icon = change-attribute (Icon, REGIONS, Regions);
    Icon = change-attribute (Icon, LAYERS, Layers); 

    return Icon;
end

_gdd-generate-shadow (Icon : structure, Info : class gdd-class-information-object) = (structure)
    Layer : structure;
    NewLayers : sequence = sequence ();
    ShadowLayer : structure;
    ShadowRegion : structure;
    SourceLayer : symbol;
    Regions : sequence;
    Layers : sequence;
    Color : symbol;  
begin    
    Color = the GDD-SHADOW-COLOR of Info;

    if the first symbol in the GDD-POINTER-REGION of Info exists then
    begin
 
      SourceLayer = the first symbol in the GDD-POINTER-REGION of Info;

      If the REGIONS of Icon exists then
          Regions = the REGIONS of Icon
      else
          Regions = sequence ();
 
      Layers = the Layers of Icon; 

      for Layer = each structure in Layers do
        if the REGION-OR-COLOR of Layer = SourceLayer then
        begin
            ShadowRegion = structure (REGION-NAME : symbol("[the NAME of Info]-SHADOW"), COLOR-NAME : Color);
            ShadowLayer = Layer;
            ShadowLayer = change-attribute (ShadowLayer, REGION-OR-COLOR, symbol  ("[the NAME of Info]-SHADOW"));

            Regions = insert-at-end (Regions, ShadowRegion); 
            Icon = change-attribute (Icon, REGIONS, Regions); 
            NewLayers = insert-at-end (NewLayers, ShadowLayer);                     

        end; {-- If the shadow layer was found --}     
        NewLayers = insert-at-end (NewLayers, Layer); 
      end;

      Icon = change-attribute (Icon, LAYERS, NewLayers);

    end;

    return Icon;    
end

_gdd-generate-linear-labels (Icon : structure, Info : class gdd-class-information-object) = (structure)
    Layer : structure;
    Layers : sequence;
    LayerColor : symbol;
    Point : structure;
    LineElements : sequence = sequence ();
    X, Y : integer;
    XOrigin : integer;
    YOrigin : integer;
    NumLabels : integer;
    LabelIndex : integer;
    TickOffset : integer;
    TickLength : integer;
    Length : integer;
    Offset : integer;
    Label : text;
    LabelSize : symbol;
    MinimumValue : integer;
    MaximumValue : integer;
    Regions : sequence;
    LabelRegion : structure;
    LabelName : symbol = the symbol LABELS;
    Alignment : symbol;
begin
    
    XOrigin = the GDD-HORIZONTAL-CENTER of Info;
    YOrigin = the GDD-VERTICAL-CENTER of Info;
    Length = the GDD-LENGTH of Info;
    NumLabels = the GDD-LABEL-COUNT of Info;
    LayerColor = the GDD-LABEL-COLOR of Info;
    LabelSize = the GDD-LABEL-SIZE of Info;
    Offset = the GDD-LABEL-OFFSET of Info;
    MaximumValue = the GDD-MAXIMUM-VALUE of Info;
    MinimumValue = the GDD-MINIMUM-VALUE of Info;
    TickOffset = the GDD-TICKMARK-OFFSET of Info;
    TickLength = the GDD-MAJOR-TICKMARK-LENGTH of INFO;
    Alignment = the GDD-ALIGNMENT of Info; 
 
    for LabelIndex = 1 to NumLabels do
    
        if Alignment = the symbol HORIZONTAL then
        begin       
            Y = YOrigin + TickOffset + Offset;
            X = round (XOrigin + (LabelIndex - 1) * (Length / (NumLabels - 1)));

            {-- adjust for the size of the label --}
            {-- (this is a fudge factor) --}
            X = X - 6; 
        end
        else
        begin
            X = XOrigin + TickOffset + Offset;
            Y = round (YOrigin - (LabelIndex - 1) * (Length / (NumLabels - 1))); 
        end;
  
        Point = structure (X: X, Y: Y);
        Label = "[round (MinimumValue + (LabelIndex - 1) * (MaximumValue - MinimumValue) / (NumLabels - 1))]";

        LineElements = insert-at-end (LineElements, structure (TYPE: the symbol TEXT, LABEL: Label, POINT : Point, FONT-SIZE : LabelSize));               
    end;

    If the REGIONS of Icon exists then
        Regions = the REGIONS of Icon
    else
        Regions = sequence (); 

    Layers = the LAYERS of Icon;
 
    LabelRegion = structure (REGION-NAME : symbol("[the NAME of Info]-[LabelName]"), COLOR-NAME : LayerColor);
 
    Layer = structure (REGION-OR-COLOR : symbol("[the NAME of Info]-[LabelName]"), LINE-DRAWING-ELEMENTS : LineElements); 

    Regions = insert-at-end (Regions, LabelRegion); 
    Layers = insert-at-end (Layers, Layer);

    Icon = change-attribute (Icon, REGIONS, Regions);
    Icon = change-attribute (Icon, LAYERS, Layers); 

    return Icon;
end

_gdd-generate-radial-labels (Icon: structure, Info : class gdd-class-information-object) = (structure)
    Layer : structure;
    Layers : sequence;
    LayerColor : symbol;
    Point : structure;
    LineElements : sequence = sequence ();
    X, Y : integer;
    XOrigin : integer;
    YOrigin : integer;
    NumTicks : integer;
    TickIndex : integer;
    StartPoint : float;
    Range : float;
    Radius : integer;
    Offset : integer;
    Label : text;
    LabelSize : symbol;
    MinimumValue : integer;
    MaximumValue : integer;
    TickRange : Integer;
    Regions : sequence;
    LabelRegion : structure;
    LabelName : symbol = the symbol LABELS;
    Clockwise : integer = 1;
begin
    
    XOrigin = the GDD-HORIZONTAL-CENTER of Info;
    YOrigin = the GDD-VERTICAL-CENTER of Info;
    Radius = the GDD-RADIUS of Info;
    NumTicks = the GDD-LABEL-COUNT of Info;
    StartPoint = the GDD-START-POINT of Info;
    Range = the GDD-RANGE of Info;
    LayerColor = the GDD-LABEL-COLOR of Info;
    LabelSize = the GDD-LABEL-SIZE of Info;
    Offset = the GDD-LABEL-OFFSET of Info;
    MaximumValue = the GDD-MAXIMUM-VALUE of Info;
    MinimumValue = the GDD-MINIMUM-VALUE of Info;
    if the GDD-CLOCKWISE of Info is false then Clockwise = -1;

    if Range = 360.0 then
        TickRange = NumTicks
    else
        TickRange = NumTicks - 1; 

    for TickIndex = 1 to NumTicks do
            
        X = XOrigin; 
        Y = YOrigin - Radius - Offset;
 
        X, Y = call _gdd-rotate-point (X, Y, XOrigin, YOrigin, Clockwise * (TickIndex - 1) *  Range / (TickRange) + StartPoint);

        {-- adjust for the size of the label --}
        {-- (this is a fudge factor) --}
        X = X - 6;
        Y = Y + 5;
   
        Point = structure (X: X, Y: Y);
        Label = "[round (MinimumValue + (TickIndex - 1) * (MaximumValue - MinimumValue) / (TickRange))]";

        LineElements = insert-at-end (LineElements, structure (TYPE: the symbol TEXT, LABEL: Label, POINT : Point, FONT-SIZE : LabelSize));               
    end;

    If the REGIONS of Icon exists then
        Regions = the REGIONS of Icon
    else
        Regions = sequence (); 

    Layers = the LAYERS of Icon;
 
    LabelRegion = structure (REGION-NAME : symbol("[the NAME of Info]-[LabelName]"), COLOR-NAME : LayerColor);
 
    Layer = structure (REGION-OR-COLOR : symbol("[the NAME of Info]-[LabelName]"), LINE-DRAWING-ELEMENTS : LineElements); 

    Regions = insert-at-end (Regions, LabelRegion); 
    Layers = insert-at-end (Layers, Layer);

    Icon = change-attribute (Icon, REGIONS, Regions);
    Icon = change-attribute (Icon, LAYERS, Layers); 

    return Icon;
end

_gdd-generate-linear-tickmarks (Icon: structure, Info : class gdd-class-information-object, Major: truth-value) = (structure)
    Layer : structure;
    Layers : sequence;
    LayerColor : symbol;
    Point : structure;
    PointIndex : integer;
    NumPoints : integer = 2;
    Points : sequence;
    LineElements : sequence = sequence ();
    X, Y : integer;
    XOrigin : integer;
    YOrigin : integer;
    NumTicks : integer;
    Length : integer;
    TickOffset : integer;
    TickIndex : integer;
    TickLength : integer;
    Regions : sequence;
    TickRegion : structure;
    TickName : symbol = the symbol TICKMARKS;
    Alignment : symbol;
begin
    
    XOrigin = the GDD-HORIZONTAL-CENTER of INFO;
    YOrigin = the GDD-VERTICAL-CENTER of INFO;
    TickOffset = the GDD-TICKMARK-OFFSET of INFO;
    NumTicks = the GDD-TICKMARK-COUNT of INFO;
    Length = the GDD-LENGTH of INFO;
    LayerColor = the GDD-TICKMARK-COLOR of INFO;
    Alignment = the GDD-ALIGNMENT of Info;

    if Major then 
    begin
       TickName = the symbol MAJOR-TICKMARKS;
       NumTicks = the GDD-MAJOR-TICKMARK-COUNT of INFO; 
       TickLength = the GDD-MAJOR-TICKMARK-LENGTH of INFO
    end
    else
    begin
        NumTicks = the GDD-TICKMARK-COUNT of INFO; 
        TickLength = the GDD-TICKMARK-LENGTH of INFO; 
    end;

    for TickIndex = 1 to NumTicks do
        Points = sequence ();

        for PointIndex = 1 to NumPoints do

            if Alignment = the symbol HORIZONTAL then 
            begin
                if PointIndex = NumPoints then 
                    Y = YOrigin + TickOffset
                else 
                    Y = YOrigin + TickOffset - TickLength;

                X = round (XOrigin + (TickIndex - 1) * (Length /( NumTicks - 1)));
            end
            else
            begin
                if PointIndex = NumPoints then 
                    X = XOrigin + TickOffset
                else 
                    X = XOrigin + TickOffset - TickLength;

                Y = round (YOrigin - (TickIndex - 1) * (Length / (NumTicks - 1)));
            end;
   
            Point = structure (X: X, Y: Y);
            Points = insert-at-end (Points, Point); 

        end;

        LineElements = insert-at-end (LineElements, structure (TYPE: the symbol LINES, POINTS : Points));               
    end;
 
    If the REGIONS of Icon exists then
        Regions = the REGIONS of Icon
    else
        Regions = sequence ();
 
    Layers = the LAYERS of Icon;
 
    TickRegion = structure (REGION-NAME : symbol("[the NAME of Info]-[TickName]"), COLOR-NAME : LayerColor);
 
    Layer = structure (REGION-OR-COLOR : symbol("[the NAME of Info]-[TickName]"), LINE-DRAWING-ELEMENTS : LineElements); 

    Regions = insert-at-end (Regions, TickRegion); 
    Layers = insert-at-end (Layers, Layer);

    Icon = change-attribute (Icon, REGIONS, Regions);
    Icon = change-attribute (Icon, LAYERS, Layers); 

    return Icon;
end

_gdd-generate-radial-tickmarks (Icon : structure, Info : class gdd-class-information-object, Major: truth-value) = (structure)
    Layer : structure;
    Layers : sequence;
    LayerColor : symbol;
    Point : structure;
    PointIndex : integer;
    NumPoints : integer = 2;
    Points : sequence;
    LineElements : sequence = sequence ();
    X, Y : integer;
    XOrigin : integer;
    YOrigin : integer;
    NumTicks : integer;
    StartPoint : float;
    Range : float;
    Radius : integer;
    TickIndex : integer;
    TickLength : integer;
    TickRange : integer;
    Regions : sequence;
    TickRegion : structure;
    TickName : symbol = the symbol TICKMARKS;
    Clockwise : integer = 1;
begin
    
    XOrigin = the GDD-HORIZONTAL-CENTER of INFO;
    YOrigin = the GDD-VERTICAL-CENTER of INFO;
    Radius = the GDD-RADIUS of INFO;
    NumTicks = the GDD-TICKMARK-COUNT of INFO;
    StartPoint = the GDD-START-POINT of INFO;
    Range = the GDD-RANGE of INFO;
    LayerColor = the GDD-TICKMARK-COLOR of INFO;
    if the GDD-CLOCKWISE of Info is false then Clockwise = -1; 

    if Major then 
    begin
       TickName = the symbol MAJOR-TICKMARKS;
       NumTicks = the GDD-MAJOR-TICKMARK-COUNT of INFO; 
       TickLength = the GDD-MAJOR-TICKMARK-LENGTH of INFO
    end
    else
    begin
        NumTicks = the GDD-TICKMARK-COUNT of INFO; 
        TickLength = the GDD-TICKMARK-LENGTH of INFO; 
    end;

    if Range = 360.0 then
        TickRange = NumTicks
    else
        TickRange = NumTicks - 1; 

    for TickIndex = 1 to NumTicks do
        Points = sequence ();

        for PointIndex = 1 to NumPoints do

            X = XOrigin;

            if PointIndex = NumPoints then 
                Y = YOrigin - Radius - TickLength 
            else 
                Y = YOrigin - Radius;
 
            X, Y = call _gdd-rotate-point (X, Y, XOrigin, YOrigin, Clockwise * (TickIndex - 1) *  Range / (TickRange) + StartPoint);
   
            Point = structure (X: X, Y: Y);
            Points = insert-at-end (Points, Point); 

        end;

        LineElements = insert-at-end (LineElements, structure (TYPE: the symbol LINES, POINTS : Points));               
    end;

    If the REGIONS of Icon exists then
        Regions = the REGIONS of Icon
    else
        Regions = sequence ();

    Layers = the LAYERS of Icon;
 
    TickRegion = structure (REGION-NAME : symbol("[the NAME of Info]-[TickName]"), COLOR-NAME : LayerColor);
 
    Layer = structure (REGION-OR-COLOR : symbol("[the NAME of Info]-[TickName]"), LINE-DRAWING-ELEMENTS : LineElements); 

    Regions = insert-at-end (Regions, TickRegion); 
    Layers = insert-at-end (Layers, Layer);

    Icon = change-attribute (Icon, REGIONS, Regions);
    Icon = change-attribute (Icon, LAYERS, Layers); 
  
    return Icon;
end

gdd-clear-all-gdd-info (Def: class class-definition)
    Icon : structure;
    Region : structure;
    Layer : structure;
    NewRegions : sequence = sequence ();
    Regions : sequence;
    Layers : sequence;
    NewLayers : sequence = sequence ();
    RegionName : symbol;
    LayerName : symbol;
begin
  if Def exists and the ICON-DESCRIPTION of Def exists then
  begin
    Icon = the ICON-DESCRIPTION of Def;
    if the REGIONS of Icon exists then
    begin 

        Regions = the REGIONS of Icon; 

        for Region = each structure in Regions do 
            RegionName = the REGION-NAME of Region;
            Icon = call gdd-make-icon-layer-inactive (Icon, RegionName); 
            if not (is-contained-in-text ("-[the symbol TICKMARKS]", "[RegionName]") or is-contained-in-text ("-[the symbol LABEL]", "[RegionName]") or is-contained-in-text ("-[the symbol SHADOW]", "[RegionName]" )) then
                NewRegions = insert-at-end (NewRegions, Region); 
        end;

        Layers = the LAYERS of Icon; 

        for Layer = each structure in Layers do
            LayerName = the REGION-OR-COLOR of Layer;
            if not (is-contained-in-text ("-[the symbol TICKMARKS]", "[LayerName]") or is-contained-in-text ("-[the symbol LABEL]", "[LayerName]") or is-contained-in-text ("-[the symbol SHADOW]", "[LayerName]")) then
                NewLayers = insert-at-end (NewLayers, Layer); 
        end;

        if the count of each structure in NewRegions = 0 then
            Icon = remove-attribute (Icon, Regions) 
        else
            Icon = change-attribute (Icon, REGIONS, NewRegions);

        Icon = change-attribute (Icon, LAYERS, NewLayers);

        conclude that the ICON-DESCRIPTION of Def = Icon;

    end; {-- If REGIONS exist --}

  end; {-- If Icon Exists --}

end

gdd-make-icon-layer-inactive (Icon : structure, LayerName : symbol) = (structure)
    Variable : structure;
    Variables : sequence;
begin   

    if the VARIABLES of Icon exists then
    begin
        Variables = the VARIABLES of Icon;

        for Variable = each structure in Variables do
            if is-contained-in-text ("[LayerName]", "[the VARIABLE-NAME of Variable]") then
                Icon = call _gdd-remove-variable (Icon, the VARIABLE-NAME of Variable, the INITIAL-VALUE of Variable)
        end;    
        
    end;
    return Icon; 
end

gdd-make-icon-inactive (Def : class class-definition)
    Regions : sequence;
    EachRegion : structure;
    Icon : structure;
begin
    If the ICON-DESCRIPTION of Def exists then
    begin
        Icon = the ICON-DESCRIPTION of Def;
        if the REGIONS of Icon exists then
        begin
            Regions = the REGIONS of Icon;
            
            for EachRegion = each structure in Regions do
                Icon = call gdd-make-icon-layer-inactive (Icon, the REGION-NAME of EachRegion);
            end;

            conclude that the ICON-DESCRIPTION of Def = Icon;
        end;
    end; 
end

gdd-clear-class-definition (Info : class gdd-class-information-object)
    Icon : structure;
    Region : structure;
    Layer : structure;
    NewRegions : sequence = sequence ();
    Regions : sequence;
    Layers : sequence;
    NewLayers : sequence = sequence ();
    InfoName : symbol;
    Pointer : symbol;
    Def : class class-definition;
    ShadowLayer : symbol;
    TrackingLayer : symbol;
begin
  if Info exists and the class-definition named by the GDD-TARGET-CLASS of Info exists then
  begin
    Def = the class-definition named by the GDD-TARGET-CLASS of Info;
    Icon = the ICON-DESCRIPTION of Def; 

    Layers = the GDD-POINTER-REGION of Info;

    for Pointer = each symbol in Layers do
        Icon = call gdd-make-icon-layer-inactive (Icon, Pointer);
    end;

    InfoName = the NAME of Info; 
    ShadowLayer = symbol ("[InfoName]-[the symbol SHADOW]");
    TrackingLayer = symbol ("[InfoName]-[the symbol TRACKING-LABEL]"); 

    Icon = call gdd-make-icon-layer-inactive (Icon, TrackingLayer); 
    Icon = call gdd-make-icon-layer-inactive (Icon, ShadowLayer); 
    
    if the REGIONS of Icon exists then
    begin
        Regions = the REGIONS of Icon; 
        Layers = the LAYERS of Icon; 

        for Region = each structure in Regions do
            if the REGION-NAME of Region /= symbol ("[InfoName]-[the symbol TICKMARKS]") and the REGION-NAME of Region /= symbol ("[InfoName]-[the symbol MAJOR-TICKMARKS]") and the REGION-NAME of Region /= symbol ("[InfoName]-[the symbol LABELS]") and the REGION-NAME of Region /= ShadowLayer and the REGION-NAME of Region /= TrackingLayer then
                NewRegions = insert-at-end (NewRegions, Region); 
        end;

        for Layer = each structure in Layers do
            if the REGION-OR-COLOR of Layer /= symbol ("[InfoName]-[the symbol TICKMARKS]") and the REGION-OR-COLOR of Layer /= symbol ("[InfoName]-[the symbol MAJOR-TICKMARKS]") and the REGION-OR-COLOR of Layer /= symbol ("[InfoName]-[the symbol LABELS]") and the REGION-OR-COLOR of Layer /= ShadowLayer and the REGION-OR-COLOR of Layer /= TrackingLayer then
                NewLayers = insert-at-end (NewLayers, Layer); 
        end;

        if the count of each structure in NewRegions = 0 then
            Icon = remove-attribute (Icon, Regions) 
        else
            Icon = change-attribute (Icon, REGIONS, NewRegions);

        Icon = change-attribute (Icon, LAYERS, NewLayers);

        conclude that the ICON-DESCRIPTION of Def = Icon;

    end; {-- If REGIONS exist --}

    conclude that the GDD-STATUS of Info = the symbol NONE;

  end; {-- If Info Exists --}

end

gdd-setup-class-definition (Info : class gdd-class-information-object) 
    Layer : symbol;
    Layers : sequence;
    Def : class class-definition;
    Icon : structure;
    InfoCount : integer = 1;
    EachInfo : class gdd-class-information-object;
    TargetAttr : symbol;
    Obj : class item;
begin
    if Info exists and the class-definition named by the GDD-TARGET-CLASS of Info exists and the ICON-DESCRIPTION of the class-definition named by the GDD-TARGET-CLASS of Info exists then
    begin
        Def = the class-definition named by the GDD-TARGET-CLASS of Info; 

        {-- If Info has no name then give it one --}
        if the NAME of Info does not exist then
        begin
            for EachInfo = each gdd-class-information-object that is a-gdd-class-information-object-for-the-class-of Def do
                InfoCount = InfoCount + 1;
            end;
            change the NAME of Info to symbol ("[the NAME of Def]-INFO-[InfoCount]");
            make Info permanent;
        end;

        {-- If TARGET-ATTRIBUTE is does not exist in Def the attempt to assign 
            a target attribute --}
        if the GDD-TARGET-ATTRIBUTE of Info does not exist then 
        begin 
             create an instance Obj of the class named by the NAME of Def; 
             for TargetAttr = each symbol that is a user-defined attribute name of Obj do
                 conclude that the GDD-TARGET-ATTRIBUTE of Info = TargetAttr;
                 exit if true;
             end;
             delete Obj;
        end;  

        {-- Clear out the class info it it exists --}
        call gdd-clear-class-definition (Info);

        conclude that Info is now a-gdd-class-information-object-for-the-class-of Def;

        Icon = the ICON-DESCRIPTION of Def;

        if the GDD-SHADOW of Info is true then
        begin
            Icon = call _gdd-generate-shadow (Icon, Info);
            Icon = call gdd-make-icon-layer-active (Icon, symbol ("[the NAME of Info]-SHADOW"));

             {-- Strip out the anchor points if this is a linear style --}
             if Info is a gdd-linear-class-information-object then
                 Icon = call _gdd-make-anchor-points-inactive (Icon, symbol ("[the NAME of Info]-SHADOW"), the GDD-ALIGNMENT of Info); 
        end;

        if Info is a gdd-radial-class-information-object then
        begin
            if the GDD-TICKMARK-COUNT of Info > 0 then
                Icon = call _gdd-generate-radial-tickmarks (Icon, Info, false);
            if the GDD-MAJOR-TICKMARK-COUNT of Info > 0 then 
                Icon = call _gdd-generate-radial-tickmarks (Icon, Info, true);
            if the GDD-LABEL-COUNT of Info > 0 then 
                Icon = call _gdd-generate-radial-labels (Icon, Info); 
        end
        else
        begin
            if the GDD-TICKMARK-COUNT of Info > 0 then 
                Icon = call _gdd-generate-linear-tickmarks (Icon, Info, false);
            if the GDD-MAJOR-TICKMARK-COUNT of Info > 0 then 
                Icon = call _gdd-generate-linear-tickmarks (Icon, Info, true);
            if the GDD-LABEL-COUNT of Info > 0 then 
                Icon = call _gdd-generate-linear-labels (Icon, Info);
        end;

        Layers = the GDD-POINTER-REGION of Info;

        for Layer = each symbol in Layers do
            Icon = call gdd-make-icon-layer-active (Icon, Layer);

             {-- Strip out the anchor points if this is a linear style --}
             if Info is a gdd-linear-class-information-object then
                 Icon = call _gdd-make-anchor-points-inactive (Icon, Layer, the GDD-ALIGNMENT of Info);
        end;

        if the GDD-TRACKING-LABEL of Info is true then
        begin
            Icon = call _gdd-generate-tracking-label (Icon, Info);
            Icon = call gdd-make-icon-layer-active (Icon, symbol ("[the NAME of Info]-TRACKING-LABEL"));
        end;

        conclude that the GDD-STATUS of Info = the symbol ACTIVE; 
        conclude that the ICON-DESCRIPTION of Def = Icon;

    end;

end

gdd-make-icon-layer-active (Icon : structure, LayerName : symbol) = (structure)
    Layer : structure;
    OldLayer : structure; 
    LayerIndex : integer;
    Layers : sequence;
    NewLayer : structure;
    NewLayers : sequence = sequence();
    NewLayerIndex : integer = 0; 
    Variables : sequence;
    NewVariables : sequence;
    Variable : structure;
begin

    Layer, LayerIndex = call _gdd-get-icon-layer (Icon, LayerName);
    if the REGION-OR-COLOR of Layer exists then
    begin
        NewVariables, NewLayer = call _gdd-generate-variables (Layer);

        {-- If Icon variables exist, then add the new variables to the list --}
    
        if the VARIABLES of Icon exists then
        begin
            Variables = the VARIABLES of Icon;

            for Variable = each structure in NewVariables do
                Variables = insert-at-end (Variables, Variable);
            end;
        end
        else
            Variables = NewVariables;

        Icon = change-attribute (Icon, VARIABLES, Variables); 

        Layers = the LAYERS of Icon;
    
        {-- Build the new layers sequence for the icon description --}

        for OldLayer = each structure in Layers do 
            if NewLayerIndex = LayerIndex then
                NewLayers = insert-at-end (NewLayers, NewLayer)
            else
                NewLayers = insert-at-end (NewLayers, OldLayer);
 
            NewLayerIndex = NewLayerIndex + 1; 
        end;
 
        Icon = change-attribute (Icon, LAYERS, NewLayers); 

    end;

    return Icon;
end

gdd-make-icon-active (Def : class class-definition)
    Regions : sequence;
    EachRegion : structure;
    Icon : structure;
begin
    If the ICON-DESCRIPTION of Def exists then
    begin
        Icon = the ICON-DESCRIPTION of Def;
        if the REGIONS of Icon exists then
        begin
            Regions = the REGIONS of Icon;
            
            for EachRegion = each structure in Regions do
                Icon = call gdd-make-icon-layer-active (Icon, the REGION-NAME of EachRegion);
            end;

            conclude that the ICON-DESCRIPTION of Def = Icon;
        end;
    end;
end
