g2:c.PROCEDURE.g2:a.TEXT:
upp-check-for-button-repositioning (button: class uil-button) 
wksp: class kb-workspace;
MEMS: class text-array;
i: integer;
NB, LeadB, LastB, B, PosB: item-or-value;
DEBUG: truth-value = false;
begin 
if (DEBUG) then
    inform the operator that "Running upp-check-for-button-repositioning.";

    if not (button exists) then
        return;

    wksp = the workspace of button;

    if (button is a uil-selection-button) and (the uil-selection-box that is the-group-master-of button exists) then
        begin
        MEMS = the members of the uil-selection-box that is the-group-master-of button;
        if (the array-length of MEMS > 1) then
            begin
            {-- trying to get the horizontal base line of selection-buttons in the selection-box --}
            LeadB = call uil-get-selection-button ("[MEMS[0]]");
            if (LeadB is the same object as button) then
                begin               
                B = call uil-get-selection-button ("[MEMS[1]]");
                if (the item-y-position of button > the item-y-position of B) then
                    return;
                end
            else
                B = LeadB;

            {-- if button's X is within the width of the B's, cont. --}
            if (the item-x-position of button > (the item-x-position of B - the item-width of B/2) and the item-x-position of button < (the item-x-position of B + the item-width of B/2)) then
                begin
{
                NB = the uil-selection-button nearest to button;
                if (NB exists and NB is a item) and (the class of NB = the class of button) then
                    begin
                    LastB = call uil-get-selection-box-last-button (the uil-selection-box that is the-group-master-of button);

                    --COMMENTS: check Y's. If NB is LastB, and button is LeadB and too far away, return. --
                    if (LastB exists) and (NB is the same object as LastB) and (button is the same object as LeadB) then
                        begin
                        if (the item-y-position of NB - the item-y-position of button > (2 * the item-width of NB)) then
                            return;
                        end;
                   
                    if (button is a uil-radio-button) then
                        call upp-reorder-radio-buttons(the uil-selection-box that is the-group-master-of button, NB, button, wksp, the item-x-position of B, the item-y-position of B)
                    else
                        call upp-reorder-check-buttons(the uil-selection-box that is the-group-master-of button, NB, button, wksp, the item-x-position of B, the item-y-position of B);                       
                    end;              
}
                {-- using a new way to find the real position in selection-box of button --}
                PosB = LeadB;
                for i = 0 to the array-length of MEMS - 1 do
                    NB = call uil-get-selection-button("[MEMS[i]]");
                    if (NB exists and NB is a item) and (the class of NB = the class of button) then
                        begin
                            exit if the item-y-position of button > the item-y-position of NB;
                            PosB = NB;
                        end;
                end;
                    
                if PosB is the same object as button then
                    begin
                        {-- button is still in the original place, no need to changing order at all, but all buttons need to be line up because some buttons moved --}
                        call upp-lineup-selection-buttons(LeadB);
                    end
                else
                    begin
                        {-- button's position changed, all buttons need to be re-arranged --}
                        if (button is a uil-radio-button) then
                            call upp-reorder-radio-buttons(the uil-selection-box that is the-group-master-of button, PosB, button, wksp, the item-x-position of B, the item-y-position of B)
                        else
                            call upp-reorder-check-buttons(the uil-selection-box that is the-group-master-of button, PosB, button, wksp, the item-x-position of B, the item-y-position of B); 
                    end;
                end;
            end;
        call uil-make-grobj-permanent (the uil-selection-box that is the-group-master-of button);
        end;
end

upp-grouped-by 
(
    TheObj: item-or-value
) = (class g2-list)
    RelatedItems: class g2-list;
    RelatedItem: item-or-value;
begin
    create a g2-list RelatedItems;
    for RelatedItem = each item that is grouped-by TheObj do
        insert RelatedItem at the end of RelatedItems;
    end;
    return RelatedItems;
end

