uil-get-related-items
(
    TheRelationName: symbol,
    TheObj: item-or-value
) = (item-or-value)
    RelatedItems: class g2-list;
    RelatedItem: item-or-value;
begin
    create a g2-list RelatedItems;
    case (TheRelationName) of
        the-uil-text-label-of :
          for RelatedItem = each item that is the-uil-text-label-of TheObj do
              insert RelatedItem at the end of RelatedItems;
          end;
        the-group-master-of :
          for RelatedItem = each item that is the-group-master-of TheObj do
              insert RelatedItem at the end of RelatedItems;
          end;
        grouped-by :
          for RelatedItem = each item that is grouped-by TheObj do
              insert RelatedItem at the end of RelatedItems;
          end;
        a-uil-dialog-presently-in-use-on-window :
          for RelatedItem = each item that is a-uil-dialog-presently-in-use-on-window TheObj do
              insert RelatedItem at the end of RelatedItems;
          end;
        the-g2-window-now-showing-dialog :
          for RelatedItem = each item that is the-g2-window-now-showing-dialog TheObj do
              insert RelatedItem at the end of RelatedItems;
          end;
        the-uil-dialog-resulting-from-action :
          for RelatedItem = each item that is the-uil-dialog-resulting-from-action TheObj do
              insert RelatedItem at the end of RelatedItems;
          end;
        the-object-initiating-dialog-activity-for :
          for RelatedItem = each item that is the-object-initiating-dialog-activity-for TheObj do
              insert RelatedItem at the end of RelatedItems;
          end;
        the-uil-parent-dialog-of-this-cascaded-dialog :
          for RelatedItem = each item that is the-uil-parent-dialog-of-this-cascaded-dialog TheObj do
              insert RelatedItem at the end of RelatedItems;
          end;
        a-uil-child-cascaded-dialog-of :
          for RelatedItem = each item that is a-uil-child-cascaded-dialog-of TheObj do
              insert RelatedItem at the end of RelatedItems;
          end;
        the-current-edit-field-of :
          for RelatedItem = each item that is the-current-edit-field-of TheObj do
              insert RelatedItem at the end of RelatedItems;
          end;
        the-tab-button-of-the-tab-page :
          for RelatedItem = each item that is the-tab-button-of-the-tab-page TheObj do
              insert RelatedItem at the end of RelatedItems;
          end;
        the-uil-selected-tab-page-of :
          for RelatedItem = each item that is the-uil-selected-tab-page-of TheObj do
              insert RelatedItem at the end of RelatedItems;
          end;
        the-current-uil-message-being-edited-on :
          for RelatedItem = each item that is the-current-uil-message-being-edited-on TheObj do
              insert RelatedItem at the end of RelatedItems;
          end;
        the-combo-box-button-of :
          for RelatedItem = each item that is the-combo-box-button-of TheObj do
              insert RelatedItem at the end of RelatedItems;
          end;
        the-combo-scroll-area-for :
          for RelatedItem = each item that is the-combo-scroll-area-for TheObj do
              insert RelatedItem at the end of RelatedItems;
          end;
        the-combo-list-of :
          for RelatedItem = each item that is the-combo-list-of TheObj do
              insert RelatedItem at the end of RelatedItems;
          end;
        a-spin-button-controlling :
          for RelatedItem = each item that is a-spin-button-controlling TheObj do
              insert RelatedItem at the end of RelatedItems;
          end;
        the-partner-spin-button-of :
          for RelatedItem = each item that is the-partner-spin-button-of TheObj do
              insert RelatedItem at the end of RelatedItems;
          end;
        the-topmost-message-of :
          for RelatedItem = each item that is the-topmost-message-of TheObj do
              insert RelatedItem at the end of RelatedItems;
          end;
        the-bottommost-message-of :
          for RelatedItem = each item that is the-bottommost-message-of TheObj do
              insert RelatedItem at the end of RelatedItems;
          end;
        a-message-belonging-to :
          for RelatedItem = each item that is a-message-belonging-to TheObj do
              insert RelatedItem at the end of RelatedItems;
          end;
        the-handler-for-the-message-of :
          for RelatedItem = each item that is the-handler-for-the-message-of TheObj do
              insert RelatedItem at the end of RelatedItems;
          end;
        a-selected-message-of :
          for RelatedItem = each item that is a-selected-message-of TheObj do
              insert RelatedItem at the end of RelatedItems;
          end;
        the-item-appended-to-the-scroll-message :
          for RelatedItem = each item that is the-item-appended-to-the-scroll-message TheObj do
              insert RelatedItem at the end of RelatedItems;
          end;
        the-uil-message-appended-to-the-item :
          for RelatedItem = each item that is the-uil-message-appended-to-the-item TheObj do
              insert RelatedItem at the end of RelatedItems;
          end;
        the-uil-button-owning-this-text-label :
          for RelatedItem = each item that is the-uil-button-owning-this-text-label TheObj do
              insert RelatedItem at the end of RelatedItems;
          end;
        the-uil-boxed-message-of :
          for RelatedItem = each item that is the-uil-boxed-message-of TheObj do
              insert RelatedItem at the end of RelatedItems;
          end;
        the-uil-editor-field-mask-for :
          for RelatedItem = each item that is the-uil-editor-field-mask-for TheObj do
              insert RelatedItem at the end of RelatedItems;
          end;
        the-object-receiving-dialog-activity-from :
          for RelatedItem = each item that is the-object-receiving-dialog-activity-from TheObj do
              insert RelatedItem at the end of RelatedItems;
          end;
        the-uil-parent-of-this-cascaded-dialog :
          for RelatedItem = each item that is the-uil-parent-of-this-cascaded-dialog TheObj do
              insert RelatedItem at the end of RelatedItems;
          end;
        the-uil-temporary-storage-object-of :
          for RelatedItem = each item that is the-uil-temporary-storage-object-of TheObj do
              insert RelatedItem at the end of RelatedItems;
          end;
        a-uil-dialog-using-the-temporary-storage-object :
          for RelatedItem = each item that is a-uil-dialog-using-the-temporary-storage-object TheObj do
              insert RelatedItem at the end of RelatedItems;
          end;
        the-item-appended-to-the-uil-scroll-message :
          for RelatedItem = each item that is the-item-appended-to-the-uil-scroll-message TheObj do
              insert RelatedItem at the end of RelatedItems;
          end;
        the-uil-editable-surfaces-list-of :
          for RelatedItem = each item that is the-uil-editable-surfaces-list-of TheObj do
              insert RelatedItem at the end of RelatedItems;
          end;
        the-uil-button-that-launched :
          for RelatedItem = each item that is the-uil-button-that-launched TheObj do
              insert RelatedItem at the end of RelatedItems;
          end;
        uil-wait-semiphore :
          for RelatedItem = each item that is uil-wait-semiphore TheObj do
              insert RelatedItem at the end of RelatedItems;
          end;
        the-uil-dialog-manager-of :
          for RelatedItem = each item that is the-uil-dialog-manager-of TheObj do
              insert RelatedItem at the end of RelatedItems;
          end;
        the-procedure-invocation-that-created-delay-notification :
          for RelatedItem = each item that is the-procedure-invocation-that-created-delay-notification TheObj do
              insert RelatedItem at the end of RelatedItems;
          end;
        the-object-undergoing-configuration-edit-in-dialog :
          for RelatedItem = each item that is the-object-undergoing-configuration-edit-in-dialog TheObj do
              insert RelatedItem at the end of RelatedItems;
          end;
        otherwise :
        begin
        end;
    end;
        if (the number of elements in RelatedItems = 0) then 
            begin
            delete RelatedItems;
            return the symbol no-related-items; 
        end;
    return RelatedItems;
end

uil-get-related-item
(
    TheRelationName: symbol,
    TheObj: item-or-value
) = (item-or-value)
    RelatedItems: item-or-value;
    RelatedItem: item-or-value;
begin
    RelatedItem = the symbol no-related-items; 
    RelatedItems = call uil-get-related-items(TheRelationName, TheObj);
    if (RelatedItems is a g2-list and the number of elements in RelatedItems > 1) then
        signal the symbol RELATION-CARDINALITY-ERROR, "The item is related to more than one item";
    if (RelatedItems is a g2-list and the number of elements in RelatedItems = 1) then
        RelatedItem = the first item-or-value in RelatedItems;
    if (RelatedItems is a g2-list) then delete RelatedItems; 
    return RelatedItem ;
end

uil-is-related-to
(
    TheRelatedItem: item-or-value,
    TheRelationName: symbol,
    TheObj: item-or-value
) = (truth-value)
    RelatedItems: item-or-value;
    IsRelated: truth-value = false;
begin
    RelatedItems = call uil-get-related-items(TheRelationName, TheObj); 
    if (RelatedItems is a g2-list and the number of elements in RelatedItems > 0 and TheRelatedItem is a member of RelatedItems) then
        IsRelated = true; 
    if (RelatedItems is a g2-list) then delete RelatedItems;
    return IsRelated;
end

uil-are-any-related-to
(
    TheRelationName: symbol,
    TheObj: item-or-value
) = (truth-value)
    RelatedItems: item-or-value;
    AnyRelated: truth-value = false;
begin
    RelatedItems = call uil-get-related-items(TheRelationName, TheObj); 
    if (RelatedItems is a g2-list and the number of elements in RelatedItems > 0) then
        AnyRelated = true; 
    if (RelatedItems is a g2-list) then delete RelatedItems;
    return AnyRelated;
end

uil-get-windows-on-which-dialog-is-managed (D: item-or-value, IL: class item-list)
W: class ui-client-item;

begin
    if (there exists a ui-client-item W that is the-g2-window-now-showing-dialog D) then 
        begin 
            for W = each ui-client-item that is the-g2-window-now-showing-dialog D do 
                if (upp-is-client-connected (W)) then 
                    insert W at the end of IL;
                end; 
        end;
end

uil-get-dialogs-managed-on-window (W: class ui-client-item, IL: class item-list) 
D: item-or-value;
begin
    if (there exists a uil-dialog that is a-uil-dialog-presently-in-use-on-window W) then
        begin
        for D = each uil-dialog that is a-uil-dialog-presently-in-use-on-window W do
            insert D at the end of IL;
            end;
        end;
end

uil-get-buttons-for-this-selection-box (BOX: item-or-value, IL: class item-list) 
B: class uil-button;
begin
    if (there exists a uil-button that is grouped-by BOX) then
        begin
        for B = each uil-button that is grouped-by BOX do
            insert B at the end of IL;
            end;
        end;
end

uil-get-selection-box-for-button (B: item-or-value) = (item-or-value)
RET: item-or-value;
begin
    RET = the symbol no-selection-box-found;
    if (the uil-selection-box that is the-group-master-of B exists) then
        RET = the uil-selection-box that is the-group-master-of B;
    return RET;
end

uil-get-message-appended-to-this-item (ITM: item-or-value) = (item-or-value)
M: class item;
RET: item-or-value;
begin
    RET = the symbol no-message-found;

    if (the item M that is the-uil-message-appended-to-the-item ITM exists) then
        RET = M;

    return RET;
end

uil-get-item-appended-to-message (M: item-or-value) = (item-or-value)
ITM: item-or-value;
SA: item-or-value;
WS: class kb-workspace;
repository: symbol = the symbol uil-message-repository; 
RET: item-or-value;
SAClass: symbol = the symbol uil-scroll-area;
ItmClass: symbol = the symbol uil-appended-item;
begin
    RET = the symbol no-item-found;
    if (the object-definition named by SAClass does not exist or the object-definition named by ItmClass does not exist) then
        return RET;

    if (the item ITM that is the-item-appended-to-the-uil-scroll-message M exists) then
        RET = ITM
    else if (the uil-appended-item-id of M exists and the uil-appended-item-id of M /= "") then
        begin
        if (the item that is the-handler-for-the-message-of M exists) then
            SA = the item that is the-handler-for-the-message-of M;
        if (the workspace of M exists) then
            begin
            WS = the workspace of M; 
            if (there exists a item ITM upon WS such that (ITM is an instance of the class named by (ItmClass) and the uil-item-id of ITM = "[the uil-appended-item-id of M]")) then
                RET = ITM;
            end
        else if (SA exists and SA is an item and the subworkspace of SA exists) then
            begin
            WS = the subworkspace of SA; 
            if (there exists an item ITM upon WS such that (ITM is an instance of the class named by (ItmClass) and the uil-item-id of ITM = "[the uil-appended-item-id of M]")) then
                RET = ITM; 
            end
        else if (the subworkspace of the object named by repository exists) then
            begin
            WS = the subworkspace of the object named by repository; 
            if (there exists an item ITM upon WS such that (ITM is an instance of the class named by (ItmClass) and the uil-item-id of ITM = "[the uil-appended-item-id of M]")) then
                RET = ITM; 
            end
        else
            begin     
            if (there exists an item ITM that is of the class named by ItmClass such that (the uil-item-id of ITM = "[the uil-appended-item-id of M]")) then
               RET = ITM; 
            end;
        end;

    return RET;
end

uil-get-dialog-for-this-temporary-storage-object (TSO: item-or-value) = (item-or-value)
D: class uil-dialog;
RET: item-or-value;
begin
    RET = the symbol no-dialog-found;
    
    if (there exists a uil-dialog D such that (TSO is the-uil-temporary-storage-object-of D)) then
        RET = D;

    return RET;
end

