    return RET;
end

uil-is-message-selected (M: item-or-value) = (truth-value)
    SA: item-or-value;
begin
    SA = call uil-get-scroll-area-for-this-message (M);
    if (M is a-selected-message-of SA) then
        return TRUE
    else
        return FALSE;
end

