uil-utility-return-current-real-time() = (quantity)
C-YEAR: integer;
C-MON: integer;
C-DAY: integer;
C-HR: integer;
C-MIN: integer;
C-SEC: integer;
C-TIME, T: quantity;
DEBUG: truth-value = false;
begin
    T = the current real time;

    C-YEAR = year (T);
    C-MON = month (T);
    C-DAY = day-of-the-month (T);
    C-HR = hour (T);
    C-MIN = minute (T);
    C-SEC = second (T);

    if (DEBUG) then
        inform the operator that "YEAR: [C-YEAR]  MONTH: [C-MON]  DAY: [C-DAY]  HR: [C-HR]  MIN: [C-MIN]  SEC: [C-SEC]";

    C-TIME = time (C-YEAR, C-MON, C-DAY, C-HR, C-MIN, C-SEC);

    return C-TIME;
end

upp-make-items-on-subworkspace-of-navigation-button-permanent (WS: class kb-workspace)
MO, IT: class item;
SAClass: symbol = the symbol uil-scroll-area;
DEBUG: truth-value = false;
en: symbol; et: text;
begin
if (DEBUG) then
    inform the operator that "Running upp- make-items-on-subworkspace-of-navigation-button-permanent";
    
    for IT = each item upon WS do
        if (IT is a uil-object or IT is a uil-message) then
            begin
            if (the object-definition named by SAClass exists and IT is not an instance of the class named by (SAClass) and the subworkspace of IT exists) then
                begin
                call upp-make-items-on-subworkspace-of-navigation-button-permanent (the subworkspace of IT);
                if (the uil-is-permanent of IT exists) then
                    conclude that the uil-is-permanent of IT is true;
                end
            else if (the uil-is-permanent of IT exists) then
                begin
                conclude that the uil-is-permanent of IT is true;
                if (the object-definition named by SAClass exists and IT is an instance of the class named by (SAClass)) then
                    begin
                    for MO = each item that is a-message-belonging-to IT do
                        conclude that the uil-is-permanent of MO is true;
                        end;
                    end;
                end;
            end;
        end;
end
on error (en, et)
if (DEBUG) then
    inform the operator that "[en] [et]";
end

uil-utility-build-symbol-list-of-values-for-class-attribute 
(
    ClassName: symbol, 
    AttrName:   symbol
) = (item-or-value)
    AttrList:       item-or-value;
    AttrType:    symbol;
    InitialValue: value;
    HasValues:  truth-value;
begin
    AttrType, HasValues, AttrList, InitialValue = call utl-extract-attribute-information(ClassName, AttrName);
    if AttrType = the symbol utl-unknown then
        return the symbol attribute-is-not-defined-in-object-definition 
    else
        begin 
        if HasValues then 
            return AttrList
        else
            return the symbol attribute-does-not-contain-values; 
        end
end

upp-manage-button-method (grobj: class uil-grobj, wksp: class kb-workspace, xpos: integer, ypos: integer)
T: item-or-value;
label-x-offset, label-y-offset, size-x-offset, size-y-offset: integer;
current-x: integer;
DEBUG: truth-value = false;
begin
if DEBUG then
    inform the operator that "Running upp-manage-button-method.";

    T = call uil-get-label-text (grobj);
    if (T exists and T is a uil-text) then
        begin
        call upp-make-item-transient (T);
        {make T transient;}

        {-- The text for an icon button with a label is placed to the right of the icon, not in the middle of the button icon, but according to class given the different sizes of text margins in G2 --}

        label-x-offset, label-y-offset = call upp-get-button-label-offsets (grobj);
        size-x-offset = 0;
        size-y-offset = 0;
        end;

    call upp-make-item-transient (grobj);
    transfer grobj off;

    transfer grobj to wksp at (xpos, ypos); 

    T = call uil-get-label-text (grobj);
    if (T exists and T is a uil-text) then
        begin
        if (grobj is a uil-icon-button) then 
            begin
            transfer T to wksp at (xpos + label-x-offset, ypos + label-y-offset);
            call upp-make-item-permanent (T);
            end
        else 
            begin
            transfer T to wksp at (xpos, ypos);
            call upp-make-item-permanent (T);
            end;
        end;

    conclude that the uil-is-managed of grobj = 1;

    if (T exists and T is a uil-text and the uil-is-managed of T exists) then
        begin
        conclude that the uil-is-managed of T = 1;
        {call g2-lift-item-to-top (T);}   {formerly needed for GDA's old dialogs  -jtidwell 8-3-95}
        end;

    if (the uil-is-permanent of grobj is true) then 
        call upp-make-item-permanent (grobj);
end

uil-manage-grobj-method (G: class item, WS: item-or-value, CONFIG: symbol, X: integer, Y: integer) = (class item)  
DEBUG: truth-value = false;
begin
{-- This procedure makes a grobj visible --}

if (DEBUG) then
    inform the operator that "1. Running uil-manage-grobj-method for [the class of G]";

    if (the uil-manage-method of G does not exist) or (the procedure named by the uil-manage-method of G does not exist) or ((the procedure named by the uil-manage-method of G exists) and (the uil-manage-method of G is uil-manage-grobj or the uil-manage-method of G is uil-manage-grobj-method)) then
        begin
        {-- UIL Method --}
        if (DEBUG) then
            inform the operator that "Built-In: calling uil-manage-grobj";
        G = call uil-manage-grobj (G, WS, CONFIG, X, Y);
        end

    else if (the uil-manage-method of G exists) and (the procedure named by the uil-manage-method of G exists) then
        begin
        {-- User-defined Procedure --}
        if (DEBUG) then
            inform the operator that "User Defined: calling [the uil-manage-method of G]";

        G = call the procedure named by the uil-manage-method of G (G, WS, CONFIG, X, Y);
        end;

if (DEBUG) then   
    inform the operator that "1. Leaving uil-manage-grobj-method for [the class of G]";

    return G;
end

upp-convert-text-time-to-quantity (TTime: text) = (quantity)
C-TIME: quantity;
YrT, MonT, DayT, HrT, MinT, SecT, T: text;
C-YEAR: integer;
C-MON: integer;
C-DAY: integer;
C-HR: integer;
C-MIN: integer;
C-SEC: integer;
Pos: integer;
IsPM: truth-value = false;
DEBUG: truth-value = false;
Digits-in-year: integer;
begin
    {-- "9 Mar 95  10:56:17 a.m." --}

    if (is-contained-in-text ("p.m.", TTime)) then
        IsPM = true;

{-- Day --}
    Pos = position-of-text (" ", TTime);
    DayT = get-from-text (TTime, 1, Pos - 1);

    TTime = omit-from-text (TTime, 1, Pos);

{-- Month --}
    Pos = position-of-text (" ", TTime);
    MonT = get-from-text (TTime, 1, Pos - 1);

    TTime = omit-from-text (TTime, 1, Pos);

{-- Year --}
    Pos = position-of-text (" ", TTime);
    YrT = get-from-text (TTime, 1, Pos - 1);

    TTime = omit-from-text (TTime, 1, Pos);

{-- Hour --}
    Pos = position-of-text (":", TTime);
    HrT = get-from-text (TTime, 1, Pos - 1);

    TTime = omit-from-text (TTime, 1, Pos);

{-- Minute --}
    Pos = position-of-text (":", TTime);
    MinT = get-from-text (TTime, 1, Pos - 1);

    TTime = omit-from-text (TTime, 1, Pos);

{-- Second --}
    Pos = position-of-text (" ", TTime);
    SecT = get-from-text (TTime, 1, Pos - 1);

    TTime = omit-from-text (TTime, 1, Pos);

    C-YEAR = quantity (YrT);
    Digits-in-year = length-of-text ("[C-Year]");
    if Digits-in-year <= 2 then begin
        if C-YEAR >= 50 then
            C-YEAR = C-YEAR + 1900
        else C-YEAR = C-YEAR + 2000;
    end;
    
    if (MonT = "Jan") then
        C-MON = 1
    else if (MonT = "Feb") then
        C-MON = 2
    else if (MonT = "Mar") then
        C-MON = 3
    else if (MonT = "Apr") then
        C-MON = 4
    else if (MonT = "May") then
        C-MON = 5
    else if (MonT = "Jun") then
        C-MON = 6
    else if (MonT = "Jul") then
        C-MON = 7
    else if (MonT = "Aug") then
        C-MON = 8
    else if (MonT = "Sep") then
        C-MON = 9
    else if (MonT = "Oct") then
        C-MON = 10
    else if (MonT = "Nov") then
        C-MON = 11
    else
        C-MON = 12;

    C-DAY = quantity (DayT);
    C-HR = quantity (HrT);

    if (IsPM) and (C-HR /= 12) then
        C-HR = C-HR + 12;
   
    C-MIN = quantity (MinT);
    C-SEC = round (quantity (SecT));  {hate to lose accuracy, but this is how it's gotta be until the "time" function can cope with a fractional number of seconds.   -jtidwell 10/8/96}

    if (C-SEC = 60) then
        begin
        C-MIN = C-MIN + 1;
        C-SEC = 0;
        end;

    if (DEBUG) then
        inform the operator that "YEAR: [C-YEAR]  MONTH: [C-MON]  DAY: [C-DAY]  HR: [C-HR]  MIN: [C-MIN]  SEC: [C-SEC]";

    C-TIME = time (C-YEAR, C-MON, C-DAY, C-HR, C-MIN, C-SEC);

    return C-TIME; 
end

upp-get-coords-of-next-open-selection-box-slot(bin : class uil-button) = (integer, integer)
  x: integer = 0;
  y: integer = 0;
  x-offset:integer = 110; 
  y-offset:integer = 76; 
  b: class uil-selection-box;
begin 
  {This procedure returns the x,y coordinates for the next open slot to place a selection box on the appropriate bin.  The idea is to place them down so that no two overlap}
  

{----  dwr 4/12/94  ---- A performance enhancement to reduce search time.}
   return random (200), random (200);


  repeat 
    {The algorithm used here is:
            1.  Set x,y to (o,o).
            2.  Check if a selection box already has been placed there.
            3.  if there is one then increment x,y to the next slot and check if a box has been placed there.
            4.  Keep going until an open slot is found and return the coordinates of that open slot} 

    if there exists a uil-selection-box b such that 
      (the workspace of b exists and
       the workspace of b is the same object as the subworkspace of bin and 
       x > round(the item-x-position of b - (the item-width of b/2)) and
       x < round(the item-x-position of b + (the item-width of b/2)) and
       y > round(the item-y-position of b - (the item-height of b/2)) and
       y < round(the item-y-position of b + (the item-height of b/2))) then
      begin 
        if x > 600 then begin
          x = 0;
          y = y - y-offset; end
        else
          x = x + x-offset; end
    else
      return x,y; 
  end;
end

upp-move-label (L: class uil-text)
WS: class kb-workspace;
OldB, NewB: class uil-text-button;
T: class uil-text;
ComboClass : symbol = the symbol uil-combo-box;
move-combo-box-proc: symbol = the symbol upp-move-combo-box;
X, Y: integer;
RootClass: truth-value = false;
DEBUG: truth-value = false;
begin 
if (DEBUG) then
    inform the operator that "running upp-move-label";
    if (the item that is the-uil-button-owning-this-text-label L exists) then
        begin

{-- TEXT BUTTON --}
        if (the item that is the-uil-button-owning-this-text-label L is a uil-text-button) then
            begin
            if (the workspace of L is not the same object as the workspace of the item that is the-uil-button-owning-this-text-label L) then
                begin
                OldB = the uil-text-button that is the-uil-button-owning-this-text-label L;
                X = the item-x-position of L;
                Y = the item-y-position of L;
                WS = the workspace of L;
                
                RootClass = call upp-is-item-a-uil-root-class (OldB);
                if (RootClass) then
                    begin
                    if (OldB is a uil-text-pushbutton) then
                        NewB = call uil-create-styled-pushbutton (the id of OldB, the symbol text, the uil-size of OldB, the label of OldB, the callback of OldB, the symbol none)
                    else
                        NewB = call uil-create-styled-toggle-button (the id of OldB, the symbol text, the uil-size of OldB, the label of OldB, the togglelabel of OldB, the on-value of OldB, the off-value of OldB, the callback of OldB, the symbol none);
                    end
                else
                    begin
                    {-- User Defined Class --}
                    NewB = call uil-create-custom-button (the class of OldB, the label of OldB);
                    if (the label of NewB /= "") then 
                        call upp-make-text-for-button (NewB);
                    end;

                call utl-copy-object-attributes (OldB, NewB);

                T = call uil-get-label-text (NewB);
                if (T exists and T is an item) then
                    call uil-update-text (T, the text of T);

                call uil-delete-grobj (OldB);

                call uil-manage-grobj-method (NewB, WS, the symbol none, X, Y); 
                call uil-make-grobj-permanent (NewB);               
                end
            else
                begin
                make the item that is the-uil-button-owning-this-text-label L transient;

                transfer the item that is the-uil-button-owning-this-text-label L to the workspace of L at (the item-x-position of L, the item-y-position of L);

                call upp-move-button (the item that is the-uil-button-owning-this-text-label L, true); 
                end;
            end

{-- EDIT BOX --}
        else if (the item that is the-uil-button-owning-this-text-label L is a uil-edit-box) then
            begin
            make the item that is the-uil-button-owning-this-text-label L transient;

            transfer the item that is the-uil-button-owning-this-text-label L to the workspace of L at (round(the item-x-position of L + the item-width of L/2 + 5 + the item-width of the item that is the-uil-button-owning-this-text-label L/2), the item-y-position of L);

            if (the item that is the-uil-button-owning-this-text-label L is a uil-spin-control-entry-box) then
                call upp-move-spin-control (the item that is the-uil-button-owning-this-text-label L)
            else if (the message-definition named by ComboClass exists) and (the item that is the-uil-button-owning-this-text-label L is an instance of the class named by (ComboClass)) then
               call the procedure named by move-combo-box-proc (the item that is the-uil-button-owning-this-text-label L) 
            else
                call upp-move-edit-box (the item that is the-uil-button-owning-this-text-label L);
            end

{-- ICON BUTTON --}
        else if (the item that is the-uil-button-owning-this-text-label L is a uil-icon-button) then
            begin
            if (the workspace of L is the same object as the workspace of the item that is the-uil-button-owning-this-text-label L) then 
                begin
                make the item that is the-uil-button-owning-this-text-label L transient;

                transfer the item that is the-uil-button-owning-this-text-label L to the workspace of L at (round(the item-x-position of L - the item-width of L/2 - 5), the item-y-position of L);

                call uil-move-grobj (the item that is the-uil-button-owning-this-text-label L);
                end;
            end;
        end;
end

upp-make-text-for-item-using-template (ITM:class item, template: class uil-text)
T: class uil-text;
begin
    create a uil-text T by cloning template;
    call utl-copy-object-attributes (template, T);
    call uil-update-text (T, "");

{-- Remove this before 4.0 --}
    call upp-bind-symbol-name-for-label (ITM, T);

    call upp-bind-label-to-object (ITM, T);
end

upp-is-item-a-uil-root-class (ITM: class item) = (truth-value)
T: text;
POS: integer;
SAClass: symbol = the symbol uil-scroll-area;
MsgClass: symbol = the symbol uil-message-object;
item-is-root-class: truth-value = false;
DEBUG: truth-value = false;
begin
    case (the class of ITM) of
{-- CHECK UIL-OBJECT ROOT CLASSES --}
        uil-object: item-is-root-class = true;         
        uil-grobj: item-is-root-class = true; 
        uil-scroll-area: item-is-root-class = true; 
        uil-button: item-is-root-class = true;
        uil-icon-button: item-is-root-class = true; 
        uil-text-button: item-is-root-class = true; 
        uil-selection-button: item-is-root-class = true; 

{-- RADIO-BUTTON --}
        uil-radio-button: item-is-root-class = true; 
        UIL-RADIO-BUTTON-SMALL: item-is-root-class = true; 
        UIL-SMALL-RADIO-BUTTON-MSW: item-is-root-class = true; 
        UIL-RADIO-BUTTON-MEDIUM: item-is-root-class = true; 
        UIL-MEDIUM-RADIO-BUTTON-MSW: item-is-root-class = true; 
        UIL-RADIO-BUTTON-LARGE: item-is-root-class = true; 
        UIL-LARGE-RADIO-BUTTON-MSW: item-is-root-class = true; 

{-- CHECK-BUTTON --}
        UIL-CHECK-BUTTON: item-is-root-class = true; 
        UIL-CHECK-BUTTON-SMALL: item-is-root-class = true; 
        UIL-SMALL-CHECK-BUTTON-MSW: item-is-root-class = true; 
        UIL-CHECK-BUTTON-MEDIUM: item-is-root-class = true; 
        UIL-MEDIUM-CHECK-BUTTON-MSW: item-is-root-class = true; 
        UIL-CHECK-BUTTON-LARGE: item-is-root-class = true; 
        UIL-LARGE-CHECK-BUTTON-MSW: item-is-root-class = true; 

{-- ICON-TOGGLE-BUTTON --}
        UIL-ICON-TOGGLE-BUTTON: item-is-root-class = true; 
        UIL-SMALL-ICON-TOGGLE-BUTTON: item-is-root-class = true; 
        UIL-MEDIUM-ICON-TOGGLE-BUTTON: item-is-root-class = true; 
        UIL-LARGE-ICON-TOGGLE-BUTTON: item-is-root-class = true; 
        UIL-SMALL-ICON-TOGGLE-BUTTON-MSW: item-is-root-class = true; 
        UIL-MEDIUM-ICON-TOGGLE-BUTTON-MSW: item-is-root-class = true; 
        UIL-LARGE-ICON-TOGGLE-BUTTON-MSW: item-is-root-class = true; 
    
{-- ICON-PUSHBUTTON --}
        UIL-ICON-PUSHBUTTON: item-is-root-class = true; 
        UIL-SMALL-ICON-PUSHBUTTON: item-is-root-class = true; 
        UIL-MEDIUM-ICON-PUSHBUTTON: item-is-root-class = true; 
        UIL-LARGE-ICON-PUSHBUTTON: item-is-root-class = true;         
        UIL-SMALL-ICON-PUSHBUTTON-MSW: item-is-root-class = true; 
        UIL-MEDIUM-ICON-PUSHBUTTON-MSW: item-is-root-class = true; 
        UIL-LARGE-ICON-PUSHBUTTON-MSW: item-is-root-class = true;

        UIL-UP-ARROW-PUSHBUTTON: item-is-root-class = true; 
        UIL-DOWN-ARROW-PUSHBUTTON: item-is-root-class = true; 
        UIL-MORE-ICON-PUSHBUTTON: item-is-root-class = true; 

{-- WORKSPACE/NAVIGATION BUTTONS --}
        UIL-WORKSPACE-BUTTON: item-is-root-class = true; 
        UIL-NAVIGATION-BUTTON: item-is-root-class = true; 
        UIL-GOTO-WORKSPACE-BUTTON: item-is-root-class = true; 
        UIL-GOTO-SUPERIOR-BUTTON: item-is-root-class = true; 
        UIL-GOTO-NEXT-BUTTON: item-is-root-class = true; 
        UIL-GOTO-PREVIOUS-BUTTON: item-is-root-class = true; 
        UIL-HIDE-BUTTON: item-is-root-class = true; 
        UIL-HELP-BUTTON: item-is-root-class = true; 
        UIL-WORKSPACE-OPERATION-BUTTON: item-is-root-class = true; 
        UIL-PRINT-WORKSPACE-BUTTON: item-is-root-class = true; 
    
{-- TEXT-TOGGLE-BUTTON --}
        UIL-TEXT-TOGGLE-BUTTON: item-is-root-class = true; 
        UIL-SMALL-TEXT-TOGGLE-BUTTON: item-is-root-class = true; 
        UIL-SMALL-TEXT-TOGGLE-BUTTON-MSW: item-is-root-class = true; 
        UIL-MEDIUM-TEXT-TOGGLE-BUTTON: item-is-root-class = true; 
        UIL-MEDIUM-TEXT-TOGGLE-BUTTON-MSW: item-is-root-class = true; 
        UIL-LARGE-TEXT-TOGGLE-BUTTON: item-is-root-class = true; 
        UIL-LARGE-TEXT-TOGGLE-BUTTON-MSW: item-is-root-class = true; 

{-- TEXT-PUSHBUTTON --}
        UIL-TEXT-PUSHBUTTON: item-is-root-class = true; 
        UIL-SMALL-TEXT-PUSHBUTTON: item-is-root-class = true; 
        UIL-SMALL-TEXT-PUSHBUTTON-MSW: item-is-root-class = true;
        UIL-MEDIUM-TEXT-PUSHBUTTON: item-is-root-class = true; 
        UIL-MEDIUM-TEXT-PUSHBUTTON-MSW: item-is-root-class = true; 
        UIL-LARGE-TEXT-PUSHBUTTON: item-is-root-class = true; 
        UIL-LARGE-TEXT-PUSHBUTTON-MSW: item-is-root-class = true; 

{-- CHECK UIL-MESSAGE ROOT CLASSES --}
        uil-message: item-is-root-class = true;         
        uil-grmes: item-is-root-class = true;

{-- UIL-TEXT --}
        uil-text: item-is-root-class = true; 
        uil-small-text: item-is-root-class = true; 
        uil-medium-text: item-is-root-class = true; 
        uil-large-text: item-is-root-class = true; 
        uil-error-text: item-is-root-class = true;
        uil-error-text-small: item-is-root-class = true; 
        
{-- DIALOG-TITLE --}
        uil-dialog-title: item-is-root-class = true; 
        uil-small-dialog-title: item-is-root-class = true; 
        uil-medium-dialog-title: item-is-root-class = true;
        uil-large-dialog-title: item-is-root-class = true; 

{-- EDIT-BOX --}
        uil-edit-box-small: item-is-root-class = true; 
        uil-edit-box-medium: item-is-root-class = true; 
        uil-edit-box-large: item-is-root-class = true; 
        uil-edit-box-extra-large: item-is-root-class = true; 

{-- MESSAGE-OBJECT --}
        uil-message-object: item-is-root-class = true; 

        otherwise: 
            begin
            if (the message-definition named by MsgClass exists and ITM is an instance of the class named by (MsgClass)) or (the object-definition named by SAClass exists and ITM is an instance of the class named by (SAClass)) then
                begin
                if (is-contained-in-text ("-", "[the class of ITM]")) then
                    begin
                    POS = position-of-text ("-", "[the class of ITM]");
                    T = get-from-text ("[the class of ITM]", 1, Pos - 1);

                    if (DEBUG) then
                        inform the operator that "T: [T]";

                    if (T = "guidemo") or (upp-is-module-a-guide-required-module (symbol(T))) then
                        item-is-root-class = true
                    else
                        item-is-root-class = false;
                    end
                else
                    item-is-root-class = false;  
                end
            else
                item-is-root-class = false;
            end;
    end;

    {-- Return Status --}
if (DEBUG) then
    inform the operator that "item-is-root-class: [item-is-root-class]";
    return item-is-root-class;
end

upp-make-text-for-edit-box(edit-box: class uil-edit-box)
T: class uil-text;
size: symbol;
DEBUG: truth-value = false; 
begin
    if DEBUG then inform the operator that "[the name of this procedure]: [the uil-size of edit-box]"; 
    if (the label of edit-box exists) then
        begin
        size = call uil-return-size-of-grmes-as-symbol (edit-box);
        if DEBUG then inform the operator that "[the name of this procedure]: [size]";
          
        case (size) of 
            SMALL: create a uil-small-text T;
            MEDIUM: create a uil-medium-text T;
            LARGE: create a uil-large-text T;
            EXTRA-LARGE: create a uil-large-text T;
            otherwise: signal the symbol uil-internal-error, uil-internal-error-unknown-edit-box-size(size);
            end;

        call uil-update-text (T, the label of edit-box);

{-- This needs to be removed before 4.0 --}
        call upp-bind-symbol-name-for-label (edit-box, T);

        call upp-bind-label-to-object (edit-box, T);     
        end; 
end

uil-update-button-label (B: class item)
LeadB, T: item-or-value;
DEBUG: truth-value = false;
begin
{--- dwr 2/9/2000 ---}
if not (B Exists) then
    return;

if (DEBUG) then
    inform the operator that "Running uil-update-button-label, uil-label-size is [the uil-label-size of B]";

    if (the uil-label-id of B exists and the uil-label-id of B = "5.0") then
        begin
        call upp-update-button-label (B);
        return;
        end;

    if (B exists and B is a uil-button and the label of B exists) then 
        begin
        T = call uil-get-label-text (B);
        if (T exists and T is a uil-text) then
            begin
            {-- Navigation Button --}
            if (B is a uil-navigation-button) then
                begin
                call uil-update-text (T, the label of B);
                
                if (the uil-is-permanent of B is true) then
                    call upp-make-item-permanent (T);
                call upp-reposition-related-text-for-button (B);
                end

            {-- Check Button or Radio Button --}
            else if (B is a uil-check-button) or (B is a uil-radio-button) then
                begin
                call uil-update-text (T, the label of B);
                
                if (the uil-is-permanent of B is true) then
                    call upp-make-item-permanent (T);
                call upp-reposition-related-text-for-button (B);

                if (the uil-button-orientation of the uil-selection-box that is the-group-master-of B = the symbol horizontal) then
                    begin
                    LeadB = call uil-get-selection-box-lead-button (the uil-selection-box that is the-group-master-of B);
                    call upp-manage-horizontal-selection-buttons (the uil-selection-box that is the-group-master-of B, the workspace of B, the item-x-position of LeadB, the item-y-position of LeadB);
                    call upp-resize-selection-box-to-frame-buttons (B);
                    end;
                end
            else
                begin
                T = call uil-get-label-text (B);
                if (T exists and T is a uil-text) then
                    begin 
                    call uil-update-text (T, the label of B);                    
                    call upp-reposition-related-text-for-button (B); 
                    end
                else
                    begin
                    call upp-make-text-for-button (B);
                    if (the workspace of B exists) then
                        call uil-manage-grobj-method (B, the workspace of B, the symbol none, the item-x-position of B, the item-y-position of B);
                    end;
                end;
            end {-- T exists --}
        else
            begin
            call upp-make-text-for-button (B);
            {-- sdm 2/14/95 --}
            if (the workspace of B exists) then
                call uil-manage-grobj-method (B, the workspace of B, the symbol none, the item-x-position of B, the item-y-position of B);
            end;       
        end;
        T = call uil-get-label-text (B);
        if (T exists and T is a uil-text) and (B exists and B is a uil-text-button) then 
            call upp-resize-text-button-to-fit-label (B);
end

upp-manage-grobj-internals (grobj: class item, win-or-wksp: item-or-value, config: symbol, xpos: integer, ypos: integer) = (class item) 
configuration: item-or-value;
save-config: symbol = the symbol nope;
manage-extension-proc: symbol = the symbol upp-manage-extension-object-method;
manage-dialog-proc: symbol = the symbol upp-manage-dialog-method;
manage-tab-page-proc: symbol = the symbol upp-manage-tab-page-method;
manage-edit-box-proc: symbol = the symbol upp-manage-edit-box-method;
manage-restricted-dialog-proc: symbol = the symbol upp-manage-restricted-dialog-method;  
manage-dialog-title-proc: symbol = the symbol upp-manage-dialog-title-method;
manage-selection-box-proc: symbol = the symbol upp-manage-selection-box-method;
manage-box-border-proc: symbol = the symbol upp-manage-box-border-method;
manage-scroll-area-proc: symbol = the symbol upp-manage-scroll-area-method;
manage-slider-proc : symbol = the symbol upp-manage-slider-method;
manage-spin-control-proc: symbol = the symbol upp-manage-spin-control-method;
manage-combo-box-proc: symbol = the symbol upp-manage-combo-box-method;
SAClass: symbol = the symbol uil-scroll-area;
TabClass: symbol = the symbol uil-tab-dialog;
TabPBClass: symbol = the symbol uil-tab-pushbutton;
TabPageClass: symbol = the symbol uil-tab-page;
SliderClass: symbol = the symbol uil-slider;
ComboClass: symbol = the symbol uil-combo-box;
DEBUG: truth-value = false;
begin
{-- This procedure makes a grobj visible --}

if (DEBUG) then
    inform the operator that "1. Running upp-manage-grobj-internals for [the class of grobj]";

    if win-or-wksp is a uil-dialog then
        win-or-wksp = the subworkspace of win-or-wksp;

    configuration = call uil-get-configuration (grobj);
    if configuration is an item and the configuration of grobj exists then
        save-config = the configuration of grobj
    else
        save-config = the symbol nope;
   conclude that the configuration of grobj = config;

    if save-config /= the symbol nope then
        conclude that the configuration of grobj = save-config;

{-- Button --}
    if grobj is a uil-button then
        call upp-manage-button-method(grobj, win-or-wksp, xpos, ypos)

{-- Selection-Box --}
    else if grobj is a uil-selection-box then
        call the procedure named by manage-selection-box-proc (grobj, win-or-wksp, xpos, ypos)

{-- Scroll-Area --}
    else if (the object-definition named by SAClass exists and grobj is an instance of the class named by (SAClass)) and (the procedure named by manage-scroll-area-proc exists) then 
        grobj = call the procedure named by manage-scroll-area-proc (grobj, win-or-wksp, xpos, ypos)

{-- Box Border --}
    else if grobj is a uil-box-border then
        call the procedure named by manage-box-border-proc (grobj, win-or-wksp, xpos, ypos)

{-- Spin Control --}
    else if grobj is a uil-spin-control-entry-box or grobj is a uil-spin-button then
        call the procedure named by manage-spin-control-proc (grobj, win-or-wksp, the symbol none, xpos, ypos)

{-- Combo Box --}
    else if (the message-definition named by ComboClass exists and grobj is an instance of the class named by (ComboClass)) then
        call the procedure named by manage-combo-box-proc (grobj, win-or-wksp, the symbol none, xpos, ypos)

{-- Edit-Box --}
    else if grobj is a uil-edit-box then
        call the procedure named by manage-edit-box-proc (grobj, win-or-wksp, xpos, ypos)

{-- Dialog Title --}
    else if grobj is a uil-dialog-title then 
        call the procedure named by manage-dialog-title-proc (grobj, win-or-wksp, xpos, ypos)

{-- Dialog --}
    else if grobj is a uil-dialog then
        begin
            call the procedure named by manage-dialog-proc(grobj, win-or-wksp, config, xpos, ypos); 
        end 

{-- Tab Page --}
    else if (the message-definition named by TabPageClass exists and grobj is an instance of the class named by (TabPageClass)) then
        call the procedure named by manage-tab-page-proc (grobj, win-or-wksp, config, xpos, ypos)

{-- Extension Object --}
    else if grobj is a uil-extension-object then 
        call the procedure named by manage-extension-proc (grobj, win-or-wksp, xpos, ypos)

{-- Slider --}
    else if (the object-definition named by SliderClass exists and grobj is an instance of the class named by (SliderClass)) then
        call the procedure named by manage-slider-proc (grobj, win-or-wksp, config, xpos, ypos)

{-- All Other Objects --}
    else
        begin
            { GENSYMCID-1313: How to see icon in a uil-tab-page ? Note: UIL-GROBJ on master dialog is always permanent.--binghe }
            { if not (the uil-is-permanent of grobj exists and the uil-is-permanent of grobj is true) then }
                transfer grobj to win-or-wksp at (xpos, ypos);
        end;

    conclude that the uil-is-managed of grobj = 1; 

if (DEBUG) then
    inform the operator that "1. Leaving upp-manage-grobj-internals for [the class of grobj]";

    return grobj;
end

uil-configure-grobj-method (grobj: class item)
DEBUG: truth-value = false;
begin
{---
    This procedure will dispatch the appropriate method (procedure) based upon the class of GROBJ being passed in. If the procedure is not uil-configure-grobj then that procedure will be run directly according to the specification of arguments for a configure call.
---}

if DEBUG then
    inform the operator that "2. Running uil-configure-grobj-method for: [the class of grobj].";

    if (grobj exists) and 
      (grobj exists and (the uil-configuration-method of grobj does not exist) or 
      (the procedure named by the uil-configuration-method of grobj does not exist) or 
      (the procedure named by the uil-configuration-method of grobj exists) and 
      (the uil-configuration-method of grobj is uil-configure-grobj or the uil-configuration-method of grobj is uil-configure-grobj-method)) then 
        begin 
        {-- UIL Configuration Method --}
        if (DEBUG) then
            inform the operator that "2a. UIL method";

        call uil-configure-grobj (grobj);
        end

    else if (grobj exists) and 
            ((grobj exists) and the uil-configuration-method of grobj exists and the procedure named by the uil-configuration-method of grobj exists) then
        begin
        {-- User-defined Configuration Method --}
        if (DEBUG) then
            inform the operator that "2a. User-defined method: [the uil-configuration-method of grobj]";
 
        call the procedure named by the uil-configuration-method of grobj (grobj);
        end;

if (DEBUG) then
    inform the operator that "2. Leaving uil-configure-grob-method for: [the class of grobj]";
end

upp-manage-grobj-without-configure (G: class item, win-or-wksp: item-or-value, config: symbol, xpos: integer, ypos: integer) = (class item) 
DEBUG: truth-value = false;
begin
if (DEBUG) then
    inform the operator that "1. Running upp-manage-grobj-without-configure for: [the class of G]";

    if (the uil-manage-method of G does not exist) or (the procedure named by the uil-manage-method of G does not exist) or ((the procedure named by the uil-manage-method of G exists) and (the uil-manage-method of G is uil-manage-grobj or the uil-manage-method of G is uil-manage-grobj-method)) then
        begin
        {-- UIL Method --}
        if (DEBUG) then
            inform the operator that "Built-In: calling upp-manage-grobj-internals";

        G = call upp-manage-grobj-internals (G, win-or-wksp, config, xpos, ypos);
        end

    else if (the uil-manage-method of G exists) and (the procedure named by the uil-manage-method of G exists) then
        begin
        {-- User-defined Procedure --}
        if (DEBUG) then
            inform the operator that "User Defined: calling [the uil-manage-method of G]";

        {G = call the procedure named by the uil-manage-method of G (G);}
        G = call the procedure named by the uil-manage-method of G (G, win-or-wksp, config, xpos, ypos); 
        end;

if (DEBUG) then
    inform the operator that "1. Leaving upp-manage-grobj-without-configure for: [the class of G]";

    return G;
end

upp-configure-grobj-permanently (grobj: class item)
begin    
    call uil-configure-grobj-method (grobj);
    if (the uil-is-permanent of grobj exists and the uil-is-permanent of grobj) then
        call upp-make-item-permanent (grobj);
end

uil-move-grobj (G: class item) = (item-or-value) ComboClass: symbol = the symbol uil-combo-box; SAClass: symbol = the symbol uil-scroll-area; uil-slider-name: symbol = the symbol uil-slider; move-scroll-area-proc: symbol = the symbol upp-move-scroll-area; move-combo-box-proc: symbol = the symbol upp-move-combo-box; upp-move-slider-name: symbol = the symbol upp-move-slider; DEBUG: truth-value = false; begin if (DEBUG) then inform the operator that "Running uil-move-grobj for: <[(if the id of G exists then the id of G else "no id")]>"; if (G is a uil-spin-control-entry-box) then call upp-move-spin-control (G)
else if (the message-definition named by ComboClass exists) and (G is an instance of the class named by (ComboClass)) then call the procedure named by move-combo-box-proc (G)
else if (G is a uil-edit-box) then call upp-move-edit-box (G)
else if (G is a uil-button) then call upp-move-button (G, true)
else if (G is a uil-dialog) then call upp-move-dialog (G)
else if (G is a uil-dialog-title) then call uil-justify-dialog-title (G, the workspace of G)
else if (G is a uil-text and the uil-stand-alone-text of G is false) then call upp-move-label (G)
else if (the object-definition named by uil-slider-name exists) and (G is an instance of the class named by (uil-slider-name)) then call the procedure named by upp-move-slider-name (G)
else if (the object-definition named by SAClass exists and G is an instance of the class named by (SAClass)) and (the procedure named by move-scroll-area-proc exists) then G = call the procedure named by move-scroll-area-proc (G, false)
else if (G is a uil-line-separator) then G = call upp-align-line-separator-objects (G)
else if (G is a uil-selection-box) then call upp-move-selection-box (G);
return G; end

uil-manage-grobj (grobj: class item, win-or-wksp: item-or-value, config: symbol, xpos: integer, ypos: integer) = (class item)  
DEBUG: truth-value = false;
begin
{-- This procedure makes a grobj visible --}

if (DEBUG) then
    inform the operator that "1. Running uil-manage-grobj for [the class of grobj]";

    grobj = call upp-manage-grobj-internals (grobj, win-or-wksp, config, xpos, ypos);

    if (grobj is not a uil-dialog) then
        begin
        if (DEBUG) then
            inform the operator that "calling uil-configure-grobj-method for: <[the class of grobj]>"; 
        call uil-configure-grobj-method (grobj);
        end;

if (DEBUG) then   
    inform the operator that "1. Leaving uil-manage-grobj for [the class of grobj]";

    return grobj;
end

uil-configure-grobj (grobj: class item)
MsgClass: symbol = the symbol uil-message-object;
SAClass: symbol = the symbol uil-scroll-area;
TabClass: symbol = the symbol uil-tab-dialog;
TabPBClass: symbol = the symbol uil-tab-pushbutton; 
TabPageClass: symbol = the symbol uil-tab-page;
ComboClass: symbol = the symbol uil-combo-box;
SliderClass: symbol = the symbol uil-slider;
configure-scroll-area-proc: symbol = the symbol upp-configure-scroll-area-method;
configure-scroll-message-proc: symbol = the symbol upp-configure-scroll-message-method;
configure-tab-pushbutton-proc: symbol = the symbol upp-configure-tab-pushbutton-method; 
configure-tab-page-proc: symbol = the symbol upp-configure-tab-page-method;
configure-tab-dialog-proc: symbol = the symbol upp-configure-tab-dialog-method;
configure-combo-box-proc: symbol = the symbol upp-configure-combo-box-method;
configure-slider-proc: symbol = the symbol upp-configure-slider-method;
DEBUG: truth-value = false;
begin
if DEBUG then
    begin
    inform the operator that "2. Running uil-configure-grobj for: <[the class of grobj]>";

    {if (the label of grobj exists) then
        inform the operator that "label: <[the label of grobj]>"
    else if (the message-contents of grobj exists) then
        inform the operator that "mc: <[the message-contents of grobj]>";}
    end;

{-- Combo Box Button --}
    if (the message-definition named by ComboClass exists and grobj is an instance of the class named by (ComboClass)) and (the uil-edit-box that is the-combo-box-button-of grobj exists) and (the procedure named by configure-combo-box-proc exists) then
        call the procedure named by configure-combo-box-proc (grobj) 

{-- Check Button --}
    else if grobj is a uil-check-button then
        call upp-configure-check-button-method (grobj)

{-- Radio Button --}
    else if grobj is a uil-radio-button then
        call upp-configure-radio-button-method (grobj)

{-- Tab Push Button --}
    else if (the object-definition named by TabPBClass exists and grobj is an instance of the class named by (TabPBClass)) and (the procedure named by configure-tab-pushbutton-proc exists) then
        call the procedure named by configure-tab-pushbutton-proc (grobj) 

{-- Text Push Button --}
     else if grobj is a uil-text-pushbutton then
        call upp-configure-text-pushbutton-method (grobj)

{-- Icon Push Button --}
    else if grobj is a uil-icon-pushbutton then
        call upp-configure-icon-pushbutton-method (grobj)

{-- Line Separator --}
    else if grobj is a uil-line-separator then 
        call upp-configure-line-separator-method (grobj)

{-- Extension Object --}
    else if grobj is a uil-extension-indicator or grobj is a uil-extension-object then
        call upp-configure-extension-object-method (grobj)

{-- Box Object --}
    else if grobj is a uil-box-object then
        call upp-configure-box-border-method (grobj)

{-- Text Toggle Button --}
    else if grobj is a uil-text-toggle-button then
        call upp-configure-text-toggle-button-method (grobj)

{-- Icon Toggle Button --}
    else if grobj is a uil-icon-toggle-button then
        call upp-configure-icon-toggle-button-method (grobj)

{-- Scroll Area --}
    else if (the object-definition named by SAClass exists and grobj is an instance of the class named by (SAClass)) and (the procedure named by configure-scroll-area-proc exists) then
        call the procedure named by configure-scroll-area-proc (grobj)

{-- Tab Dialog --}
    else if (the object-definition named by TabClass exists and grobj is an instance of the class named by (TabClass)) and (the procedure named by configure-tab-dialog-proc exists) then
        call the procedure named by configure-tab-dialog-proc (grobj) 

{-- Dialog --}
    else if grobj is a uil-dialog then
        call upp-configure-dialog-method (grobj)

{-- Selection Box --}
    else if grobj is a uil-selection-box then
        call upp-configure-selection-box-method (grobj)

{-- Navigation Button --}
    else if grobj is a uil-navigation-button then
        call upp-configure-navigation-button-method (grobj)

{-- Dialog Title --}
     else if grobj is a uil-dialog-title then 
        call upp-configure-dialog-title-method (grobj)

{-- Slider --} 
    else if (the object-definition named by SliderClass exists and grobj is an instance of the class named by (SliderClass)) and (the procedure named by configure-slider-proc exists) then
        call the procedure named by configure-slider-proc (grobj) 

{-- Message Object --}
    else if (the message-definition named by MsgClass exists and grobj is an instance of the class named by (MsgClass)) and (the procedure named by configure-scroll-message-proc exists) then
         call the procedure named by configure-scroll-message-proc (grobj)

{-- Combo Box --}
    else if (the message-definition named by ComboClass exists and grobj is an instance of the class named by (ComboClass)) and (the procedure named by configure-combo-box-proc exists) then
        call the procedure named by configure-combo-box-proc (grobj) 

{-- Edit Box --}
    else if grobj is a uil-edit-box then
        call upp-configure-edit-box-method (grobj)

{-- Text --}
    else if ((grobj is a uil-text) and (the uil-stand-alone-text of grobj exists and the uil-stand-alone-text of grobj is true) ) then
        call upp-configure-text-method (grobj)

{-- Tab Page --} 
    else if (the message-definition named by TabPageClass exists and grobj is an instance of the class named by (TabPageClass)) and (the procedure named by configure-tab-page-proc exists) then
        call the procedure named by configure-tab-page-proc (grobj);

{--
    Call upp-make-item-permanent (not uil-make-grobj-permanent which would become infinite loop) IF:
    1. the workspace of grobj exists and the item superior to the workspace of grobj does NOT exist  -or-
    2. the workspace of grobj exists and the item superior to the workspace of grobj exists and IS a MASTER dialog
--}
    if (the uil-is-permanent of grobj exists) and (the workspace WS of grobj exists) then
        begin
        if (the item superior to WS does not exist) or (the item ITM superior to WS exists and ITM is a uil-dialog and the uil-origin of ITM = the symbol master) then
            call upp-make-item-permanent (grobj);
        end;

if (DEBUG) then
    inform the operator that "2. Leaving uil-configure-grobj for: [the class of grobj]";
end

uil-select-button (B: class uil-button, W: class ui-client-item, drawable: class item)
dlg-or-wksp: class item;
Dialog-Mechanism: symbol;
DEBUG: truth-value = false;
en: symbol; et: text;
Status: Symbol;

begin

    {-- drawable can be either a uil-dialog or a kb-workspace. The old dialog mechanism expects to be able to pass the dialog. The new mechanism typically passes the workspace. --}

    {-- Check to ensure W exists and the connection status is valid --}
    if (W exists) then begin        
        if (upp-is-client-connected (W)) then 
            begin 
            end
    end
    else
        return;
       
    {-- Check that both the button and the drawable exist since the dialog may be deleted if the window is closed --kwf 9/28/95 --}
    if (B exists) and (drawable exists) then
        begin
        if (DEBUG) or (GUIDE-DEBUG-BUTTON-SELECTION) then
            inform the operator that "Running uil-select-button: Label: <[(if the label of B exists then the label of B else "")]>  Id: <[(if the id of B exists then the id of B else "")]>  Callback: <[(if the callback of B exists then the callback of B else "")]>";

        {-- Select & Highlight button --}
        if (the uil-button-selection-status of B exists) then
            conclude that the uil-button-selection-status of B is selected; 
       
        {-- Configure the button to indicate selected state --}
        call uil-highlight-button (B); 

        {-- Process Button Selection: B is either on a dialog or not --}
        if (the item superior to drawable exists) and (the item superior to drawable is an instance of the class named by (the symbol uil-dialog)) then
            dlg-or-wksp = the item superior to drawable
        else
            dlg-or-wksp = drawable;

        if (dlg-or-wksp is a uil-dialog) then
            begin
            if (DEBUG) then
                inform the operator that "[the label of B] is on a dialog";

            {-- Is it the new dialog mechanism introduced with 4.0, or not? --}
            if (the uil-dialog-using-new-mechanism of dlg-or-wksp is true) and (not (the uil-dialog-mode of dlg-or-wksp = the symbol wait-mode)) then
                Dialog-Mechanism = the symbol New-Dialog-Mechanism
            else
                Dialog-Mechanism = the symbol Old-Dialog-Mechanism;

            if (Dialog-Mechanism = the symbol New-Dialog-Mechanism) then
                begin
                {-- New Dialog Mechanism --}

                if (DEBUG) or (GUIDE-DEBUG-BUTTON-SELECTION) then
                    inform the operator that "Button is on a dialog using GUIDE 4.0 Dialog Mechanism"; 

                if (the procedure-invocation that is the-uil-dialog-manager-of dlg-or-wksp exists) then                                                                                                                                                                                                                                                                               
                    begin
                    if (the procedure named by the uil-handler-method of B exists) then
                        begin  
                        if (the uil-release-button-after-selection of B exists and the uil-release-button-after-selection of B is true) then 
                            start the procedure named by the uil-handler-method of B (B, W, drawable)
                        else         
                            call the procedure named by the uil-handler-method of B (B, W, drawable);
                        end
                    else
                        begin
                        inform the operator that "the uil-handler-method <[the uil-handler-method of B]> does not exists";
                        signal the symbol uil-internal-error, uil-internal-error-unknown-handler(the uil-handler-method of B); 
                        end;

                    if (the uil-conclude-value-immediately of B) and (the uil-conclude-method of B exists) and (the procedure named by the uil-conclude-method of B exists) then
                        begin
                        call the procedure named by the uil-conclude-method of B (B, W, dlg-or-wksp, B, the symbol none); 
                        end;

                    {-- Only pushbuttons have actions, as well as have influence on editor --}
                    if (B is a uil-text-pushbutton or B is a uil-icon-pushbutton) then
                        begin   
                        Status = call upp-handle-button-selections-effect-on-edit-session (B, dlg-or-wksp, W);

                        {--- sdm: 11/28/99 (dwr 2/8/2000) Don't insert dialog events if validation failed. ---}  

                        if (Status /= the symbol FLUSH_QUEUE) then
                          begin
                            { GENSYMCID-1539: GUIDE creates instances of uil-edit-standby-object that are not deleted }
                            {-- aj: 10/21/15: Don't run upp-handle-button-selections-effect-on-edit-session a second time --}
                            { call upp-handle-button-selections-effect-on-edit-session (B, dlg-or-wksp, W); }
                            if (DEBUG) or (GUIDE-DEBUG-BUTTON-SELECTION) then
                                inform the operator that "Inserting button's actions into dialog event queue for dialog: <[the id of dlg-or-wksp]>";                  
                            call upp-insert-button-actions-into-dialog-event-queue (B, dlg-or-wksp, W);   
                          end; 
                        end;
                    end
                else
                    begin
                    if (DEBUG) then
                        inform the operator that "upp-control-dialog-events is not running for this dialog";

                    if (the procedure named by the uil-handler-method of B exists) then
                        begin
                        if (DEBUG) or (GUIDE-DEBUG-BUTTON-SELECTION) then
                            inform the operator that "calling the button handler method: <[the uil-handler-method of B]>";

                        if (the uil-release-button-after-selection of B exists and the uil-release-button-after-selection of B is true) then 
                            start the procedure named by the uil-handler-method of B (B, W, drawable)
                        else
                            call the procedure named by the uil-handler-method of B (B, W, drawable);
                        end
                    else
                        begin
                        inform the operator that "the uil-handler-method <[the uil-handler-method of B]> does not exists";
                        signal the symbol uil-internal-error, uil-internal-error-unknown-handler(the uil-handler-method of B); 
                        end;
                    end;
                end

            else if (Dialog-Mechanism = the symbol Old-Dialog-Mechanism) then
                begin
                {-- Old Dialog Mechanism --}

                if (DEBUG) or (GUIDE-DEBUG-BUTTON-SELECTION) then                   
                    inform the operator that "Button is on a dialog using GUIDE 3.0 Dialog Mechanism"; 

                conclude that the uil-which-button-pressed of dlg-or-wksp = the id of B;
 
                {-- Only pushbuttons can influence the edit session --}
                if (B is a uil-text-pushbutton or B is a uil-icon-pushbutton) then 
                    call upp-handle-button-selections-effect-on-edit-session (B, dlg-or-wksp, W); 

                if (the procedure named by the uil-handler-method of B exists) then
                    begin
                    if (GUIDE-DEBUG-BUTTON-SELECTION) then
                        inform the operator that "calling the button handler method: <[the uil-handler-method of B]>";
                    call the procedure named by the uil-handler-method of B (B, W, dlg-or-wksp);
                    end
                else
                    begin
                     inform the operator that "the uil-handler-method <[the uil-handler-method of B]> does not exists";
                    signal the symbol uil-internal-error, uil-internal-error-unknown-handler(the uil-handler-method of B); 
                    end;
                end

            else
                begin
                if (DEBUG) or (GUIDE-DEBUG-BUTTON-SELECTION) then
                    inform the operator that "Button is on a dialog using a hybrid of GUIDE 3.0 and 4.0 Dialog Mechanisms";

                if (the procedure named by the uil-handler-method of B exists) then
                    begin
                    if (GUIDE-DEBUG-BUTTON-SELECTION) then
                        inform the operator that "calling the button handler method: <[the uil-handler-method of B]>";

                    if (the uil-release-button-after-selection of B exists and the uil-release-button-after-selection of B is true) then
                        start the procedure named by the uil-handler-method of B (B, W, dlg-or-wksp)
                    else
                        call the procedure named by the uil-handler-method of B (B, W, dlg-or-wksp);
                    end
                else
                    begin
                    inform the operator that "the uil-handler-method <[the uil-handler-method of B]> does not exists";
                    signal the symbol uil-internal-error, uil-internal-error-unknown-handler(the uil-handler-method of B);   
                    end;
                end;
            end {- B is on a dialog -}

        else
            begin
            if (DEBUG) or (GUIDE-DEBUG-BUTTON-SELECTION) then
                inform the operator that "Button is not on a dialog";

            if (the procedure named by the uil-handler-method of B exists) then
                begin  
                if (DEBUG) or (GUIDE-DEBUG-BUTTON-SELECTION) then
                    inform the operator that "calling the button handler method: <[the uil-handler-method of B]>";    
        
                if (the uil-release-button-after-selection of B exists and the uil-release-button-after-selection of B is true) then
                    start the procedure named by the uil-handler-method of B (B, W, dlg-or-wksp)
                else
                    call the procedure named by the uil-handler-method of B (B, W, dlg-or-wksp); 

                if (the uil-conclude-value-immediately of B is true) and (the uil-conclude-method of B exists) and (the procedure named by the uil-conclude-method of B exists) then
                    call the procedure named by the uil-conclude-method of B (B, W, dlg-or-wksp, B, the symbol none);  
                end
            else
                begin
                inform the operator that "the uil-handler-method <[the uil-handler-method of B]> does not exists";
                signal the symbol uil-internal-error, uil-internal-error-unknown-handler(the uil-handler-method of B);
                end;       
            end;
        
        {-- Done Processing Button Selection --}

if (DEBUG) then
    inform the operator that "Done checking button cases, should have fallen into one";

        if (B exists) and (the uil-button-selection-status of B exists) then
            begin
            if (DEBUG) then
                inform the operator that "setting uil-button-selection-status for <[the label of B]> to unselected";
            conclude that the uil-button-selection-status of B is unselected;
            call upp-reset-button-internals (B);
            end;        

        if (DEBUG) or (GUIDE-DEBUG-BUTTON-SELECTION) and (B exists) then
            inform the operator that "Leaving uil-select-button for: Label: <[(if the label of B exists then the label of B else "")]>  Id: <[(if the id of B exists then the id of B else "")]>";
        end;
end  
on error (en, et)
if (B exists) and (the uil-button-selection-status of B exists) then
    begin
    conclude that the uil-button-selection-status of B is unselected; 
    call  upp-reset-button-internals (B);
    end;

{-- If W loses its binding, it's likely due to a TW shutdown while a dialog is managed. This condition is normal, and the error processing should swallow the error.  ----- dwr 7/29/95 --}
if W does not exist or not (upp-is-client-connected(W)) then 
    return;
{-- If the button and or the drawable do not exist then this is also likely to be due to the TW shutdown (now that we delete the dialog in that case). This is also normal so just swallow the error. ---- kwf 9/28/95 --}
if B does not exist or drawable does not exist then
    return;

{Otherwise it might be a user error so, we'll post on the message board.}
inform the operator that "[en] [et]";
end

uil-make-grobj-permanent (grobj: class item)
WS: class kb-workspace;
BOX: class uil-selection-box;
B: item-or-value;
IL: class item-list;
G: class item;
TXT: item-or-value;
arr: class text-array;
T: text;
error-type: symbol; error-text: text;

make-extension-object-permanent-proc: symbol = the symbol upp-make-extension-object-permanent;
make-dialog-title-permanent-proc: symbol = the symbol upp-make-dialog-title-permanent;

SAClass: symbol = the symbol uil-scroll-area;
SAObject: symbol = the symbol uil-scroll-object;
SACorner: symbol = the symbol guide-scroll-corner-object;
SAComponent: symbol = the symbol uil-scroll-component;
MsgClass: symbol = the symbol uil-message-object;
ComboClass: symbol = the symbol uil-combo-box; 
SliderClass: symbol = the symbol uil-slider;
SpinClass: symbol = the symbol uil-spin-control-entry-box;

make-scroll-area-permanent-proc: symbol = the symbol upp-make-scroll-area-permanent;
make-scroll-message-permanent-proc: symbol = the symbol upp-make-scroll-message-permanent;
make-slider-permanent-proc: symbol = the symbol upp-make-slider-permanent;

DEBUG: truth-value = false;
DEBUG-ITM: truth-value = false;

begin 
if (DEBUG) then
    inform the operator that "Running uil-make-grobj-permanent for: [the class of grobj]";

  {-- This procedure makes a grobj permanent. If the grobj is not managed (i.e. on a workspace, an error results. --}  

  {-- Scroll area parts such as the scroll bar, scroll segments are never made permanent.  They are re-created upon restarts.  Thus, these parts should never fall into this routine.  This check makes sure this does not happen. --}

{-- 1. ERROR Checking: Discover and report error conditions. --}

{-- Workspace --}
    if (grobj is a kb-workspace) then
        begin
        if (DEBUG) then
            inform the operator that "error checking case of kb-workspace";

        if (there exists a uil-selection-button upon grobj) then
            begin
            create an item-list IL;
            change the text of the allow-duplicate-elements of IL to "no";
            for g = each uil-selection-button upon grobj do
                if (the uil-selection-box that is the-group-master-of g is not a member of the item list IL) then
                    insert the uil-selection-box that is the-group-master-of g at the end of the item list IL;
                end;
            end; 
        go to bypass-error-checking;
        end;

{-- Navigation Button --}
    if (grobj is a uil-navigation-button) then 
        go to bypass-error-checking;

{-- ScrollArea, Message-Object, Scroll-Corner Object --}
    if ((the object-definition named by SAObject exists and grobj is an instance of the class named by (SAObject)) or (the message-definition named by MsgClass exists and grobj is an instance of the class named by (MsgClass)) or (the object-definition named by SACorner exists and grobj is an instance of the class named by (SACorner))) then 
        begin
        if (the object-definition named by SAClass exists and grobj is not an instance of the class named by (SAClass) and the message-definition named by MsgClass exists and grobj is not an instance of the class named by (MsgClass)) then 
            return;
        end;

    if ((the uil-is-managed of grobj exists and the uil-is-managed of grobj = 0) and (the workspace of grobj does not exist)) and (grobj is not a uil-dialog) then 
        signal the symbol uil-error, uil-error-grobj-perm (the id of grobj)
    else 
        begin
        {-- Selection Buttons:  can only be made permanent if selection box is permanent --}
        if (grobj is a uil-selection-button) and (the uil-is-permanent of the uil-selection-box that is the-group-master-of grobj is false) then 
            begin
            if (grobj is a uil-radio-button) then
                signal the symbol uil-error, uil-error-radio-button-no-perm (the id of grobj)
            else
                signal the symbol uil-error, uil-error-check-button-no-perm (the id of grobj);
            end 
        else
            begin
            {-- Message Objects: cannot be made permanent unless scroll-area is permanent --}
            if (the message-definition named by MsgClass exists and grobj is an instance of the class named by (MsgClass)) and (the object-definition named by SAClass exists and the uil-is-permanent of the item that is the-handler-for-the-message-of grobj is false) then 
                signal the symbol uil-error, uil-error-message-object-no-perm (the id of grobj);
            end;
        end; 

{-- Dialog: anticipate a dialog with selection boxes and make the box and its buttons perm. once rather than once per button instance. --}
    if (grobj is a uil-dialog) then 
        begin
        if (DEBUG) then
            inform the operator that "error checking: grobj is a dialog";

        WS = the subworkspace of grobj;
        if (there exists a uil-selection-button upon WS) then 
        begin
            create an item-list IL;
            change the text of the allow-duplicate-elements of IL to "no";
            for G = each uil-selection-button upon WS do
                if (the uil-selection-box that is the-group-master-of G is not a member of the item list IL) then
                    insert the uil-selection-box that is the-group-master-of G at the end of the item list IL;
                end;
            end;
        end; {- uil-dialog -}

{-- 2. END of ERROR checking: now make the grobj permanent --}

if (DEBUG) then
    inform the operator that "END of ERROR checking: now make the grobj permanent";

bypass-error-checking:

    if (grobj is not a kb-workspace) and (the uil-is-permanent of grobj exists) then
        conclude that the uil-is-permanent of grobj is true;

{-- Navigation Button --}
    if (grobj is a uil-navigation-button) then
        begin
        if (DEBUG) then
            inform the operator that "grobj is a uil-navigation-button";

        call upp-make-item-permanent (grobj);
        if (the uil-label-id of grobj /= "5.0") then
            begin
            TXT = call uil-get-label-text (grobj);
            if (TXT exists and TXT is a uil-text) then 
                call upp-make-item-permanent (TXT); 
            end;

        if (the subworkspace of grobj exists) then
            call upp-make-items-on-subworkspace-of-navigation-button-permanent (the subworkspace of grobj);

        go to exit-routine; 
        end 

{-- Button --}
    else if (grobj is a uil-button) then
        begin
        call upp-make-item-permanent (grobj);
        if (the uil-label-id of grobj /= "5.0") then
            begin
            TXT = call uil-get-label-text (grobj);
            if (TXT exists and TXT is a uil-text) then 
                call upp-make-item-permanent (TXT); 
            end;
        go to exit-routine;
        end;

{-- Scroll Area --}
    if (the object-definition named by SAClass exists and grobj is an instance of the class named by (SAClass)) and (the procedure named by make-scroll-area-permanent-proc exists) then 
        begin
        if (DEBUG-ITM) then
            begin
            inform the operator that "calling upp-make-scroll-area-permanent (wait for 5 seconds)";
            wait for 5 seconds;
            end;
        call the procedure named by make-scroll-area-permanent-proc (grobj); 
        if (DEBUG-ITM) then 
        begin
            inform the operator that "back from calling upp-make-scroll-area-permanent (wait for 5 seconds)";
            wait for 5 seconds;
            end; 
        go to exit-routine;
        end;

{-- Message Object --}
    if (the message-definition named by MsgClass exists and grobj is an instance of the class named by (MsgClass)) and (the procedure named by make-scroll-message-permanent-proc exists) then 
        begin 
        if (DEBUG-ITM) then
            begin
            inform the operator that "calling make scroll-message permanent (wait 5 seconds)";
            wait for 5 seconds;
            end; 
        call the procedure named by make-scroll-message-permanent-proc (grobj); 
        if (DEBUG-ITM) then
            begin
            inform the operator that "back from make scroll-message permanent (wait 5 seconds)";
            wait for 5 seconds;
            end; 
        go to exit-routine;
        end;

{-- Extension Object --}
    if (grobj is a uil-extension-object) then
        begin 
        call the procedure named by make-extension-object-permanent-proc (grobj);
        go to exit-routine;
        end;

{-- Selection Box --}
    if (grobj is a uil-selection-box) then 
        begin
        call upp-make-item-permanent (grobj);
        ARR = the members of grobj;
        for T = each text in ARR do
            B = call uil-get-selection-button (T);
            
            if (the uil-label-id of B /= "5.0") then
                begin
                TXT = call uil-get-label-text (B);
                if (TXT exists and TXT is a uil-text) then
                    call upp-make-item-permanent (TXT); 
                end;

            call upp-make-item-permanent (B);
            conclude that the uil-is-permanent of B is true;
            end; 
        end
        else if (the object-definition named by SliderClass exists and grobj is an instance of the class named by (SliderClass)) and (the procedure named by make-slider-permanent-proc exists) then call the procedure named by make-slider-permanent-proc(grobj);

{-- UIL-GRMES, but NOT Message-Object --}
    if (grobj is a uil-grmes) and (the message-definition named by MsgClass exists and grobj is not an instance of the class named by (MsgClass)) then 
        begin
        if (DEBUG) then
            inform the operator that "grobj is a uil-grmes";

        if (grobj is a uil-text and the uil-stand-alone-text of grobj exists and the uil-stand-alone-text of grobj is true) then 
        begin
            if (DEBUG) then
                inform the operator that "grobj is a uil-text. standalone? <[the uil-stand-alone-text of grobj]>";        
            call upp-make-item-permanent (grobj);
            end
        else if (grobj is a uil-grmes) then
            begin
            {-- Make sure related pieces are permanent (border, label, spin buttons) --}
            if (DEBUG) then
                inform the operator that "case of uil-grmes";

            if (the item that is the-uil-text-label-of grobj exists) then
                begin
                if (DEBUG) then
                    inform the operator that "making label permanent";
                call upp-make-item-permanent (the item that is the-uil-text-label-of grobj);
                end;

            B = call uil-get-border (grobj);
            if (B exists and B is an item) then
                begin
                if (DEBUG) then
                    inform the operator that "making border permanent";
                call uil-make-grobj-permanent (B); 
                end;

            if (the message-definition named by ComboClass exists) and (grobj is an instance of the class named by (ComboClass)) then
                begin
                if (DEBUG) then
                    inform the operator that "special case of combo-box, deal with button and scroll-area";
                if (the uil-button B that is the-combo-box-button-of grobj exists) then
                    call upp-make-item-permanent (B); 

                if (the item B that is the-combo-scroll-area-for grobj exists) then
                    call uil-make-grobj-permanent (B);
                end;

            if (the message-definition named by SpinClass exists) and (grobj is a uil-spin-control-entry-box) then begin
                for B = each uil-spin-button that is a-spin-button-controlling grobj do
                    call upp-make-item-permanent (B);
                end;
            end;

            if (DEBUG) then
                inform the operator that "make actual uil-grmes permanent";
            call upp-make-item-permanent (grobj);
            end;
        end;

{-- Dialog --}
    if (grobj is a uil-dialog) then 
        begin
        if (DEBUG-ITM) then
            begin
            inform the operator that "start process for dialog (wait 5 seconds)";
            wait for 5 seconds;
            end;
        {-- This check is necessary because once a dialog is made permanent, the scroll pieces upon the dialog's subworkspace must be made transient - except the scroll-area itself --}

        if (DEBUG) then
            inform the operator that "calling upp-make-item-permanent for dialog";
            
        call upp-make-item-permanent (grobj);

        if IL exists then
            begin 
            for BOX = each uil-selection-box in IL do
                call uil-make-grobj-permanent (BOX);
                end;
            end;

        for G = each item upon the subworkspace of grobj do 
            if (G is not a uil-grobj) and (G is not a uil-grmes) then 
                call upp-make-item-permanent (G)
            {else if (the object-definition named by SAComponent exists and G is an uil-scroll-component) then
                make G transient}  {scroll components should remain permanent}
            else if (G is a uil-selection-button) or (G is a uil-selection-box) then 
            begin
                {-- do nothing: handled above --}
                end 
            else if (G is a uil-dialog-title) then
                call the procedure named by make-dialog-title-permanent-proc (G)
            else if (G is a uil-text) and (the uil-stand-alone-text of G is true) then
                {make G permanent} call upp-make-item-permanent (G) 
            else
                begin
                if (DEBUG) then
                    inform the operator that "calling uil-make-grobj-permanent (from make-perm) for: [the class of G]";
                call uil-make-grobj-permanent (G);
                end;
            end; {- end for G -}

        if (DEBUG-ITM) then
            begin
            inform the operator that "done with process for dialog (wait 5 seconds)";
            wait for 5 seconds;
            end; 
        end; {- end Dialog -}
 
{-- Workspace --}
    if (grobj is a kb-workspace) then 
        begin
        {-- This check is necessary because once a workspace is made permanent, the scroll pieces upon the workspace must be made transient - except the scroll-area itself --}
        make grobj permanent;

        if IL exists then
            begin 
            for BOX = each uil-selection-box in IL do
                call uil-make-grobj-permanent (BOX);
                end;
            end;

        for G = each item upon grobj do 
            if (G is not a uil-grobj) and (G is not a uil-grmes) then 
                call upp-make-item-permanent (G)
            {else if (the object-definition named by SAComponent exists and G is an uil-scroll-component) then 
                make G transient}
            else if (G is a uil-selection-button) or (G is a uil-selection-box) then 
                begin
                {-- do nothing: handled above --}
                end 
            else if (G is a uil-text) and (the uil-stand-alone-text of G is true) then
                {make G permanent} call upp-make-item-permanent (G)
            else
                call uil-make-grobj-permanent (G);
            end; {- end for G -}
        end; {- end Workspace -}

    if (grobj is a uil-action-description-array or grobj is a uil-field-edit-style or grobj is a uil-format-specification-class or grobj is a uil-configuration-class) then
        make grobj permanent; 

exit-routine:
    if IL exists then delete IL;

end 
on error (error-type, error-text)
if (DEBUG) then
inform the operator that "While attempting to make a [(if grobj exists then "[the class of grobj]" else "<unknown class>")] permanent, this error occurred:[crlf]--------------------------------------------------[crlf][error-type][crlf][error-text]";
end

upp-cleanup-bin (WS: class kb-workspace, x-spacing: integer, y-spacing: integer, line-limit: integer)
x, y, line-count: integer;
ITM: class item;
begin
x = 0; y = 0; line-count = 0;
for ITM = each item upon WS do 
 
{Do selection buttons, configurations, and dialogs}
    if ITM is a uil-dialog or ITM is a uil-configuration-class then begin
        if line-count > line-limit then begin
            y = 0;
            line-count = 0;
            x = x + utl-width (ITM) + x-spacing;
            end;
        move ITM to (x, y);
        make ITM permanent;
        y = y - truncate (utl-height (ITM) / 2) - y-spacing;
        line-count = line-count + 1;
    end;

{Do scroll area definitions}
    if ITM is an object-definition then begin
    end; 

  end;

{-- Finalize WS --}
   change the size of WS to minimum;
   call upp-make-item-permanent (WS);
end

uil-utility-change-module (old-module-name: symbol, new-module-name: symbol, W: class ui-client-item)
SB: class uil-selection-button;
SA: item-or-value;
NotifD, D: class uil-dialog;
NB: class uil-navigation-button;
MN: class module-information;
NotifT: class uil-text;
m: symbol;
create-module-proc: symbol = the symbol g2-create-module;
new-found: truth-value = false;
new-SWS, SWS, WS: class kb-workspace;
NewStorageWS: item-or-value;
cc: class uil-configuration-class;
cc-count: integer;
confirm: text;
module-assignment-exists: truth-value;

storage-bin-name, button-bin-name, dialog-bin-name, scroll-bin-name, configuration-bin-name, new-configuration-bin-name: symbol;
storage-bin, button-bin, dialog-bin, scroll-bin, configuration-bin, new-configuration-bin: class uil-navigation-button;

move-scroll-area-proc: symbol = the symbol upp-move-scroll-area;
remove-dangling-definitions-proc: symbol = the symbol upp-remove-dangling-definitions-for-custom-scroll-areas;

SAClass: symbol = the symbol uil-scroll-area;
DEBUG: truth-value = false;
begin
{--
    1. Re-modularize kb-workspaces
    2. Move all uil-selection-buttons, uil-scroll-areas, uil-dialogs
    3. Move all uil-configuration-objects
    4. Remove old bins if empty. If not empty, ERROR!

    Note that if old-module-name = new-module-name, a consistency enforcement operation is done rather than a remodularization.
--}

{***** Start Setup Process *****}

    {-- Set up notification dialog --}
    NotifD = call uil-post-notification-dialog ("", the symbol medium, W, the symbol uil-information-icon); 
    NotifT = the uil-text upon the subworkspace of NotifD;

    confirm = call uil-post-generic-dialog ("uil-utility-change-module will change the module assignment of every workspace residing in <[old-module-name]> to reside in <[new-module-name]>.[crlf][crlf]Continue with module name change?", the symbol confirmation, the symbol medium, W, the symbol uil-question-icon);

    if confirm /= "yes" then
        begin
        call upp-post-inform-dialog ("Aborting this invocation of uil-utility-change-module, as per user request. ", W);
        go to exit-processing;
        end;

    {-- Check to see if new-module-name exists --}
    for MN = each module-information do
        M = the top-level-module of MN;
        if M = new-module-name then 
            begin
            new-found = true;    
            exit if true;
            end;
        end;

    if not (new-found) and (the procedure named by create-module-proc exists) then
        begin
        confirm = call upp-post-confirm-dialog ("The module, [new-module-name], does not exist. Can not continue with module renaming unless this module is created. Would you like to create this module and continue with the renaming process?", W);

        if confirm /= "yes" then 
            go to exit-processing
        else
            call the procedure named by create-module-proc (new-module-name);
        end;

    {-- Make sure that the top-level bin workspace exists --}
    NewStorageWS = the symbol not-found;
    for WS = each kb-workspace do
        allow other processing;
        if (the item superior to WS does not exist) then
            begin
            m = call utl-get-module-owning-this-workspace (WS);
            if (m = new-module-name) and (there exists a uil-navigation-button NB upon WS such that (the name of NB exists and "[the name of NB]" = "[new-module-name]-storage-bins")) then 
                begin
                NewStorageWS = WS;
                exit if true;
                end; 
            end;
        end; {-- for --}

    if (NewStorageWS is not an item) then
        begin
        for MN = each module-information do
            M = the top-level-module of MN;
            if M = new-module-name then
                begin
                new-found = true;    
                exit if true;
                end;
            end; 

        if (new-found) then
            call upp-generate-application-components-for-module (MN)
        else
            begin
            call uil-update-notification-text (NotifD, NotifT, "No module-information object exists for [new-module-name], aborting module renaming.");
            go to exit-processing;
            end;
        end;
{***** End Setup Process *****}

{***** Start Processing Modules *****}
    if not (old-module-name = new-module-name) then
        begin
        call uil-update-notification-text (NotifD, NotifT, "Beginning to restructure application modules. (This procedure might take a few minutes. Note also that there may logbook errors when attempting to access the module-assignment attribute of certain workspaces. These errors can be ignored.[crlf][crlf]A message will be displayed when this process is complete.)" ); 
        end
    else
        begin
        call uil-update-notification-text (NotifD, NotifT, "Beginning to check module consistency of bins. This involves visiting each item requiring a bin (scroll-area, dialogs, and configurations) and ensures that these are all properly modularized. [crlf][crlf]A message will be displayed when this process is complete.");        
        end;

{-- Workspaces --}
    if not (old-module-name = new-module-name) then 
        begin
        for WS = each kb-workspace do
            allow other processing;
            if (the item superior to WS does not exist) then            
                begin
                m = call utl-get-module-owning-this-workspace (WS);
                if (m = old-module-name) and not (there exists a uil-navigation-button NB upon WS such that (the name of NB exists and "[the name of NB]" = "[old-module-name]-storage-bins")) then 
                    begin
                    change the text of the module-assignment of WS to "[new-module-name]";
                    call upp-make-item-permanent (WS);
                    end;
                end;
            end; {-- for --}

        call uil-update-notification-text (NotifD, NotifT, "Finished remodularizing affected top-level workspaces.");       
        end;

{-- Dialogs --}
    call uil-update-notification-text (NotifD, NotifT, "Checking uil-dialogs for module consistency.");
    dialog-bin-name = text-to-symbol ("[new-module-name]-DIALOG-BIN");
    dialog-bin = the uil-navigation-button named by dialog-bin-name;
    
    for D = each uil-dialog do
        allow other processing;
        if (the uil-dialog-home-module-name of D = old-module-name) then
            conclude that the uil-dialog-home-module-name of D = new-module-name;
        call uil-store-dialog-in-home-bin (D);
        end;
    call upp-cleanup-bin (the subworkspace of dialog-bin, 50, 75, 10); 

{-- Scroll Areas --}
    if (the object-definition named by SAClass exists) then
        begin
        call uil-update-notification-text (NotifD, NotifT, "Checking uil-scroll-areas for module consistency."); 

        for SA = each item that is of the class named by SAClass do
            allow other processing;
            if (the class of SA is not uil-scroll-area and the procedure named by move-scroll-area-proc exists) then
                SA = call the procedure named by move-scroll-area-proc (SA, false);
            end;

        if (the procedure named by remove-dangling-definitions-proc exists) then
            call the procedure named by remove-dangling-definitions-proc ();
        end;
 
{-- Selection Buttons --}
    call uil-update-notification-text (NotifD, NotifT, "Checking uil-selection-buttons for module consistency."); 

    button-bin-name = text-to-symbol ("[new-module-name]-BUTTON-BIN");
    button-bin = the uil-navigation-button named by button-bin-name;
    
    for SB = each uil-selection-button do
        allow other processing;
        call upp-move-button (SB, true);
        end;
    call upp-cleanup-bin (the subworkspace of button-bin, 50, 75, 10);

{-- We might be done if module names are the same --}
    if (old-module-name = new-module-name) then 
        begin
        call uil-update-notification-text (NotifD, NotifT, "Finished checking for UIL module consistency.");
        go to exit-processing;
        end;

{-- Configurations --}
    call uil-update-notification-text (NotifD, NotifT, "Beginning to remodularize affected uil-configuration-classes."); 
    configuration-bin-name = symbol ("[old-module-name]-CONFIGURATIONS");
    if (the uil-navigation-button named by configuration-bin-name exists) then
        begin
        configuration-bin = the uil-navigation-button named by configuration-bin-name;

        new-configuration-bin-name = symbol ("[new-module-name]-CONFIGURATIONS");
        new-configuration-bin = the uil-navigation-button named by new-configuration-bin-name;
        if (the subworkspace of configuration-bin exists) then
            begin
            SWS = the subworkspace of configuration-bin;
            new-SWS = the subworkspace of new-configuration-bin;
            cc-count = the count of each uil-configuration-class upon SWS;
            if (cc-count > 0) then
                begin
                for cc = each uil-configuration-class upon SWS do
                    call upp-place-new-configuration-into-bin (cc, new-SWS);
                    end;
                
                call uil-update-notification-text (NotifD, NotifT, "BIN: <[configuration-bin-name]> contained [cc-count] configurations that were moved to <[new-configuration-bin-name]>.");                
                end; {-- cc-count > 0 --}  
            end; 
        end;
    
    {-- Delete old module? --}
    confirm = call uil-post-generic-dialog ("Done renaming modules and transfering uil-objects. Would you like to delete module [old-module-name] and its associated workspaces?", the symbol confirmation, the symbol medium, W, the symbol uil-question-icon);

    if confirm = "yes" then
        call g2-delete-module (old-module-name, true);
    
exit-processing:
    call uil-update-notification-text (NotifD, NotifT, "Finished restructuring application modules.");
    wait for 2 seconds;
    call uil-remove-notification-dialog (NotifD, W);
end

uil-unselect-button (B: class uil-button)
begin
    {-- This only works for pushbuttons --}
    if (the uil-button-selection-status of B exists) then
        begin
        if (GUIDE-DEBUG-BUTTON-SELECTION) then
            inform the operator that "Running uil-unselect-button for: Label: <[(if the label of B exists then the label of B else "")]>  Id: <[(if the id of B exists then the id of B else "")]>";

        conclude that the uil-button-selection-status of B is unselected;
        call uil-reset-button(B);
        end;
end

uil-get-configuration (G: class item) = (item-or-value)
BOX: class uil-selection-box;
SA: item-or-value;
ITM, ITM2: item-or-value;
SAClass: symbol = the symbol uil-scroll-area;
MsgClass: symbol = the symbol uil-message-object;
SAConfigClass: symbol = the symbol uil-scroll-configuration-class;
MsgConfigClass: symbol = the symbol uil-scroll-message-configuration-class;
SADefMsgConfig: symbol = the symbol uil-default-scroll-message-configuration;
SADefSAConfig: symbol = the symbol uil-default-scroll-configuration;
DEBUG: truth-value = false;
en: symbol; et: text;
begin
if (DEBUG) then
    inform the operator that "Running uil-get-configuration";

{-- Navigation Button --}
    if (G is a uil-navigation-button) then 
        begin
        if the configuration of G exists and the uil-navigation-button-configuration-class named by the configuration of G exists then
            return the uil-navigation-button-configuration-class named by the configuration of G
        else if uil-navigation-button-configuration exists then
            return uil-navigation-button-configuration
        else
            return the symbol configuration-not-found;
        end;

{-- Icon Toggle Button --}
    if (G is a uil-icon-toggle-button) then 
        begin
        if the configuration of G exists and the uil-icon-toggle-button-configuration-class named by the configuration of G exists then
            return the uil-icon-toggle-button-configuration-class named by the configuration of G
        else if uil-icon-toggle-button-configuration exists then
            return uil-icon-toggle-button-configuration
        else
            return the symbol configuration-not-found;
        end;

{-- Text Toggle Button --}
    if (G is a uil-text-toggle-button) then 
        begin
        if the configuration of G exists and the uil-text-toggle-button-configuration-class named by the configuration of G exists then
            return the uil-text-toggle-button-configuration-class named by the configuration of G
        else if uil-text-toggle-button-configuration exists then
            return uil-text-toggle-button-configuration
        else
            return the symbol configuration-not-found;
        end;

{-- Icon Pushbutton --}
    if (G is a uil-icon-pushbutton) then 
        begin
        if the configuration of G exists and the uil-icon-pushbutton-configuration-class named by the configuration of G exists then
            return the uil-icon-pushbutton-configuration-class named by the configuration of G
        else if uil-icon-pushbutton-configuration exists then
            return uil-icon-pushbutton-configuration
        else
            return the symbol configuration-not-found;
        end;

{-- Text Pushbutton --}
    if (G is a uil-text-pushbutton) then 
        begin
        if the configuration of G exists and the uil-text-pushbutton-configuration-class named by the configuration of G exists then
            return the uil-text-pushbutton-configuration-class named by the configuration of G
        else if uil-text-pushbutton-configuration exists then
            return uil-text-pushbutton-configuration
        else
            return the symbol configuration-not-found;
        end;

{-- Radio Button --}
    if (G is a uil-radio-button) then 
        begin
        if the configuration of G exists and the uil-radio-button-configuration-class named by the configuration of G exists then
            return the uil-radio-button-configuration-class named by the configuration of G
        else if the uil-selection-box that is the-group-master-of G exists then 
            begin
            BOX = the uil-selection-box that is the-group-master-of G;
            if the configuration of BOX exists and the uil-radio-button-configuration-class named by the configuration of BOX exists then
                return the uil-radio-button-configuration-class named by the configuration of BOX
            end
        else if uil-radio-button-configuration exists then
            return uil-radio-button-configuration
        else
            return the symbol configuration-not-found;
        end;

{-- Check Button --}
    if (G is a uil-check-button) then 
        begin
        if the configuration of G exists and the uil-check-button-configuration-class named by the configuration of G exists then
            return the uil-check-button-configuration-class named by the configuration of G
        else if the uil-selection-box that is the-group-master-of G exists then 
            begin
            BOX = the uil-selection-box that is the-group-master-of G;
            if the configuration of BOX exists and the uil-check-button-configuration-class named by the configuration of BOX exists then
                return the uil-check-button-configuration-class named by the configuration of BOX
            end
        else if uil-check-button-configuration exists then
            return uil-check-button-configuration
        else
            return the symbol configuration-not-found;
        end;

{-- Dialog --}
    if (G is a uil-dialog) then 
        begin
        if the configuration of G exists and the uil-dialog-configuration-class named by the configuration of G exists then
            return the uil-dialog-configuration-class named by the configuration of G
        else if uil-dialog-configuration exists then
            return uil-dialog-configuration
        else
            return the symbol configuration-not-found;
        end;

{-- Edit Box --}
    if (G is a uil-edit-box) then 
        begin
        if the configuration of G exists and the uil-edit-box-configuration-class named by the configuration of G exists then
            return the uil-edit-box-configuration-class named by the configuration of G
        else if uil-edit-box-configuration exists then
            return uil-edit-box-configuration
        else
            return the symbol configuration-not-found;
        end;

{-- Text --}
    if (G is a uil-text) then 
        begin
        if the configuration of G exists and the uil-text-configuration-class named by the configuration of G exists then
            return the uil-text-configuration-class named by the configuration of G
        else if uil-text-configuration exists then
            return uil-text-configuration
        else
            return the symbol configuration-not-found;
        end;

{-- Borders & Separators --}
    if (G is a uil-box-border or G is a uil-line-separator) then 
        begin
        if the configuration of G exists and the uil-line-and-border-configuration-class named by the configuration of G exists then
            return the uil-line-and-border-configuration-class named by the configuration of G
        else if uil-line-and-border-configuration exists then
            return uil-line-and-border-configuration
        else
            return the symbol configuration-not-found;
        end;

{-- Scroll-Area --}
    if (the object-definition named by SAClass exists and G is an instance of the class named by (SAClass)) then 
        begin
        if (the configuration of G exists and the object-definition named by SAConfigClass exists and the item ITM named by the configuration of G exists and ITM is an uil-scroll-configuration-class) then
            return ITM
        else if (the item named by SADefSAConfig exists) then
            return the item named by SADefSAConfig 
        else
            return the symbol configuration-not-found;
        end;

{-- Message Object --}
    if (the message-definition named by MsgClass exists and G is an instance of the class named by (MsgClass)) then 
        begin
        if (DEBUG) then
            inform the operator that "case of message-object";

        if (the configuration of G exists and the object-definition named by MsgConfigClass exists and the item ITM named by the configuration of G exists and ITM is an uil-scroll-message-configuration-class) then
            begin
            if (DEBUG) then
                inform the operator that "MO's config exists: returning [the configuration of G]";
            return ITM;
            end

        else if (the item that is the-handler-for-the-message-of G exists) then
            begin
            SA = the item that is the-handler-for-the-message-of G;

            if (DEBUG) then
                inform the operator that "config for SA: [the configuration of SA]";

            if (the configuration of SA exists) and (the object-definition named by SAConfigClass exists and the item ITM named by the configuration of SA exists and ITM is an uil-scroll-configuration-class) and (the object-definition named by MsgConfigClass exists and the uil-scroll-message-configuration of ITM exists) and (the object-definition named by MsgConfigClass exists and the item ITM2 named by the uil-scroll-message-configuration of ITM exists) then
                return ITM2;
            end
        else if (the item named by SADefMsgConfig exists) then
            begin
            if (DEBUG) then
                inform the operator that "default case";
            return the item named by SADefMsgConfig;
            end
        else
            return the symbol configuration-not-found;
        end

{-- Else --}
    else if (the configuration of G exists) then
        return the uil-configuration-class named by the configuration of G;
 
return the symbol configuration-not-found;

end 

on error (en, et)
return the symbol configuration-not-found; 
end

upp-extended-configuration (grobj: class item)
C: item-or-value;
en: symbol;
et: text;
begin
    C = call uil-get-configuration (grobj); 
    if (C is an item) then
        begin
        {change the uil-button-face-color icon-color of grobj to the uil-selected-region-color of C;}
        end;
end 
on error (en, et) 
end

upp-reposition-related-text-for-edit-box (EB: class uil-edit-box) 
WKSP: class kb-workspace;
T: item-or-value;
X, Y, W, H, T-W, T-H, X-offset, Y-offset: integer;
begin 

    if (the label of EB exists) and (the workspace of EB exists) then 
        begin
        WKSP = the workspace of EB;
       
        T = call uil-get-label-text (EB);
        if (T exists and T is a uil-text) then
            begin
            X = the item-x-position of EB;
            Y = the item-y-position of EB;
            W = the item-width of EB;
            H = the item-height of EB;

            {-- Get Offsets --}
            T-W = the item-width of T;
            T-H = the item-height of T;
            X-offset = round ((W/2) + (T-W/2) + 5);
            Y-offset = 0;
            {This might be how we do this in the future when we get smarter.
              Y-offset = round ((H / 2) - (T-H / 2) + 5);}

            make T transient;

            transfer T to WKSP at (X - X-offset, Y + Y-offset);

            move EB to (X, Y);

            if (the uil-is-permanent of EB is true) then
                begin
                call upp-make-item-permanent (EB); 
                call upp-make-item-permanent (T);
                end;
            end
        end;
end

uil-update-edit-box-label (EB: class item)
T: item-or-value;
DEBUG: truth-value = false;
begin
if (DEBUG) then
    inform the operator that "Running uil-update-edit-box-label";

   { if (the label of EB exists) then
        begin
        T = call uil-get-label-text (EB);
        if not (T exists and T is a uil-text) then
            call upp-make-text-for-edit-box (EB);

        T = call uil-get-label-text (EB);}

    if (the label of EB exists) then
        begin
        if (DEBUG) then inform the operator that "label exists";
        T = call uil-get-label-text (EB);
        if not (T exists and T is a uil-text) then begin
            if (DEBUG) then inform the operator that "T doesn't exist"; 
            call upp-make-text-for-edit-box (EB);
            T = call uil-get-label-text (EB);
        end;

        if (T exists and T is a uil-text) then
            begin 
            call uil-update-text (T, the label of EB);
            call upp-reposition-related-text-for-edit-box (EB); 
            end;
   
        if (the workspace of EB exists) then
            begin
            call uil-manage-grobj-method (EB, the workspace of EB, the symbol none, the item-x-position of EB, the item-y-position of EB);
            call upp-move-edit-box (EB);
            end;

        if (the item superior to T exists and the item superior to T is permanent) and (the uil-is-permanent of EB exists and the uil-is-permanent of EB is true) then
            make T permanent;
        call uil-make-grobj-permanent (EB);
        end;
end

upp-move-edit-box (EB: class uil-edit-box) 
wksp: class kb-workspace;
trash-it: truth-value = false;
EB-deleted: truth-value = false;
S: symbol;
DEBUG: truth-value = false;
begin 
if (DEBUG) then
    inform the operator that "Running upp-move-edit-box -- edit-box is: [the label of EB]";

    wksp = the workspace of EB;
   
    {--- If the edit box has a text-relation, move the related text to be near the edit box ---}
    call upp-reposition-related-text-for-edit-box (EB);

    call uil-move-related-border (EB);
 
    {--- Check if a edit-box is moved over a garbage-pail.  guide-check-for-trash returns true if it deleted the edit-box. ---}
    S = the symbol guide-check-for-trash;
    if (the procedure named by S exists) then
        begin 
        EB-deleted = call the procedure named by S (EB, wksp);
        end;
end

upp-manage-edit-box-method (grobj: class uil-grmes, wksp: class kb-workspace, X: integer, Y: integer)
T: item-or-value;
label-x-offset, label-y-offset, size-x-offset, size-y-offset: integer;
current-x: integer;
DEBUG: truth-value = false;
begin
if (DEBUG) then
    inform the operator that "Running upp-manage-edit-box-method.";

    T = call uil-get-label-text (grobj);

    if (T exists and T is a uil-text) then
        begin
        if (DEBUG) then
            inform the operator that "T exists and is a uil-text";
        make T transient;
        end;

    make grobj transient;
    transfer grobj off;

    transfer grobj to wksp at (X, Y); 

    if (T exists and T is a uil-text) then
        begin
        if (DEBUG) then
            inform the operator that "calling upp-reposition-related-text-for-edit-box";
        call upp-reposition-related-text-for-edit-box (grobj);
        end;

    conclude that the uil-is-managed of grobj = 1;

    if (T exists and T is a uil-text and the uil-is-managed of T exists) then
        conclude that the uil-is-managed of T = 1;

    if (the uil-is-permanent of grobj is true) then 
        call upp-make-item-permanent (grobj);
end

uil-get-next-selection-button-y-position (ButtonLabel: class uil-text)= (integer)
Y: integer;
begin
    Y = the item-y-position of ButtonLabel;
    Y = truncate( Y - (the item-height of ButtonLabel/2) );
    return Y;
end

uil-create-text-for-button-from-label (B: class item, SIZE: symbol)
T: item-or-value;
TXT: text;
DEBUG: truth-value = false;
begin
if (DEBUG) then
    inform the operator that "Running uil-create-text-for-button-from-label: size: <[SIZE]>";

    {-- 9/20/94: this procedure was: uil-make-styled-text-for-button --}
    if (the label of B exists) then
        begin
        if (B is a uil-text-toggle-button or B is a uil-icon-toggle-button) then       
            begin
            if the toggle-state of B is off then 
                TXT = the label of B
            else 
                TXT = the togglelabel of B; 
            end

        else 
            TXT = the label of B;
       
        {skip if B is a edit-box, selection box, or MSW-style button}
        if (uil-use-icon-text-for-button-labels and B is not a uil-edit-box and B is not a uil-selection-box and not(is-contained-in-text ("msw", "[the uil-size of B]"))) then 
            begin
            if (the uil-label-size of B exists) then
                conclude that the uil-label-size of B = SIZE;
            conclude that the uil-label-id of B = "5.0";
            call upp-set-button-label (B, TXT);
            return;
            end;

{-- sdm 9/20/94 - put this in and back it out, problems resulted.
        T = call uil-get-label-text (B);
        if (T exists and T is a uil-text) then
            call uil-delete-grobj-method (T);

        call uil-add-label-to-button (B, TXT, SIZE);       
--}

        case (size) of 
            SMALL: create a uil-small-text T;
            MEDIUM: create a uil-medium-text T;
            LARGE: create a uil-large-text T;
            otherwise: signal the symbol uil-internal-error, uil-internal-error-unknown-button-size(size);
        end;

        if (DEBUG) then
            inform the operator that "TXT: <[TXT]> - T is a: [the class of T], uil-size of T: [the uil-size of T]";

        call uil-update-text (T, TXT);

{-- Remove this before 4.0 --}
        call upp-bind-symbol-name-for-label (B, T);

        call upp-bind-label-to-object (B, T);       

        if (DEBUG) then
            inform the operator that "Leaving uil-create-text-for-button-from-label <[TXT]>. T is a: [the class of T]  uil-size: [the uil-size of T]";
        end;  
end

upp-reposition-related-text-for-button (button: class item) 
wksp: class kb-workspace;
T: item-or-value;
x, y, x-offset, y-offset: integer;
DEBUG: truth-value = false;
begin
if (DEBUG) then
    inform the operator that "Running upp-reposition-related-text-for-button";

    if (the label of button exists) then 
        begin
        if (the workspace of button exists) then
            begin
            wksp = the workspace of button;

            x = the item-x-position of button;
            y = the item-y-position of button;
            x-offset, y-offset = call upp-get-button-label-offsets (button);

            T = call uil-get-label-text (button);
            if (T exists and T is a uil-text) then
                begin
                if (button is a uil-text-button) then
                    begin
                    make T transient;
                    { make button transient; }
                    transfer T to wksp at (the item-x-position of button, the item-y-position of button);
                    { transfer} move button to {wksp at} (x,y); 
                    if (the uil-is-permanent of button is true) then
                        begin
                        call upp-make-item-permanent (button); 
                        call upp-make-item-permanent (T);
                        end;
                    end 
                else
                    begin
                    {  make button transient; }
                    make T transient;

                    { transfer } move button to {wksp at} (x,y); 
                    transfer T to wksp at (x + x-offset, y + y-offset);
                    
                   if (the uil-is-permanent of button is true) then
                        begin
                        call upp-make-item-permanent (button); 
                        call upp-make-item-permanent (T); 
                        end;
                    end;      
                end; {-- button label text exists --}
            end;   
        end;
end

uil-select-text (T: class uil-text, W: class ui-client-item)
B: class uil-button;
DEBUG: truth-value = false;
begin
if (DEBUG) then
    inform the operator that "Running select text which will run select-button for: [the text of T]";
 
    {-- Select the button if the text belongs to one. --}
    if (the uil-button that is the-uil-button-owning-this-text-label T exists) then
        begin
        {inform the operator that "Found a related button for this text [the text of t]";}
        B = the uil-button that is the-uil-button-owning-this-text-label T;
        call uil-select-button (B, W, the workspace of T);
        end
    else
        begin
        if (DEBUG) then
            inform the operator that "calling utl-lift-to-top from uil-select-text";
        call utl-lift-to-top (T, W);
        end;
end

uil-return-size-of-button-as-symbol (button:class item) = (symbol)
  size: symbol;
begin
  case (the class of button) of

  {-- Motif Sizes --}
    uil-small-text-pushbutton: size = the symbol SMALL;
    uil-small-text-toggle-button: size = the symbol SMALL;

    uil-medium-text-pushbutton: size = the symbol MEDIUM;
    uil-medium-text-toggle-button: size = the symbol MEDIUM;

    uil-large-text-pushbutton: size = the symbol MEDIUM;
    uil-large-text-toggle-button: size = the symbol MEDIUM;

    uil-radio-button-large: size = the symbol MEDIUM;
    uil-radio-button-medium: size = the symbol MEDIUM;
    uil-radio-button-small: size = the symbol SMALL;
    uil-radio-button: size =  the symbol MEDIUM;

    uil-check-button-large: size = the symbol MEDIUM;
    uil-check-button-medium: size = the symbol MEDIUM;
    uil-check-button-small: size = the symbol SMALL;
    uil-check-button: size =  the symbol MEDIUM;

    uil-small-icon-toggle-button: size = the symbol SMALL;
    uil-medium-icon-toggle-button: size = the symbol MEDIUM;
    uil-large-icon-toggle-button: size = the symbol MEDIUM;

    uil-small-icon-pushbutton: size = the symbol SMALL;
    uil-medium-icon-pushbutton: size = the symbol MEDIUM;
    uil-large-icon-pushbutton: size = the symbol MEDIUM;

  {-- MSW Sizes --}
    uil-small-text-pushbutton-msw: size = the symbol SMALL;
    uil-small-text-toggle-button-msw: size = the symbol SMALL;

    uil-medium-text-pushbutton-msw: size = the symbol MEDIUM;
    uil-medium-text-toggle-button-msw: size = the symbol MEDIUM;

    uil-large-text-pushbutton-msw: size = the symbol LARGE;
    uil-large-text-toggle-button-msw: size = the symbol LARGE;

    uil-radio-button-large-msw: size = the symbol MEDIUM;
    uil-radio-button-medium-msw: size = the symbol MEDIUM;
    uil-radio-button-small-msw: size = the symbol SMALL;
    uil-radio-button-msw: size =  the symbol MEDIUM;

    uil-check-button-large-msw: size = the symbol MEDIUM;
    uil-check-button-medium-msw: size = the symbol MEDIUM;
    uil-check-button-small-msw: size = the symbol SMALL;
    uil-check-button-msw: size =  the symbol MEDIUM;

    uil-small-icon-toggle-button-msw: size = the symbol SMALL;
    uil-medium-icon-toggle-button-msw: size = the symbol MEDIUM;
    uil-large-icon-toggle-button-msw: size = the symbol LARGE;

    uil-small-icon-pushbutton-msw: size = the symbol SMALL;
    uil-medium-icon-pushbutton-msw: size = the symbol MEDIUM;
    uil-large-icon-pushbutton-msw: size = the symbol LARGE;

    otherwise: {A safe default} size = the symbol MEDIUM ;
  end;
  return size;
end

utl-get-module-information (S: symbol) = (item-or-value)
M: class module-information;
begin
    for M = each module-information do
        if the text of the top-level-module of M = "[S]" then
            return M;
    end;

    return the symbol not-found;
end

upp-lineup-selection-buttons (B: class uil-button)
BOX: class uil-selection-box;
SB: item-or-value;
MEMS: class text-array;
DEBUG: truth-value = false;
begin
if (DEBUG) then
    inform the operator that "Running upp-lineup-selection-buttons.";

    BOX = the uil-selection-box that is the-group-master-of B;
    MEMS = the members of BOX;
    if (the array-length of MEMS > 1) then
        begin
        SB = call uil-get-selection-button ("[MEMS[0]]");
        if (SB exists and SB is a uil-selection-button) and (B is the same object as SB) then
            call uil-manage-grobj-method (BOX, the workspace of B, the symbol none, the item-x-position of B, the item-y-position of B);
        end;

if (DEBUG) then
    inform the operator that "Leaving upp-lineup-selection-buttons.";
end

upp-unique-identifier-generator (prefix: text, mn: symbol) = (text)
CTime, T: text;
S: symbol;
Len, pos, I: integer;
G: class item;
Q: quantity;
SAClass: symbol = the symbol uil-scroll-area;
DEBUG: truth-value = false;
begin
{-- A unique identifier implies uniqueness across module boundaries. When applications are combined, the uniqueness must be preserved. Thus a combination of module and random numbers will further uniquify the identifier. --}

    repeat
        I = random (999999);

        if mn = the symbol uil-none then 
            T = "[prefix][I]"
        else
            T = "[mn]-[prefix][I]";

        { GENSYMCID-1656: Dalkia Memory Leak - Dump files analysis
          Here, to generate unique symbols, current month and year were appended, however this will cause
          symbols that can never been reused in the future. And G2 cannot reclaim symbols, as a result it's
          a huge memory leak. The current solution is to remove these date-dependent parts.
          -- Chun Tian (binghe), Mar 2016. }

{ -- Begin of commented code of GENSYMCID-1656 --

        Q = call uil-utility-return-current-real-time ();
        CTime = "[Q as a time stamp]";

        if (DEBUG) then
            inform the operator that "CTime: [CTime]";

check-for-blanks:
        pos = position-of-text(" ", CTime);
        if (pos /= 0) then
            begin
            CTime = omit-from-text (CTime, pos, pos);
            go to check-for-blanks;
            end;

check-for-colons:
        pos = position-of-text(":", CTime);
        if (pos /= 0) then
            begin
            CTime = omit-from-text (CTime, pos, pos);
            go to check-for-colons;
            end;

        Len = length-of-text (CTime);
        CTime = omit-from-text (CTime, Len - 3, Len);
        
        if (DEBUG) then
            inform the operator that "CTime = [CTime]";

        T = "[T]-[CTime]";

 -- End of commented code of GENSYMCID-1656 -- }

        {S = symbol(T);
 
        if the object named by S does not exist and 
           the message named by S does not exist and
           not (there exists a uil-grobj G such that (the id of G = T)) and 
           not (there exists a uil-grmes G such that (the id of G = T)) and 
           not (the object-definition named by SAClass exists and there exists an item G that is of the class named by SAClass such that (the saved-scroll-area-key of G = T)) then
            return t;}

        if  not (there exists a uil-grobj G such that (the id of G = T)) and 
           not (there exists a uil-grmes G such that (the id of G = T)) and 
           not (the object-definition named by SAClass exists and there exists an item G that is of the class named by SAClass such that (the saved-scroll-area-key of G = T)) then
            return t; 
    end;
end

uil-utility-unique-name (prefix: text) = (text)
T: text;
S: symbol;
mn: symbol = symbol (the text of the top-level-module of module-information);
begin
    T = "[mn]";

check-for-quantity:
    if (length-of-text (T) > 0) then
        begin
        if (text-begins-with-quantity (T)) then
            begin
            T = omit-from-text (T, 1, 1);
            go to check-for-quantity;
            end
        else
            begin
            mn = symbol (T);
            end;
        end
    else
        begin
        mn = symbol (prefix);
        end;

    T = call upp-unique-identifier-generator (prefix, mn);
    S = symbol(t);
    return T;
end

upp-get-button-label-offsets (button: class item) = (integer, integer)
T: item-or-value;
x-offset, y-offset: integer;
DEBUG: truth-value = false;

begin
if (DEBUG) then
    inform the operator that "Running upp-get-button-label-offsets";
T = call uil-get-label-text (button);
if (the label of button does not exist) or ((not (T exists and T is a uil-text)) and (the uil-button-label-color of button does not exist)) then
    return 0,0;

if (DEBUG) then
    inform the operator that "Finding offsets for a [the class of button]";

if (button is a uil-icon-button) then 
    begin
    if (DEBUG) then
        inform the operator that "Positioning label without hard-coded numbers";
    x-offset = truncate (the item-width of button/2);
    if (T exists and T is a uil-text) then
        x-offset = x-offset + truncate (the item-width of T/2);
    y-offset = 0;
    end
else if (button is a uil-radio-button-small or 
    button is a uil-check-button-small or 
    button is a uil-small-icon-pushbutton or 
    button is a uil-small-icon-toggle-button or 
    button is a uil-small-icon-pushbutton-msw or 
    button is a uil-small-icon-toggle-button-msw) then
    begin 
    if (DEBUG) then
        inform the operator that "Positioning label with hard-coded numbers:  small";
    x-offset = truncate (the item-width of button / 2) - 11;
    y-offset = 14;
    end
else if (button is a uil-radio-button-medium or 
    button is a uil-check-button-medium or 
    button is a uil-medium-icon-pushbutton or 
    button is a uil-medium-icon-toggle-button or 
    button is a uil-medium-icon-pushbutton-msw or 
    button is a uil-medium-icon-toggle-button-msw) then 
    begin
    if (DEBUG) then
        inform the operator that "Positioning label with hard-coded numbers:  medium";
    x-offset = truncate (the item-width of button / 2) - 14;
    y-offset = 17;
    end
else if (button is a uil-radio-button-large or 
    button is a uil-check-button-large or 
    button is a uil-large-icon-pushbutton or 
    button is a uil-large-icon-toggle-button or 
    button is a uil-large-icon-pushbutton-msw or 
    button is a uil-large-icon-toggle-button-msw) then 
    begin
    if (DEBUG) then
        inform the operator that "Positioning label with hard-coded numbers:  large";
    x-offset = -3; 
    y-offset = 16;
    end
else
    begin
    x-offset = 0;
    if (T exists and T is a uil-text) then
        y-offset = the item-height of T
    else 
        y-offset = the item-height of button;
    end;

return x-offset, y-offset;
end

upp-make-text-for-button (button:class item)
T: class uil-text;
size: symbol;
nm, txt: text;
begin

    if (the label of button exists) then
        begin
        {-- can't use text-in-icons for MSW classes, also, watch out for user-subclassed buttons still in the old style!!! --}
        if (uil-use-icon-text-for-button-labels) and (the uil-button-label-value of the icon-variables of button exists) and ((the uil-size of button = the symbol small) or (the uil-size of button = the symbol medium) or (the uil-size of button = the symbol large) or (the uil-size of button = the symbol unspecified)) then
            begin
            conclude that the uil-label-id of button = "5.0";
            call upp-update-button-label (button);
            return;
            end;

        size = call uil-return-size-of-button-as-symbol (button);
        if (button is a uil-text-toggle-button) or (button is a uil-icon-toggle-button) then 
            begin
            if (the toggle-state of button = the symbol off) then 
                txt = the label of button
            else 
                txt = the togglelabel of button; 
            end
        else 
            begin
            txt = the label of button;
            end;

        case (size) of 
            SMALL: create a uil-small-text T;
            MEDIUM: create a uil-medium-text T;
            LARGE: create a uil-large-text T;
            otherwise: signal the symbol uil-internal-error, uil-internal-error-unknown-button-size(size);
        end;

        call uil-update-text (T, txt);

    {-- Enforce non-text-swipable button labels --}
        {call upp-restrict-text-swiping-for-button-label (T);}

{-- This needs to be removed before 4.0 --}
        call upp-bind-symbol-name-for-label (button, T);

        call upp-bind-label-to-object (button, T);
        end; 
end

upp-make-item-permanent (IT: class item)
en: symbol; et: text;
DEBUG: truth-value =false;
begin
if (DEBUG) then
    inform the operator that "Running upp-make-item-permanent for: [the class of IT], [the containing-module of IT]";

    make IT permanent;       
    if the uil-is-permanent of IT exists then 
        conclude that the uil-is-permanent of IT is true;
end
on error (en, et)
if (DEBUG) then
    inform the operator that "(ok error) [en] [et]";
end

g2:c.METHOD.g2:a.TEXT:
gfr-initialize (Itm : class guide-root , Win : class ui-client-item)
    P : class guide-garbage-pail;
    Delete : truth-value = false;
    X, Y : integer;
    Wksp : class kb-workspace;
    I : class item;
    NewItm : item-or-value;
    GuidePalette : symbol = the symbol G2-GUIDE;
    TabPageDef : symbol = the symbol uil-tab-page;
    AdjustTabPageProc : symbol = the symbol upp-adjust-tab-page-contents-list-if-appropriate;
    DEBUG: truth-value = false;
begin
    if DEBUG then inform the operator that "called initialize method for [the class of Itm]";
    Wksp = the workspace of Itm;
    if there exists a guide-garbage-pail P upon Wksp and Itm is not the same object as P then 
        Delete = call guide-is-contained-in(Itm, P);

    if Delete then begin
        make Itm transient;
        delete Itm;
        return;
    end;
    
    if the item named by GuidePalette exists and Wksp is the same object as the item named by GuidePalette then return;
    if the item I superior to Wksp exists then begin
        if the workspace of I exists and the item named by GuidePalette exists and the workspace of I is the same object as the item named by GuidePalette then
            return;
    end;

    X = the item-x-position of Itm;
    Y = the item-y-position of Itm;

    NewItm = call upp-replace-template-with-grobj(Itm, Wksp, X, Y);

{cmh: 6/11/97}
    if NewItm is an item then call upp-adjust-dialog-grobj-list-as-appropriate (NewItm);

    if NewItm is an item and the definition named by TabPageDef exists and there exists a uil-grmes G upon Wksp such that (the class of G = TabPageDef) then 
         call the procedure named by AdjustTabPageProc(NewItm);

    if Itm is not a guide-garbage-pail then begin
        make Itm transient;
        delete Itm;
    end;
end
