g2:c.PROCEDURE.g2:a.TEXT:
uil-raise-tab-page-to-top (TabDialog : class uil-tab-dialog, TabPage : class uil-tab-page, W : item-or-value)
TabButton : class uil-tab-pushbutton;
debug: truth-value = false;
begin
if debug then 
  inform the operator that "Running uil-raise-tab-page-to-top.";

if the uil-tab-pushbutton that is the-tab-button-of-the-tab-page TabPage exists then
  begin
    TabButton = the uil-tab-pushbutton that is the-tab-button-of-the-tab-page TabPage;
    {call g2-lift-item-to-top (TabPage);}
    call uil-configure-grobj (TabPage);
    call g2-lift-item-to-top (TabPage); 
    call g2-work-on-drawing (TabPage);
    call g2-lift-item-to-top (TabButton);
    if W is a ui-client-item then
      begin
        call upp-deactivate-field-edit-for-tab-page-if-appropriate (TabPage, W);
        call upp-disallow-all-edit-fields-for-dialog (TabDialog, W);
      end;
    call upp-raise-tab-page-contents-to-top (TabPage, W);
    if W is a ui-client-item then
      begin
        call upp-allow-field-edit-for-tab-page (TabPage, W);
        if the uil-origin of TabDialog /= the symbol master then
            call upp-activate-field-edit-for-tab-page-if-appropriate (TabPage, W);
      end;
  end; 

end

uil-remove-tab-page-from-dialog (TabPage: class uil-tab-page, D: class uil-tab-dialog)

Page: class uil-tab-page;

begin

call upp-remove-element-from-list (TabPage, the uil-tab-page-list of D);

end

uil-move-grobj-to-tab-page-layer (G : Class item, tab-page : class uil-tab-page)
TPCL : class item-list = the uil-tab-page-contents-list of tab-page;
itm, itm2, itm3 : class item;
raise-scroll-components-proc : symbol = the symbol upp-raise-scroll-area-components; 
SAClass: symbol = the symbol uil-scroll-area;
DEBUG: truth-value = false;
begin
{-- SCROLL-AREA --}
if G is an instance of the class named by (SAClass) and the procedure named by raise-scroll-components-proc exists then
  begin
    if debug then 
      inform the operator that "In uil-move-grobj-to-tab-page-layer >>[crlf]Adding> [the class of G]"; 
    call g2-lift-item-in-front-of (G, tab-page); 
    call the procedure named by raise-scroll-components-proc (G);
    end 
  else

{-- SELECTION-BOX --}
if G is a uil-selection-box then 
  begin 
    if debug then 
      inform the operator that "In uil-move-grobj-to-tab-page-layer >>[crlf]Adding> [the class of G]"; 
    call g2-lift-item-in-front-of (G, tab-page); 
    for itm = each uil-selection-button that is grouped-by G do 
      call g2-lift-item-in-front-of (itm, G); 
      if the uil-text that is the-uil-text-label-of itm exists then
        call g2-lift-item-in-front-of (the uil-text that is the-uil-text-label-of itm, itm); 
    end; 
  end
  else

{-- BUTTON --}
if G is a uil-button then
  begin 
    if debug then 
      inform the operator that "In uil-move-grobj-to-tab-page-layer >>[crlf]Adding> [the class of G]"; 
    if the uil-text that is the-uil-text-label-of G exists then
        call g2-lift-item-in-front-of (the uil-text that is the-uil-text-label-of G, G); 
  end

{-- EDIT-BOX, SPIN CONTROL, COMBO-BOX --}
else
if G is a uil-edit-box then
  begin 
    if debug then 
      inform the operator that "In uil-move-grobj-to-tab-page-layer >>[crlf]Adding> [the class of G]"; 
    call g2-lift-item-in-front-of (G, tab-page); 
    if the uil-text that is the-uil-text-label-of G exists then
        call g2-lift-item-in-front-of (the uil-text that is the-uil-text-label-of G, G);
  end
else

{-- TEXT --}
if G is a uil-text then
  begin 
    if debug then 
      inform the operator that "In uil-move-grobj-to-tab-page-layer >>[crlf]Adding> [the class of G]"; 
    call g2-lift-item-in-front-of (G, tab-page)
  end
else

{-- BORDER, SEPARATOR --}
if G is a uil-box-border or G is a uil-line-separator then
  begin 
    if debug then 
      inform the operator that "In uil-move-grobj-to-tab-page-layer >>[crlf]Adding> [the class of G]"; 
    for itm = each item connected to G do
        call g2-lift-item-in-front-of (itm, tab-page) 
    end;
    for itm = each connection at either end of G do
        call g2-lift-item-in-front-of (itm, tab-page) 
    end;

{    call g2-lift-item-in-front-of (itm, tab-page)}
    call g2-lift-item-in-front-of (G, tab-page);
  end

{-- DEFAULT --}
else
  begin 
    if debug then 
      inform the operator that "In uil-move-grobj-to-tab-page-layer >>[crlf]Adding> [the class of G]"; 
       call g2-lift-item-in-front-of (G, tab-page) 
    end;
end

uil-create-tab-page (id : text, size : symbol, style : symbol, label : text) = (class uil-tab-page)

tab-page : class uil-tab-page;
tab-page-button : class uil-tab-pushbutton;
type : symbol;
begin

create a uil-tab-page tab-page;
if id /= "" then
  change the text of the id of tab-page to id;
tab-page-button = call uil-create-tab-pushbutton (size, label); 
conclude that tab-page-button is the-tab-button-of-the-tab-page tab-page;
conclude that the uil-label-id of tab-page-button = "5.0";

return tab-page;

end

uil-remove-grobj-from-tab-page (G : Class item, tab-page : class uil-tab-page)
TPCL : class item-list = the uil-tab-page-contents-list of tab-page;
itm, itm2, itm3 : class item;
debug: truth-value = false;
begin

if debug then
    inform the operator that "Running uil-remove-grobj-from-tab-page for a [the class of G]";

call upp-remove-element-from-list (G, TPCL);

end

uil-add-grobj-to-tab-page (G : Class item, tab-page : class uil-tab-page)
TPCL : class item-list = the uil-tab-page-contents-list of tab-page;
CornerClass: symbol = the symbol guide-scroll-corner-object; 
DEBUG: truth-value = false;
begin
if debug then
    inform the operator that "Running uil-add-grobj-to-tab-page id: [the id of tab-page] -- for a <<[the class of G]>>"; 

    if (the class-definition named by CornerClass exists and G is an instance of the class named by (CornerClass)) then
        return;

    call upp-insert-element-into-list (G, TPCL);
    call uil-move-grobj-to-tab-page-layer (G, tab-page);
end

uil-create-tab-pushbutton (size: symbol, label : text) = (class uil-tab-pushbutton)
button : class uil-tab-pushbutton;
begin 

create a uil-tab-pushbutton button ; 
conclude that the uil-size of button = size;
conclude that the label of button = label;

return button;

end

uil-select-tab-page (D : class uil-tab-dialog, tab-page : class uil-tab-page, W : item-or-value) 
button : class uil-tab-pushbutton;
tab-dialog : class uil-tab-dialog;
debug: truth-value = false;
page-list: class g2-list;
other-page : class uil-tab-page; 
begin
if debug then
    inform the operator that "Running uil-select-tab-page.";
{if the item that is the-uil-selected-tab-page-of D exists and tab-page is the same object as the item that is the-uil-selected-tab-page-of D then
    return;}

conclude that tab-page is now the-uil-selected-tab-page-of D;

page-list = the uil-tab-page-list of D;
for other-page = each uil-tab-page in page-list do
    if other-page is not the same object as tab-page then
         call upp-hide-items-behind (tab-page);       
end; 

call uil-raise-tab-page-to-top (D, tab-page, W); 

end

uil-raise-tab-page-to-top-callback (B : class uil-button, W : class ui-client-item, D : class item)
tab-page : class uil-tab-page;
debug: truth-value = false;
begin
if D is a kb-workspace then
    D = the uil-dialog superior to D;
if the uil-tab-page that is the-tab-page-handled-by-the-tab-button B exists then
  begin
    if debug then
        inform the operator that "Running uil-raise-tab-page-to-top-callback on [the class of B]";
    tab-page = the uil-tab-page that is the-tab-page-handled-by-the-tab-button B;
    call uil-select-tab-page (D, tab-page, W);
  end;
end

uil-create-tab-dialog (id : text) = (class uil-tab-dialog)
DLG : class uil-tab-dialog;
WS : class kb-workspace;
begin

create a uil-tab-dialog DLG;
create a uil-dialog-subworkspace WS;
make WS the subworkspace of DLG;
change the text of the id of DLG to id;
return DLG;

end

uil-add-tab-page-to-dialog (TabPage: class uil-tab-page, D: class uil-tab-dialog) = (truth-value)

DT: class uil-dialog-title;
LEdge, TEdge, XPos, YPos: integer;
Page, XPage: class uil-tab-page;
TabButt, XButt: class uil-tab-pushbutton;
TPB: class uil-text-pushbutton;
WS: class kb-workspace = the subworkspace of D;
DEBUG: truth-value = false;

begin

if (DEBUG) then
    post "running uil-add-tab-page-to-dialog";
{-- calling upp-insert-element-into-list does nothing since the case of tab-page is ignored
call upp-insert-element-into-list (tab-page, the uil-tab-page-list of D);
--}

TabButt = the uil-tab-pushbutton that is the-tab-button-of-the-tab-page TabPage; 
if there exists a uil-tab-page XPage upon WS then begin
    XButt = the uil-tab-pushbutton that is the-tab-button-of-the-tab-page XPage;
    if the uil-size of TabButt /= the uil-size of XButt then
        return false;
    XPos = the item-x-position of XPage;
    YPos = the item-y-position of XPage;
end else if there exists a uil-text-pushbutton TPB upon WS and there exists a uil-dialog-title DT upon WS then begin
    XPos = the item-x-position of DT;
    YPos = the item-y-position of DT - floor ((the item-y-position of DT - the item-y-position of TPB) / 2) - 10;    {--- The 10 lowers to allow for the tab button ---}
end else begin
    XPos = 0;
    YPos = 0;
end; 

transfer TabPage to WS at (XPos, YPos);
insert TabPage at the end of the uil-tab-page-list of D;
insert TabButt at the end of the uil-dialog-grobj-list of D;
call upp-move-tab-page (TabPage);
call upp-make-tab-page-permanent (TabPage); 

if the number of elements in the uil-tab-page-list of D > 1 then begin
    for Page = each uil-tab-page in the uil-tab-page-list of D do
        call g2-change-size-of-item-per-area (TabPage, truncate (utl-left-edge (Page)), truncate (utl-top-edge (Page)), truncate (utl-right-edge (Page)), truncate (utl-bottom-edge (Page)), the symbol NONE);
        exit if true;
    end;
end;

if (DEBUG) then
    post "leaving uil-add-tab-page-to-dialog";
return true;

end

upp-align-tab-buttons (Dlg: class uil-tab-dialog)

TabPageList: class item-list; 
Page: class uil-tab-page;
X, Y, ItemCount: integer; 
Btn, PrevButton: class uil-tab-pushbutton; 

begin

TabPageList = the uil-tab-page-list of Dlg; 
ItemCount = 0;
for Page = each uil-tab-page in TabPageList do 
    Btn = the uil-tab-pushbutton that is the-tab-button-of-the-tab-page Page;
    if ItemCount = 0 then 
        PrevButton = Btn; 
    X, Y = call upp-get-coordinates-for-tab-page-button (Dlg, Page, Btn, PrevButton, ItemCount); 
    ItemCount = ItemCount + 1; 
    move Btn to (X, Y); 
    PrevButton = Btn;
    if Page is the-uil-selected-tab-page-of Dlg then 
        call g2-lift-item-to-top (Btn);
end;

end

upp-manage-tab-page-method (tab-page : class uil-tab-page, WS : class kb-workspace, C : item-or-value, x : integer, y : integer) = (class uil-tab-page)
W : class ui-client-item;
delta-x, delta-y, x1, y1 : integer;
x-adjust : integer = 5;
y-adjust : integer = 5;
item-count : integer = 0;
D : class uil-tab-dialog;
itm, itm2, MO : class item;
TPL : class item-list;
page: class uil-tab-page;
SAClass: symbol = the symbol uil-scroll-area;
MsgClass: symbol = the symbol uil-message-object;
SAComponentClass: symbol = the symbol uil-scroll-component; 
move-scroll-area-proc: symbol = the symbol upp-move-scroll-area-on-tab-page ;
button, prev-button, first-button: class uil-tab-pushbutton;
debug: truth-value = false;
begin
if debug then
    inform the operator that "Running upp-manage-tab-page-method.";

{-- Bind W to any window --}
  for W = each ui-client-item do
      exit if true; 
  end; 

{-- This procedure actually manages all tab-pages for a dialog regardless of the tab page passed into this procedure. Thus this can be a somewhat expensive operation. However, the tab-page passed in will be used to orient the positioning of all other tab pages. Also note that when managing a tab-page this implies managing all items contained in a tab-page. A tab-page is then a container in the same fashion that a radio-box is a container for radio buttons.--}

if debug then
    inform the operator that "Running upp-manage-tab-page-method.";

D = the uil-dialog superior to WS;
TPL = the uil-tab-page-list of D;
call upp-make-item-transient (tab-page);
call utl-transfer-to-at (tab-page, WS, x, y); 
delta-x = x - the uil-tab-page-x-position of D ;
delta-y = y - the uil-tab-page-y-position of D ; 
if debug then
    inform the operator that "delta-x, y = [delta-x], [delta-y] "; 
conclude that the uil-tab-page-x-position of D = x;
conclude that the uil-tab-page-y-position of D = y; 

for page = each uil-tab-page in TPL do
  {-- Deal with the tab-page first --}
    call upp-make-item-transient (page);
    call utl-transfer-to-at (page, WS, x, y); 
    move page to (the item-x-position of tab-page, the item-y-position of tab-page); 
    
  {-- Deal with the tab-page-button next --}
    button = the uil-tab-pushbutton that is the-tab-button-of-the-tab-page page ;
    make button transient;
    transfer button to WS at (x, y) ;
    call upp-orient-tab-button (D, button);
    if item-count = 0 then 
      begin
          prev-button = button;
          first-button = button;
      end;
    x1, y1 = call upp-get-coordinates-for-tab-page-button (D, page, button, prev-button, item-count); 
    item-count = item-count + 1;
    if debug then
        inform the operator that "For button [item-count]: [the label of button] at x:[x1], y:[y1]";
    move button to (x1, y1);
    call uil-configure-grobj-method (button);
    call uil-configure-grobj-method (page); 

    if the uil-is-permanent of page then
        call upp-make-tab-page-permanent (page); 

    prev-button = button; 

  {-- Manage every item contained in the tab-page --}
  for itm = each item in the uil-tab-page-contents-list of page do
    if (the object-definition named by SAClass exists and itm is not an instance of the class named by (SAClass)) and (the object-definition named by SAComponentClass exists and itm is not an instance of the class named by (SAComponentClass)) and (the message-definition named by MsgClass exists and itm is not an instance of the class named by (MsgClass)) then
      begin
        move itm to (the item-x-position of itm + delta-x, the item-y-position of itm + delta-y);
        call uil-move-grobj (itm);
      end;
    if (the object-definition named by SAClass exists and itm is an instance of the class named by (SAClass)) then
      begin  
        move ITM to (the item-x-position of ITM + delta-x, the item-y-position of ITM + delta-y);
        call the procedure named by move-scroll-area-proc (ITM, false);
      end;  {begin}
   end;  {for}
end;  {for} 

call upp-resize-tab-pages-if-necessary (D);

{---- allow other processing; ----}

call uil-select-tab-page (D, tab-page, W);

if the uil-is-permanent of D then
    call uil-make-grobj-permanent (D);

return tab-page;

end

upp-hide-items-behind (Obj: class uil-tab-page)

Items : class item-list;
EachItem : class item;
EachConnection : class connection;
Regions : class symbol-array;
EachRegion : symbol;
TabList : class g2-list = the uil-tab-page-contents-list of Obj;

begin

create an item-list Items; 

call g2-get-items-in-area (the workspace of Obj,
                                    round(utl-left-edge (Obj)),
                                    round(utl-top-edge (Obj)),
                                    round(utl-right-edge (Obj)),
                                    round(utl-bottom-edge (Obj)),
                                    Items);

for EachItem = each item in Items do
    if EachItem is a member of TabList then begin
        Regions = call g2-get-item-color-regions (EachItem);
        for EachRegion = each symbol in Regions do
            call g2-set-item-color (EachItem, EachRegion, the symbol TRANSPARENT);
        end;
        delete Regions;
        
        for EachConnection = each connection connected to EachItem do
            Regions = call g2-get-item-color-regions (EachConnection);
            for EachRegion = each symbol in Regions do
                 call g2-set-item-color (EachConnection, EachRegion, the symbol TRANSPARENT);
            end;
            delete Regions; 
        end;
    end; 
end;

delete items;
  
end

upp-hide-item (Obj: class item)

Regions : class symbol-array;
EachRegion : symbol;

begin

Regions = call g2-get-item-color-regions (Obj);

for EachRegion = each symbol in Regions do
    call g2-set-item-color (Obj, EachRegion, the symbol TRANSPARENT);
end;

delete Regions;

end

upp-adjust-tab-page-contents-list-if-appropriate (I : class item)
TPCL, IL : class item-list;
ITM : class item;
D : class item;
TabDialog : class uil-tab-dialog;
TabPage : class uil-tab-page;
WS : class kb-workspace;
debug: truth-value = false;
begin
if debug then inform the operator that "called upp-adjust-tab-page-contents-list-if-appropriate for: [the class of I]";
if not (I exists) then
    return;

{---- Find every g2-list that contains I and if its a tab-page then remove it from the list. ----}

  for IL = each item-list that contains I do
    if the item superior to IL exists and the item superior to IL is a uil-tab-page then
        begin
            if debug then
                inform the operator that "About to remove a [the class of I] from a [the class of IL] which is an attribute of a [the class of the item superior to IL].";
            remove I from IL;
        end;
  end;


{---- Determine if the destination of I is a tab-page then add it. ----}

  if the item superior to the workspace of I exists and the item superior to the workspace of I is a uil-tab-dialog then
      begin
          TabDialog = the item superior to the workspace of I;
          WS = the workspace of I;
          for TabPage = each uil-tab-page in the uil-tab-page-list of TabDialog do
              if debug then
                inform the operator that "About to add a [the class of I] to a [the class of TabPage].";
              call upp-add-item-to-tab-page-if-appropriate (I, TabPage, WS, true);
              exit if true;  {--only add I to the top tab page--}
          end;
      end;

end

upp-transfer-tab-page-contents-to-location (tab-page : class uil-tab-page, Dlg : class uil-tab-dialog, WS : class kb-workspace, x-pos : integer, y-pos : integer) 
ref-page : class uil-tab-page;
itm : class item;
x, y : integer;
DEBUG: truth-value = false;
begin
if (DEBUG) then
    inform the operator that "running upp-transfer-tab-page-contents-to-location";

for ref-page = each uil-tab-page in the uil-tab-page-list of Dlg do
  exit if true;
end;

if ref-page does not exist then
    ref-page = tab-page;
    
for ITM = each item in the uil-tab-page-contents-list of tab-page do
    x-pos = the item-x-position of ref-page - the item-x-position of ITM;
    y-pos = the item-y-position of ref-page - the item-y-position of ITM;

    if (ITM is a uil-box-border or ITM is a uil-line-separator) then
        begin
        if (DEBUG) then
            inform the operator that "case of uil-box-border";
        call uil-manage-grobj-method (ITM, WS, the symbol none, the item-x-position of tab-page - x-pos, the item-y-position of tab-page - y-pos); 
        end
    else
        begin
        make ITM transient;        
        transfer ITM to WS at (the item-x-position of tab-page - x-pos, the item-y-position of tab-page - y-pos);
        call uil-move-grobj (ITM);       
        end;

    call uil-make-grobj-permanent (ITM);
    end;
end

upp-move-tab-pages (TabDialog: class uil-tab-dialog)

P: class uil-tab-page;
WS: class kb-workspace;

begin

WS = the subworkspace of TabDialog;
if there exists a uil-tab-page P upon WS then
    call upp-move-tab-page (P);

end

upp-move-tab-page (tab-page : class uil-tab-page)
W : class ui-client-item;
EditBox : class uil-edit-box;
page : class uil-tab-page;
D, D2, from-dialog: class uil-dialog;
WS : class kb-workspace;
x-pos, y-pos, dx, dy: integer;
debug: truth-value = false;
begin
if debug then
    inform the operator that "Running upp-move-tab-page for a [the class of tab-page]";

if the item superior to the workspace of tab-page exists then 
  begin
    if the uil-tab-dialog superior to the workspace of tab-page exists then
        D = the uil-tab-dialog superior to the workspace of tab-page
  end;

if not (D exists) or D is not a uil-tab-dialog then
  begin
    call upp-return-tab-page-to-owning-dialog (tab-page);
    return;
  end;

for W = each ui-client-item do
   call upp-deactivate-field-edit-for-tab-page-if-appropriate (tab-page, W);
end ;
   
if tab-page is a member of the uil-tab-page-list of D then 
  begin
  end
else
  begin
    {-- this tab page was transferred to a different dialog --}
    for D2 = each uil-tab-dialog do
        if tab-page is a member of the uil-tab-page-list of D2 then
          begin
            call uil-remove-tab-page-from-dialog (tab-page, D2);
            from-dialog = D2;
          end;
    end;
  end;

WS = the subworkspace of D;
x-pos = the item-x-position of tab-page; 
y-pos = the item-y-position of tab-page; 

if tab-page is a member of the uil-tab-page-list of D then
    begin
    if (DEBUG) then
        inform the operator that "running uil-manage-grobj-method for tab-page";
    call uil-manage-grobj-method (tab-page, WS, the symbol none, x-pos, y-pos);
    end
else 
    begin
        insert tab-page at the end of the uil-tab-page-list of D;
        if from-dialog exists then 
            call upp-transfer-tab-page-contents-to-location (tab-page, from-dialog, WS, x-pos, y-pos)
        else
            call upp-transfer-tab-page-contents-to-location (tab-page, D, WS, x-pos, y-pos);
        call upp-resize-this-tab-page-into-conformance (tab-page, D);
        call uil-manage-grobj-method (tab-page, WS, the symbol none, x-pos, y-pos);  
    end;

call upp-set-tab-pushbutton-movement-limits (tab-page);

if from-dialog exists then
  begin
    for page = each uil-tab-page in the uil-tab-page-list of from-dialog do
        if debug then
            inform the operator that "About to manage a tab-page";
        call uil-manage-grobj-method (page, the workspace of page, the symbol none, the item-x-position of page, the item-y-position of page);  
        call upp-set-tab-pushbutton-movement-limits (page);
        exit if true;
    end;
  end;

end

upp-configure-tab-page-method (Page: class uil-tab-page)

Orient: symbol;
button: class uil-button;
C: class uil-text-pushbutton-configuration-class;
debug: truth-value = false;

begin

if debug then
    inform the operator that "Running uil-configure-tab-page-method.";

if the uil-tab-pushbutton button that is the-tab-button-of-the-tab-page Page exists and the configuration of button exists and the uil-text-pushbutton-configuration-class named by the configuration of button exists then
    C = the uil-text-pushbutton-configuration-class named by the configuration of button
else
    return;

if the state of page is disabled then
    begin
      change the color-pattern of page so that background-color = the uil-button-disabled-color of C, border-color = the uil-button-border-color of C ;
      return;
    end ; 

 change the color-pattern of page so that background-color = the uil-button-background-color of C, border-color = the uil-button-border-color of C ;

end

upp-return-tab-page-to-owning-dialog (tab-page : class uil-tab-page)
page : class uil-tab-page;
WS : class kb-workspace;
D, D2 : class uil-tab-dialog;
IL : class item-list;
debug: truth-value = false;
begin
if debug then
    inform the operator that "Running upp-return-tab-page-to-owning-dialog.";

 for IL = each item-list that contains tab-page do
  if debug then
    inform the operator that "Checking a list to see if it contains the tab-page.[the class of the item superior to IL]";
  if the item superior to IL exists and the item superior to IL is a uil-tab-dialog then 
    begin
      D2 = the uil-tab-dialog superior to IL;
      WS = the subworkspace of D2;
      if debug then
        inform the operator that "Found the original location of the tab-page. Transferring back at [the uil-tab-page-x-position of D2], [the uil-tab-page-y-position of D2]";
      make tab-page transient;
      transfer tab-page to WS at (the uil-tab-page-x-position of D2, the uil-tab-page-y-position of D2);
      if debug then
        inform the operator that "Now calling uil-select-tab-page.";
      call uil-select-tab-page (D2, tab-page, the symbol no-window);
      if the uil-is-permanent of tab-page then
        call upp-make-tab-page-permanent (tab-page);
      return;
    end;
 end ;

call upp-delete-item (tab-page);

end

upp-enable-tab-page-method (page : class  uil-tab-page)
g : class item;
D : class uil-dialog;
WS : class kb-workspace = the workspace of page;
debug: truth-value = false;
begin
if debug then
    inform the operator that "Running uil-enable-tab-page-method.";

    conclude that the state of page is enabled;

    for G = each item in the uil-tab-page-contents-list of page do
        if the uil-enable-method of G exists and the procedure named by the uil-enable-method of G exists then
            call the procedure named by the uil-enable-method of G (G);
        end;

    call uil-configure-grobj-method (page);
end

upp-enable-tab-pushbutton-method (B : class uil-tab-pushbutton)
P : class uil-tab-page; 
debug: truth-value = false;
begin
if debug then
    inform the operator that "Running upp-enable-tab-pushbutton-method."; 

if there exists a uil-tab-page that is the-tab-page-handled-by-the-tab-button B then
  begin
    conclude that the state of B is enabled;
    call uil-configure-grobj-method (B);
  end;
end

upp-disable-tab-page-method (page : class  uil-tab-page)
g : class item;
D : class uil-dialog;
WS : class kb-workspace = the workspace of page;
debug: truth-value = false;
begin
if debug then
    inform the operator that "Running uil-disable-tab-page-method.";

conclude that the state of page is disabled ;

for G = each item in the uil-tab-page-contents-list of page do
    if the uil-disable-method of G exists and the procedure named by the uil-disable-method of G exists then
        call the procedure named by the uil-disable-method of G (G);
end;

call uil-configure-grobj-method (page);

end

upp-disable-tab-pushbutton-method (B : class uil-tab-pushbutton)
P : class uil-tab-page;
debug: truth-value = false;
begin
if debug then
    inform the operator that "Running upp-disable-tab-pushbutton-method.";

if there exists a uil-tab-page that is the-tab-page-handled-by-the-tab-button B then
  begin
    conclude that the state of B is disabled;
    call uil-configure-grobj-method (B);
  end;
end

upp-delete-tab-page-method (page : class  uil-tab-page)
g : class item;
D : class uil-dialog;
WS : class kb-workspace = the workspace of page;
debug: truth-value = false;
begin
if debug then
    inform the operator that "Running upp-delete-tab-page-method.";

for G = each item in the uil-tab-page-contents-list of page do
    if the uil-delete-method of G exists and the procedure named by the uil-delete-method of G exists then
        call the procedure named by the uil-delete-method of G (G)
    else
        call upp-delete-item (G);
end;

if there exists a uil-tab-pushbutton G that is the-tab-button-of-the-tab-page page then
    call upp-delete-item (G);

call upp-delete-item (page);

if the item superior to WS exists and the item superior to WS is a uil-tab-dialog then
    begin
        D = the uil-dialog superior to WS ;
        for g = each uil-tab-page in the uil-tab-page-list of D do
            call upp-move-tab-page (g);
            exit if true;
        end;
    end;
end

upp-configure-tab-pushbutton-method (Button : class uil-tab-pushbutton)
orient : symbol;
page : class uil-tab-page;
C : class uil-text-pushbutton-configuration-class;
debug: truth-value = false;
begin
if debug then
    inform the operator that "Running uil-configure-tab-pushbutton-method.";

if the configuration of button exists and
  the uil-text-pushbutton-configuration-class named by the configuration of button exists then
    C = the uil-text-pushbutton-configuration-class named by the configuration of button
else
    return;

if debug then
    inform the operator that "Configuration=[the name of C]";

if the state of Button is disabled then
    begin
      change the color-pattern of button so that uil-button-light-color = the uil-button-disabled-color of C, uil-button-dark-color = the uil-button-disabled-color of C, uil-button-reversible-color = the uil-button-disabled-color of C, uil-button-background-color = the uil-button-disabled-color of C ; 
      return;
    end ; 

orient = call g2-get-reflection-and-rotation (button);
if debug then
    inform the operator that "Orientation=[orient]";

{-- Top orientation --}
if orient is normal then
        change the color-pattern of button so that uil-button-light-color = the uil-button-light-color of C, uil-button-dark-color = the uil-button-dark-color of C, uil-button-reversible-color = the uil-button-light-color of C;

{-- Right side orientation --}
if orient is clockwise-90 then
        change the color-pattern of button so that uil-button-light-color = the uil-button-light-color of C, uil-button-dark-color = the uil-button-dark-color of C, uil-button-reversible-color = the uil-button-dark-color of C;

{-- Left side orientation --}
if orient is clockwise-270 then
        change the color-pattern of button so that uil-button-light-color = the uil-button-dark-color of C, uil-button-dark-color = the uil-button-light-color of C, uil-button-reversible-color = the uil-button-light-color of C;

change the color-pattern of button so that uil-button-border-color = the uil-button-border-color of C; 
change the color-pattern of button so that uil-button-background-color = the uil-button-background-color of C; 

if the uil-tab-page that is the-tab-page-handled-by-the-tab-button button exists then
  begin
    page = the uil-tab-page that is the-tab-page-handled-by-the-tab-button button;
    call uil-configure-grobj-method (page);
  end;

{-- Note that there are no tab buttons permitted on the bottom of tab pages --}

end

upp-configure-tab-dialog-method (D : class uil-tab-dialog)
B : class uil-tab-pushbutton;
P : class uil-tab-page;
TPL : class item-list;
debug: truth-value = false;
begin
if debug then
    inform the operator that "Running uil-configure-tab-dialog-method.";

    call upp-configure-dialog-method (D);

    TPL = the uil-tab-page-list of D;

    for P = each uil-tab-page in TPL do
        B = the uil-tab-pushbutton that is the-tab-button-of-the-tab-page P;
        call uil-configure-grobj-method (B);
        end;
end

upp-tab-button-mouse-tracking-internals (s1: symbol, W: class g2-window, IV1: item-or-value, IV2: item-or-value, I1: integer, I2: integer, I3: integer, I4: integer)
case-no : symbol;
B  : class uil-tab-pushbutton;
Page: class uil-tab-page;
D : class uil-tab-dialog;
DEBUG: truth-value = false;
en: symbol; et : text;
begin
if (DEBUG) then
    inform the operator that "Running upp-tab-button-mouse-tracking-internals: <[s1]> for a [the class of IV1]";

if IV1 does not exist then
  begin
    if debug then
      inform the operator that "Returning since IV1 does not exist.";
    return; 
  end;

B = IV1;
if the uil-tab-page that is the-tab-page-handled-by-the-tab-button B exists then
    page = the uil-tab-page that is the-tab-page-handled-by-the-tab-button B;
If page exists and the uil-tab-dialog superior to the workspace of Page exists then
    D = the uil-tab-dialog superior to the workspace of Page;

{-- START TRACKING --}
if debug then
    inform the operator that "Testing for select. [(if D exists then "true" else "false")], [(if page exists then "true" else "false")],";
if s1 = the symbol start-tracking and the state of B is enabled and D exists and Page exists then
  begin
    if debug then 
        inform the operator that "Selecting a tab page.";
    {call uil-select-tab-page (D, page, W);}
    call uil-select-button (B, W, D);
  end;

end

upp-raise-uil-components-to-top (G: class item)
{G represents the uil-grobj that is being moved to the top of the workspace stack.}
itm2, itm3, itm4 : class item;
{itmn represent items that are related in various ways to G.}
TXT : class uil-text;
raise-scroll-resize-tool-proc: symbol = the symbol upp-raise-resize-tool-to-top ;
ComboBtnDef: symbol = the symbol uil-combo-box-button;
SAClass: symbol = the symbol uil-scroll-area;
SliderClass: symbol = the symbol uil-slider;
debug: truth-value = false;
begin
if debug then
    inform the operator that "Running upp-raise-uil-components-to-top for a <[the class of G]>";

{----Scroll Areas----}
if (the object-definition named by SAClass exists) and G is an instance of the class named by (SAClass) then
  begin
    call g2-lift-item-to-top (G);
    for itm2 = each item that is the-uil-scroll-bar-of G do
        call g2-lift-item-to-top (itm2); 
    end;
    for itm2 = each item that is a-scroll-segment-of G do
        call g2-lift-item-to-top (itm2); 
    end;
    for itm2 = each item that is the-uil-scroll-thumb-of G do 
        call g2-lift-item-to-top (itm2); 
    end;
    for itm2 = each item that is the-uil-top-scroll-arrow-of G do 
        call g2-lift-item-to-top (itm2); 
    end;
    for itm2 = each item that is the-uil-bottom-scroll-arrow-of G do 
        call g2-lift-item-to-top (itm2); 
    end;
    {-- sdm 6/13/97: added the following. for some reason, needed the first time added to tab page --}
    for itm2 = each item that is a-message-belonging-to G do
        if (the workspace WS of itm2 exists and the workspace of G exists and WS is the same object as the workspace of G) then
            call g2-lift-item-to-top (itm2);
        end;
    call the procedure named by raise-scroll-resize-tool-proc (G);
  end {begin}
else

{----Edit Box----}
if G is a uil-edit-box then 
  begin
    call g2-lift-item-to-top (G);
    if the uil-text that is the-uil-text-label-of G exists then
        call g2-lift-item-to-top (the uil-text that is the-uil-text-label-of G);
    for itm2 = each uil-box-object that is a-uil-bounding-box-for G do
        call g2-lift-item-to-top (itm2);
        for itm3 = each connection at either end of itm2 do
            call g2-lift-item-to-top (itm3);
        end {for}
    end;{for}

    for itm2 = each uil-spin-button that is a-spin-button-controlling G do
     call uil-configure-grobj-method (itm2);
     call g2-work-on-drawing (itm2);       
     call g2-lift-item-to-top (itm2);
    end;{for}

    for itm2 = each item that is the-combo-box-button-of G do
     call uil-configure-grobj-method (itm2);
     call g2-work-on-drawing (itm2); 
     call g2-lift-item-to-top (itm2);
    end;{for} 

  end {begin}
else

{----non-Selection Button----}
if G is a uil-button and (G is not a uil-selection-button and G is not a uil-spin-button) then
  begin
    call g2-lift-item-to-top (G);
    if the uil-text that is the-uil-text-label-of G exists then
        call g2-lift-item-to-top (the uil-text that is the-uil-text-label-of G);
  end {begin}
else

{----Text----}
if G is a uil-text and the uil-stand-alone-text of G is true then
  call g2-lift-item-to-top (G)
else

{----Selection Boxes----}
if G is a uil-selection-box then 
  begin
    call g2-lift-item-to-top (G);
    if the uil-text that is the-uil-text-label-of G exists then
      call g2-lift-item-to-top (the uil-text that is the-uil-text-label-of G);
    for itm2 = each uil-selection-button that is grouped-by G do
      call g2-lift-item-to-top (itm2);
      for itm3 = each uil-text that is the-uil-text-label-of itm2 do
        call g2-lift-item-to-top (itm3);
      end {for}
    end {for}
  end {begin}
else

{----Separators----}
if G is a uil-line-separator then 
  begin
    call g2-lift-item-to-top (G);
    if the connection itm3 at either end of G exists then
      call g2-lift-item-to-top (itm3);
    if the item itm3 connected to G exists then
      call g2-lift-item-to-top (itm3);
    if the item itm3 that is the-line-separator-loose-end-for G exists then
      call g2-lift-item-to-top (itm3); 
  end {begin}
else

{----Box Border----}
if G is a uil-box-border then 
  begin
    call g2-lift-item-to-top (G);
    if the item itm3 connected to G exists then
      call g2-lift-item-to-top (itm3);
    if the connection itm3 at either end of G exists then
      call g2-lift-item-to-top (itm3);
    if the item itm3 that is the-box-connector-loose-end-for G exists then
      call g2-lift-item-to-top (itm3); 
  end {begin}
else

{----Sliders----}
if (the object-definition named by SliderClass exists) and (G is an instance of the class named by (SliderClass)) then
  begin
    call g2-lift-item-to-top (G);
    if the item itm3 that is the-pointer-in G exists then
        call g2-lift-item-to-top (itm3); 
  end {begin}
else

{---- All other cases, added in GENSYMCID-1313 'How to see icon in a uil-tab-page ?' ----}
if G is a uil-object then
    call g2-lift-item-to-top (G);

end

upp-get-tab-pushbutton-perimeter (TabDialog: class uil-tab-dialog) = (integer, integer, integer, integer)
WS : class kb-workspace;
BTN : class uil-tab-pushbutton;
l, r, t, b, max-l, max-r, max-t, max-b : integer;
firsttime : truth-value = true;
debug: truth-value = false;
begin
WS = the subworkspace of TabDialog;
for BTN = each uil-tab-pushbutton upon WS do
    l = truncate (utl-left-edge (BTN));
    r = truncate (utl-right-edge (BTN));
    b = truncate (utl-bottom-edge (BTN));
    t = truncate (utl-top-edge (BTN));

    if firsttime then
      begin
        max-l = l; max-r = r; max-t = t; max-b = b;
        firsttime = false;
      end;

    if l < max-l then max-l = l;
    if r > max-r then max-r = r;
    if t < max-t then max-t = t;
    if b < max-b then max-b = b;

end;

return max-l, max-r, max-t, max-b;

end

upp-resize-tab-pages-if-necessary (TabDialog : class uil-tab-dialog)
TabPage : class uil-tab-page;
orientation : symbol;
loc : symbol;
resize : truth-value = false;
margin: integer = 20;
TPL : class item-list;
WS : class kb-workspace;
l, r, t, b, max-l, max-r, max-t, max-b: integer;
DEBUG: truth-value = false;
begin
TPL = the uil-tab-page-list of TabDialog;

{---- Get first tab page in list for size purposes.}
for TabPage = each uil-tab-page in TPL do
    exit if true;
end;

if not (TabPage exists) then
    return;

orientation = the uil-tab-page-button-placement of TabDialog;
WS = the subworkspace of TabDialog;
l = truncate (utl-left-edge (TabPage));
r = truncate (utl-right-edge (TabPage));
t = truncate (utl-top-edge (TabPage));
b = truncate (utl-bottom-edge (TabPage));

max-l, max-r, max-t, max-b = call upp-get-tab-pushbutton-perimeter (TabDialog);

if debug then
    inform the operator that "LRTB = [l], [r], [t], [b][crlf]max-LRTB = [max-l], [max-r], [max-t], [max-b]";

loc = the uil-tab-page-button-placement of TabDialog;

if orientation is top and max-r > r then 
  begin
    r = max-r + margin;
    resize = true;
  end;

if  (orientation is left or orientation is right) and max-b < b then
  begin
    b = max-b - margin;
    resize = true;
  end; 

if debug then
    inform the operator that "Resize=[resize] count = [the count of each uil-tab-pushbutton upon WS]";

if resize then
  begin
    call g2-change-size-of-item-per-area (TabPage, l, t, r, b, the symbol none);
    call upp-resize-tab-pages-to-this-page-size (TabPage);
  end;

end

upp-resize-tab-pages-to-this-page-size (TabPage: class uil-tab-page)

Page: class uil-tab-page;
D: class uil-tab-dialog;
WS: class kb-workspace;
L, R, T, B: integer;

begin

D = the uil-tab-dialog superior to the workspace of TabPage;
WS = the subworkspace of D;
L = truncate (utl-left-edge (TabPage));
R = truncate (utl-right-edge (TabPage));
T = truncate (utl-top-edge (TabPage));
B = truncate (utl-bottom-edge (TabPage));

for Page = each uil-tab-page upon WS do
    if Page is not the same object as TabPage then
        call g2-change-size-of-item-per-area (Page, L, T, R, B, the symbol NONE);
end;

call upp-manage-tab-page-method (Page, WS,  the symbol NONE, the item-x-position of Page, the item-y-position of Page); 

end

upp-allow-field-edit-for-tab-page (TabPage: class uil-tab-page, W : class ui-client-item)
WS : class kb-workspace;
TabDialog : class uil-tab-dialog;
EditBox: class uil-edit-box;
TPL : class item-list;
begin
WS = the workspace of TabPage;
if there exists a uil-tab-dialog superior to WS then
    TabDialog = the uil-tab-dialog superior to WS
  else
    return;

for EditBox = each uil-edit-box in the uil-tab-page-contents-list of TabPage do
    conclude that the uil-allow-field-edit-of-this-message of EditBox = true ;
  end;

end

upp-disallow-all-edit-fields-for-tab-page (tab-page : class uil-tab-page, W : item-or-value)
WS : class kb-workspace;
D : class uil-dialog;
TPL : class item-list;
TPCL : class item-list;
itm : class item;
begin

WS = the workspace of tab-page;
D = the item superior to WS;
TPL = the uil-tab-page-list of D;

for itm = each item in the uil-tab-page-contents-list of tab-page do
    if itm is a uil-edit-box then
        conclude that the uil-allow-field-edit-of-this-message of itm = false;
end 

end

guide-clone-tab-page (tab-page : class uil-tab-page, W : class ui-client-item)
cloned-tab-page : class uil-tab-page;
cloned-tab-button: class uil-tab-pushbutton;
tab-dialog : class uil-tab-dialog;
b: class uil-tab-pushbutton;
WS : class kb-workspace;
x, y : integer;
DEBUG: truth-value = false;
begin     
if (DEBUG) then
    inform the operator that "running guide-clone-tab-page";

    if (the workspace WS of tab-page exists) and (the uil-button b that is the-tab-button-of-the-tab-page tab-page exists) then
        begin   
        tab-dialog = the uil-tab-dialog superior to WS;
        x = the item-x-position of tab-page;
        y = the item-y-position of tab-page;


        cloned-tab-button = call uil-clone-grobj-method (b);
        cloned-tab-page = call uil-clone-grobj-method (tab-page);

        conclude that cloned-tab-button is now the-tab-button-of-the-tab-page cloned-tab-page;

        {-- sdm: 9/9/97 => uil-add-tab-page-to-dialog is called from upp-clone-tab-page-method, so do not call here
        call uil-add-tab-page-to-dialog (cloned-tab-page, tab-dialog);
        --}

        call uil-manage-grobj-method (cloned-tab-page, WS, the symbol none, x, y);     
        call upp-move-tab-page (cloned-tab-page); 
        call uil-make-grobj-permanent (cloned-tab-page);
        call uil-make-grobj-permanent (cloned-tab-button);
        end;    

if (DEBUG) then
    inform the operator that "leaving guide-clone-tab-page"; 
end

upp-refresh-tab-pages (TabElement: class item) 

TabButton: class uil-tab-pushbutton;
TabPage: class uil-tab-page;
TabDialog: class uil-tab-dialog;
WS : class kb-workspace;

begin

{---The purpose of this procedure is to obtain the tab-page from what was passed in and determine if the tab-page is the-selected-tab-page of some dialog. If it is, then refresh it by selecting it again.---}

if TabElement is a uil-tab-dialog then
    TabDialog = TabElement
else if the item superior to the workspace of TabElement is a uil-tab-dialog then
    TabDialog = the item superior to the workspace of TabElement;

if TabDialog exists and the subworkspace of TabDialog exists then
    WS = the subworkspace of TabDialog;

if TabElement is a uil-tab-page then
    TabPage = TabElement
else if TabElement is a uil-tab-pushbutton then begin
    TabButton = TabElement;
    for TabPage = each uil-tab-page upon WS do
        exit if true;
    end;
end else if TabElement is a uil-tab-dialog then begin
    for TabPage = each uil-tab-page upon WS do
        exit if true;
    end;
end;

if TabPage exists and TabDialog exists then
    call upp-move-tab-page (TabPage);

end

upp-insert-element-into-ordered-list (Itm: class item, Position: symbol, Itm2: class item, ItmList: class item-list)
Et: text; En: symbol;
begin
if Position is before then insert Itm before Itm2 in ItmList else if position is after then insert Itm after Itm2 in ItmList else insert Itm at the end of the item list ItmList;
end
on error (En, Et) end

upp-remove-element-from-list (Itm: class item, TPCL: class item-list)

Et: text;
En: symbol;

begin

remove Itm from TPCL;

end

on error (En, Et)
end

upp-insert-element-into-list (I : class item, TPCL : class item-list)
D, tab-page, tmpItem, masterItem: item-or-value;
SAComponentClass: symbol = the symbol uil-scroll-component; 
TopSAClass: symbol = the symbol uil-top-scroll-arrow;
BotSAClass: symbol = the symbol uil-bottom-scroll-arrow; 
SAThumbClass: symbol = the symbol uil-scroll-thumb;
en : symbol; et : text;
DEBUG: truth-value = false;
begin
if (DEBUG) then
    inform the operator that "running upp-insert-element-into-list for a [the class of I]";

{-- first determine if I is a compound object --}
    if (the item superior to TPCL exists) then
        tab-page = the item superior to TPCL
    else
        return;
   
{-- SELECTION BUTTONS --}
    if (I is a uil-selection-button) then
        begin
        if (the item masterItem that is the-group-master-of I exists) then
            begin
            tmpItem = call uil-get-selection-box-lead-button (masterItem);
            if (tmpItem is the same object as I) then
                begin
                {-- check if the selection-box is within the bounds of the tab page --}
                if utl-left-edge (masterItem) > utl-left-edge (tab-page) and utl-top-edge (masterItem) < utl-top-edge (tab-page) and utl-right-edge (masterItem) < utl-right-edge (tab-page) and utl-bottom-edge (masterItem) > utl-bottom-edge (tab-page) then
                    begin
                    I = masterItem;
                    end
                else
                    begin
                    inform the operator that "cannot add the uil-selection-box that manages the uil-selection-button onto the tab page since it does not reside completely on the tab page as well";
                    return;
                    end;
                end
            else
                return;
            end
        else
            begin
            inform the operator that "ERROR: the selection-box that manages this button does not exists";
            return;
            end;     
        end

{-- TEXT THAT IS NOT STANDALONE --}
    else if (I is a uil-text and the uil-stand-alone-text of I is false) then
        begin
        if (the item masterItem that is the-uil-button-owning-this-text-label I exists) then
            begin
            if utl-left-edge (masterItem) > utl-left-edge (tab-page) and utl-top-edge (masterItem) < utl-top-edge (tab-page) and utl-right-edge (masterItem) < utl-right-edge (tab-page) and utl-bottom-edge (masterItem) > utl-bottom-edge (tab-page) then
                begin
                I = masterItem;
                end
            else
                begin
                inform the operator that "cannot add [the class of I] to the tab page since its associated components, a [the class of masterItem], are not within the bounds of the tab page as well.";
                return;
                end; 
            end
        else
            begin
            inform the operator that "cannot add the [the class of I] to the tab page, the text is not standalone, but its associated item is missing";
            return;
            end;     
        end

{-- SPIN BUTTONS --}
    else if (I is a uil-spin-button) then
        begin
        if (the item masterItem that is  the-grmes-controlled-by-spin-button I exists) then
            begin
            if utl-left-edge (masterItem) > utl-left-edge (tab-page) and utl-top-edge (masterItem) < utl-top-edge (tab-page) and utl-right-edge (masterItem) < utl-right-edge (tab-page) and utl-bottom-edge (masterItem) > utl-bottom-edge (tab-page) then
                begin
                I = masterItem;
                end
            else
                begin
                inform the operator that "cannot add [the class of I] to the tab page since its associated components, a [the class of masterItem], are not within the bounds of the tab page as well.";
                return;
                end; 
            end
        else
             begin
             inform the operator that "cannot add the [the class of I] to the tab page, cannot locate the uil-spin-control-box associated with the button.";
             return;
             end;   
        end

    else if (I is a uil-edit-box) then
        begin
        if (I is a uil-spin-control-entry-box) and (there exists an item tmpItem that is a-spin-button-controlling I) then
            begin
            call uil-move-grobj (I);
            if not (utl-left-edge (tmpItem) > utl-left-edge (tab-page) and utl-top-edge (tmpItem) < utl-top-edge (tab-page) and utl-right-edge (tmpItem) < utl-right-edge (tab-page) and utl-bottom-edge (tmpItem) > utl-bottom-edge (tab-page)) then               
                begin
                inform the operator that "cannot add [the class of I] to the tab page since its associated components, a [the class of tmpItem], are not within the bounds of the tab page as well.";
                return;
                end; 
            end;

        tmpItem = call uil-get-label-text (I);
        if (tmpItem exists and tmpItem is an item) then
            begin
            if not (utl-left-edge (tmpItem) > utl-left-edge (tab-page) and utl-top-edge (tmpItem) < utl-top-edge (tab-page) and utl-right-edge (tmpItem) < utl-right-edge (tab-page) and utl-bottom-edge (tmpItem) > utl-bottom-edge (tab-page)) then               
                begin
                inform the operator that "cannot add [the class of I] to the tab page since its associated components, a [the class of tmpItem], are not within the bounds of the tab page as well.";
                return;
                end; 
            end;
        end

{-- SCROLL COMPONENT --}
    else if (the object-definition named by SAComponentClass exists and I is an instance of the class named by (SAComponentClass)) then
        begin
        if (the object-definition named by TopSAClass exists and I is an instance of the class named by (TopSAClass)) or (the object-definition named by BotSAClass exists and I is an instance of the class named by (BotSAClass)) or (the object-definition named by SAThumbClass exists and I is an instance of the class named by (SAThumbClass)) then
            begin
            if (the object-definition named by TopSAClass exists and I is an instance of the class named by (TopSAClass) and the item masterItem that is the-scroll-area-for-the-top-arrow I exists) or (the object-definition named by BotSAClass exists and I is an instance of the class named by (BotSAClass) and the item masterItem that is the-scroll-area-for-the-bottom-arrow I exists) or (the object-definition named by SAThumbClass exists and I is an instance of the class named by (SAThumbClass) and the item masterItem that is the-scroll-area-for-the-uil-scroll-thumb I exists) then
                begin
                if utl-left-edge (masterItem) > utl-left-edge (tab-page) and utl-top-edge (masterItem) < utl-top-edge (tab-page) and utl-right-edge (masterItem) < utl-right-edge (tab-page) and utl-bottom-edge (masterItem) > utl-bottom-edge (tab-page) then
                    begin
                    I = masterItem;
                    end
                else
                    begin
                    inform the operator that "cannot add [the class of I] to the tab page since its associated components, a [the class of masterItem], are not within the bounds of the tab page as well.";
                    call uil-move-grobj (masterItem);
                    return;
                    end; 
                end
            else
                begin
                inform the operator that "cannot ad the [the class of I] to the tab page, cannot locate the uil-scroll-area associated with this object";
                return;
                end;
            end
        else
            return;
        end

{-- ITEMS TO IGNORE --}
    else if (I is a uil-tab-page) or (I is a uil-tab-pushbutton) or (I is a uil-private-master-dialog-text) or (I is a uil-dialog-title) then
        begin
        if (DEBUG) then
            inform the operator that "ignoring this call..@.";
        return;
        end;

    
if (DEBUG) then
    inform the operator that "inserting [the class of I] at the end of [the class of TPCL]";

    insert I at the end of the item list TPCL;
end
on error (en, et)
if (DEBUG) then
    inform the operator that "[en][et]";
end

upp-add-item-to-tab-page-if-appropriate (I : class item, P : class uil-tab-page, WS : class kb-workspace, place-item-on-top : truth-value) 
D : class uil-dialog;
tab-page : class uil-tab-page;
TPL : class item-list ;
mem : truth-value = true;
debug: truth-value = false;
begin
if debug then
    inform the operator that "Running upp-add-item-to-tab-page-if-appropriate for a class of type: [the class of I]";
if not (I exists) then
    return;
if I is a uil-tab-page then 
    return;
if the item superior to WS exists and the item superior to WS is a uil-tab-dialog then
    D = the item superior to WS 
  else
    return;

TPL = the uil-tab-page-list of D;
if the uil-tab-page that is the-uil-selected-tab-page-of D exists then
    tab-page = the uil-tab-page that is the-uil-selected-tab-page-of D 
else
    for tab-page = each uil-tab-page in TPL do
      exit if true;
    end;

mem = call upp-is-item-a-member-of-tab-page-lists (I, D); 

if debug then
    inform the operator that "Member of this tab-page-list? [mem]";

if utl-left-edge (I) > utl-left-edge (tab-page) and
        utl-top-edge (I) < utl-top-edge (tab-page) and
        utl-right-edge (I) < utl-right-edge (tab-page) and
        utl-bottom-edge (I) > utl-bottom-edge (tab-page) then
   begin
        if debug then
            inform the operator that "Item fits on tab page. Now checking to see if item is already a member of the tab-page.";
        if not (mem) then
            call uil-add-grobj-to-tab-page (I, tab-page);
        if debug then
            inform the operator that "Added a [the class of I] to a tab-page. Now lifting to top.";
        call upp-raise-uil-components-to-top (I);
        return;  
   end;


if mem then
    call uil-remove-grobj-from-tab-page (I, tab-page);

end

upp-clear-tab-page-list (D : class uil-tab-dialog)
P : class uil-tab-page;
begin
for P = each uil-tab-page in the uil-tab-page-list of D do
    call uil-delete-grobj (P);
end ;

end

upp-is-item-a-member-of-tab-page-lists (g : class item, D : class uil-tab-dialog) = (truth-value)
tab-page-list : class item-list;
tab-page-contents-list : class item-list;
tab-page : class uil-tab-page;
itm : class item;
is-member-p : truth-value = false;
box: class uil-selection-box;
begin
for tab-page = each uil-tab-page in the uil-tab-page-list of D do
    if g is a member of the uil-tab-page-contents-list of tab-page then
      return true;
{-- SELECTION BUTTON --}
    if (g is a uil-selection-button) then begin
        box = the uil-selection-box that is the-group-master-of g;
        if box is a member of the uil-tab-page-contents-list of tab-page then return true;
    end;
  end;
  return false;
end

upp-orient-tab-button (D : class uil-tab-dialog, button : class uil-tab-pushbutton)  

begin 
if the uil-tab-page-button-placement of D is top then
  begin
    rotate button to the heading 0 degrees;
  end; 

if the uil-tab-page-button-placement of D is left then
  begin
    rotate button to the heading 270 degrees;
  end;

if the uil-tab-page-button-placement of D is right then
  begin
    rotate button to the heading 90 degrees;
  end; 

end

upp-get-coordinates-for-tab-page-button (D : class uil-tab-dialog, page : class uil-tab-page, button : class uil-tab-pushbutton, prev-button : class uil-tab-pushbutton, indx : integer)  = (integer, integer)
x, y, prev-x, prev-y, incr: integer;
TPL : class item-list;
debug: truth-value = false;
begin
incr = 5;
TPL = the uil-tab-page-list of D;
prev-x = the item-x-position of prev-button;
prev-y = the item-y-position of prev-button;
if not (the uil-tab-page-button-placement of D is top or
    the uil-tab-page-button-placement of D is left or 
    the uil-tab-page-button-placement of D is right)
  then
    conclude that the uil-tab-page-button-placement of D is top; 

{TOP}
if the uil-tab-page-button-placement of D is top then 
  begin
    if indx = 0 then 
      begin
        x = truncate (utl-left-edge (page) + the item-width of button / 2) + incr;
        y = truncate (utl-top-edge (page) + the item-height of button / 2 - 2); 
        go to return-values;
      end
    else
      begin
        x = prev-x + truncate (the item-width of prev-button / 2) + truncate (the item-width of button / 2);
        y = truncate (utl-top-edge (page) + the item-height of button / 2 - 2); 
        go to return-values; 
      end;
  end;

{LEFT} 
if the uil-tab-page-button-placement of D is left then 
  begin 
    if indx = 0 then 
      begin
        x = truncate (utl-left-edge (page) - the item-width of button / 2 + 1);
        y = truncate (utl-top-edge (page) - the item-height of button / 2) - incr; 
        go to return-values;
      end 
    else 
      begin 
        x = truncate (utl-left-edge (page) - the item-width of button / 2 + 1); 
        y = the item-y-position of prev-button - truncate (the item-height of prev-button / 2) - truncate (the item-height of button / 2);   
        go to return-values; 
      end;
  end; 

{RIGHT}
if the uil-tab-page-button-placement of D is right then 
  begin 
    if indx = 0 then 
      begin
        x = truncate (utl-right-edge (page) + the item-width of button / 2 - 2);
        y = truncate (utl-top-edge (page) - the item-height of button / 2) - incr; 
        go to return-values;
      end
    else
      begin
        x = truncate (utl-right-edge (page) + the item-width of button / 2 - 2); 
        y = prev-y - truncate (the item-height of prev-button / 2) - truncate (the item-height of button / 2);   
        go to return-values; 
      end;
  end; 
 


return-values:
  if debug then
    inform the operator that "for indx:[indx], placement=[the uil-tab-page-button-placement of D], x:[x], y:[y]";

  return x, y;
end

upp-raise-tab-page-contents-to-top (tab-page : class uil-tab-page, W : item-or-value)
ITM : class item;
TXT : class uil-text;
D : class uil-tab-dialog;
display-message-list-proc : symbol = the symbol uil-display-message-list;
debug: truth-value = false;
other-page : class uil-tab-page;
page-list : class g2-list;
SAClass: symbol = the symbol uil-scroll-area; 
begin
if debug then
    inform the operator that "Running upp-raise-tab-page-contents-to-top.";

if the item superior to the workspace of tab-page exists and the item superior to the workspace of tab-page is a uil-tab-dialog then
    D = the item superior to the workspace of tab-page;
 
{page-list = the uil-tab-page-list of D;

for other-page = each uil-tab-page in page-list do
    if other-page is not the same object as tab-page then
         call upp-hide-items-behind (tab-page);       
end;}

for ITM = each item in the uil-tab-page-contents-list of tab-page do    
   call uil-configure-grobj-method (ITM); 

   call upp-raise-uil-components-to-top (ITM); 

    if (the object-definition named by SAClass exists and ITM is an instance of the class named by (SAClass)) then
        call the procedure named by display-message-list-proc (ITM, the current-message-pointer of ITM); 
   {call g2-work-on-drawing (ITM);}
   {call upp-raise-uil-components-to-top (ITM);}
end;

{call g2-work-on-drawing (true);}

{for ITM = each item in the uil-tab-page-contents-list of tab-page do
    if (the object-definition named by SAClass exists and ITM is an instance of the class named by (SAClass)) then
        call the procedure named by display-message-list-proc (ITM, the current-message-pointer of ITM);
end }

end

upp-deactivate-field-edit-for-tab-page-if-appropriate (TabPage: class uil-tab-page, W : class ui-client-item)
WS : class kb-workspace;
TabDialog : class uil-tab-dialog;
EditBox: class uil-edit-box;
DEBUG: truth-value = false;
begin
if (DEBUG) then
    inform the operator that "running upp-deactivate-field-edit-for-tab-page-if-appropriate";

    WS = the workspace of TabPage;
    if there exists a uil-tab-dialog superior to WS then
        TabDialog = the uil-tab-dialog superior to WS
    else
        return;

    if (DEBUG) then
        inform the operator that "check for an open edit-session on tab page";

    if (there exists a uil-edit-box EditBox that is the-current-uil-message-being-edited-on W) and (the workspace of EditBox exists and the workspace of EditBox is the same object as WS) then
        begin
        if (DEBUG) then
            inform the operator that "calling uil-unselect-edit-box on: [the id of EditBox]";
        call uil-unselect-edit-box (EditBox, TabDialog, W, the symbol end-edit-and-do-nothing);
        end;
end

g2:c.METHOD.g2:a.TEXT:
upp-set-button-label (B: class uil-tab-pushbutton, T: text)
    FS: symbol;
    TW, TH: integer;
    X-margin, Y-margin, min-width: integer;
    Baseline-depth: integer;
    D : class item;
    DEBUG: truth-value = false;
begin 
    if (DEBUG) then
        inform the operator that "Running uil-text-button::upp-set-button-label, text is @"[T]@" Size=[the uil-size of B]";
    min-width = 69;
    FS = the uil-size of B;
    if (FS = the symbol small) then
        begin
        X-margin = {18} 4;
        Y-margin = {10} 3; 
        Baseline-depth = 10;
        end
    else if (FS = the symbol medium) or (FS = the symbol unspecified) then
        begin
        FS = the symbol large;
        X-margin = {22} 4;
        Y-margin = {10} 2; 
        Baseline-depth = 13;
        end
    else if (FS = the symbol large) or (FS = the symbol extra-large) then
        begin
        FS = the symbol extra-large; 
        X-margin = 6;
        Y-margin = 4;
        Baseline-depth = 16;
        end
    else
        return;
         
    {Note:  the "baseline-depth" figures above were obtained empirically by trial-and-error.  They represent the distance between the actual top of the text and the baseline of the first line of text, which is used as the zero-point for positioning the text.}
    if (the uil-resize-to-fit-label of B is true) then
        begin 
        TW, TH = call uil-get-text-extent (T, FS);
        if (DEBUG) then
            inform the operator that "Resizing button to fit label:  width = [TW+ (2 * X-margin)], height = [TH + (2 * Y-margin)]";
        conclude that the icon-variables of B = evaluated-structure (
            the symbol uil-button-label-value, T, 
            the symbol uil-button-label-font-size, FS, 
            the symbol uil-button-label-x-position, X-margin + 4,
            the symbol uil-button-label-y-position, Baseline-depth + Y-margin + 5 ,
            the symbol width, max (min-width, TW + (2 * X-margin)),
            the symbol height, TH + (2 * Y-margin) );
        call uil-center-label-in-text-button (B); 
        end
    else
        begin 
        if (DEBUG) then
            inform the operator that "Centering label in button";
        conclude that the uil-button-label-value of the icon-variables of B = T;
        conclude that the uil-button-label-font-size of the icon-variables of B = FS;
        call uil-center-label-in-text-button (B); 
        end;

{call upp-refresh-tab-pages (B);}
if the workspace of B exists and the item D superior to the workspace of B exists and D is a uil-tab-dialog then call upp-align-tab-buttons (D);
end

g2:c.PROCEDURE.g2:a.TEXT:
upp-resize-this-tab-page-into-conformance (TabPage: class uil-tab-page, D: class uil-tab-dialog)

Page : class uil-tab-page;
TPL : class item-list;
WS : class kb-workspace;
L, R, T, B : integer;
DEBUG: truth-value = false;

begin

if DEBUG then 
    post "Running upp-resize-this-tab-page-into-conformance.";

if the workspace of TabPage does not exist then
    return;

D = the uil-tab-dialog superior to the workspace of TabPage;
TPL = the uil-tab-page-list of D;
for Page = each uil-tab-page in TPL do
    exit if true;
end;

WS = the subworkspace of D;
L = truncate (utl-left-edge (Page));
R = truncate (utl-right-edge (Page));
T = truncate (utl-top-edge (Page));
B = truncate (utl-bottom-edge (Page));

call g2-change-size-of-item-per-area (TabPage, L, T, R, B, the symbol NONE);

if DEBUG then
    post "Values are: l=[l], r=[r], t=[t], b=[b]";

end

upp-activate-field-edit-for-tab-page-if-appropriate (TabPage: class uil-tab-page, W : class ui-client-item)
TabDialog : class uil-tab-dialog;
X, Y : integer;
TopEditBox : item-or-value;
EditBox: class uil-edit-box;
TPL : class item-list;
DEBUG: truth-value = false;
begin
if (DEBUG) then inform the operator that "running upp-activate-field-edit-for-tab-page-if-appropriate";
TabDialog = the uil-tab-dialog superior to the workspace of TabPage;
if the uil-origin of TabDialog = the symbol master then return;
TPL = the uil-tab-page-contents-list of TabPage;
TopEditBox = "unspecified";
for EditBox = each uil-edit-box in TPL do 
    if (TopEditBox is not an item) then begin
        TopEditBox = EditBox;
        X = the item-x-position of EditBox;
        Y = the item-y-position of EditBox;
    end
    else begin
        if (the item-y-position of EditBox > Y) then begin
            TopEditBox = EditBox;
            X = the item-x-position of EditBox;
            Y = the item-y-position of EditBox; 
        end
        else begin
            if (the item-y-position of EditBox = Y) and (the item-x-position of EditBox < X) then begin
                TopEditBox = EditBox;
                X = the item-x-position of EditBox;
                Y = the item-y-position of EditBox; 
            end;
        end;
    end;
end;

if TopEditBox is an item then call uil-launch-edit-session(TopEditBox, W); 
 
end

upp-make-tab-page-permanent (TabPage : class uil-tab-page)
TabButton : class uil-tab-pushbutton;
debug: truth-value = false;
begin

if debug then
    inform the operator that "Running upp-make-tab-page-permanent."; 

if the uil-tab-pushbutton that is the-tab-button-of-the-tab-page TabPage exists then
  TabButton = the uil-tab-pushbutton that is the-tab-button-of-the-tab-page TabPage;


conclude that the uil-is-permanent of TabPage is true;
make TabPage permanent;

If TabButton exists then
  begin
    conclude that the uil-is-permanent of TabButton is true;
    make TabButton permanent;
  end;

end

upp-clone-tab-page-method (tab-page : class uil-tab-page) = (class uil-tab-page)
D : class uil-tab-dialog;
WS : class kb-workspace;
tab-page-list: class item-list; 
cloned-tab-page : class uil-tab-page;
tab-page-button, cloned-tab-page-button : class uil-tab-pushbutton;
tab-page-contents-list, cloned-tab-page-contents-list: class item-list;
g: class item; 
i, ri: class item;
cloned-item: item-or-value;
cycle: integer = 0;
cloned-border, cloned-item-test: item-or-value;
MsgClass: symbol = the symbol uil-message-object;
SAClass: symbol = the symbol uil-scroll-area;
SACornerTerminator: symbol = the symbol uil-scroll-area-corner-object-terminator; 
SliderClass: symbol = the symbol uil-slider;
ComboClass: symbol = the symbol uil-combo-box; 
BoxButtonClass: symbol = the symbol uil-combo-box-button;
BDR, TXT, TXT2: item-or-value;

DEBUG: truth-value = false; 
en: symbol; et: text;
begin
if (DEBUG) then
    inform the operator that "Running upp-clone-tab-page-method.";

D = the uil-tab-dialog superior to the workspace of tab-page;
WS = the subworkspace of D;
tab-page-list = the uil-tab-page-list of D;

{-- Walk tab-page-contents-list and uil-clone UIL objects --}
create a uil-tab-page cloned-tab-page by cloning tab-page;
if there exists a uil-tab-pushbutton that is the-tab-button-of-the-tab-page tab-page then
  begin
    tab-page-button = the uil-tab-pushbutton that is the-tab-button-of-the-tab-page tab-page;
    create a item cloned-tab-page-button by cloning tab-page-button;
    conclude that cloned-tab-page-button is now the-tab-button-of-the-tab-page cloned-tab-page;
  end;

call uil-add-tab-page-to-dialog (cloned-tab-page, D);
cloned-tab-page-contents-list = the uil-tab-page-contents-list of cloned-tab-page; 
tab-page-contents-list = the uil-tab-page-contents-list of tab-page;

{---- transfer cloned-tab-page to WS at (the item-x-position of tab-page, the item-y-position of tab-page);  ----}

for g = each item in tab-page-contents-list do
    cycle = cycle + 1; 
    if (remainder (cycle, 2) = 0) then 
        allow other processing;

    if (DEBUG) then
        inform the operator that "processing: [the class of g]";

    if (g is a uil-text and the uil-stand-alone-text of g is false) then
        go to get-next-item;
    if g is a uil-selection-button then
        go to get-next-item; 
    if not (the uil-clone-method of g exists) then
        go to get-next-item; 

{-- BUTTON, SCROLL AREA, EDIT-BOX, TEXT (stand-alone) --}
    if (g is a uil-button and not (g is a uil-selection-button or g is a uil-spin-button or (the message-definition named by ComboClass exists and g is an instance of the class named by (BoxButtonClass)))) or (the object-definition named by SAClass exists and g is an instance of the class named by (SAClass) or g is a uil-edit-box) or (g is a uil-text and the uil-stand-alone-text of g is true) then
            begin
            if (DEBUG) then
                inform the operator that "1. case of: [the class of g]";
            if (g is a uil-edit-box) and (the id of g exists) and (is-contained-in-text ("id-for-edit-mask-xxx", the id of g)) then
                begin
                if (DEBUG) then
                    inform the operator that "Ignoring field edit";
                end
            else
                begin
                cloned-item = call uil-clone-grobj-method (g);
                transfer cloned-item to the workspace of g at (the item-x-position of g, the item-y-position of g); 
                call uil-add-grobj-to-tab-page (cloned-item, cloned-tab-page); 

                if (DEBUG) then
                    inform the operator that "after clone. cloned-item is: <[(if cloned-item exists and cloned-item is an item then the class of cloned-item else "cloned-item does not exist")]>";

                if (the object-definition named by SAClass exists and g is an instance of the class named by (SAClass)) then
                    allow other processing;

                if (g is not a uil-dialog-title) and (cloned-item exists and cloned-item is an item) then 
                    cloned-item = call upp-manage-grobj-without-configure (cloned-item, WS, the symbol none, the item-x-position of g, the item-y-position of g)                
                else if (cloned-item exists and cloned-item is an item) then
                    transfer cloned-item to WS at (the item-x-position of g, the item-y-position of g);

                BDR = call uil-get-border (g);
                if (g is a uil-edit-box or g is a uil-text) and (BDR exists and BDR is an item) then
                    begin
                    cloned-border = call upp-clone-box-border-method (BDR);
                    if (cloned-item exists and cloned-item is an item) then
                        begin
                        call upp-name-and-relate-border-to-text (cloned-item, cloned-border);
                        call upp-wrap-border-around-text (cloned-item, cloned-border);
                        end;
                    conclude that the uil-has-been-cloned of BDR is true;
                    end;

                TXT = call uil-get-label-text (g);
                 if (g is a uil-edit-box) and (TXT exists and TXT is a uil-text) and (cloned-item exists and cloned-item is an item) then
                    begin
                    call uil-update-edit-box-label (cloned-item);
                    TXT2 = call uil-get-label-text (cloned-item);
                    if (TXT2 exists and TXT2 is an item) then
                        call upp-copy-grmes-attributes (TXT, TXT2);
                    end;
                end; {-- else not a field-mask --}
            end

{-- Message Object --}
        else if (the message-definition named by MsgClass exists and g is an instance of the class named by (MsgClass)) then
            begin
            {-- do nothing. messages cloned with scroll-area --}
            end

{-- BOX OBJECT --}
        else if (the class of g is uil-box-object) and (the uil-has-been-cloned of g is false) and (the uil-grmes that is the-uil-boxed-message-of g does not exist) then
            begin
            if (DEBUG) then
                inform the operator that "case of uil-box-object";
            conclude that the uil-has-been-cloned of the uil-box-object connected to g is true; 
            cloned-item = call upp-create-box-object-specific (g, WS);
            call uil-add-grobj-to-tab-page (cloned-item, cloned-tab-page); 
            {conclude that the uil-has-been-cloned of g is true;}
            end 

{-- EXTENSION OBJECT --}
        else if (g is a uil-extension-object) then
            begin
            if (DEBUG) then
                inform the operator that "EXTENSION OBJECT";
            if (g is a uil-box-object or g is a uil-line-separator) and (the uil-has-been-cloned of g is false) and (the uil-grmes that is the-uil-boxed-message-of g does not exist) and (the uil-dialog-title that is a-uil-title-bar-anchor-for g does not exist) then
                begin
                if g is a uil-box-border-right then g = the uil-box-border connected to g;
                if g is a uil-line-separator-right then g = the uil-line-separator connected to g;
                if not (g has a name) then 
                    begin 
                    {-- this means that the border is standalone, and not a border wrapped around some text. --}
                    cloned-item = call uil-clone-grobj-method (g);
                    call uil-add-grobj-to-tab-page (cloned-item, cloned-tab-page);                  
                    if (cloned-item exists and cloned-item is an item) then
                        begin                                               
                        call uil-manage-grobj (cloned-item, WS, the symbol none, the item-x-position of g, the item-y-position of g);
                        if (the uil-corner-anchored-to-workspace of g exists and the uil-corner-anchored-to-workspace of g is true) then
                            begin
                            if (DEBUG) then
                                inform the operator that "manage WITH configure: the uil-corner-anchored-to-workspace of cloned-item is: <[the uil-corner-anchored-to-workspace of cloned-item]>"; 
                            call uil-manage-grobj (cloned-item, WS , the symbol none, the item-x-position of g, the item-y-position of g);
                            end
                        else
                            begin
                            if (DEBUG) then
                                inform the operator that "manage without configure"; 
                            call upp-manage-grobj-without-configure (cloned-item, WS, the symbol none, the item-x-position of g, the item-y-position of g);
                            end;
                        end;
                    
                    conclude that the uil-has-been-cloned of g is true;
                    conclude that the uil-has-been-cloned of the uil-extension-object connected to g is true;
                    end
                end
            else if (g is a uil-warning-icon or g is a uil-question-icon) then 
                begin 
                  create an item cloned-item by cloning g;
                  transfer cloned-item to WS at (the item-x-position of g, the item-y-position of g);
                end;

            {-- This ensures that border separator are configured properly. --}
            if (g is a uil-line-separator and cloned-item exists and cloned-item is an item) then
                call uil-move-grobj (cloned-item);
            end

        else
            begin
                cloned-item = call the procedure named by the uil-clone-method of g (g);
                transfer cloned-item to the workspace of g at (the item-x-position of g, the item-y-position of g);
                call uil-add-grobj-to-tab-page (cloned-item, cloned-tab-page);
                {call uil-manage-grobj (cloned-item, WS, the symbol none, the item-x-position of g, the item-y-position of g);}
            end;

get-next-item:
end;

{ ----- call upp-move-tab-page (cloned-tab-page);    -----}

if (DEBUG) then
    inform the operator that "Leaving upp-clone-tab-page-method.";
 
return cloned-tab-page;
end

upp-set-tab-pushbutton-movement-limits (P: class uil-tab-page)
D : Class uil-dialog;
TPL : class item-list;
WS : class kb-workspace;
BTN : class uil-tab-pushbutton;
l, r, t, b, w, h : integer;
debug: truth-value = false;
begin
WS = the workspace of P;
if there exists a uil-tab-dialog superior to WS then
    D = the uil-tab-dialog superior to WS 
else
    return;
TPL = the uil-tab-page-list of D;
w = 0 ; h = 0;
for BTN = each uil-tab-pushbutton upon WS do
    if the item-width of BTN > w then
        w = the item-width of BTN;
    if the item-height of BTN > h then
        h = the item-height of BTN;
end;

case (the uil-tab-page-button-placement of D) of

    left : 
        begin
            l = truncate (utl-left-edge (P)) - w;
            r = truncate (utl-left-edge (P));
            b = truncate (utl-bottom-edge (P));
            t = truncate (utl-top-edge (P));
        end;
    right:
        begin
            l = truncate (utl-right-edge (P));
            r = truncate (utl-right-edge (P)) + w; 
            b = truncate (utl-bottom-edge (P));
            t = truncate (utl-top-edge (P)); 
        end;
    otherwise:
        begin
            l = truncate (utl-left-edge (P));
            r = truncate (utl-right-edge (P));
            b = truncate (utl-top-edge (P));
            t = truncate (utl-top-edge (P)) + h; 
        end;
    end;

for P = each uil-tab-page in TPL do
    BTN = the uil-tab-pushbutton that is the-tab-button-of-the-tab-page P;
    if debug then
        inform the operator that "[the user-data of BTN] [l], [r], [b], [t]";
    call g2-set-movement-limits (BTN, the symbol unless, the symbol administrator, l, r, b, t);
end ;

end

upp-check-for-reordered-tab-pages (P : class uil-tab-page)
D : class uil-tab-dialog;
TPL : class item-list;
WS : class kb-workspace;
L : class item-list;
side : symbol;
x, y, item-count: integer;
BTN, BTN2, prev-button, first-button: class uil-tab-pushbutton;
begin
WS = the workspace of P;
D = the item superior to WS;
TPL = the uil-tab-page-list of D;
side = the uil-tab-page-button-placement of D;
create an item-list L;
for BTN = each uil-tab-pushbutton upon WS do
  for BTN2 = each uil-tab-pushbutton in L do
    if side is left or side is right then
      begin
        if the item-y-position of BTN > the item-y-position of BTN2 then
          begin
            insert BTN before BTN2 in the item list L;
            go to inner-loop-exit;
          end;
      end;
    if side is top then
      begin 
        if the item-x-position of BTN < the item-x-position of BTN2 then
          begin
            insert BTN before BTN2 in the item list L;
            go to inner-loop-exit;
          end; 
      end;
  end;
  insert BTN at the end of the item list L;
  inner-loop-exit:
end;

for P = each uil-tab-page in TPL do
    remove P from TPL ;
end;

for BTN = each uil-tab-pushbutton in L do
    P = the uil-tab-page that is the-tab-page-handled-by-the-tab-button BTN;
    insert P at the end of the item list TPL;
end;

delete L; 

{-- Realign all tab-page buttons ---}
item-count = 0;
for P = each uil-tab-page in TPL do 
    BTN = the uil-tab-pushbutton that is the-tab-button-of-the-tab-page P ;
    if item-count = 0 then 
      begin
          prev-button = BTN;
          first-button = BTN;
      end; 
    x, y = call upp-get-coordinates-for-tab-page-button (D, P, BTN, prev-button, item-count); 
    item-count = item-count + 1; 
    move BTN to (x, y);
    call uil-configure-grobj-method (BTN);
    if the uil-is-permanent of BTN then
        call upp-make-item-permanent (BTN); 
    prev-button = BTN; 
end; 

{--- select top page ---}
for P = each uil-tab-page in TPL do
    call uil-select-tab-page (D, P, the symbol no-window-to-pass);
    exit if true;
end;

end

upp-tab-page-movement-adjustments (P : class uil-tab-page)
begin
call upp-check-for-reordered-tab-pages (P);
call upp-set-tab-pushbutton-movement-limits (P);
end

upp-disallow-all-edit-fields-for-dialog (D : class uil-tab-dialog, W : item-or-value)
WS : class kb-workspace;
Page : class uil-tab-page;
TPL : class item-list;
TPCL : class item-list;
itm : class item;
begin

TPL = the uil-tab-page-list of D;

for page = each uil-tab-page in TPL do
    call upp-disallow-all-edit-fields-for-tab-page (page, W);
end

end

upp-clone-tab-dialog-contents (WS-FROM: class kb-workspace, WS-TO: class kb-workspace) 
item-is-member-of-tab-page-list : truth-value; 
g: class item;
D : class uil-tab-dialog;
i, ri: class item;
box: class uil-selection-box;
cloned-item: item-or-value;
b, pos-button: item-or-value;
cb1, cb2, ob: class uil-box-object;  
p: class uil-point;
cloned-border, cloned-item-test: item-or-value;
BDR, TXT, TXT2: item-or-value;
mems: class text-array;
t: text;
x, y: integer;
cycle: integer = 0;
found : truth-value = false;
MsgClass: symbol = the symbol uil-message-object;
SAClass: symbol = the symbol uil-scroll-area;
SACornerTerminator: symbol = the symbol uil-scroll-area-corner-object-terminator; 
DEBUG: truth-value = false;
DEBUG-RBOX: truth-value = false;
en: symbol; et: text;
begin
if (DEBUG) then
    inform the operator that "Running upp-clone-tab-dialog-contents.";

    change the background-color of WS-TO to the background-color of WS-FROM;
    change the foreground-color of WS-TO to the foreground-color of WS-FROM;
    change the text of the workspace-margin of WS-TO to the text of the workspace-margin of WS-FROM;

    {-- Walk WS-FROM and uil-clone UIL objects onto WS-TO --}
    D = the uil-tab-dialog superior to WS-FROM;
    for g = each item upon WS-FROM do
        cycle = cycle + 1; 
        if (remainder (cycle, 2) = 0) then 
            allow other processing;
        found = call upp-is-item-a-member-of-tab-page-lists (G, D);
        if found then
            go to iterate-to-next-grobj-on-workspace;
        if g is a uil-tab-pushbutton or g is a uil-tab-page then
            go to iterate-to-next-grobj-on-workspace; 
        if (DEBUG) then
            inform the operator that "processing: [the class of g]";

{-- SELECTION BUTTON --}
        if (g is a uil-selection-button) and (the uil-has-been-cloned of g is false) then
            begin
            {-- When a selection-button is found on a dialog, the entire selection box is cloned and moved to the new dialog.  Each successive button in the original selection-box is marked as already-cloned by setting the uil-has-been-cloned attribute to true.  This works, but the downside is the cloned selection buttons will always line up under the top button, so if the user manually set up the selection buttons in a different order then the default, the positioning is lost.  A fix could be to keep the coordinates of a selection button as attrs of the button object and use them to position. --}
            box = the uil-selection-box that is the-group-master-of g;
            cloned-item = call uil-clone-grobj-method (box);

{inform the operator that "1. T.O.: cloned=> [the uil-event-target-object of cloned-item] orig=> [the uil-event-target-object of box]";}
     
            {-- Get the top radio-button for correct positioning --}
            mems = the members of box;            
            pos-button = call uil-get-selection-button ("[mems[0]]");

            call upp-manage-grobj-without-configure (cloned-item, WS-TO, the symbol none, the item-x-position of pos-button, the item-y-position of pos-button);

            {-- This next call adjusts the positions of the cloned buttons in case they were moved after being managed --}
            call upp-adjust-selection-buttons-position (WS-TO, box, cloned-item);
            for b = each uil-selection-button that is grouped-by box do
                conclude that the uil-has-been-cloned of b is true;
                end;

{inform the operator that "2. T.O.: cloned=> [the uil-event-target-object of cloned-item] orig=> [the uil-event-target-object of box]";}       
      end {if-begin}

{-- BUTTON, SCROLL AREA, EDIT-BOX, TEXT (stand-alone) --}
        else if (g is a uil-button and g is not a uil-selection-button) or (the object-definition named by SAClass exists and g is an instance of the class named by (SAClass) or g is a uil-edit-box) or (g is a uil-text and the uil-stand-alone-text of g is true) then
            begin
            if (DEBUG) then
                inform the operator that "1. case of: [the class of g]";
            if (g is a uil-edit-box) and (the id of g exists) and (is-contained-in-text ("id-for-edit-mask-xxx", the id of g)) then
                begin
                if (DEBUG) then
                    inform the operator that "Ignoring field edit";
                end
            else
                begin
                cloned-item = call uil-clone-grobj-method (g);

                if (DEBUG) then
                    inform the operator that "after clone. cloned-item is: <[(if cloned-item exists and cloned-item is an item then the class of cloned-item else "cloned-item does not exist")]>";

                if (the object-definition named by SAClass exists and g is an instance of the class named by (SAClass)) then
                    allow other processing;

                if (g is not a uil-dialog-title) and (cloned-item exists and cloned-item is an item) then 
                    cloned-item = call upp-manage-grobj-without-configure (cloned-item, WS-TO, the symbol none, the item-x-position of g, the item-y-position of g)                
                else if (cloned-item exists and cloned-item is an item) then
                    transfer cloned-item to WS-TO at (the item-x-position of g, the item-y-position of g);

                BDR = call uil-get-border (g);
                if (g is a uil-edit-box or g is a uil-text) and (BDR exists and BDR is an item) then
                    begin
                    cloned-border = call upp-clone-box-border-method (BDR);
                    if (cloned-item exists and cloned-item is an item) then
                        begin
                        call upp-name-and-relate-border-to-text (cloned-item, cloned-border);
                        call upp-wrap-border-around-text (cloned-item, cloned-border);
                        end;
                    conclude that the uil-has-been-cloned of BDR is true;
                    end;

                TXT = call uil-get-label-text (g);
                 if (g is a uil-edit-box) and (TXT exists and TXT is a uil-text) and (cloned-item exists and cloned-item is an item) then
                    begin
                    call uil-update-edit-box-label (cloned-item);
                    TXT2 = call uil-get-label-text (cloned-item);
                    if (TXT2 exists and TXT2 is an item) then
                        call upp-copy-grmes-attributes (TXT, TXT2);
                    end;
                end; {-- else not a field-mask --}
            end

{-- BOX OBJECT --}
        else if (the class of g is uil-box-object) and (the uil-has-been-cloned of g is false) and (the uil-grmes that is the-uil-boxed-message-of g does not exist) then
            begin
            conclude that the uil-has-been-cloned of the uil-box-object connected to g is true; 
            call upp-create-box-object-specific (g, WS-TO);
            conclude that the uil-has-been-cloned of g is true;
            end 

{-- EXTENSION OBJECT --}
        else if (g is a uil-extension-object) then
            begin
            if (DEBUG) then
                inform the operator that "EXTENSION OBJECT";
            if (g is a uil-box-object or g is a uil-line-separator) and (the uil-has-been-cloned of g is false) and (the uil-grmes that is the-uil-boxed-message-of g does not exist) and (the uil-dialog-title that is a-uil-title-bar-anchor-for g does not exist) then
                begin
                if g is a uil-box-border-right then g = the uil-box-border connected to g;
                if g is a uil-line-separator-right then g = the uil-line-separator connected to g;
                if not (g has a name) then 
                    begin 
                    {-- this means that the border is standalone, and not a border wrapped around some text. --}
                    cloned-item = call uil-clone-grobj-method (g);
                    if (cloned-item exists and cloned-item is an item) then
                        begin                       
                        {call uil-manage-grobj (cloned-item, WS-TO , the symbol none, the item-x-position of g, the item-y-position of g);}
                        if (the uil-corner-anchored-to-workspace of g exists and the uil-corner-anchored-to-workspace of g is true) then
                            begin
                            if (DEBUG) then
                                inform the operator that "manage WITH configure: the uil-corner-anchored-to-workspace of cloned-item is: <[the uil-corner-anchored-to-workspace of cloned-item]>"; 
                            call uil-manage-grobj (cloned-item, WS-TO , the symbol none, the item-x-position of g, the item-y-position of g);
                            end
                        else
                            begin
                            if (DEBUG) then
                                inform the operator that "manage without configure"; 
                            call upp-manage-grobj-without-configure (cloned-item, WS-TO , the symbol none, the item-x-position of g, the item-y-position of g);
                            end;
                        end;
                    conclude that the uil-has-been-cloned of g is true;
                    conclude that the uil-has-been-cloned of the uil-extension-object connected to g is true;
                    end
                end
            else if (g is a uil-warning-icon or g is a uil-question-icon) then 
                begin 
                  create an item cloned-item by cloning g;
                  transfer cloned-item to WS-TO at (the item-x-position of g, the item-y-position of g);
                end;

            {-- This ensures that border separator are configured properly. --}
            if (g is a uil-line-separator and cloned-item exists and cloned-item is an item) then
                call uil-move-grobj (cloned-item);
            end

{-- DIALOG --}
        else if (g is a uil-dialog) then
            begin
            cloned-item = call uil-clone-grobj-method (g);
            allow other processing; 
            transfer cloned-item to WS-TO at (the item-x-position of g, the item-y-position of g);
            conclude that the uil-is-managed of cloned-item = 1;
            call uil-make-grobj-permanent (cloned-item);
            end

{-- Message Object --}
        else if (the message-definition named by MsgClass exists and g is an instance of the class named by (MsgClass)) then
            begin
            {-- do nothing. messages cloned with scroll-area --}
            end

{-- UIL-OBJECT & UIL-MESSAGE --}
        else if (g is a uil-object) or (g is a uil-message) then
            begin
            if (the uil-has-been-cloned of g exists and the uil-has-been-cloned of g is false) or (the uil-clone-method of g exists and the procedure named by the uil-clone-method of g exists and the uil-clone-method of g /= the symbol uil-clone-grobj-method and the uil-clone-method of g /= the symbol uil-clone-grobj) then
                begin
                cloned-item = call the procedure named by the uil-clone-method of g (g);
                call uil-manage-grobj-method (cloned-item, WS-TO , the symbol none, the item-x-position of g, the item-y-position of g); 
                end;
            end

{-- NOT A GROBJ or GRMES --}
        else if (g is not a uil-grobj and g is not a uil-grmes) and (the object-definition named by SACornerTerminator exists and g is not an instance of the class named by (SACornerTerminator)) then 
            begin
            if (DEBUG) then
                inform the operator that "g is not a uil-grobj or uil-grmes";
            end

        else
            begin
            {-- Unhandled uil-grobj or uil-grmes. --}
            if (DEBUG) then
                inform the operator that "potentially unhandled uil-grobj or uil-grmes <[the class of g]>: uil-clone-method: [the uil-clone-method of g]"; 

            if (the uil-has-been-cloned of g exists and the uil-has-been-cloned of g is false) or (the uil-clone-method of g exists and the procedure named by the uil-clone-method of g exists and the uil-clone-method of g /= the symbol uil-clone-grobj-method and the uil-clone-method of g /= the symbol uil-clone-grobj) then
                begin
                cloned-item = call the procedure named by the uil-clone-method of g (g);
                call uil-manage-grobj-method (cloned-item, WS-TO , the symbol none, the item-x-position of g, the item-y-position of g); 
                end

            else
                begin
                if (DEBUG) then
                    inform the operator that "g is unhandled: [the class of g]";
                end;                         
            end;
    iterate-to-next-grobj-on-workspace:
        end; {for}

    {-- Restore the has-been-cloned attr of selection-buttons and uil-box-borders so they can be cloned again, naturally --}
    for b = each uil-selection-button upon WS-FROM do
        conclude that the uil-has-been-cloned of b is false;
        end;

    for i = each uil-extension-object upon WS-FROM do
        conclude that the uil-has-been-cloned of i is false;
        end;

    for b = each uil-dialog-title upon WS-TO do
        call uil-adjust-border-on-workspace (WS-TO);
        end;

    change the size of WS-TO to minimum;

if (DEBUG-RBOX) then
    begin
    for i = each uil-selection-box upon WS-TO do
        inform the operator that "T.O.: [the uil-event-target-object of i]  T.A.: [the uil-event-target-attribute of i] ";
        end;
    end;

if (DEBUG) then
    inform the operator that "Leaving upp-clone-workspace-contents.";
end 
on error (en, et)
if (G exists) then
    inform the operator that "

-- UIL INTERNAL ERROR --

Running: UPP-CLONE-WORKSPACE-CONTENTS

While attempting to clone a [the class of G], the following error occured:

[et]"
else
    inform the operator that "

-- UIL INTERNAL ERROR --

Running: UPP-CLONE-WORKSPACE-CONTENTS

[et]";
end

upp-clone-tab-page-internals (tab-page : class uil-tab-page, D : class uil-tab-dialog, cloned-dlg: class uil-tab-dialog) 
WS-TO : class kb-workspace = the subworkspace of cloned-dlg;
WS-FROM : class kb-workspace = the subworkspace of D;
cloned-tab-page : class uil-tab-page;
tab-page-button, cloned-tab-page-button : class uil-tab-pushbutton;
g: class item; 
tab-page-contents-list, cloned-tab-page-contents-list: class item-list;
i, ri: class item;
box: class uil-selection-box;
cloned-item: item-or-value;
b, pos-button: item-or-value;
cb1, cb2, ob: class uil-box-object;  
p: class uil-point;
cloned-border, cloned-item-test: item-or-value;
BDR, TXT, TXT2: item-or-value;
mems: class text-array;
SliderClass: symbol = the symbol uil-slider;
MsgClass: symbol = the symbol uil-message-object;
SAClass: symbol = the symbol uil-scroll-area;
SACornerTerminator: symbol = the symbol uil-scroll-area-corner-object-terminator; 
t: text;
x, y: integer;
cycle: integer = 0;
en: symbol; et: text;
DEBUG-RBOX: truth-value = false;
DEBUG: truth-value = false;
begin
if (DEBUG) then
    inform the operator that "Running upp-clone-tab-page-internals.";

    {-- Walk tab-page-contents-list and uil-clone UIL objects onto WS-TO --}
    create a uil-tab-page cloned-tab-page by cloning tab-page;
    conclude that the uil-is-permanent of cloned-tab-page is false;
    insert cloned-tab-page at the end of the uil-tab-page-list of cloned-dlg;
    if there exists a uil-tab-pushbutton that is the-tab-button-of-the-tab-page tab-page then
      begin
        tab-page-button = the uil-tab-pushbutton that is the-tab-button-of-the-tab-page tab-page;
        create a item cloned-tab-page-button by cloning tab-page-button;
        conclude that cloned-tab-page-button is now the-tab-button-of-the-tab-page cloned-tab-page; 
        conclude that the uil-is-permanent of cloned-tab-page-button is false; 
      end;
    transfer cloned-tab-page to WS-TO at (the item-x-position of tab-page, the item-y-position of tab-page);
    cloned-tab-page-contents-list = the uil-tab-page-contents-list of cloned-tab-page; 
    tab-page-contents-list = the uil-tab-page-contents-list of tab-page;

    for g = each item in tab-page-contents-list do
        cycle = cycle + 1; 
        if (remainder (cycle, 2) = 0) then 
            allow other processing;

        if (DEBUG) then
            inform the operator that "processing: [the class of g]";

{-- SELECTION BUTTON --}
        if (g is a uil-selection-box) then
            begin
            cloned-item = call uil-clone-grobj-method (g);

            {inform the operator that "1. T.O.: cloned=> [the uil-event-target-object of cloned-item] orig=> [the uil-event-target-object of box]";}
     
            {-- Get the top radio-button for correct positioning --}
            mems = the members of g;            
            pos-button = call uil-get-selection-button ("[mems[0]]");

            call upp-manage-grobj-without-configure (cloned-item, WS-TO, the symbol none, the item-x-position of pos-button, the item-y-position of pos-button);

            call uil-add-grobj-to-tab-page (cloned-item, cloned-tab-page);

            {-- This next call adjusts the positions of the cloned buttons in case they were moved after being managed --}
            call upp-adjust-selection-buttons-position (WS-TO, g, cloned-item);

            {inform the operator that "2. T.O.: cloned=> [the uil-event-target-object of cloned-item] orig=> [the uil-event-target-object of box]";}       
      end {if-begin}

{-- BUTTON, SCROLL AREA, EDIT-BOX, TEXT (stand-alone) --}
        else if (g is a uil-button and g is not a uil-selection-button) or (the object-definition named by SAClass exists and g is an instance of the class named by (SAClass) or g is a uil-edit-box) or (g is a uil-text and the uil-stand-alone-text of g is true) then
            begin
            if (DEBUG) then
                inform the operator that "1. case of: [the class of g]";
            if (g is a uil-edit-box) and (the id of g exists) and (is-contained-in-text ("id-for-edit-mask-xxx", the id of g)) then
                begin
                if (DEBUG) then
                    inform the operator that "Ignoring field edit";
                end
            else
                begin
                cloned-item = call uil-clone-grobj-method (g);
                if (DEBUG) then
                    inform the operator that "after clone. cloned-item is: <[(if cloned-item exists and cloned-item is an item then the class of cloned-item else "cloned-item does not exist")]>";

                if (the object-definition named by SAClass exists and g is an instance of the class named by (SAClass)) then
                    allow other processing;

                if (g is not a uil-dialog-title) and (cloned-item exists and cloned-item is an item) then 
                  begin
                    cloned-item = call upp-manage-grobj-without-configure (cloned-item, WS-TO, the symbol none, the item-x-position of g, the item-y-position of g) ;
                  end           
                else if (cloned-item exists and cloned-item is an item) then
                    transfer cloned-item to WS-TO at (the item-x-position of g, the item-y-position of g);

                BDR = call uil-get-border (g);
                if (g is a uil-edit-box or g is a uil-text) and (BDR exists and BDR is an item) then
                    begin
                    cloned-border = call upp-clone-box-border-method (BDR);
                    if (cloned-item exists and cloned-item is an item) then
                        begin
                          call upp-name-and-relate-border-to-text (cloned-item, cloned-border);
                          call upp-wrap-border-around-text (cloned-item, cloned-border);
                        end;
                    conclude that the uil-has-been-cloned of BDR is true;
                    end;

                TXT = call uil-get-label-text (g);
                 if (g is a uil-edit-box) and (TXT exists and TXT is a uil-text) and (cloned-item exists and cloned-item is an item) then
                    begin
                      call uil-update-edit-box-label (cloned-item);
                      TXT2 = call uil-get-label-text (cloned-item);
                      if (TXT2 exists and TXT2 is an item) then
                          call upp-copy-grmes-attributes (TXT, TXT2);
                    end;
                end; {-- else not a field-mask --} 

                call uil-add-grobj-to-tab-page (cloned-item, cloned-tab-page); 
            end

{-- BOX OBJECT --}
        else if (the class of g is uil-box-object) and (the uil-has-been-cloned of g is false) and (the uil-grmes that is the-uil-boxed-message-of g does not exist) then
            begin
              conclude that the uil-has-been-cloned of the uil-box-object connected to g is true; 
              call upp-create-box-object-specific (g, WS-TO);
              conclude that the uil-has-been-cloned of g is true;
            end 

{-- EXTENSION OBJECT --}
        else if (g is a uil-extension-object) then
            begin
            if (DEBUG) then
                inform the operator that "EXTENSION OBJECT";
            if (g is a uil-box-object or g is a uil-line-separator) and (the uil-has-been-cloned of g is false) and (the uil-grmes that is the-uil-boxed-message-of g does not exist) and (the uil-dialog-title that is a-uil-title-bar-anchor-for g does not exist) then
                begin
                if g is a uil-box-border-right then g = the uil-box-border connected to g;
                if g is a uil-line-separator-right then g = the uil-line-separator connected to g;
                if not (g has a name) then 
                    begin 
                    {-- this means that the border is standalone, and not a border wrapped around some text. --}
                    cloned-item = call uil-clone-grobj-method (g);
                    if (cloned-item exists and cloned-item is an item) then
                        begin                       
                        {call uil-manage-grobj (cloned-item, WS-TO , the symbol none, the item-x-position of g, the item-y-position of g);}
                        if (the uil-corner-anchored-to-workspace of g exists and the uil-corner-anchored-to-workspace of g is true) then
                            begin
                            if (DEBUG) then
                                inform the operator that "manage WITH configure: the uil-corner-anchored-to-workspace of cloned-item is: <[the uil-corner-anchored-to-workspace of cloned-item]>"; 
                            call uil-manage-grobj (cloned-item, WS-TO , the symbol none, the item-x-position of g, the item-y-position of g); 
                            call uil-add-grobj-to-tab-page (cloned-item, cloned-tab-page); 
                            end
                        else
                            begin
                            if (DEBUG) then
                                inform the operator that "manage without configure"; 
                            call upp-manage-grobj-without-configure (cloned-item, WS-TO , the symbol none, the item-x-position of g, the item-y-position of g); 
                            call uil-add-grobj-to-tab-page (cloned-item, cloned-tab-page); 
                            end;
                        end;
                    conclude that the uil-has-been-cloned of g is true;
                    conclude that the uil-has-been-cloned of the uil-extension-object connected to g is true;
                    end
                end
            else if (g is a uil-warning-icon or g is a uil-question-icon) then 
                begin 
                  create an item cloned-item by cloning g;
                  transfer cloned-item to WS-TO at (the item-x-position of g, the item-y-position of g);
                end;

            {-- This ensures that border separator are configured properly. --}
            if (g is a uil-line-separator and cloned-item exists and cloned-item is an item) then
                call uil-move-grobj (cloned-item);
            end

{-- DIALOG --}
        else if (g is a uil-dialog) then
            begin
            cloned-item = call uil-clone-grobj-method (g);
            allow other processing; 
            transfer cloned-item to WS-TO at (the item-x-position of g, the item-y-position of g);
            conclude that the uil-is-managed of cloned-item = 1;
            call uil-make-grobj-permanent (cloned-item);
            end

{-- Message Object --}
        else if (the message-definition named by MsgClass exists and g is an instance of the class named by (MsgClass)) then
            begin
            {-- do nothing. messages cloned with scroll-area --}
            end

{-- SLIDER --}
        else if (the object-definition named by SliderClass exists and g is an instance of the class named by (SliderClass)) then
            begin
            cloned-item = call uil-clone-grobj-method (g);
            call uil-manage-grobj-method (cloned-item, WS-TO, the symbol none, the item-x-position of g, the item-y-position of g);
            call uil-add-grobj-to-tab-page (cloned-item, cloned-tab-page);
            end

{-- UIL-OBJECT & UIL-MESSAGE --}
        else if (g is a uil-object) or (g is a uil-message) then
            begin
            if (the uil-has-been-cloned of g exists and the uil-has-been-cloned of g is false) or (the uil-clone-method of g exists and the procedure named by the uil-clone-method of g exists and the uil-clone-method of g /= the symbol uil-clone-grobj-method and the uil-clone-method of g /= the symbol uil-clone-grobj) then
                begin
                cloned-item = call the procedure named by the uil-clone-method of g (g);
                call uil-manage-grobj-method (cloned-item, WS-TO , the symbol none, the item-x-position of g, the item-y-position of g); 
                call uil-add-grobj-to-tab-page (cloned-item, cloned-tab-page); 
                end;
            end

{-- NOT A GROBJ or GRMES --}
        else if (g is not a uil-grobj and g is not a uil-grmes) and (the object-definition named by SACornerTerminator exists and g is not an instance of the class named by (SACornerTerminator)) then 
            begin
            if (DEBUG) then
                inform the operator that "g is not a uil-grobj or uil-grmes";
            end

        else
            begin
            {-- Unhandled uil-grobj or uil-grmes. --}
            if (DEBUG) then
                inform the operator that "potentially unhandled uil-grobj or uil-grmes <[the class of g]>: uil-clone-method: [the uil-clone-method of g]"; 

            if (the uil-has-been-cloned of g exists and the uil-has-been-cloned of g is false) or (the uil-clone-method of g exists and the procedure named by the uil-clone-method of g exists and the uil-clone-method of g /= the symbol uil-clone-grobj-method and the uil-clone-method of g /= the symbol uil-clone-grobj) then
                begin
                cloned-item = call the procedure named by the uil-clone-method of g (g);
                call uil-manage-grobj-method (cloned-item, WS-TO , the symbol none, the item-x-position of g, the item-y-position of g); 
                call uil-add-grobj-to-tab-page (cloned-item, cloned-tab-page); 
                end
            else
                begin
                  if (DEBUG) then
                    inform the operator that "g is unhandled: [the class of g]";
                end;                         
            end;
        end; {for}

    {-- Restore the has-been-cloned attr of selection-buttons and uil-box-borders so they can be cloned again, naturally --}
    for b = each uil-selection-button upon WS-FROM do
        conclude that the uil-has-been-cloned of b is false;
        end;

    for i = each uil-extension-object upon WS-FROM do
        conclude that the uil-has-been-cloned of i is false;
        end;

    for b = each uil-dialog-title upon WS-TO do
        call uil-adjust-border-on-workspace (WS-TO);
        end;

    change the size of WS-TO to minimum;

if (DEBUG-RBOX) then
    begin
    for i = each uil-selection-box upon WS-TO do
        inform the operator that "T.O.: [the uil-event-target-object of i]  T.A.: [the uil-event-target-attribute of i] ";
        end;
    end;

if (DEBUG) then
    inform the operator that "Leaving upp-clone-workspace-contents.";
end 
on error (en, et)
if (G exists) then
    inform the operator that "

-- UIL INTERNAL ERROR --

Running: upp-clone-tab-page-internals 

While attempting to clone a [the class of G], the following error occured:

[et]"
else
    inform the operator that "

-- UIL INTERNAL ERROR --

Running: UPP-CLONE-WORKSPACE-CONTENTS

[et]";
end

upp-clone-tab-pages (DLG : class uil-tab-dialog, cloned-dlg : class uil-tab-dialog) 
WS-FROM: class kb-workspace = the subworkspace of DLG;
WS-TO: class kb-workspace = the subworkspace of cloned-dlg;
tab-page : class uil-tab-page;
D : class uil-tab-dialog;
DEBUG: truth-value = false;
en: symbol; et: text;
begin
if (DEBUG) then
    inform the operator that "Running upp-clone-tab-pages.";

deactivate the subworkspace of  tab-guide-clone-rules;

for tab-page = each uil-tab-page in the uil-tab-page-list of DLG do
    if (the uil-clone-method of tab-page = the symbol uil-clone-grobj-method or the uil-clone-method of tab-page = the symbol uil-clone-grobj) or (the procedure named by the uil-clone-method of tab-page does not exist) then
        call upp-clone-tab-page-internals (tab-page, DLG, cloned-dlg)
    else if the procedure named by the uil-clone-method of tab-page exists then
        call the procedure named by the uil-clone-method of tab-page (tab-page, DLG, cloned-dlg)
end;

for tab-page = each uil-tab-page in the uil-tab-page-list of cloned-dlg do
    call uil-manage-grobj-method (tab-page, WS-TO, the symbol none, the item-x-position of tab-page, the item-y-position of tab-page);
    exit if true; 
end;

if (DEBUG) then
    inform the operator that "Leaving upp-clone-tab-pages.";
end 

on error (en, et)
if (tab-page exists) then
    inform the operator that "

-- UIL INTERNAL ERROR --

Running: upp-clone-tab-pages 

While attempting to clone a [the class of tab-page], the following error occured:

[et]"
else
    inform the operator that "

-- UIL INTERNAL ERROR --

Running: UPP-CLONE-TAB-PAGES

[et]";
end

upp-clone-tab-dialog-method (DLG: class uil-tab-dialog) = (item-or-value)
cloned-dialog: item-or-value;
WS-FROM, WS-TO : class kb-workspace;
page : class uil-tab-page;
T: text;
S: symbol;
X, Y: integer;
P: class uil-point;
I: class item;
DT: class uil-dialog-title;
MDT: class uil-private-master-dialog-text;
DEBUG: truth-value = false;
begin
if (DEBUG) then
    inform the operator that "3. Running upp-clone-tab-dialog-method";

    allow other processing; {clock update}
    create a uil-tab-dialog cloned-dialog by cloning DLG;
    WS-FROM = the subworkspace of DLG;
    WS-TO = the subworkspace of cloned-dialog;

    change the background-color of WS-TO to the background-color of WS-FROM;
    change the foreground-color of WS-TO to the foreground-color of WS-FROM;
    change the text of the workspace-margin of WS-TO to the text of the workspace-margin of WS-FROM;
 
    {-- transfer cloned-dialog to master's dialog-bin --}
    {S = the uil-origin of DLG; 
    conclude that the uil-dialog-home-module-name of cloned-dialog = S;
    S = text-to-symbol ("[the uil-dialog-home-module-name of cloned-dialog]-DIALOG-BIN");}

    S = text-to-symbol ("[the containing-module of DLG]-PUBLIC-BIN");
    if the object named by S does not exist then 
        S = the symbol uillib-dialog-bin;
    call upp-make-item-transient (cloned-dialog);       
    call upp-transfer-dialog-to-location (cloned-dialog, the subworkspace of the object named by S);

    {-- clean up the cloned-dialog --}
    conclude that the uil-is-permanent of cloned-dialog is false;
    {T = call uil-utility-unique-name ("dialog-");
    change the name of cloned-dialog to symbol (T);}

    {--- delete all uil-grobj and uil-grmes items on the subworkspace of cloned-dialog ---}
    for I = each item upon the subworkspace of cloned-dialog do
        begin
        if (I is an uil-object) or (I is an uil-grobj) or (I is a uil-message) or (I is an uil-grmes) then
            begin
                if (DEBUG) then
                    inform the operator that "deleting: [the class of I]";
                delete I;
            end;
        end; 
    end;

    allow other processing; {clock update}

    if (DEBUG) then 
        inform the operator that "3a. calling upp-clone-tab-dialog-contents";

  {--Clone all tab pages and their contents--} 
    if (DEBUG) then 
        inform the operator that "3a. Calling upp-clone-tab-pages."; 
    call upp-clone-tab-pages (DLG, cloned-dialog); 
    if (DEBUG) then
        inform the operator that "3b. Back from upp-clone-tab-pages."; 

  {--Clone all tab pages and their contents--}
    if (DEBUG) then 
        inform the operator that "3a. Calling upp-clone-tab-dialog-contents"; 
    call upp-clone-tab-dialog-contents (WS-FROM, WS-TO);
    if (DEBUG) then
        inform the operator that "3b. Back from upp-clone-tab-dialog-contents"; 

    allow other processing;
    
    {-- Layout the dialog object in the dialog bin --}
    S = the symbol uillib-dialog-bin; 
    X, Y = call upp-get-coords-of-next-open-dialog-slot ();

    transfer cloned-dialog to the subworkspace of the item named by S at (X, Y); 
    for P = each uil-point upon the subworkspace of cloned-dialog do
        delete P;
      end;

    allow other processing; {clock update}
    change the size of the subworkspace of cloned-dialog to minimum;

    for DT = each uil-dialog-title upon the subworkspace of cloned-dialog do
        call upp-manage-dialog-title-method (DT, the subworkspace of cloned-dialog, the item-x-position of DT, the item-y-position of DT);
    end;

    for MDT {master dialog text} = each uil-private-master-dialog-text upon the subworkspace of cloned-dialog 
      do
        make MDT transient;
        delete MDT;
      end;
    
    call upp-initialize-dialog (cloned-dialog);

for page = each uil-tab-page in the uil-tab-page-list of cloned-dialog do
    call upp-move-tab-page (page);
    exit if true;
end;

if (DEBUG) then
    inform the operator that "3. Leaving upp-clone-dialog-method"; 

  return cloned-dialog;
end

upp-replace-doomed-grobj-on-tab-page (OldObj: class item, NewObj: class item)

TP: class uil-tab-page;

begin

if there exists a uil-tab-page TP such that (OldObj is a member of the uil-tab-page-contents-list of TP) then
    insert NewObj at the end of the uil-tab-page-contents-list of TP;

end
