g2:c.PROCEDURE.g2:a.TEXT:
grm-clear-authors (LogText: text) = (text)

CRLF: text = "
";
X: class item;
En: symbol;
Et: text;

begin

LogText = "[LogText][CRLF]    Starting to clear authors"; 

for X = each item do
    begin
        allow other processing; 
        if the authors of X exists then
            conclude that the authors of X has no value;
    end 
    on error (En, Et)
        LogText = "[LogText][CRLF]    Error [En]:  [Et]";
    end;
end;

LogText = "[LogText][CRLF]    Finished clearing authors";
return LogText;

end

grm-resume-automated-promote-from-failed-step (WS: class kb-workspace, Win: class g2-window)

TheStep: class grm-step;
ResumeStep, StepNo: integer;
Steps: integer = grm-number-of-steps;
Passed: truth-value;

begin

conclude that grm-promote-in-progress is true;  

if there exists a grm-step TheStep such that (the state of TheStep = the symbol FAILED) then
    ResumeStep = the stepnum of TheStep
else
    signal the symbol NOFAILEDSTEP, "No failed step found";

for StepNo = ResumeStep to Steps do
    if there exists a grm-step TheStep such that (the stepnum of TheStep = StepNo) then begin 
        conclude that the state of TheStep = the symbol WORKING;
        change the passfail icon-color of TheStep to the symbol GOLD;
    end else
        signal the symbol NOSTEP, "Step number [StepNo] is missing"; 

    Passed = call the grm-procedure upon the subworkspace of TheStep (TheStep, Win);
    call grm-check-step (TheStep, Passed, Win); 
    if not (Passed) then 
        exit if true;
end;

conclude that grm-promote-in-progress is false;

if not (Passed) then begin
    post "Error in automated release -- pausing KB";
    pause knowledge-base;
end; 

end

grm-run-automated-promote (UserCheck: truth-value, WS: class kb-workspace, Win: class g2-window)

{CVSChanges, VCChanges: sequence;}
BldData: class grm-data-object = release-build-data;
CVSFile, Et: text;
En: symbol;
File: class g2-stream;
FreeText: class borderless-free-text;
Passed: truth-value = true;
RunPromote: truth-value; 
StepNo: integer;
Steps: integer = grm-number-of-steps;
TheStep: class grm-step;

begin
    
conclude that grm-promote-in-progress is true;
change the text of the g2-user-mode of Win to "administrator";

{--- If set, show settings to user for confirmation before executing ---}
Passed = call grm-set-build-data (UserCheck, Win);
if Passed = false then    {--- The error has already been posted to log ---}
    shut down g2;

for TheStep = each grm-step upon release-top-level do
    conclude that the state of TheStep = the symbol UNDONE;
    change the passfail icon-color of TheStep to the symbol WHITE;
end;

for freeText = each borderless-free-text upon WS do 
    change the text of freeText to "";
end;

{-- get rid of dev-tools if it is currently loaded --}
if (there exists a module-information M such that ("[the text of the top-level-module of M]" = "devtools")) then begin
    call grm-remove-module (the symbol DEVTOOLS, Win);
end;

{--- Stash the list of modules that changed in CVS ---}
CVSFile = "[the grm-utils-path of BldData]/cvs-changes.txt";
call grm-get-cvs-changes (CVSFile, Win);

{--- Stash the list of modules that had version or copyright changes ---}
call grm-get-vc-changes (Win);

for StepNo = 1 to Steps do 
    if there exists a grm-step TheStep such that (the stepnum of TheStep = StepNo) then begin
        conclude that the state of TheStep = the symbol WORKING;
        change the passfail icon-color of TheStep to the symbol GOLD;
        Passed = call the grm-procedure upon the subworkspace of TheStep (TheStep, Win);
        call grm-check-step (TheStep, Passed, Win);
    end else begin
        Passed = false;
        File = call g2-open-file-for-append (the grm-log-file of BldData, true);
        call g2-write-line (File, "*** Step [StepNo] ***");
        call g2-write-line (File, "FAILED: In Step [StepNo] -- Step is missing");
        call g2-write-line (File, "");
        call g2-close-file (File);
    end;

    {--- Failed somewhere - get out ---}
    exit if Passed = false;
end;

conclude that grm-promote-in-progress is false;

{--- Shut down G2 whether we fail or succeed for Supervisor ---} 
allow other processing;
shut down g2;

end

on error (En, Et)
    File = call g2-open-file-for-append (the grm-log-file of BldData, true);
    call g2-write-line (File, "*** Step (unknown) ***");
    call g2-write-line (File, "FAILED: In unknown Step");
    call g2-write-line (File, "[En] -- [Et]"); 
    call g2-write-line (File, "");
    call g2-close-file (File); 
    allow other processing;
    shut down g2;
end

grm-set-version-information (Package: text, Version: text, VersionNumber: quantity, OldestVersion: quantity, MinG2: text, CopyrightInfo: text, BuildInfo: text, LogText: text) = (text)

CRLF: text = "
";
VI: class gfr-version-information-object;
En: symbol; 
Et: text;

begin

LogText = "[LogText][CRLF]    Setting gfr-version-information-objects";
    
for VI = each gfr-version-information-object do
    if the gfr-package-name of VI = Package then begin 
        conclude that the gfr-version-description of VI = Version;
        conclude that the gfr-version-number of VI = VersionNumber;
        conclude that the gfr-oldest-compatible-version of VI = OldestVersion;
        conclude that the gfr-build-information of VI = BuildInfo;
        conclude that the gfr-copyright-information of VI = CopyrightInfo;
        conclude that the gfr-minimum-g2-version of VI = MinG2;
    end on error (En, Et) 
        LogText = "[LogText][CRLF]    Failed to update [gfr-get-module-of-item (VI)] -- continuing"; 
    end;
end;

LogText = "[LogText][CRLF]    Finished setting gfr-version-information-objects";
return LogText;

end

grm-show-title-blocks-rev-number (Client: class ui-client-item)

TB: class uil-title-block;
FT: class borderless-free-text;
WS: class kb-workspace;

begin

create a kb-workspace WS;
create a borderless-free-text FT;
conclude that the text-font of FT = structure (font-size: the symbol small);
change the text of FT to "";
for TB = each uil-title-block do
    change the text of FT to "[the text of FT]
Module: [the uil-module-title of TB]
    Version: [the version of the icon-variables of TB]"; 
end;
transfer FT to WS;
change the size of WS to minimum; 
show WS on Client;

end

grm-save-modules (DirType: symbol, SaveType: symbol, SaveWinLayout: truth-value, LogText: text, Win: class g2-window) = (truth-value, text)

CRLF: text = "
";
CycleCode: text = the grm-cycle-code of release-build-data; 
En, Modul: symbol;
Et, ModName, PathName: text;
ModuleArray: class symbol-array = the grm-modules-to-save of release-build-data;
ModCVSChanges: sequence = the grm-modules-being-saved of release-build-data; 
ModVCChanges: sequence = the grm-modules-sources-saved of release-build-data; 
N, Len: integer;
Passed: truth-value = true;
Result: truth-value;
ShortVer: text = the grm-short-version of release-build-data;

{*** This procedure saves the Guide, Uil, GDI & GDD modules to the 
    directory named by the current-directory attribute of 
    _guidepro-module-save-info.  The list (actually an array) of modules
    to save is _guidepro-module-array.  The array is currently setup for 
    51r8 but may have to be changed due to additions or deletions in 
    future 5.X versions. ***}

{*** DirType is SRC, DEV or DIST ***}
{*** SaveType is ALLMODS, ONLYDEMOS or NODEMOS ***}

begin

LogText = "[LogText][CRLF]    Starting Release module save@.@.@.";

case (DirType) of
    SRC:
        PathName = "[the grm-source-path of release-build-data]/kbs/sources"; 
    DEV:
        PathName = "[the grm-utils-path of release-build-data]/dev/[ShortVer]-[CycleCode]";
    DIST:
        {PathName = "[the grm-utils-path of release-build-data]/dist/[ShortVer]-[CycleCode]";}
        PathName = "[the grm-source-path of release-build-data]/kbs";
end; 
for N = 0 to the array-length of ModuleArray - 1 do
    Modul = ModuleArray[N];
    {*** Save all, all but demos or only demos ***}
       ModName = lower-case-text ("[Modul]");
    if (SaveType = the symbol ALLMODS)
        or (SaveType = the symbol NODEMOS and Modul /= the symbol GUIDEMO and Modul /= the symbol GDDDEMO and Modul /= the symbol GMSDEMO and Modul /= the symbol GXLDEMO )
        or (SaveType = the symbol ONLYDEMOS and (Modul = the symbol GUIDEMO or Modul = the symbol GDDDEMO or Modul = the symbol GMSDEMO or Modul = the symbol GXLDEMO)) then begin
        if (DirType = the symbol SRC and Modul is a member of ModVCChanges)
            or (DirType = the symbol DIST and (Modul is a member of ModCVSChanges or Modul is a member of ModVCChanges))
            or DirType = the symbol DEV then begin
            Passed = call g2-save-module ("[PathName]/[ModName].kb", true, SaveWinLayout, Modul, false);
            { also save a XML version --binghe }
            if DirType = the symbol SRC then
                call g2-save-module-xml ("[PathName]/kb-xml/[ModName].xml", true, SaveWinLayout, Modul, false);
            LogText= "[LogText][CRLF]        Saved module [ModName]"; end;
        if Passed is false then
            LogText = "[LogText][CRLF]        FAILED: saving module [ModName]";
    end;
end;

LogText = "[LogText][CRLF]    Finished Release module save";
return Passed, LogText;

end

on error (En, Et)
    Et = "[Et]

Associated Log Text:
[LogText]";
    signal En, Et;
end

grm-set-cycle-code (Win: class g2-window) = (text)

DayNum, MonthNum, YearNum: integer;
Now: integer = the current time;
Result, MonthText, YearText: text;

begin

YearNum = remainder (year (Now) - 1998, 26) + 1;
MonthNum = month (Now);
DayNum = day-of-the-month (Now);
YearText = get-from-text ("ABCDEFGHIJKLMNOPQRSTUVWXYZ", YearNum, YearNum);
MonthText = get-from-text ("ABCDEFGHIJKL", MonthNum, MonthNum);
Result = "[YearText][MonthText][(if DayNum < 10 then "0" else "")][DayNum]";
return Result;

end

grm-set-title-blocks-info (Package: text, Version: text, CopyrightInfo: text, Win: item-or-value)

TB: class uil-title-block;

begin

for TB = each uil-title-block do
    if the uil-package-name of TB = Package then begin
        conclude that the width of the icon-variables of TB = the uil-title-block-width of TB; 
        conclude that the module-name of the icon-variables of TB = the uil-module-title of TB;
        conclude that the version of the icon-variables of TB = "Version [Version]";
        conclude that the package of the icon-variables of TB = the uil-package-name of TB;
        conclude that the copyright of the icon-variables of TB = CopyrightInfo;

        if there exists a gfr-version-information-object VI such that (the workspace of VI exists and the workspace W of VI is the same object as the workspace of TB) then begin
            move VI to (gfr-left (TB) + 16, gfr-bottom (TB) + 20);
            change the size of W to minimum;    
        end;
    end  
end;

end

grm-test-text-stripping (LogText: text, Win: class g2-window) = (truth-value, text)

CRLF: text = "
";
Mod, S, En: symbol;
Passed: truth-value;
Proc: class procedure;
PText, Et: text;
UnstrippedCount: integer = 0;
UProcs: class symbol-list = release-unstripped-procs;

begin

for S = each symbol in UProcs do
    remove S from UProcs;
end;

for Proc = each procedure do
    Mod = the containing-module of Proc;
    if Mod is a member of textstrip-modules-list then begin
        if the class of Proc is not method-declaration then begin
            PText = the text of Proc;
            if PText /= "@.@.@.@." and "[the name of Proc]" is not a member of textstrip-exceptions-list 
                and (get-from-text ("[the name of Proc]", 1, 1) = "_"
                {### or the proprietary-package of the workspace of Proc does not exist ###}
                or the text of the proprietary-package of the workspace of Proc /= "not proprietary")
                then begin 
                    insert the name of Proc at the end of UProcs;
                    UnstrippedCount = UnstrippedCount + 1;
                    LogText = "[LogText][CRLF]        Unstripped Proc: [the name of Proc] in module [Mod]";
            end                       
        end on error (En, Et)
        end;
    end;        
end;

if the number of elements in UProcs > 0 then begin
    Passed = false;
    LogText = "[LogText][CRLF]    [UnstrippedCount] unstripped procedures found";
end else
    Passed = true;

return Passed, LogText;

end

grm-reset-step (Step: class grm-step, Win: class g2-window)

begin

conclude that the state of Step = the symbol UNDONE;
change the passfail icon-color of Step to the symbol WHITE;

end

grm-check-step (Step: class grm-step, Passed: truth-value, Win: class g2-window)

File: class g2-stream;
StepNo: integer = the stepnum of Step;

begin

if Passed then begin
    conclude that the state of Step = the symbol PASSED;
    change the passfail icon-color of Step to the symbol LIME-GREEN;
end else begin
    conclude that the state of Step = the symbol FAILED;
    change the passfail icon-color of Step to the symbol RED;

    File = call g2-open-file-for-append (the grm-log-file of release-build-data, true);
    call g2-write-line (File, "*** Step [StepNo] ***");
    call g2-write-line (File, "FAILED: In Step [StepNo]");
    call g2-write-line (File, "");
    call g2-close-file (File); 
end;

end

grm-signal-error (Step: class grm-step, En: symbol, Et: text, Win: class g2-window)

File: class g2-stream;

begin

post "Error in Step [the stepnum of Step]";
post "[En]  [Et]";

File = call g2-open-file-for-append (the grm-log-file of release-build-data, true);
call g2-write-line (File, "*** Step [the stepnum of Step] ***");
call g2-write-line (File, "FAILED: In Step [the stepnum of Step]");
call g2-write-line (File, "[En] -- [Et]"); 
call g2-write-line (File, "");
call g2-close-file (File); 

end

grm-remove-module (Modul: symbol, Win: class g2-window)

ModInfo: class module-information;
N: integer;
ReqMods: sequence;
TopMod, OrphanMod: symbol;

begin

{*** Module does not exist ***}
if not (there exists a  module-information ModInfo such that (the top-level-module of ModInfo = Modul)) then begin
    post "Trying to remove Module [Modul] but it was not found";
    return;
end;

{*** Cannot remove the top module ***}
TopMod = gfr-get-top-level-module ();
if the top-level-module of ModInfo = TopMod then begin
    post "Trying to remove Module [Modul] but it is the top level module";
    return;
end; 

{*** Remove all requirements for the removed module ***}
for ModInfo = each module-information do
    ReqMods = the directly-required-modules of ModInfo; 
    if Modul is a member of ReqMods then begin
        for N = 0 to the number of elements in ReqMods do
            if ReqMods[N] = Modul then begin
                ReqMods = remove (ReqMods, N);
                conclude that the directly-required-modules of ModInfo = ReqMods;
                make ModInfo permanent;
                exit if true;
            end; 
        end;
    end;
end;

call g2-delete-module (Modul, true);

{*** Cleanup orphaned modules ***}
for ModInfo = each module-information do
    OrphanMod = the top-level-module of ModInfo;
    if OrphanMod /= TopMod 
        and the notes of ModInfo /= "OK" 
        and is-contained-in-text ("the module [OrphanMod] is not required by the KB", the text of the notes of ModInfo) then
            call g2-delete-module (OrphanMod, true);
end;

end

grm-print-gold-books-list ()

File: class g2-stream;
Line, L1, L2, L3, L4: text;
LL1: integer = 17;
LL2: integer = 22;
LL3: integer = 25;
Spaces: text = "                              ";
B: class gold-book;

begin

post "Starting";

File = call g2-open-file-for-write ("d:\gensym\doclist.txt");
Line = "Gold book name   Gold key table file   Gold search table file   Gold file path";
call g2-write-line (File, Line);
call g2-write-line (File, "");
for B = each gold-book do
    L1 = "[the gold-book-name of B]";
    L2 = the gold-key-table-file-name of B;
    L3 = the gold-search-table-file-name of B;
    L4 = the gold-win32-default-installation-directory of B;

    L1 = "[L1][(get-from-text (Spaces, 1, LL1 - length-of-text (L1)))]";
    L2 = "[L2][(get-from-text (Spaces, 1, LL2 - length-of-text (L2)))]";
    L3 = "[L3][(get-from-text (Spaces, 1, LL3 - length-of-text (L3)))]";

    Line = "[L1][L2][L3][L4]";
    call g2-write-line (File, Line);
end;

call g2-close-file (File);
post "Finished";

end

grm-set-doc-paths (LogText: text) = (text)

Book: class gold-book;
BookPathText: text;
Counter: integer = 0;
CRLF: text = "
";
Pos: integer;
PreText, PostText: text;
VersionSeg: text = the grm-doc-segment of release-build-data;
VmsVersionSeg: text;
DEBUG: truth-value = false;

begin

LogText = "[LogText][CRLF]    Setting book paths";

{--- Need to remove the dot from the VMS version ---}
Pos = position-of-text (".", VersionSeg);
if Pos > 0 then
    VmsVersionSeg = omit-from-text (VersionSeg, Pos, Pos)
else
    VmsVersionSeg = VersionSeg;

for Book = each gold-book do
    if not (Book has a name) then
       go to NO-BOOK-NAME;
    Counter = Counter + 1;

    {--- Unix path ---}
    BookPathText = the gold-unix-default-installation-directory of Book;
    if position-of-text ("/home/gensym/", BookPathText) = 1 then
        BookPathText = omit-from-text (BookPathText, 1, 13)
    else if position-of-text ("/usr/gensym/", BookPathText) = 1 then
        BookPathText = omit-from-text (BookPathText, 1, 12)
    else    {--- Presumably starts with "/gensym/" ---}
        BookPathText = omit-from-text (BookPathText, 1, 8); 
    Pos = position-of-text ("/doc", BookPathText) - 1;
    PostText = omit-from-text (BookPathText, 1, Pos);
    BookPathText = "/usr/gensym[VersionSeg][PostText]";

    if DEBUG then
        post "U: [BookPathText]"
    else
        conclude that the gold-unix-default-installation-directory of Book = BookPathText;

    {--- Windows path ---}
    BookPathText = the gold-win32-default-installation-directory of Book;
    PreText = get-from-text (BookPathText, 1, 24);
    BookPathText = omit-from-text (BookPathText, 1, 24); 
    Pos = position-of-text ("\doc", BookPathText) - 1;
    PostText = omit-from-text (BookPathText, 1, Pos);
    BookPathText = "[PreText][VersionSeg][PostText]";

    if DEBUG then 
        post "W: [BookPathText]"
    else
        conclude that the gold-win32-default-installation-directory of Book = BookPathText;
 
    {--- VMS path ---}
    BookPathText = the gold-vms-default-installation-directory of Book;
    PreText = get-from-text (BookPathText, 1, 16);
    BookPathText = omit-from-text (BookPathText, 1, 16); 
    Pos = position-of-text (".doc", BookPathText) - 1;
    PostText = omit-from-text (BookPathText, 1, Pos);
    BookPathText = "[PreText][VmsVersionSeg][PostText]";

    if DEBUG then 
        post "V: [BookPathText]" 
    else
        conclude that the gold-vms-default-installation-directory of Book = BookPathText;

    NO-BOOK-NAME:
end;

LogText = "[LogText][CRLF]    Finished setting book paths -- [Counter] books found";
return LogText;

end

grm-set-build-data (UserCheck: truth-value, Win: class g2-window) = (truth-value)

BFT: class borderless-free-text;
BldData: class grm-data-object = release-build-data;
CRLF: text = "
";
CycleCode, Et, Line, LongVer, WorkText, ShortVer, TokMod, TokVer: text; 
En, ModSym, SysType: symbol;
File: class g2-stream;
BldDataPath, Line1, Line2, Line3, Line4, SandBox, UtilsDir: text;
MiscVers: sequence = sequence ();
Pos: integer;
Passed: truth-value = false;
SummaryText: text = "Build Data Summary
";
WS: class kb-workspace = the subworkspace of grm-setup-verification;
Separator: text = "/";

begin

SandBox = call g2-get-environment-variable ("SRCBOX");

if (is-contained-in-text("\", SandBox)) then begin
    Separator = "\";
end;

BldDataPath = "[SandBox][Separator]kbs[Separator]tools";
UtilsDir = BldDataPath; { call g2-get-environment-variable ("UTILS"); }

begin
    File = call g2-open-file-for-read ("[BldDataPath][Separator]build-data.txt");
end on error (En, Et)
    File = call g2-open-file-for-append ("[UtilsDir][Separator]utilities-build-log.txt", true);
    call g2-write-line (File, "*** Could not open Build Data file: [BldDataPath][Separator]build-data.txt ***");
    call g2-write-line (File, "FAILED: Opening build data file"); 
    call g2-write-line (File, "");
    call g2-close-file (File);
    return Passed;
end;

{--- Cycle code ---}
CycleCode = call grm-set-cycle-code (Win);
conclude that the grm-cycle-code of BldData = CycleCode;
SummaryText = "[SummaryText][CRLF]Cycle Code: [CycleCode]";

{--- Short G2 Version ---}
Line = call g2-read-line (File);
SummaryText = "[SummaryText][CRLF][Line]";
Pos = position-of-text (":", Line);
ShortVer = get-from-text (Line, Pos + 2, length-of-text (Line));
conclude that the grm-short-version of BldData = ShortVer;

{--- Long G2 Version ---}
Line = call g2-read-line (File);
SummaryText = "[SummaryText][CRLF][Line]"; 
Pos = position-of-text (":", Line);
LongVer = get-from-text (Line, Pos + 2, length-of-text (Line));
conclude that the grm-long-version of BldData = LongVer;

{--- Minimum G2 Version ---}
Line = call g2-read-line (File);
SummaryText = "[SummaryText][CRLF][Line]"; 
Pos = position-of-text (":", Line);
conclude that the grm-min-g2-version of BldData = get-from-text (Line, Pos + 2, length-of-text (Line)); 
    
{--- Previous G2 Version ---}
Line = call g2-read-line (File);
SummaryText = "[SummaryText][CRLF][Line]"; 
Pos = position-of-text (":", Line);
conclude that the grm-prev-version of BldData = get-from-text (Line, Pos + 2, length-of-text (Line)); 

{--- Doc Version (Segment) ---}
Line = call g2-read-line (File);
SummaryText = "[SummaryText][CRLF][Line]"; 
Pos = position-of-text (":", Line);
conclude that the grm-doc-segment of BldData = get-from-text (Line, Pos + 2, length-of-text (Line)); 

{--- Guide & Uil Versions ---}
conclude that the grm-guide-version of BldData = "GUIDE-[LongVer]";
SummaryText = "[SummaryText][CRLF]Guide Version: GUIDE-[LongVer]"; 
conclude that the grm-uil-version of BldData = "UIL-[LongVer]";
SummaryText = "[SummaryText][CRLF]Uil Version: UIL-[LongVer]"; 

{--- Current G2 Build # ---}
Line = call g2-read-line (File);
SummaryText = "[SummaryText][CRLF][Line]"; 
Pos = position-of-text (":", Line);
conclude that the grm-current-build of BldData = get-from-text (Line, Pos + 2, length-of-text (Line)); 

{--- Oldest G2 Compatible Build # ---}
Line = call g2-read-line (File);
SummaryText = "[SummaryText][CRLF][Line]"; 
Pos = position-of-text (":", Line);
conclude that the grm-oldest-build of BldData = get-from-text (Line, Pos + 2, length-of-text (Line));

{--- Release Type ---}
Line = call g2-read-line (File);
SummaryText = "[SummaryText][CRLF][Line]"; 
Pos = position-of-text (":", Line);
conclude that the grm-release-type of BldData = symbol (get-from-text (Line, Pos + 2, length-of-text (Line))); 
 
{--- Copyright ---}
Line = call g2-read-line (File);
SummaryText = "[SummaryText][CRLF][Line]"; 
Pos = position-of-text (":", Line);
conclude that the grm-copyright of BldData = get-from-text (Line, Pos + 2, length-of-text (Line)); 

{--- Package Name ---}
Line = call g2-read-line (File);
SummaryText = "[SummaryText][CRLF][Line]"; 
Pos = position-of-text (":", Line);
conclude that the grm-util-pkg-name of BldData = get-from-text (Line, Pos + 2, length-of-text (Line));

{--- Advanced Package Name ---}
Line = call g2-read-line (File);
SummaryText = "[SummaryText][CRLF][Line]"; 
Pos = position-of-text (":", Line);
conclude that the grm-adv-util-pkg-name of BldData = get-from-text (Line, Pos + 2, length-of-text (Line)); 

{--- Current Utilities Directory ---}
WorkText = call g2-default-directory ();
conclude that the grm-current-directory of BldData = WorkText;
SummaryText = "[SummaryText][CRLF]Current Directory: [WorkText]"; 

{--- Source (sandbox) Path ---}
conclude that the grm-source-path of BldData = SandBox;
SummaryText = "[SummaryText][CRLF]Source Path: [SandBox]"; 

{--- Utilities Path ---}
conclude that the grm-utils-path of BldData = UtilsDir;
SummaryText = "[SummaryText][CRLF]Utils Path: [UtilsDir]";

{--- Short GDA Version ---}
Line = call g2-read-line (File);
SummaryText = "[SummaryText][CRLF][Line]";
Pos = position-of-text (":", Line);
ShortVer = get-from-text (Line, Pos + 2, length-of-text (Line));
conclude that the arm-gda-short-version of BldData = ShortVer;

{--- Long GDA Version ---}
Line = call g2-read-line (File);
SummaryText = "[SummaryText][CRLF][Line]"; 
Pos = position-of-text (":", Line);
LongVer = get-from-text (Line, Pos + 2, length-of-text (Line));
conclude that the arm-gda-long-version of BldData = LongVer;
    
{--- Previous GDA Version ---}
Line = call g2-read-line (File);
SummaryText = "[SummaryText][CRLF][Line]"; 
Pos = position-of-text (":", Line);
conclude that the arm-gda-prev-version of BldData = get-from-text (Line, Pos + 2, length-of-text (Line));

{--- Current GDA Build # ---}
Line = call g2-read-line (File);
SummaryText = "[SummaryText][CRLF][Line]"; 
Pos = position-of-text (":", Line);
conclude that the arm-gda-current-build of BldData = get-from-text (Line, Pos + 2, length-of-text (Line)); 

{--- Oldest GDA Compatible Build # ---}
Line = call g2-read-line (File);
SummaryText = "[SummaryText][CRLF][Line]"; 
Pos = position-of-text (":", Line);
conclude that the arm-gda-oldest-build of BldData = get-from-text (Line, Pos + 2, length-of-text (Line));

{--- Short NOL Version ---}
Line = call g2-read-line (File);
SummaryText = "[SummaryText][CRLF][Line]";
Pos = position-of-text (":", Line);
ShortVer = get-from-text (Line, Pos + 2, length-of-text (Line));
conclude that the arm-nol-short-version of BldData = ShortVer;

{--- Long NOL Version ---}
Line = call g2-read-line (File);
SummaryText = "[SummaryText][CRLF][Line]"; 
Pos = position-of-text (":", Line);
LongVer = get-from-text (Line, Pos + 2, length-of-text (Line));
conclude that the arm-nol-long-version of BldData = LongVer;
    
{--- Previous NOL Version ---}
Line = call g2-read-line (File);
SummaryText = "[SummaryText][CRLF][Line]"; 
Pos = position-of-text (":", Line);
conclude that the arm-nol-prev-version of BldData = get-from-text (Line, Pos + 2, length-of-text (Line));

{--- Current NOL Build # ---}
Line = call g2-read-line (File);
SummaryText = "[SummaryText][CRLF][Line]"; 
Pos = position-of-text (":", Line);
conclude that the arm-nol-current-build of BldData = get-from-text (Line, Pos + 2, length-of-text (Line));

{--- Oldest NOL Compatible Build # ---}
Line = call g2-read-line (File);
SummaryText = "[SummaryText][CRLF][Line]"; 
Pos = position-of-text (":", Line);
conclude that the arm-nol-oldest-build of BldData = get-from-text (Line, Pos + 2, length-of-text (Line)); 

{--- Alternate Copyright ---}
Line = call g2-read-line (File);
SummaryText = "[SummaryText][CRLF][Line]"; 
Pos = position-of-text (":", Line);
conclude that the arm-alt-copyright of BldData = get-from-text (Line, Pos + 2, length-of-text (Line)); 

{--- GCC Copyright ---}
Line = call g2-read-line (File);
SummaryText = "[SummaryText][CRLF][Line]"; 
Pos = position-of-text (":", Line);
Line1 = get-from-text (Line, Pos + 2, length-of-text (Line));
Line2 = call g2-read-line (File);
conclude that the arm-gcc-copyright of BldData = "[Line1][CRLF][Line2]";

{--- GDL Copyright ---}
Line = call g2-read-line (File);
SummaryText = "[SummaryText][CRLF][Line]"; 
Pos = position-of-text (":", Line);
Line1 = get-from-text (Line, Pos + 2, length-of-text (Line));
Line2 = call g2-read-line (File); 
Line3 = call g2-read-line (File); 
Line4 = call g2-read-line (File); 
conclude that the arm-gdl-copyright of BldData = "[Line1][CRLF][Line2][CRLF][Line3][CRLF][Line4]";

{--- System Type ---}
SysType = call g2-type-of-file-system ();
conclude that the grm-system-type of BldData = SysType;

{--- Build Log File ---}
conclude that the grm-log-file of BldData = "[BldDataPath][Separator]utilities-build-log.txt";
SummaryText = "[SummaryText][CRLF]Build Log File: [the grm-log-file of BldData]";

{--- Load miscellaneous versions ---}
repeat
    Line = call g2-read-line (File);
    exit if the g2-stream-status of File = the symbol END-OF-FILE-REACHED;
    exit if get-from-text (Line, 1, 3) /= "MV:";
    Line = omit-from-text (Line, 1, 4);
    Pos = position-of-text (" ", Line);
    TokMod = get-from-text (Line, 1, Pos);
    Line = omit-from-text (Line, 1, Pos);
    TokVer = Line; 
    MiscVers = insert-at-end (MiscVers, structure (module: symbol (TokMod), version: TokVer));
end;
conclude that the grm-misc-versions of BldData = MiscVers;

call g2-close-file (File);

{--- Display Data & Verify that all is OK ---}
if UserCheck then begin
    if not (there exists a borderless-free-text BFT upon WS) then
        create a borderless-free-text BFT;
    change the text of BFT to SummaryText;
    transfer BFT to WS at (0, 0);
    change the size of WS to minimum;
    show WS on Win at the center of the screen;
    wait until grm-verified receives a value;
    Passed = grm-verified;
    hide WS;
end else
    Passed = true;

if not (Passed) then begin
    File = call g2-open-file-for-append (the grm-log-file of release-build-data, true);
    call g2-write-line (File, "*** Step (setting build data) ***");
    call g2-write-line (File, "FAILED: User check required?");
    call g2-write-line (File, "");
    call g2-close-file (File); 
end; 

return Passed;

end
    
on error (En, Et)
    File = call g2-open-file-for-append (the grm-log-file of release-build-data, true);
    call g2-write-line (File, "*** Step (setting build data) ***");
    call g2-write-line (File, "FAILED: Could not set Build Data");
    call g2-write-line (File, "[En] -- [Et]");
    call g2-write-line (File, "");
    call g2-close-file (File); 
    return false; 
end

c.GRM-PROCEDURE.g2:a.TEXT:
grm-open-log (KBTitle: text, Win: class g2-window)

File: class g2-stream;

begin

File = call g2-open-file-for-append (the grm-log-file of release-build-data, true);
call g2-write-line (File, "");
call g2-write-line (File, "Utilities Build -- Version [the grm-short-version of release-build-data] [the grm-cycle-code of release-build-data] -- [KBTitle]");
call g2-write-line (File, "*****************************************************"); 
call g2-write-line (File, "");
call g2-close-file (File);

end

grm-log-step (Step: class grm-step, LogText: text, Passed: truth-value, Win: class g2-window)

File: class g2-stream;


begin

File = call g2-open-file-for-append (the grm-log-file of release-build-data, true);
call g2-write-line (File, "*** Step [the stepnum of Step] ***");
call g2-write-line (File, "    [LogText]");
call g2-write-line (File, "--- Step [the stepnum of Step]: [(if Passed then "No Errors Detected" else "Error(s) Found")] -- [the current subsecond time as a time stamp]");
if Passed = false then
     call g2-write-line (File, "FAILED: In Step [the stepnum of Step]"); 
call g2-write-line (File, "");
call g2-close-file (File);

end

g2:c.PROCEDURE.g2:a.TEXT:
grm-get-cvs-changes (FileName: text, Win: class g2-window) 

ChangedKBs: sequence = the grm-modules-being-saved of release-build-data;
File: class g2-stream;
Line: text;
ModName: symbol;
ModsToSave: class symbol-array = the grm-modules-to-save of release-build-data; 
Pattern: text = "U <g2symbol>+.kb";
Pos: integer;
S: structure;

begin

File = call g2-open-file-for-read (FileName);

repeat
    Line = call g2-read-line (File);
    exit if the g2-stream-status of File = the symbol END-OF-FILE-REACHED;
    S = find-next-pattern (Pattern, Line, 1);
    if the start-index of S = 1 then begin
        ModName = symbol (get-from-text (Line, 3, length-of-text (Line) - 3));
        if ModName is a member of the g2-array-sequence of ModsToSave and ModName is not a member of ChangedKBs then 
        ChangedKBs = insert-at-end (ChangedKBs, ModName);
    end;
end;
call g2-close-file (File);
conclude that the grm-modules-being-saved of release-build-data = ChangedKBs;

end

grm-get-vc-changes (Win: class g2-window)

BldData: class grm-data-object = release-build-data;
ChangedKBs: sequence = the grm-modules-being-saved of BldData;
ChangedSrcKBs: sequence = the grm-modules-sources-saved of BldData; 
Line, VerText: text;
ModName: symbol;
ModsToSave: class symbol-array = the grm-modules-to-save of BldData;
Pos: integer;
S, VCStruct: structure;
TitleBlock: class uil-title-block;

begin

for TitleBlock = each uil-title-block do
    VCStruct = the icon-variables of TitleBlock;
    Line = the version of VCStruct;
    VerText = omit-from-text (Line, 1, 8);        { -- skip text "Version " -- }
{post "VT: [vertext]  GLV: [the grm-long-version of BldData]";}
    if VerText /= the grm-long-version of BldData then begin 
        ModName = the containing-module of TitleBlock;
        if ModName is a member of the g2-array-sequence of ModsToSave then begin
            if ModName is not a member of ChangedKBs then
                ChangedKBs = insert-at-end (ChangedKBs, ModName);
            if ModName is not a member of ChangedSrcKBs then
                ChangedSrcKBs = insert-at-end (ChangedSrcKBs, ModName);
        end;
    end;
{post "VC: [the copyright of VCStruct]  GC: [the grm-copyright of BldData]";}
    if the copyright of VCStruct /= the grm-copyright of BldData then begin
        ModName = the containing-module of TitleBlock;
        if ModName is a member of the g2-array-sequence of ModsToSave then begin
            if ModName is not a member of ChangedKBs then
                ChangedKBs = insert-at-end (ChangedKBs, ModName);
            if ModName is not a member of ChangedSrcKBs then
                ChangedSrcKBs = insert-at-end (ChangedSrcKBs, ModName); 
        end;
    end;
{post "VCS: [VCStruct]";}
end;

{post "C: [changedKBs]";}
conclude that the grm-modules-being-saved of BldData = ChangedKBs;
conclude that the grm-modules-sources-saved of BldData = ChangedSrcKBs; 

end

arm-set-version-info (LogText: text) = (truth-value, text)

AppPkgs: integer = 0;
BldData: class grm-data-object = release-build-data;
CRLF: text = "
";
Et: text; 
GrlVersionMods: sequence = the g2-array-sequence of grl-version-modules; 
Mod, En: symbol; 
OldCopyrightMods: sequence = the g2-array-sequence of old-copyright-modules;
Passed: truth-value = false;
GDAVersionMods: class symbol-list = the arm-gda-version-modules of BldData;
NOLVersionMods: class symbol-list = the arm-nol-version-modules of BldData; 
VInfo, GfrInfo: class gfr-version-information-object;
WS: class kb-workspace; 

begin

GfrInfo = gfr-version-information;

{*** Set gfr-version-information-objects ***}
for VInfo = each gfr-version-information-object do
    Mod = the containing-module of VInfo;
    if Mod is a member of GDAVersionMods or Mod is a member of NOLVersionMods then begin             
        if Mod is a member of GrlVersionMods then begin
            conclude that the gfr-version-description of VInfo = the grm-long-version of BldData;
            conclude that the gfr-version-number of VInfo = the gfr-version-number of GfrInfo;
            conclude that the gfr-oldest-compatible-version of VInfo = the gfr-oldest-compatible-version of GfrInfo;
        end else if Mod is a member of GDAVersionMods then begin
            conclude that the gfr-version-description of VInfo = the arm-gda-long-version of BldData;
            conclude that the gfr-version-number of VInfo = quantity (the arm-gda-current-build of BldData);
            conclude that the gfr-oldest-compatible-version of VInfo = quantity (the arm-gda-oldest-build of BldData);
        end else if Mod is a member of NOLVersionMods then begin
            conclude that the gfr-version-description of VInfo = the arm-nol-long-version of BldData;
            conclude that the gfr-version-number of VInfo = quantity (the arm-nol-current-build of BldData);
            conclude that the gfr-oldest-compatible-version of VInfo = quantity (the arm-nol-oldest-build of BldData);
        end;
        conclude that the gfr-minimum-g2-version of VInfo = the grm-min-g2-version of BldData;
        conclude that the gfr-build-information of VInfo = "[the current time as a time stamp]"; 
        if Mod is a member of OldCopyrightMods then 
            conclude that the gfr-copyright-information of VInfo = the arm-alt-copyright of BldData
        else
            conclude that the gfr-copyright-information of VInfo = the grm-copyright of BldData;
        make VInfo permanent;
        if VInfo is a gdl-application-package then begin 
            AppPkgs = AppPkgs + 1;
            conclude that the gfr-minimum-g2-version of VInfo = the grm-min-g2-version of BldData; 
        end; 
        LogText = "[LogText][CRLF]   [the gfr-package-name of VInfo], [the gfr-version-description of VInfo],  [the gfr-version-number of VInfo], [the gfr-oldest-compatible-version of VInfo], [the gfr-copyright-information of VInfo], [the gfr-minimum-g2-version of VInfo]";
    end on error (En, Et) 
        LogText = "[LogText][CRLF]  Failed to update version information objects in [gfr-get-module-of-item (VInfo)]";
        return Passed, LogText;
    end;
end;

if AppPkgs /= 3 then begin
    LogText = "[LogText][CRLF]  An unexpected number of application packages ([AppPkgs]) was encountered.";
    return Passed, LogText;
end; 

Passed = true;
return Passed, LogText;

end

arm-save-modules (DirType: symbol, SaveWinLayout: truth-value, LogText: text, Win: class g2-window) = (text)

BldData: class grm-data-object = release-build-data;
CRLF: text = "
";
CycleCode: text = the grm-cycle-code of BldData; 
Modul: symbol;
ModulesToSave: sequence = the g2-array-sequence of the grm-modules-to-save of BldData;
ModVCChanges: sequence = the grm-modules-sources-saved of release-build-data; 
GModules: class symbol-list = the arm-gda-modules of BldData;
NModules: class symbol-list = the arm-nol-modules of BldData ;
ComModules: class symbol-list = the arm-common-modules of BldData; 
ModName, ComPathName, GPathName, NPathName: text;
N, Len: integer;
Result: truth-value;
GDAShortVer: text = the arm-gda-short-version of BldData;
NOLShortVer: text = the arm-nol-short-version of BldData; 

{*** This procedure saves the GDA, NOL, and their required modules 
    to the directory named by the current-directory attribute of 
    release-build-data. ***}

{*** DirType is SRC, DEV or DIST ***}

begin

LogText = "[LogText][CRLF]    Starting Release module save@.@.@.";
LogText = "[LogText][CRLF]    Source modules saved -- [ModVCChanges]";

case (DirType) of
    SRC:  begin
        GPathName = "[the grm-source-path of BldData]/gdanol/gda/kbs/"; 
        NPathName = "[the grm-source-path of BldData]/gdanol/nol/kbs/";
        ComPathName = "[the grm-source-path of BldData]/gdanol/common/kbs/"; 
    end;
    DEV:  begin
        GPathName = "[the grm-utils-path of BldData]/gda/dev/[GDAShortVer]-[CycleCode]";
        NPathName = "[the grm-utils-path of BldData]/nol/dev/[NOLShortVer]-[CycleCode]";
    end;
    DIST:  begin
        {Save to fixed directory names for bundle scripts copying easily. --binghe}
        GPathName = "[the grm-utils-path of BldData]/gda/dist/kbs";
        NPathName = "[the grm-utils-path of BldData]/nol/dist/kbs";
    end;
end; 

if DirType = the symbol SRC then begin {--- SRC - Save GDADEV1 to Common area ---}
    call g2-save-module ("[ComPathName]/gdadev1.kb", true, SaveWinLayout, the symbol GDADEV1, false);
    call g2-save-module-xml ("[ComPathName]/kb-xml/gdadev1.xml", true, SaveWinLayout, the symbol GDADEV1, false); 
end;
for Modul = each symbol in ModulesToSave do
    {*** Save all ***}
    ModName = lower-case-text ("[Modul]");
    if Modul is a member of GModules and (DirType /= the symbol SRC or Modul is a member of ModVCChanges) then begin {--- Save GDA to GDA area ---}
        Result = call g2-save-module ("[GPathName]/[ModName].kb", true, SaveWinLayout, Modul, false);
        if DirType = the symbol SRC then
            call g2-save-module-xml ("[GPathName]/kb-xml/[ModName].xml", true, SaveWinLayout, Modul, false);
    end
    else if Modul is a member of NModules and (DirType /= the symbol SRC or Modul is a member of ModVCChanges) then begin {---Save NOL to NOL area ---}
        Result = call g2-save-module ("[NPathName]/[ModName].kb", true, SaveWinLayout, Modul, false);
        if DirType = the symbol SRC then
            call g2-save-module-xml ("[NPathName]/kb-xml/[ModName].xml", true, SaveWinLayout, Modul, false);
    end
    else if (DirType = the symbol SRC and Modul is a member of ModVCChanges) then begin  {--- SRC - Save Common to Common area ---}
        Result = call g2-save-module ("[ComPathName]/[ModName].kb", true, SaveWinLayout, Modul, false);
        call g2-save-module-xml ("[ComPathName]/kb-xml/[ModName].xml", true, SaveWinLayout, Modul, false);
    end
    else if DirType /= the symbol SRC then  {--- not SRC - Save Common to GDA area only ---}
        Result = call g2-save-module ("[GPathName]/[ModName].kb", true, SaveWinLayout, Modul, false)
    else begin
        Result = true;
        LogText = "[LogText][CRLF]        Not saving module [ModName] -- DirType:[DirType]";
    end;
    if Result is false then
        LogText = "[LogText][CRLF]        Failed to save module [ModName]";
end;

LogText = "[LogText][CRLF]    Finished Release module save";
return LogText;

end

arm-set-title-blocks-info (LogText: text, Win: item-or-value) = (truth-value, text)

BldData: class grm-data-object = release-build-data;
Et: text;
Mod, En: symbol;
Passed: truth-value = false;
TB: class uil-title-block;
GDAVersionMods: class symbol-list = the arm-gda-version-modules of BldData;
NOLVersionMods: class symbol-list = the arm-nol-version-modules of BldData; 
GrlVersionMods: sequence = the g2-array-sequence of grl-version-modules;
CopyrightMods: sequence = the g2-array-sequence of the grm-modules-to-save of BldData; 
OldCopyrightMods: sequence = the g2-array-sequence of old-copyright-modules;
WS: class kb-workspace; 

begin

for TB = each uil-title-block do
    Mod = the containing-module of TB;
    if Mod is a member of CopyrightMods then begin         
        conclude that the width of the icon-variables of TB = the uil-title-block-width of TB;
        conclude that the package of the icon-variables of TB = the uil-package-name of TB;
        if Mod is a member of GrlVersionMods then 
            conclude that the version of the icon-variables of TB = "Version [the grm-long-version of BldData]" 
        else if Mod is a member of NOLVersionMods then 
            conclude that the version of the icon-variables of TB = "Version [the arm-nol-long-version of BldData]"
        else    {--- Should be a GDA module so use the gda-long-version ---}
            conclude that the version of the icon-variables of TB = "Version [the arm-gda-long-version of BldData]";
        conclude that the module-name of the icon-variables of TB = the uil-module-title of TB;
        if Mod is a member of OldCopyrightMods then 
            conclude that the copyright of the icon-variables of TB = the arm-alt-copyright of BldData
        else
            conclude that the copyright of the icon-variables of TB = the grm-copyright of BldData;
        make TB permanent;
        if there exists a gfr-version-information-object VInfo such that (the workspace of VInfo exists and the workspace WS of VInfo is the same object as the workspace of TB) then begin
            move VInfo to (gfr-left (TB) + 16, gfr-bottom (TB) + 20);
            make VInfo permanent;
            change the size of WS to minimum;
            make WS permanent;
        end;
    end on error (En, Et)
        LogText = "[LogText][CRLF]    Failed to update title blocks in [gfr-get-module-of-item (TB)]";
        return Passed, LogText;
    end;
end; 

Passed = true;
return Passed, LogText;

end
