g2:c.PROCEDURE.g2:a.TEXT:
set-up-directory-to-access-for-automation()
type-of-file-system: symbol;
this-default: text;
begin
type-of-file-system = call g2-type-of-file-system();
case (type-of-file-system) of
unix: conclude that directory-to-access 
= unix-directory-to-access-for-automation ;
vms: begin
this-default = call g2-default-directory ();
if get-from-text (this-default, 1, 3) = "VAX" then
conclude that directory-to-access
= vax-vms-directory-to-access-for-automation
else if get-from-text (this-default, 1, 5) = "ALPHA" then
conclude that directory-to-access 
= alpha-vms-directory-to-access-for-automation else
conclude that directory-to-access =
vms-directory-to-access-for-automation;
end;
dos, win32: 
    begin 
        call set-new-directories-for-dos-or-win32 ();
        conclude that directory-to-access = dos-or-win32-directory-to-access-for-automation;
    end
end;
end

clean-up-directory-examples-ws()
M: class message;
T: class text-list;
begin
for M = each message upon this workspace do
if M is not the same object as directory-message
then delete M; end;
for T = each text-list upon this workspace do
delete T; end;
change the size of this workspace to minimum;
end

test-disk-space-available-in-directory() 
  result: integer; 
begin 
  result = call g2-disk-space-available-in-directory(directory-to-access); 
  change the text of directory-message to "@"[directory-to-access]@" has [result] blocks of disk space"; 
end

test-change-default-directory() = () 
  new-directory: text;
  error-type: symbol;
  error-txt: text;
begin 
  call g2-change-default-directory(new-default-directory); 
  new-directory = call g2-default-directory(); 
  change the text of directory-message to "the new default directory is @"[new-directory]@""; 
end 

{on error (error-type, error-txt)
  inform the operator that "[error-type].  [error-txt]";
end}

test-default-directory() = () 
  current-default: text; 
begin 
  current-default = call g2-default-directory();
  change the text of directory-message to "the default directory is @"[current-default]@""; 
end

test-subdirectories-in-directory() = ()
  directory-list: class text-list; 
  filestring: text; 
  M: class message; 
  index: integer = 0; 
begin 
  directory-list = call g2-subdirectories-in-directory(directory-to-access); 
  transfer directory-list to this workspace at (-120, -20); 
  for filestring = each text in directory-list do
  begin     
    create a message M;
    change the background-color of M to white;    
    change the text of M to filestring;
    transfer M to this workspace at (0,index);
    index = index - 30;     
  end ; 
end;
end

test-files-in-directory() = () 
  directory-list: class text-list; 
  filestring: text; 
  M: class message; 
  index: integer = 0; 
begin 
  directory-list = call g2-files-in-directory(directory-to-access); 
  transfer directory-list to this workspace at (-120, -20); 
  for filestring = each text in directory-list do
  begin
    create a message M;
    change the background-color of M to white;   
    change the text of M to filestring;
    transfer M to this workspace at (0,index);
    index = index - 30;     
  end ; 
end ; 
end

set-new-directories-for-dos-or-win32()
def-dir: text ;
drive:text;
begin
    def-dir = call g2-get-environment-variable ("SRCBOX") ;
    def-dir = "[def-dir]\kbs\";
    drive = call g2-file-device-string (def-dir);
    conclude that dos-or-win32-directory-to-access-for-automation = def-dir;
    conclude that dos-or-win32-new-default-directory-for-automation = "[drive]\";
    conclude that dos-or-win32-default-directory-for-automation =def-dir;
end

clear-directory-lists-and-messages()
M: class message;
T:class text-list;
begin
for M = each message upon this workspace do
if M is not the same object as directory-list-message 
then delete M; end;
for T = each text-list upon this workspace do
delete T; end;
conclude that directory-converted-back-to-string = "";
end

test-file-directory-string-to-list() = ()
  directory-list, t: class text-list;
  directory-component: text;
  M: class message;
  offset: integer = 0;
begin 
  directory-list = call g2-file-directory-string-to-list(directory-to-convert-to-list); 
  for t = each text-list upon this workspace do
    delete t;
  end;
  for M = each message upon this workspace do
  if M is not the same object as directory-list-message
  then delete M;
  end;
  transfer directory-list to this workspace at (-30, 0);
  for directory-component = each text in directory-list do
  create a message M;
  change the background-color of M to white;
  transfer M to this workspace at (100,  - offset);
  change the text of M to "@"[directory-component]@"";
  offset = offset + 30;
  end; 
end

test-file-directory-list-to-string() = ()
  directory-string: text; 
begin 
  if the text-list upon this workspace exists then begin
    directory-string = call g2-file-directory-list-to-string(the text-list upon this workspace);
    conclude that directory-converted-back-to-string = directory-string;
    end
  else 
    change the text of directory-list-message to "Press the @"dirstring to list@" button first";
end

