test-partition-filestring() = () 
  host, device, directory, base-name, extension, version: text; 
  filestring: text = filename-to-partition;
begin 
host, device, directory, base-name, extension, version = call g2-partition-filestring(filestring);
change the text of partitioned-host-string to "@"[host]@"";
change the text of partitioned-device-string to "@"[device]@"";
change the text of partitioned-directory-string  to "@"[directory]@"";
change the text of partitioned-base-name-string to "@"[base-name]@"";
change the text of partitioned-extension-string to "@"[extension]@"";
change the text of partitioned-version-string to "@"[version]@"";

end

test-float-to-bytes() = () 
  byte1, byte2, byte3, byte4, byte5, byte6, byte7, byte8: integer; 
begin 
  byte1, byte2, byte3, byte4, byte5, byte6, byte7, byte8 = call g2-float-to-bytes(float-to-decompose-into-bytes); 
  change the text of decomposed-byte-1 to "[byte1]"; 
  change the text of decomposed-byte-2 to "[byte2]"; 
  change the text of decomposed-byte-3 to "[byte3]"; 
  change the text of decomposed-byte-4 to "[byte4]"; 
  change the text of decomposed-byte-5 to "[byte5]"; 
  change the text of decomposed-byte-6 to "[byte6]"; 
  change the text of decomposed-byte-7 to "[byte7]"; 
  change the text of decomposed-byte-8 to "[byte8]"; 

end

open-test-file-for-read-or-write(read-or-write: symbol) 
  S: class g2-stream; 
  this-workspace: class kb-workspace = this workspace; 

begin 
  if not (there exists a g2-stream S upon this-workspace) then begin
  if read-or-write is read then
S = call g2-open-file-for-read(file-from-which-to-read-and-write) else if read-or-write is write then S = call g2-open-file-for-write(file-from-which-to-read-and-write);
 transfer S to this workspace at (-50, 40);
end;
end

test-read-byte() = ()
  this-byte: integer; 
  stream: class g2-stream = the g2-stream upon this workspace;
begin 
  this-byte = call g2-read-byte(stream); 
  change the text of read-and-write-message to "just read byte [this-byte]"; 
end

test-read-line() = () 
  stream: class g2-stream = the g2-stream upon this workspace; 
  this-line: text; 
begin 
  if file-system-supports-set-file-position  then call g2-set-file-position(stream, file-position-for-read-or-write,true); 
  this-line = call g2-read-line(stream); 
  change the text of text-that-is-read-from-file to "@"[this-line]@""; 
end

test-write-byte()
  error-type: symbol;
  error-txt: text;
  stream: class g2-stream = the g2-stream upon this workspace;
begin
  call g2-write-byte(stream, 90); 
  call g2-write-byte(stream, 91);  
  call g2-write-byte(stream, 92);  
  call g2-write-byte(stream, 93);  
  call g2-write-byte(stream, 94);
  change the text of read-and-write-message to "Write byte complete.";
end 

{on error(error-type, error-txt)
  inform the operator that "[error-type].  [error-txt]";
end}

test-write-string()
  error-type: symbol;
  error-txt: text;
  stream: class g2-stream = the g2-stream upon this workspace; 
begin 
  call g2-write-string(stream, string-to-write-into-file); 
  change the text of read-and-write-message to "Write string complete.";
end 

{on error(error-type, error-txt)
  inform the operator that "[error-type].  [error-txt]";
end}

test-write-line() stream: class g2-stream = the g2-stream upon this workspace; 
  error-type: symbol;
  error-txt: text;
begin 
  if file-system-supports-set-file-position then call g2-set-file-position(stream, file-position-for-read-or-write, true); 
  call g2-write-line(stream, text-to-write-into-file); 
  change the text of read-and-write-message to "Write line complete.";
end 

{on error(error-type, error-txt)
  inform the operator that "[error-type].  [error-txt]";
end}

create-a-file-with-attributes () 
  S: class g2-stream; 
begin 
  S = call g2-open-file-for-write (file-with-attributes); 
  call g2-close-file (S); 
end

test-latest-date-file-was-accessed() 
  latest-date: float; 
  humanized-date: text; 
begin 
  latest-date = call g2-latest-date-file-was-accessed(file-with-attributes);
  humanized-date = call g2-unix-time-to-text (latest-date, true); 
  change the text of characteristics-message to "@"[file-with-attributes]@" was last accessed on [humanized-date]"; 
end

test-file-exists() 
  exists-p: truth-value; 
begin 
  exists-p = call g2-file-exists(file-with-attributes); 
  if exists-p then change the text of characteristics-message to "@"[file-with-attributes]@" exists" 
  else change the text of characteristics-message to "@"[file-with-attributes]@" does not exist"; 
end

