test-length-of-file() 
  file-stream: class g2-stream; 
  file-length: integer; 
begin 
  file-stream = call g2-open-file-for-read(file-with-attributes); 
  file-length = call g2-length-of-file(file-stream);
  delete file-stream; 
  change the text of characteristics-message to "@"[file-with-attributes]@" is [file-length] bytes long";
end

