end

get-next-stream-placement(the-workspace: class kb-workspace) = (integer, integer)
  S1: class g2-stream; 
  x-position: integer = 80; 
  y-position: integer = 50; 
begin 
  if there exists a g2-stream S1 upon the-workspace then 
  begin   
    x-position = 50 + the maximum over each g2-stream S1 upon the-workspace of (the item-x-position of S1);   
    y-position = -25 + the minimum over each g2-stream S1 upon the-workspace of (the item-y-position of S1);   
  end ; 
  return x-position, 
  y-position; 
end

