test-get-position(stream: class g2-stream) = ()
new-position: integer;
begin 
  call g2-set-file-position(stream, position-to-set-in-accessed-file, should-set-file-position-past-end);
  new-position = the position-in-file of stream;
  change the text of get-position-message to "position is [new-position]";
end

test-open-for-read() = () 
  S: class g2-stream; 
  x-position, y-position: integer; 
begin 
  S = call g2-open-file-for-read(file-to-access-or-control); 
  x-position, y-position = call get-next-stream-placement(this workspace);
  transfer S to this workspace at (x-position, y-position); 
end

test-close () = () 
  S: class g2-stream; 
  this-workspace: class kb-workspace = this workspace; 
  error-type: symbol;
  error-txt: text;
begin 
  if there exists a g2-stream upon this-workspace 
  then   
  begin
    S = the g2-stream nearest to file-to-access-or-control;   
    call g2-close-file (S);
  end ; 
end 

{on error(error-type, error-txt)
  inform the operator that "[error-type].  [error-txt]";
end}

test-open-for-write() = () 
  S, S1: class g2-stream; 
  x-position, y-position: integer; 
begin 
  S = call g2-open-file-for-write(file-to-access-or-control); 
  x-position, y-position = call get-next-stream-placement(this workspace);
  transfer S to this workspace at (x-position, y-position); 
end

