  S: class g2-stream; 
  x-position, y-position: integer; 
begin 
  S = call g2-open-file-for-read-and-write(file-to-access-or-control); 
  x-position, y-position = call get-next-stream-placement(this workspace);
  transfer S to this workspace at (x-position, y-position); 
end

open-for-read-and-write-to-nonexistent-file-test (node: class benchmark-test)
memory-before, memory-after, counter: integer;
success: truth-value;
begin
call delete-all-versions-of-file (file-to-access-or-control);
success = call open-for-read-and-write-to-nonexistent-file-test-internal ();
if success {attempt #1} then begin
memory-before = call read-memory-usage-meter (false);
for counter = 1 to iterations-for-leak-exposure do
success = call open-for-read-and-write-to-nonexistent-file-test-internal ();
exit if not(success); end;
if success {attempt #n} then begin
memory-after = call read-memory-usage-meter (true);
if memory-before = memory-after
then call pass(node) 
else call fail(node, "memory leak ([memory-after - memory-before] bytes)");
end {attempt #n} else call fail(node, "attempt #[counter]");
end {attempt #1} else call fail(node, "attempt #1");
end

open-for-read-and-write-to-nonexistent-file-test-internal () = (truth-value)
W: class kb-workspace;
S: class g2-stream;
error-symbol: symbol; error-text: text;
begin
W = the subworkspace of example-access;
call test-open-for-read-and-write();
wait until there exists a g2-stream S upon W 
such that 
((the name-of-file of S = file-to-access-or-control
 or the name-of-file of S = upper-case-text (file-to-access-or-control))
and the access-direction of S is input-and-output
and the g2-stream-status of S is open-for-read-and-write)
checking every 1 second;
delete S;
call g2-delete-file(file-to-access-or-control);
return false;
end
on error (error-symbol, error-text)
{jh, 11/30/94.  To be honest, the symbol failed-to-open-for-read-and-write
will occur for any problem, not just a nonexistent file.  But if
the previous open-for-read-and-write test passed, it is a pretty good bet 
that a nonexistent file caused the error}
if error-symbol is failed-to-open-for-read-and-write
then return true
else return false;
end

open-for-read-and-write-test (node: class benchmark-test)
memory-before, memory-after, counter: integer;
begin
call open-for-read-and-write-test-internal ();
memory-before = call read-memory-usage-meter (false);
for counter = 1 to iterations-for-leak-exposure do
call open-for-read-and-write-test-internal (); end;
memory-after = call read-memory-usage-meter (true);
if memory-before = memory-after
then call pass(node) 
else call fail(node, "memory leak ([memory-after - memory-before] bytes)");
end

open-for-read-and-write-test-internal () = (truth-value)
W: class kb-workspace;
S: class g2-stream;
begin
W = the subworkspace of example-access;
{can only open a pre-existing file for read-and-write}
S = call g2-open-file-for-write(file-to-access-or-control);
delete S;
start test-open-for-read-and-write();
wait until there exists a g2-stream S upon W 
such that 
((the name-of-file of S = file-to-access-or-control
 or the name-of-file of S = upper-case-text (file-to-access-or-control))
and the access-direction of S is input-and-output
and the g2-stream-status of S is open-for-read-and-write)
checking every 1 second;
delete S;
call g2-delete-file (file-to-access-or-control);
return true;
end

delete-all-versions-of-file(file-to-purge: text)
pre-exists: truth-value;
begin
{file-to-purge may have been created by previous tests.
A repeat statement is necessary rather than a simple one-shot
because a version-keeping file system like VMS could leave 
previous versions around.}
repeat
pre-exists = call g2-file-exists(file-to-purge);
exit if not (pre-exists);
call g2-delete-file(file-to-purge);
end;
end

open-for-append-to-nonexistent-file-test (node: class benchmark-test)
memory-before, memory-after, counter: integer;
success: truth-value;
begin
conclude that append-can-create-new-file is false;
call delete-all-versions-of-file (file-to-access-or-control);
success = call open-for-append-to-nonexistent-file-test-internal ();
if success {attempt #1} then begin
memory-before = call read-memory-usage-meter (false);
for counter = 1 to iterations-for-leak-exposure do
success = call open-for-append-to-nonexistent-file-test-internal ();
exit if not(success); end;
if success {attempt #n} then begin
memory-after = call read-memory-usage-meter (true);
if memory-before = memory-after
then call pass(node) 
else call fail(node, "memory leak ([memory-after - memory-before] bytes)");
end {attempt #n} else call fail(node, "attempt #[counter]");
end {attempt #1} else call fail(node, "attempt #1");
conclude that append-can-create-new-file is true;
end

open-for-append-to-nonexistent-file-test-internal () = (truth-value)
W: class kb-workspace;
S: class g2-stream;
error-symbol: symbol; error-text: text;
begin
W = the subworkspace of example-access;
call test-open-for-append();
wait until there exists a g2-stream S upon W 
such that 
((the name-of-file of S = file-to-access-or-control
 or the name-of-file of S = upper-case-text (file-to-access-or-control))
and the access-direction of S is output
and the g2-stream-status of S is open-for-append)
checking every 1 second;
delete S;
call g2-delete-file(file-to-access-or-control);
return false;
end
on error (error-symbol, error-text)
{jh, 11/30/94.  To be honest, the symbol failed-to-open-for-append
will occur for any problem, not just a nonexistent file.  But if
the previous open-for-append test passed, it is a pretty good bet 
that a nonexistent file caused the error}
if error-symbol is failed-to-open-for-append
then return true
else return false;
end

open-for-append-test (node: class benchmark-test)
memory-before, memory-after: integer;
counter: integer;
begin
call open-for-append-test-internal ();
memory-before = call read-memory-usage-meter (false);
for counter = 1 to iterations-for-leak-exposure do
call open-for-append-test-internal (); end;
memory-after = call read-memory-usage-meter (true);
if memory-before = memory-after
then call pass(node) 
else call fail(node, "memory leak ([memory-after - memory-before] bytes)");
end

open-for-append-test-internal () = (truth-value)
W: class kb-workspace;
S: class g2-stream;
begin
W = the subworkspace of example-access;
start test-open-for-write();
wait until there exists a g2-stream S upon W 
such that 
((the name-of-file of S = file-to-access-or-control
 or the name-of-file of S = upper-case-text (file-to-access-or-control))
and the access-direction of S is output
and the g2-stream-status of S is open-for-write)
checking every 1 second;
delete S;
start test-open-for-append();
wait until there exists a g2-stream S upon W 
such that 
((the name-of-file of S = file-to-access-or-control
 or the name-of-file of S = upper-case-text (file-to-access-or-control))
and the access-direction of S is output
and the g2-stream-status of S is open-for-append)
checking every 1 second;
delete S;
call g2-delete-file(file-to-access-or-control);
return true;
end

open-for-read-test (node: class benchmark-test)
memory-before, memory-after: integer;
counter: integer;
begin
call open-for-read-test-internal ();
memory-before = call read-memory-usage-meter (false);
for counter = 1 to iterations-for-leak-exposure do
call open-for-read-test-internal (); end;
memory-after = call read-memory-usage-meter (true);
if memory-before = memory-after
then call pass(node) 
else call fail(node, "memory leak ([memory-after - memory-before] bytes)");
end

open-for-read-test-internal () = (truth-value)
W: class kb-workspace;
S: class g2-stream;
begin
W = the subworkspace of example-access;
start test-open-for-write();
wait until there exists a g2-stream S upon W 
such that 
((the name-of-file of S = file-to-access-or-control
 or the name-of-file of S = upper-case-text (file-to-access-or-control))
and the access-direction of S is output
and the g2-stream-status of S is open-for-write)
checking every 1 second;
delete S;
start test-open-for-read();
wait until there exists a g2-stream S upon W 
such that 
((the name-of-file of S = file-to-access-or-control
 or the name-of-file of S = upper-case-text (file-to-access-or-control))
and the access-direction of S is input
and the g2-stream-status of S is open-for-read)
checking every 1 second;
delete S;
call g2-delete-file(file-to-access-or-control);
return true;
end

