change-default-directory-test-internal () = (truth-value, text)
type-of-file-system: symbol;
old-default, desired-default, actual-default: text;
expected-default: truth-value;
problem-text: text;
begin
old-default = call g2-default-directory();
{jh, 11/30/94.  After testing, replace following statement with preceding one}
{old-default = deletable-and-readable-development-default-directory-for-testing;}
type-of-file-system = call g2-type-of-file-system ();
case (type-of-file-system) of
unix: desired-default 
= non-automounted-unix-new-default-directory-for-automation;
vms: 
if get-from-text (old-default, 1, 3) = "VAX" then
desired-default
= vax-vms-new-default-directory-for-automation 
else if get-from-text (old-default, 1, 5) = "ALPHA"then
desired-default
= alpha-vms-new-default-directory-for-automation
else desired-default
= vms-new-default-directory-for-automation;
dos, win32: desired-default
= dos-or-win32-new-default-directory-for-automation;
end;
call g2-change-default-directory(desired-default);
actual-default = call g2-default-directory();
case (type-of-file-system) of
unix: expected-default 
= (actual-default = automounted-unix-new-default-directory-for-automation) 
or (actual-default = pseudo-automounted-unix-new-default-directory-for-automation)
or (actual-default = non-automounted-unix-new-default-directory-for-automation);
vms: expected-default
= (actual-default = alpha-vms-new-default-directory-for-automation)
or (actual-default = vax-vms-new-default-directory-for-automation)
or (actual-default = vms-new-default-directory-for-automation);
dos, win32: expected-default 
= (actual-default = dos-or-win32-new-default-directory-for-automation);
end;
call g2-change-default-directory(old-default);
if expected-default
then return true, ""
else return false, "bad new default dir @"[actual-default]@"";
end

