default-directory-test-internal () = (truth-value, text)
type-of-file-system: symbol;
sysproc-default: text;
expected-default: truth-value;
problem-text: text;
begin
sysproc-default = call g2-default-directory();
type-of-file-system = call g2-type-of-file-system ();
case (type-of-file-system) of
unix: expected-default 
= (sysproc-default = automounted-unix-default-directory-for-automation) 
or (sysproc-default = pseudo-automounted-unix-default-directory-for-automation) 
or (sysproc-default = non-automounted-unix-default-directory-for-automation)
or (sysproc-default = deletable-development-default-directory-for-testing);
vms: expected-default
= (sysproc-default = alpha-vms-default-directory-for-automation)
or (sysproc-default = vax-vms-default-directory-for-automation)
or (sysproc-default = vms-default-directory-for-automation);
dos, win32: expected-default 
= (sysproc-default = dos-or-win32-default-directory-for-automation);
end;
if expected-default
then return true, ""
else 
    if (find-next-substring-matching-pattern ("(/|\\)et-{a-z, 0-9}*(/|\\)g2(/|\\)test", sysproc-default, 1) /= "") then
        return true, ""
    else
        return false, "bad default dir @"[sysproc-default]@"";
end

