W = the subworkspace of example-directory-operations;
start test-subdirectories-in-directory();
wait until there exists a message M upon W 
such that ((the text of M = "tools") or
(the text of M = "src"))
checking every 1 second;
for M = each message upon W do
maybe-unexpected-filename = the text of M;
expected-file = call g2-directory-exists
  ("[directory-to-access][maybe-unexpected-filename]");
exit if not (expected-file)
end;
call clean-up-directory-examples-ws ();
if not(expected-file)
then return false, "unexpected file @"[maybe-unexpected-filename]@""
else return true, "";
end

