length-of-file-test-internal () = (truth-value, text)
S: class g2-stream;
actual-length: integer;
begin
S = call g2-open-file-for-write(file-with-attributes);
call g2-set-file-position(s, position-to-set-in-accessed-file-for-automation, true);
delete S;
S = call g2-open-file-for-read(file-with-attributes);
actual-length = call g2-length-of-file(S);
delete S;
call g2-delete-file(file-with-attributes);
if actual-length = position-to-set-in-accessed-file-for-automation
then return true, ""
else return false, 
"expected/got [position-to-set-in-accessed-file-for-automation]/[actual-length]"
end

