file-directory-list-to-string-test-internal () = (truth-value, text)
type-of-file-system: symbol;
actual-directory-string, desired-directory-string: text;
directory-list: class g2-list;
begin
type-of-file-system = call g2-type-of-file-system ();
case (type-of-file-system) of
unix: conclude that directory-to-convert-to-list 
= unix-file-directory-string-to-partition-for-automation;
vms: conclude that directory-to-convert-to-list 
= vms-file-directory-string-to-partition-for-automation;
dos, win32: conclude that directory-to-convert-to-list 
= dos-or-win32-file-directory-string-to-partition-for-automation;
end;
desired-directory-string = call g2-file-directory-string(directory-to-convert-to-list);
directory-list = call g2-file-directory-string-to-list (directory-to-convert-to-list);
actual-directory-string = call g2-file-directory-list-to-string(directory-list);
delete directory-list;
if desired-directory-string = actual-directory-string 
then return true, ""
else return false, 
"expected @"[desired-directory-string]@", got @"[actual-directory-string]@"";
end

