unix-filename-components-for-automation; end;
vms: begin filename-to-partition 
= vms-filename-to-partition-for-automation;
component-array =
vms-filename-components-for-automation; end;
dos, win32: begin filename-to-partition 
= dos-or-win32-filename-to-partition-for-automation;
component-array =
dos-or-win32-filename-components-for-automation; end;
end;
conclude that collectible-host-string = component-array[0];
conclude that collectible-device-string = component-array[1];
conclude that collectible-directory-string = component-array[2];
conclude that collectible-base-name-string = component-array[3];
conclude that collectible-extension-string = component-array[4];
conclude that collectible-version-string = component-array[5];
start test-collect-into-filestring ();
wait until the text of collected-filestring = "@"[filename-to-partition]@""
checking every 1 second;
for M = each message upon W do
change the text of M to ""; end;
return true;
end

