new-file-present, old-file-gone: truth-value;
begin
new-file-present, old-file-gone = call rename-file-test-internal ();
if new-file-present and old-file-gone {attempt #1} then begin
memory-before = call read-memory-usage-meter (false);
for counter = 1 to iterations-for-leak-exposure do
new-file-present, old-file-gone = call rename-file-test-internal ();
exit if not(new-file-present and old-file-gone); end;
if new-file-present and old-file-gone {attempt #n} then begin
memory-after = call read-memory-usage-meter (true);
if memory-before = memory-after
then call pass(node) 
else call fail(node, "memory leak ([memory-after - memory-before] bytes)");
end {attempt #n} 
else call fail(node, "attempt #[counter].  new/old [new-file-present]/[old-file-gone]");
end {attempt #1} 
else call fail(node, "attempt #1.  new/old [new-file-present]/[old-file-gone]");
end

rename-file-test-internal () = (truth-value, truth-value)
W: class kb-workspace;
S: class g2-stream;
new-file-exists, old-file-exists: truth-value;
begin
W = the subworkspace of example-access;
S = call g2-open-file-for-write(file-to-access-or-control);
delete S;
call test-rename-file ();
do in parallel until one completes
{branch #1}
wait for 10 seconds;
{branch #2}
repeat
new-file-exists = call g2-file-exists(new-name-for-file);
exit if new-file-exists;
wait for 1 second;
end;
end; {do in parallel}
old-file-exists = call g2-file-exists(file-to-access-or-control);
call g2-delete-file(new-name-for-file);
return new-file-exists, not (old-file-exists);
end

