do in parallel until one completes
{branch #1}
wait for 10 seconds;
{branch #2}
repeat
file-exists = call g2-file-exists(file-to-access-or-control);
exit if not(file-exists);
wait for 1 second;
end;
end; {do in parallel}
return true;
end

set-file-position-past-end-test (node: class benchmark-test)
memory-before, memory-after, counter: integer;
old-should: truth-value;
begin
old-should = should-set-file-position-past-end;
conclude that should-set-file-position-past-end is false;
call set-file-position-past-end-test-internal ();
memory-before = call read-memory-usage-meter (false);
for counter = 1 to iterations-for-leak-exposure do
call set-file-position-past-end-test-internal (); end;
memory-after = call read-memory-usage-meter (true);
if memory-before = memory-after
then call pass(node) 
else call fail(node, "memory leak ([memory-after - memory-before] bytes)");
conclude that should-set-file-position-past-end = old-should;
end

set-file-position-past-end-test-internal () = (truth-value)
W: class kb-workspace;
S: class g2-stream;
error-symbol: symbol; error-text: text;
begin
W = the subworkspace of example-access;
start test-open-for-write();
wait until there exists a g2-stream S upon W 
such that 
((the name-of-file of S = file-to-access-or-control
 or the name-of-file of S = upper-case-text (file-to-access-or-control))
and the access-direction of S is output
and the g2-stream-status of S is open-for-write)
checking every 1 second;
conclude that position-to-set-in-accessed-file 
= position-to-set-in-accessed-file-for-automation ;
change the text of get-position-message to "";
call test-get-position (S);
wait until (the text of get-position-message) 
= "position is [position-to-set-in-accessed-file-for-automation]"
checking every 1 second;
delete S;
call g2-delete-file(file-to-access-or-control);
return true;
end
on error (error-symbol, error-text)
delete S;
call g2-delete-file(file-to-access-or-control);
if is-contained-in-text("past end-or-file", error-text)
then return true
else return false;
end

