close-all-files-test-internal () = (truth-value)
W: class kb-workspace;
S1, S2, S3, S4: class g2-stream;
file-exists: truth-value;
begin
W = the subworkspace of example-access;
S1 = call g2-open-file-for-write("writable.txt");
transfer S1 to W;
S2 = call g2-open-file-for-write("readable.txt");
delete S2;
S2 = call g2-open-file-for-read("readable.txt");
transfer S2 to W;
S3 = call g2-open-file-for-write("apndable.txt");
delete S3;
S3 = call g2-open-file-for-append("apndable.txt", false);
transfer S3 to W;
S4 = call g2-open-file-for-write("rwable.txt");
delete S4;
S4 = call g2-open-file-for-read-and-write("rwable.txt");
transfer S4 to W;
start test-all-close();
wait until (the count of each g2-stream upon W) = 0
checking every 1 second;
call g2-delete-file("writable.txt");
call g2-delete-file("readable.txt");
call g2-delete-file("apndable.txt");
call g2-delete-file("rwable.txt");
return true;
end

