bytes-to-and-from-float-test (node: class benchmark-test)
memory-before, memory-after, counter: integer;
success: truth-value; 
problem: text;
begin
success, problem = call bytes-to-and-from-float-test-internal ();
if success {attempt #1} then begin
memory-before = call read-memory-usage-meter (false);
{jh, 1/11/95.  Since bytes-to-and-from-float-test-internal() picks floats at random, and since we've encountered problems on the NT machines after the first iteration, we want to iterate as many times as we did when we were checking for leaks.}
for counter = 1 to 10 {iterations-for-leak-exposure} do
success, problem = call bytes-to-and-from-float-test-internal ();
exit if not(success); end;
if success {attempt #n} then begin
memory-after = call read-memory-usage-meter (true);
if memory-before = memory-after
then call pass(node) 
else call fail(node, "memory leak ([memory-after - memory-before] bytes)");
end {attempt #n} else call fail(node, "attempt #[counter].  [problem]");
end {attempt #1} else call fail(node, "attempt #1.  [problem]");
end

bytes-to-and-from-float-test-internal () = (truth-value, text)
counter, counter2: integer;
S: class g2-stream;
b1, b2, b3, b4, b5, b6, b7, b8: integer;
i: integer = 0;
echoed-float: float;
problem-text: text;
begin
{leave the initial 0.0 in input-float-array -- a bte noire}
for counter = 1 to 10 do
change input-float-array[counter] = random(-1e-200, +1e+200);
end;
for counter = 1 to 10 do 
change input-float-array[counter + 10] = 0.0 + random(-500000000, +500000000);
end;
S = call g2-open-file-for-write(file-from-which-to-read-and-write);
for counter = 0 to 20 do {writing}
allow other processing;
b1, b2, b3, b4, b5, b6, b7, b8 = call g2-float-to-bytes(input-float-array[counter]);
call g2-write-byte(S, b1);
call g2-write-byte(S, b2); 
call g2-write-byte(S, b3); 
call g2-write-byte(S, b4); 
call g2-write-byte(S, b5); 
call g2-write-byte(S, b6); 
call g2-write-byte(S, b7); 
call g2-write-byte(S, b8);
change stored-byte-array [i] = b1;  i = i +1;
change stored-byte-array [i] = b2;  i = i +1; 
change stored-byte-array [i] = b3;  i = i +1; 
change stored-byte-array [i] = b4;  i = i +1; 
change stored-byte-array [i] = b5;  i = i +1; 
change stored-byte-array [i] = b6;  i = i +1; 
change stored-byte-array [i] = b6;  i = i +1; 
change stored-byte-array [i] = b7;  i = i +1; 
end; {writing}
delete S;
S = call g2-open-file-for-read(file-from-which-to-read-and-write);
for counter = 0 to 20 do {reading}
allow other processing;
b1 = call g2-read-byte(S);
b2 = call g2-read-byte(S);
b3 = call g2-read-byte(S); 
b4 = call g2-read-byte(S); 
b5 = call g2-read-byte(S); 
b6 = call g2-read-byte(S); 
b7 = call g2-read-byte(S); 
b8 = call g2-read-byte(S);
echoed-float = call g2-bytes-to-float(b1, b2, b3, b4, b5, b6, b7, b8);
if echoed-float /= input-float-array[counter]
then begin
delete S;
call g2-delete-file(file-from-which-to-read-and-write);
problem-text = 
"bad float [input-float-array[counter]] /= [echoed-float].  expected :";
for i = (8 * counter) to (8 * counter) + 8 do
problem-text = "[problem-text][stored-byte-array[i]]:";
end;
return false, "[problem-text], got :[b1]:[b2]:[b3]:[b4]:[b5]:[b6]:[b7]:[b8]:";
end;
end; {reading}
delete S;
call g2-delete-file(file-from-which-to-read-and-write);
return true, "";
end

