file-names-are-identical-test-internal () = (truth-value, text)
type-of-file-system: symbol;
success: truth-value; 
candidate-for-identity: text;
begin
type-of-file-system = call g2-type-of-file-system();
case (type-of-file-system) of
vms: candidate-for-identity = vms-possibly-identical-file-for-automation;
otherwise: candidate-for-identity = file-with-attributes;
end;
success = call g2-file-names-are-identical (file-with-attributes, candidate-for-identity);
if success
then return true, ""
else return false, 
"@[file-with-attributes]@" should be identical to @"[candidate-for-identity]@"";
end

read-em()
read-file: class g2-stream;
char: integer;
begin
change the text of read-byte-message to "";
read-file = call g2-open-file-for-read (file-from-which-to-read-and-write);
repeat 
allow other processing;
char = call g2-read-byte(read-file);
exit if the g2-stream-status of read-file is end-of-file-reached;
change the text of read-byte-message 
to "[the text of read-byte-message][char] ";
end;
delete read-file;
end

write-em()
counter: integer;
write-file: class g2-stream;
begin
change the text of write-byte-message to "";
write-file = call g2-open-file-for-write (file-from-which-to-read-and-write);
for counter = 0 to 255 do
allow other processing;
call g2-write-byte(write-file, counter);
change the text of write-byte-message 
to "[the text of write-byte-message][counter] ";
end;
delete write-file;
end
